import React, { useState, useRef, useCallback } from 'react';
import {
    DndContext,
    DragOverlay,
    closestCenter,
    KeyboardSensor,
    PointerSensor,
    useSensor,
    useSensors,
    MeasuringStrategy,
} from '@dnd-kit/core';
import {
    arrayMove,
    SortableContext,
    sortableKeyboardCoordinates,
    verticalListSortingStrategy,
} from '@dnd-kit/sortable';
import {
    useSortable,
    CSS,
} from '@dnd-kit/sortable';
import { restrictToVerticalAxis } from '@dnd-kit/modifiers';

import Toolbar from "./create-page/Toolbar";
import ComponentLibrary from "./create-page/ComponentLibrary";
import Canvas from "./create-page/Canvas";
import PropertiesPanel from "./create-page/PropertiesPanel";

// Draggable Component for Component Library
const DraggableComponent = ({ component, children, ...props }) => {
    const {
        attributes,
        listeners,
        setNodeRef,
        transform,
        isDragging,
    } = useSortable({
        id: component.id,
        data: {
            type: 'library-component',
            component,
        },
    });

    const style = {
        transform: CSS.Transform.toString(transform),
        opacity: isDragging ? 0.5 : 1,
    };

    return (
        <div
            ref={setNodeRef}
            style={style}
            {...attributes}
            {...listeners}
            {...props}
        >
            {children}
        </div>
    );
};

// Sortable Page Element
const SortablePageElement = ({ element, children, isSelected, onSelect }) => {
    const {
        attributes,
        listeners,
        setNodeRef,
        transform,
        transition,
        isDragging,
    } = useSortable({
        id: element.id,
        data: {
            type: 'page-element',
            element,
        },
    });

    const style = {
        transform: CSS.Transform.toString(transform),
        transition,
        opacity: isDragging ? 0.5 : 1,
        zIndex: isDragging ? 1000 : 'auto',
    };

    return (
        <div
            ref={setNodeRef}
            style={style}
            className={`relative group ${isSelected ? 'ring-2 ring-blue-500' : ''} ${
                isDragging ? 'cursor-grabbing' : 'cursor-pointer'
            }`}
            onClick={(e) => {
                e.stopPropagation();
                onSelect(element);
            }}
        >
            {/* Drag Handle */}
            <div
                {...attributes}
                {...listeners}
                className={`absolute -left-8 top-2 opacity-0 group-hover:opacity-100 transition-opacity
                    bg-gray-600 text-white p-1 rounded cursor-grab active:cursor-grabbing z-10
                    hover:bg-gray-700`}
                title="Drag to reorder"
            >
                <svg className="w-3 h-3" fill="currentColor" viewBox="0 0 20 20">
                    <path d="M7 2a2 2 0 1 1 0 4 2 2 0 0 1 0-4zM7 8a2 2 0 1 1 0 4 2 2 0 0 1 0-4zM7 14a2 2 0 1 1 0 4 2 2 0 0 1 0-4zM13 2a2 2 0 1 1 0 4 2 2 0 0 1 0-4zM13 8a2 2 0 1 1 0 4 2 2 0 0 1 0-4zM13 14a2 2 0 1 1 0 4 2 2 0 0 1 0-4z"/>
                </svg>
            </div>

            {/* Selection Indicator */}
            {isSelected && (
                <div className="absolute inset-0 ring-2 ring-blue-500 ring-opacity-50 pointer-events-none rounded" />
            )}

            {children}
        </div>
    );
};

// Drop Zone Indicator
const DropZone = ({ isOver, canDrop }) => {
    if (!isOver && !canDrop) return null;

    return (
        <div className={`h-2 mx-4 rounded transition-all duration-200 ${
            isOver
                ? 'bg-blue-500 shadow-lg'
                : canDrop
                    ? 'bg-blue-200 border-2 border-dashed border-blue-300'
                    : 'bg-gray-200'
        }`} />
    );
};

const CreateLandingPage = () => {
    const [pageStructure, setPageStructure] = useState([]);
    const [selectedElement, setSelectedElement] = useState(null);
    const [leftSidebarCollapsed, setLeftSidebarCollapsed] = useState(false);
    const [rightSidebarCollapsed, setRightSidebarCollapsed] = useState(false);
    const [quickEditElement, setQuickEditElement] = useState(null);
    const [quickEditPosition, setQuickEditPosition] = useState(null);
    const [viewMode, setViewMode] = useState('desktop'); // desktop, tablet, mobile, adaptive
    const [zoomLevel, setZoomLevel] = useState(100);
    const [activeId, setActiveId] = useState(null);
    const [draggedElement, setDraggedElement] = useState(null);

    // Configure sensors for better touch/mouse support
    const sensors = useSensors(
        useSensor(PointerSensor, {
            activationConstraint: {
                distance: 8,
            },
        }),
        useSensor(KeyboardSensor, {
            coordinateGetter: sortableKeyboardCoordinates,
        })
    );

    // Enhanced measuring strategy for better performance
    const measuring = {
        droppable: {
            strategy: MeasuringStrategy.Always,
        },
    };

    const handleDragStart = (event) => {
        const { active } = event;
        setActiveId(active.id);

        if (active.data.current?.type === 'library-component') {
            setDraggedElement(active.data.current.component);
        } else if (active.data.current?.type === 'page-element') {
            setDraggedElement(active.data.current.element);
        }
    };

    const handleDragEnd = (event) => {
        const { active, over } = event;
        setActiveId(null);
        setDraggedElement(null);

        if (!over) return;

        // Handle dropping from component library to canvas
        if (active.data.current?.type === 'library-component' && over.id === 'canvas-drop-zone') {
            const component = active.data.current.component;
            const newComponent = {
                ...component,
                id: `${component.id}-${Date.now()}`
            };
            setPageStructure(prev => [...prev, newComponent]);
            setQuickEditElement(null);
            setQuickEditPosition(null);
            return;
        }

        // Handle reordering page elements
        if (active.data.current?.type === 'page-element' && over.data.current?.type === 'page-element') {
            const activeIndex = pageStructure.findIndex(item => item.id === active.id);
            const overIndex = pageStructure.findIndex(item => item.id === over.id);

            if (activeIndex !== overIndex) {
                setPageStructure(prev => arrayMove(prev, activeIndex, overIndex));
            }
        }

        // Handle inserting at specific position
        if (active.data.current?.type === 'page-element' && over.id.startsWith('drop-zone-')) {
            const dropIndex = parseInt(over.id.split('-')[2]);
            const activeIndex = pageStructure.findIndex(item => item.id === active.id);

            if (activeIndex !== -1 && activeIndex !== dropIndex) {
                setPageStructure(prev => arrayMove(prev, activeIndex, dropIndex));
            }
        }
    };

    const handleDragOver = (event) => {
        // Optional: Handle drag over events for visual feedback
        // This can be used for more complex drop zone logic
    };

    const handleSelectElement = (element) => {
        setSelectedElement(element);
        setQuickEditElement(null);
        setQuickEditPosition(null);
    };

    const handleUpdateElement = (updatedElement) => {
        setPageStructure(prev =>
            prev.map(el => el.id === updatedElement.id ? updatedElement : el)
        );
        setSelectedElement(updatedElement);
    };

    const handleQuickEdit = (element, position) => {
        setQuickEditElement(element);
        setQuickEditPosition(position);
    };

    const handleCloseQuickEdit = () => {
        setQuickEditElement(null);
        setQuickEditPosition(null);
    };

    const handleDuplicateElement = (element) => {
        if (!element) return;
        const duplicated = {
            ...element,
            id: `${element.id}-copy-${Date.now()}`
        };
        const elementIndex = pageStructure.findIndex(el => el.id === element.id);
        const newStructure = [...pageStructure];
        newStructure.splice(elementIndex + 1, 0, duplicated);
        setPageStructure(newStructure);
        setQuickEditElement(null);
        setQuickEditPosition(null);
    };

    const handleDeleteElement = (element) => {
        if (!element) return;
        setPageStructure(prev => prev.filter(el => el.id !== element.id));
        if (selectedElement?.id === element.id) {
            setSelectedElement(null);
        }
        setQuickEditElement(null);
        setQuickEditPosition(null);
    };

    const handleZoom = (direction) => {
        const newZoom = direction === 'in'
            ? Math.min(200, zoomLevel + 10)
            : Math.max(50, zoomLevel - 10);
        setZoomLevel(newZoom);
    };

    const handleGenerateCode = async () => {
        const pageConfig = {
            components: pageStructure.map(comp => comp.json),
            meta: {
                title: "Generated Landing Page",
                description: "Page generated from JSON"
            },
            viewMode,
            zoomLevel
        };

        console.log('Generated page config:', pageConfig);
        alert('Code generated! Check console for details.');
    };

    const handlePreview = () => {
        const previewData = encodeURIComponent(JSON.stringify({
            pageStructure,
            viewMode
        }));
        window.open(`/preview?data=${previewData}`, '_blank');
    };

    const handleSave = () => {
        const saveData = {
            pageStructure,
            viewMode,
            zoomLevel,
            savedAt: new Date().toISOString()
        };
        localStorage.setItem('landing-page-builder', JSON.stringify(saveData));
        alert('Đã lưu trang thành công!');
    };

    // Auto-save functionality
    React.useEffect(() => {
        const autoSave = setTimeout(() => {
            if (pageStructure.length > 0) {
                const autoSaveData = {
                    pageStructure,
                    viewMode,
                    zoomLevel,
                    autoSavedAt: new Date().toISOString()
                };
                localStorage.setItem('landing-page-builder-autosave', JSON.stringify(autoSaveData));
            }
        }, 30000); // Auto-save every 30 seconds

        return () => clearTimeout(autoSave);
    }, [pageStructure, viewMode, zoomLevel]);

    return (
        <DndContext
            sensors={sensors}
            collisionDetection={closestCenter}
            onDragStart={handleDragStart}
            onDragEnd={handleDragEnd}
            onDragOver={handleDragOver}
            measuring={measuring}
            modifiers={[restrictToVerticalAxis]}
        >
            <div className="h-screen flex flex-col bg-gray-50">
                <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0/css/all.min.css" />

                <Toolbar
                    onSave={handleSave}
                    onPreview={handlePreview}
                    onGenerateCode={handleGenerateCode}
                    viewMode={viewMode}
                    onViewModeChange={setViewMode}
                />

                <div className="flex-1 flex overflow-hidden">
                    <ComponentLibrary
                        onDragStart={() => {}} // DnD Kit handles this now
                        isCollapsed={leftSidebarCollapsed}
                        onToggle={() => setLeftSidebarCollapsed(!leftSidebarCollapsed)}
                        DraggableComponent={DraggableComponent} // Pass the draggable wrapper
                    />

                    <div className="flex-1 flex flex-col overflow-hidden">
                        {/* Canvas Area */}
                        <SortableContext
                            items={pageStructure.map(item => item.id)}
                            strategy={verticalListSortingStrategy}
                        >
                            <Canvas
                                pageStructure={pageStructure}
                                onDrop={() => {}} // DnD Kit handles this now
                                selectedElement={selectedElement}
                                onSelectElement={handleSelectElement}
                                quickEditElement={quickEditElement}
                                quickEditPosition={quickEditPosition}
                                onQuickEdit={handleQuickEdit}
                                onCloseQuickEdit={handleCloseQuickEdit}
                                onDuplicateElement={handleDuplicateElement}
                                onDeleteElement={handleDeleteElement}
                                onMoveElement={() => {}} // DnD Kit handles this now
                                viewMode={viewMode}
                                SortablePageElement={SortablePageElement} // Pass the sortable wrapper
                                DropZone={DropZone} // Pass the drop zone component
                            />
                        </SortableContext>
                    </div>

                    <PropertiesPanel
                        selectedElement={selectedElement}
                        onUpdateElement={handleUpdateElement}
                        isCollapsed={rightSidebarCollapsed}
                        onToggle={() => setRightSidebarCollapsed(!rightSidebarCollapsed)}
                    />
                </div>

                {/* Bottom Status Bar with View Mode Controls */}
                <div className="h-10 bg-white border-t border-gray-200 flex items-center justify-between px-4">
                    <div className="flex items-center gap-4 text-sm text-gray-600">
                        <span>Elements: {pageStructure.length}</span>
                        <span>|</span>
                        <span>Selected: {selectedElement ? selectedElement.json?.type || 'Unknown' : 'None'}</span>
                        {activeId && (
                            <>
                                <span>|</span>
                                <span className="text-blue-600">Dragging: {activeId}</span>
                            </>
                        )}
                    </div>

                    <div className="flex items-center gap-2">
                        <span className="text-sm text-gray-600 mr-2">View:</span>
                        <div className="flex border border-gray-300 rounded-md overflow-hidden">
                            <button
                                onClick={() => setViewMode('desktop')}
                                className={`px-3 py-1 text-xs font-medium transition-colors ${
                                    viewMode === 'desktop'
                                        ? 'bg-blue-600 text-white'
                                        : 'bg-white text-gray-600 hover:bg-gray-50'
                                }`}
                            >
                                Responsive
                            </button>
                            <button
                                onClick={() => setViewMode('mobile')}
                                className={`px-3 py-1 text-xs font-medium transition-colors ${
                                    viewMode === 'mobile'
                                        ? 'bg-blue-600 text-white'
                                        : 'bg-white text-gray-600 hover:bg-gray-50'
                                }`}
                            >
                                Mobile Only
                            </button>
                            <button
                                onClick={() => setViewMode('adaptive')}
                                className={`px-3 py-1 text-xs font-medium transition-colors ${
                                    viewMode === 'adaptive'
                                        ? 'bg-blue-600 text-white'
                                        : 'bg-white text-gray-600 hover:bg-gray-50'
                                }`}
                            >
                                Adaptive
                            </button>
                        </div>

                        <div className="ml-4 flex items-center gap-1">
                            <button
                                className="p-1 hover:bg-gray-100 rounded text-gray-500 transition-colors"
                                title="Zoom Out"
                                onClick={() => handleZoom('out')}
                            >
                                <i className="fas fa-search-minus text-xs"></i>
                            </button>
                            <span className="text-xs text-gray-500 px-2">{zoomLevel}%</span>
                            <button
                                className="p-1 hover:bg-gray-100 rounded text-gray-500 transition-colors"
                                title="Zoom In"
                                onClick={() => handleZoom('in')}
                            >
                                <i className="fas fa-search-plus text-xs"></i>
                            </button>
                        </div>
                    </div>
                </div>

                {/* Drag Overlay for better visual feedback */}
                <DragOverlay dropAnimation={null}>
                    {activeId ? (
                        <div className="bg-white shadow-2xl border-2 border-blue-500 rounded-lg p-4 opacity-90 transform rotate-3">
                            <div className="text-sm font-medium text-gray-700">
                                {draggedElement?.json?.type || draggedElement?.type || 'Element'}
                            </div>
                            <div className="text-xs text-gray-500 mt-1">
                                Dragging...
                            </div>
                        </div>
                    ) : null}
                </DragOverlay>
            </div>
        </DndContext>
    );
};

export default CreateLandingPage;
