import React, { useState, useMemo, useCallback, useEffect } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { useHotkeys } from 'react-hotkeys-hook';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Toolbar from './create-page/Toolbar';
import ComponentLibrary from './create-page/ComponentLibrary';
import Canvas from './create-page/Canvas';
import PropertiesPanel from './create-page/PropertiesPanel';
import '../styles/CreateLanding.css';

const ItemTypes = {
    SECTION: 'section',
    ELEMENT: 'element',
    EXISTING_ELEMENT: 'existingElement',
};

const CreateLandingPage = () => {
    const [pageStructure, setPageStructure] = useState([]); // Lưu trữ các section
    const [selectedElement, setSelectedElement] = useState(null);
    const [leftSidebarCollapsed, setLeftSidebarCollapsed] = useState(false);
    const [rightSidebarCollapsed, setRightSidebarCollapsed] = useState(false);
    const [quickEditElement, setQuickEditElement] = useState(null);
    const [quickEditPosition, setQuickEditPosition] = useState(null);
    const [viewMode, setViewMode] = useState('desktop');
    const [zoomLevel, setZoomLevel] = useState(100);
    const [history, setHistory] = useState([[]]);
    const [historyIndex, setHistoryIndex] = useState(0);

    const memoizedPageStructure = useMemo(() => pageStructure, [pageStructure]);
    const updatePosition = useCallback((elementId, sectionId, newPosition) => {
        setPageStructure((prev) => {
            const newStructure = prev.map((section) => {
                if (section.id === sectionId) {
                    return {
                        ...section,
                        elements: section.elements.map((el) =>
                            el.id === elementId
                                ? {
                                    ...el,
                                    json: {
                                        ...el.json,
                                        styles: {
                                            ...el.json.styles,
                                            position: newPosition,
                                        },
                                    },
                                }
                                : el
                        ),
                    };
                }
                return section;
            });
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
            setHistoryIndex((prev) => prev + 1);
            return newStructure;
        });
        toast.success('Đã cập nhật vị trí phần tử!');
    }, [historyIndex]);
    const addSection = useCallback((sectionData, index) => {
        const newSection = {
            ...sectionData,
            id: `${sectionData.id}-${Date.now()}`,
            elements: [], // Mỗi section có danh sách các phần tử
            json: {
                ...sectionData.json,
                styles: {
                    ...sectionData.json.styles,
                    position: { x: 50, y: 50 },
                },
            },
        };
        setPageStructure((prev) => {
            const newStructure = [...prev];
            newStructure.splice(index, 0, newSection);
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
            setHistoryIndex((prev) => prev + 1);
            return newStructure;
        });
        toast.success('Đã thêm section!');
    }, [historyIndex]);

    const addElementToSection = useCallback((sectionId, elementData, index) => {
        const newElement = {
            ...elementData,
            id: `${elementData.id}-${Date.now()}`,
            json: {
                ...elementData.json,
                styles: {
                    ...elementData.json.styles,
                    position: { x: 50, y: 50 },
                },
            },
        };
        setPageStructure((prev) => {
            const newStructure = prev.map((section) =>
                section.id === sectionId
                    ? {
                        ...section,
                        elements: [...section.elements.slice(0, index), newElement, ...section.elements.slice(index)],
                    }
                    : section
            );
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
            setHistoryIndex((prev) => prev + 1);
            return newStructure;
        });
        toast.success('Đã thêm phần tử vào section!');
    }, [historyIndex]);

    const moveElement = useCallback((sectionId, elementId, newIndex) => {
        setPageStructure((prev) => {
            const newStructure = prev.map((section) => {
                if (section.id === sectionId) {
                    const currentIndex = section.elements.findIndex((el) => el.id === elementId);
                    if (currentIndex === -1 || currentIndex === newIndex) return section;
                    const newElements = [...section.elements];
                    const [movedElement] = newElements.splice(currentIndex, 1);
                    newElements.splice(newIndex, 0, movedElement);
                    return { ...section, elements: newElements };
                }
                return section;
            });
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
            setHistoryIndex((prev) => prev + 1);
            return newStructure;
        });
        toast.info('Đã di chuyển phần tử!');
    }, [historyIndex]);

    const undo = useCallback(() => {
        if (historyIndex > 0) {
            setHistoryIndex((prev) => prev - 1);
            setPageStructure([...history[historyIndex - 1]]);
            toast.info('Đã hoàn tác!');
        }
    }, [history, historyIndex]);

    const redo = useCallback(() => {
        if (historyIndex < history.length - 1) {
            setHistoryIndex((prev) => prev + 1);
            setPageStructure([...history[historyIndex + 1]]);
            toast.info('Đã khôi phục!');
        }
    }, [history, historyIndex]);

    const handleSelectElement = useCallback((element) => {
        setSelectedElement(element);
        setQuickEditElement(null);
        setQuickEditPosition(null);
    }, []);

    const handleUpdateElement = useCallback((updatedElement) => {
        setPageStructure((prev) => {
            const newStructure = prev.map((section) => ({
                ...section,
                elements: section.elements.map((el) => (el.id === updatedElement.id ? updatedElement : el)),
            }));
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
            setHistoryIndex((prev) => prev + 1);
            return newStructure;
        });
        setSelectedElement(updatedElement);
        toast.success('Đã cập nhật phần tử!');
    }, [historyIndex]);

    const handleQuickEdit = useCallback((element, position) => {
        setQuickEditElement(element);
        setQuickEditPosition(position);
    }, []);

    const handleCloseQuickEdit = useCallback(() => {
        setQuickEditElement(null);
        setQuickEditPosition(null);
    }, []);

    const handleDuplicateElement = useCallback((element) => {
        if (!element) return;
        const duplicated = {
            ...element,
            id: `${element.id}-copy-${Date.now()}`,
        };
        setPageStructure((prev) => {
            const newStructure = prev.map((section) => {
                if (section.elements.some((el) => el.id === element.id)) {
                    const index = section.elements.findIndex((el) => el.id === element.id);
                    return {
                        ...section,
                        elements: [...section.elements.slice(0, index + 1), duplicated, ...section.elements.slice(index + 1)],
                    };
                }
                return section;
            });
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
            setHistoryIndex((prev) => prev + 1);
            return newStructure;
        });
        toast.success('Đã sao chép phần tử!');
    }, [historyIndex]);

    const handleDeleteElement = useCallback((element) => {
        if (!element) return;
        setPageStructure((prev) => {
            const newStructure = prev.map((section) => ({
                ...section,
                elements: section.elements.filter((el) => el.id !== element.id),
            }));
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
            setHistoryIndex((prev) => prev + 1);
            return newStructure;
        });
        if (selectedElement?.id === element.id) {
            setSelectedElement(null);
        }
        setQuickEditElement(null);
        setQuickEditPosition(null);
        toast.success('Đã xóa phần tử!');
    }, [selectedElement, historyIndex]);

    const handleMoveElement = useCallback((element, direction) => {
        if (!element) return;
        setPageStructure((prev) => {
            const newStructure = prev.map((section) => {
                if (section.elements.some((el) => el.id === element.id)) {
                    const currentIndex = section.elements.findIndex((el) => el.id === element.id);
                    if (currentIndex === -1) return section;
                    const newIndex =
                        direction === 'up'
                            ? Math.max(0, currentIndex - 1)
                            : Math.min(section.elements.length - 1, currentIndex + 1);
                    if (newIndex === currentIndex) return section;
                    const newElements = [...section.elements];
                    const [movedElement] = newElements.splice(currentIndex, 1);
                    newElements.splice(newIndex, 0, movedElement);
                    return { ...section, elements: newElements };
                }
                return section;
            });
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
            setHistoryIndex((prev) => prev + 1);
            return newStructure;
        });
        toast.info('Đã di chuyển phần tử!');
    }, [historyIndex]);

    const handleZoom = useCallback((direction) => {
        const newZoom = direction === 'in' ? Math.min(200, zoomLevel + 10) : Math.max(50, zoomLevel - 10);
        setZoomLevel(newZoom);
    }, [zoomLevel]);

    const handleGenerateCode = useCallback(() => {
        const pageConfig = {
            sections: pageStructure.map((section) => ({
                ...section.json,
                elements: section.elements.map((el) => el.json),
            })),
            meta: { title: 'Generated Landing Page', description: 'Page generated from JSON' },
            viewMode,
            zoomLevel,
        };
        console.log('Generated page config:', pageConfig);
        toast.success('Đã tạo mã! Kiểm tra console để xem chi tiết.');
    }, [pageStructure, viewMode, zoomLevel]);

    const handlePreview = useCallback(() => {
        const previewData = encodeURIComponent(JSON.stringify({ pageStructure, viewMode }));
        window.open(`/preview?data=${previewData}`, '_blank');
    }, [pageStructure, viewMode]);

    const handleSave = useCallback(() => {
        try {
            const saveData = { pageStructure, viewMode, zoomLevel, savedAt: new Date().toISOString() };
            localStorage.setItem('landing-page-builder', JSON.stringify(saveData));
            toast.success('Đã lưu trang thành công!');
        } catch (error) {
            console.error('Lỗi khi lưu:', error);
            toast.error('Lỗi khi lưu trang. Vui lòng thử lại.');
        }
    }, [pageStructure, viewMode, zoomLevel]);

    useEffect(() => {
        const autoSave = setTimeout(() => {
            if (pageStructure.length > 0) {
                const autoSaveData = {
                    pageStructure,
                    viewMode,
                    zoomLevel,
                    autoSavedAt: new Date().toISOString(),
                };
                try {
                    localStorage.setItem('landing-page-builder-autosave', JSON.stringify(autoSaveData));
                    toast.info('Đã tự động lưu!');
                } catch (error) {
                    console.error('Lỗi khi tự động lưu:', error);
                }
            }
        }, 30000);
        return () => clearTimeout(autoSave);
    }, [pageStructure, viewMode, zoomLevel]);

    useHotkeys('ctrl+s', handleSave, [handleSave]);
    useHotkeys('ctrl+z', undo, [undo]);
    useHotkeys('ctrl+shift+z', redo, [redo]);

    return (
        <DndProvider backend={HTML5Backend}>
            <div className="lpb-container">
                <link
                    rel="stylesheet"
                    href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.1/css/all.min.css"
                    crossOrigin="anonymous"
                    referrerPolicy="no-referrer"
                />
                <ToastContainer
                    position="top-right"
                    autoClose={3000}
                    hideProgressBar
                    className="lpb-toast"
                />
                <Toolbar
                    onSave={handleSave}
                    onPreview={handlePreview}
                    onGenerateCode={handleGenerateCode}
                    viewMode={viewMode}
                    onViewModeChange={setViewMode}
                    onUndo={undo}
                    onRedo={redo}
                    canUndo={historyIndex > 0}
                    canRedo={historyIndex < history.length - 1}
                />
                <div className="lpb-main-content">
                    <div className={`lpb-sidebar-left ${leftSidebarCollapsed ? 'lpb-sidebar-collapsed' : ''}`}>
                        <ComponentLibrary
                            isCollapsed={leftSidebarCollapsed}
                            onToggle={() => setLeftSidebarCollapsed(!leftSidebarCollapsed)}
                            addSection={addSection}
                            addElement={addElementToSection}
                        />
                    </div>
                    <div className="lpb-canvas-container">
                        <div className="lpb-canvas-wrapper">
                            <Canvas
                                pageStructure={memoizedPageStructure}
                                selectedElement={selectedElement}
                                onSelectElement={handleSelectElement}
                                quickEditElement={quickEditElement}
                                quickEditPosition={quickEditPosition}
                                onQuickEdit={handleQuickEdit}
                                onCloseQuickEdit={handleCloseQuickEdit}
                                onDuplicateElement={handleDuplicateElement}
                                onDeleteElement={handleDeleteElement}
                                onMoveElement={handleMoveElement}
                                viewMode={viewMode}
                                addSection={addSection}
                                addElement={addElementToSection}
                                moveElement={moveElement}
                                ItemTypes={ItemTypes}
                                zoomLevel={zoomLevel}
                                setPageStructure={setPageStructure}
                            />
                        </div>
                    </div>
                    <div className={`lpb-sidebar-right ${rightSidebarCollapsed ? 'lpb-sidebar-collapsed' : ''}`}>
                        <PropertiesPanel
                            selectedElement={selectedElement}
                            onUpdateElement={handleUpdateElement}
                            isCollapsed={rightSidebarCollapsed}
                            onToggle={() => setRightSidebarCollapsed(!rightSidebarCollapsed)}
                        />
                    </div>
                </div>
                <div className="lpb-status-bar">
                    <div className="lpb-status-info">
                        <span>Sections: {pageStructure.length}</span>
                        <span>|</span>
                        <span>Selected: {selectedElement ? selectedElement.json?.type || 'None' : 'None'}</span>
                    </div>
                    <div className="lpb-status-controls">
                        <div className="lpb-view-mode">
                            <span>View:</span>
                            <div className="lpb-view-buttons">
                                {['desktop', 'tablet', 'mobile', 'adaptive'].map((mode) => (
                                    <button
                                        key={mode}
                                        onClick={() => setViewMode(mode)}
                                        className={`lpb-view-button ${viewMode === mode ? 'lpb-view-button-active' : ''}`}
                                    >
                                        {mode.charAt(0).toUpperCase() + mode.slice(1)}
                                    </button>
                                ))}
                            </div>
                        </div>
                        <div className="lpb-zoom-controls">
                            <button className="lpb-zoom-button" title="Zoom Out" onClick={() => handleZoom('out')}>
                                <i className="fas fa-minus"></i>
                            </button>
                            <input
                                type="range"
                                min="50"
                                max="200"
                                value={zoomLevel}
                                onChange={(e) => setZoomLevel(Number(e.target.value))}
                                className="lpb-zoom-slider"
                            />
                            <span className="lpb-zoom-value">{zoomLevel}%</span>
                            <button className="lpb-zoom-button" title="Zoom In" onClick={() => handleZoom('in')}>
                                <i className="fas fa-plus"></i>
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </DndProvider>
    );
};

export default CreateLandingPage;
