import React, { useState, useCallback, useMemo } from 'react';
import { useDrag } from 'react-dnd';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import EXTENDED_LIBRARY from '../../constants/ExtendedLibrary';

const ItemTypes = {
    SECTION: 'section',
    ELEMENT: 'element',
};

const DraggableComponent = React.memo(({ item, isSection = false, onDrag, onDragEnd }) => {
    const dragType = isSection ? ItemTypes.SECTION : ItemTypes.ELEMENT;

    const [{ isDragging }, drag] = useDrag(() => ({
        type: dragType,
        item: { componentData: item },
        collect: (monitor) => ({
            isDragging: !!monitor.isDragging(),
        }),
    }));

    const renderPreview = () => {
        const { type = 'unknown', content, styles = {} } = item.json || {};
        const styleProps = {
            ...styles,
            opacity: isDragging ? 0.5 : 1,
            margin: '5px',
            maxWidth: '100%',
            boxSizing: 'border-box',
            padding: '10px',
            border: '1px solid #ddd',
            borderRadius: '5px',
        };

        const getTemplateClass = () => {
            switch (type) {
                case 'form':
                case 'contact-form':
                    return 'lpb-template-form';
                case 'gallery':
                case 'carousel':
                    return 'lpb-template-gallery';
                case 'pricing':
                    return 'lpb-template-pricing';
                case 'testimonials':
                    return 'lpb-template-testimonials';
                case 'faq':
                    return 'lpb-template-faq';
                default:
                    return '';
            }
        };

        switch (type) {
            case 'section':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        <h3>{content?.title || 'Section'}</h3>
                        {content?.subtitle && <p>{content.subtitle}</p>}
                    </div>
                );
            case 'heading':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        <h2>{content || 'Tiêu đề'}</h2>
                    </div>
                );
            case 'paragraph':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        <p>{content || 'Đoạn văn'}</p>
                    </div>
                );
            case 'button':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        <button style={styles}>{content || 'Nút'}</button>
                    </div>
                );
            case 'button-group':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        {(content || []).map((btn, index) => (
                            <button key={index} style={{ ...styles, marginRight: index < content.length - 1 ? '10px' : 0 }}>
                                {btn || `Nút ${index + 1}`}
                            </button>
                        ))}
                    </div>
                );
            case 'form':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        <form>
                            {(content?.fields || []).map((field, index) => (
                                <input
                                    key={index}
                                    type="text"
                                    placeholder={field || `Trường ${index + 1}`}
                                    style={{ marginBottom: '8px', width: '100%', ...styles }}
                                />
                            ))}
                            <button type="submit" style={{ ...styles, marginTop: '8px' }}>Submit</button>
                        </form>
                    </div>
                );
            case 'contact-form':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        <form>
                            {(content?.fields || []).map((field, index) => (
                                <div key={index} style={{ marginBottom: '8px' }}>
                                    {field.type === 'textarea' ? (
                                        <textarea placeholder={field.placeholder} style={{ width: '100%', ...styles }} />
                                    ) : (
                                        <input type={field.type} placeholder={field.placeholder} style={{ width: '100%', ...styles }} />
                                    )}
                                </div>
                            ))}
                            <button type="submit" style={{ ...styles, marginTop: '8px' }}>Send</button>
                        </form>
                    </div>
                );
            case 'image':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        <img src={content || 'https://via.placeholder.com/150'} alt={item.name || 'Image'} style={{ ...styles, maxWidth: '100%', height: 'auto' }} onError={(e) => (e.target.src = '/fallback-image.jpg')} />
                    </div>
                );
            case 'gallery':
            case 'carousel':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        {(content || []).map((src, index) => (
                            <img
                                key={index}
                                src={src || 'https://via.placeholder.com/100'}
                                alt={`Gallery ${index}`}
                                style={{ width: '100px', marginRight: '8px', ...styles }}
                                onError={(e) => (e.target.src = '/fallback-image.jpg')}
                            />
                        ))}
                    </div>
                );
            case 'product-list':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        {(content || []).map((prod, index) => (
                            <div key={index} style={{ marginBottom: '10px', ...styles }}>
                                {prod?.title || `Sản phẩm ${index + 1}`}
                            </div>
                        ))}
                    </div>
                );
            case 'popup':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        <div>{content || 'Popup'}</div>
                    </div>
                );
            case 'hero':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        <h1>{content?.title || 'Hero Title'}</h1>
                        {content?.subtitle && <p>{content.subtitle}</p>}
                        {content?.image && <img src={content.image || 'https://via.placeholder.com/300x200'} alt="Hero" style={{ width: '100%', height: 'auto' }} />}
                    </div>
                );
            case 'testimonials':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        {(content || []).map((testimonial, index) => (
                            <div key={index} style={{ border: '1px solid #ddd', padding: '10px', marginBottom: '5px' }}>
                                <p>{testimonial.quote || 'Great product!'}</p>
                                <small>{testimonial.author || 'Anonymous'}</small>
                            </div>
                        ))}
                    </div>
                );
            case 'pricing':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        {(content || []).map((plan, index) => (
                            <div key={index} style={{ border: '1px solid #ccc', padding: '10px', textAlign: 'center' }}>
                                <h3>{plan.plan || 'Plan'}</h3>
                                <p>{plan.price || '$0/month'}</p>
                                <ul style={{ listStyle: 'none', padding: 0 }}>
                                    {(plan.features || []).map((feature, i) => (
                                        <li key={i}>{feature}</li>
                                    ))}
                                </ul>
                            </div>
                        ))}
                    </div>
                );
            case 'faq':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        {(content || []).map((item, index) => (
                            <div key={index} style={{ marginBottom: '5px' }}>
                                <strong>{item.question || 'Question?'}</strong>
                                <p>{item.answer || 'Answer'}</p>
                            </div>
                        ))}
                    </div>
                );
            case 'video':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        <iframe
                            src={content || 'https://www.youtube.com/embed/dQw4w9WgXcQ'}
                            style={{ width: '100%', height: '150px', border: 'none' }}
                            title="Preview Video"
                            allowFullScreen
                        />
                    </div>
                );
            case 'features':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        {(content || []).map((feature, index) => (
                            <div key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '5px' }}>
                                <i className={feature.icon || 'fas fa-check'} style={{ marginRight: '10px' }} />
                                <div>
                                    <strong>{feature.title || 'Feature'}</strong>
                                    <p>{feature.description || 'Description'}</p>
                                </div>
                            </div>
                        ))}
                    </div>
                );
            case 'social-proof':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        {content?.stats && (
                            <div>
                                {(content.stats || []).map((stat, index) => (
                                    <span key={index} style={{ marginRight: '10px' }}>{stat}</span>
                                ))}
                            </div>
                        )}
                        {content?.logos && (
                            <div style={{ display: 'flex', gap: '5px', marginTop: '5px' }}>
                                {(content.logos || []).map((logo, index) => (
                                    <img key={index} src={logo || 'https://via.placeholder.com/80x40'} alt={`Logo ${index}`} style={{ height: '30px' }} />
                                ))}
                            </div>
                        )}
                    </div>
                );
            case 'benefits':
                return (
                    <ul ref={drag} style={{ ...styleProps, listStyle: 'none', padding: 0 }} className={`lpb-template-item ${getTemplateClass()}`}>
                        {(content || []).map((benefit, index) => (
                            <li key={index} style={{ marginBottom: '5px' }}>{benefit || 'Benefit'}</li>
                        ))}
                    </ul>
                );
            case 'footer':
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        <div>
                            {(content?.links || []).map((link, index) => (
                                <a key={index} href="#" style={{ marginRight: '10px', color: styles.color || 'inherit' }}>{link}</a>
                            ))}
                        </div>
                        <p>{content?.copyright || '© 2025'}</p>
                    </div>
                );
            default:
                return (
                    <div ref={drag} style={styleProps} className={`lpb-template-item ${getTemplateClass()}`}>
                        {item.name || 'Unknown Component'}
                    </div>
                );
        }
    };

    return renderPreview();
});

const ComponentLibrary = React.memo(({ isCollapsed, onToggle, addSection, addElement }) => {
    const [activeCategory, setActiveCategory] = useState('sections');
    const [activeSubCategory, setActiveSubCategory] = useState(null);
    const [isDragging, setIsDragging] = useState(false);

    const categories = Object.keys(EXTENDED_LIBRARY).map((key) => ({
        id: key,
        ...EXTENDED_LIBRARY[key],
    }));

    const handleToggle = useCallback(() => {
        onToggle();
    }, [onToggle]);

    const handleCategoryChange = useCallback((categoryId) => {
        setActiveCategory(categoryId);
        setActiveSubCategory(null);
        toast.info(`Đã chuyển sang danh mục ${EXTENDED_LIBRARY[categoryId]?.name}`);
    }, []);

    const handleSubCategoryChange = useCallback((subId) => {
        setActiveSubCategory(subId === activeSubCategory ? null : subId);
        toast.info(`Đã chọn danh mục con ${EXTENDED_LIBRARY[activeCategory]?.subCategories.find((sub) => sub.id === subId)?.name}`);
    }, [activeCategory, activeSubCategory]);

    const filteredItems = useMemo(() => {
        const categoryData = EXTENDED_LIBRARY[activeCategory];
        if (!categoryData || !activeSubCategory) return [];
        const subCategory = categoryData.subCategories.find((sub) => sub.id === activeSubCategory);
        return subCategory ? subCategory.templates : [];
    }, [activeCategory, activeSubCategory]);

    categories.forEach((cat, index) => {
        useHotkeys(`ctrl+${index + 1}`, () => handleCategoryChange(cat.id), [handleCategoryChange]);
    });

    if (isCollapsed) {
        return (
            <div className="lpb-component-library lpb-component-library-collapsed">
                <div className="lpb-component-library-header">
                    <button onClick={handleToggle} className="lpb-component-library-toggle">
                        <i className="fas fa-chevron-right"></i>
                    </button>
                </div>
                {categories.map((cat) => (
                    <button
                        key={cat.id}
                        className="lpb-component-library-icon-collapsed"
                        onClick={() => {
                            handleCategoryChange(cat.id);
                            handleToggle();
                        }}
                    >
                        <i className={cat.icon}></i>
                    </button>
                ))}
            </div>
        );
    }

    return (
        <div className="lpb-component-library">
            <div className="lpb-component-library-wrapper">
                <div className="lpb-component-library-category-nav">
                    <div className="lpb-component-library-header">
                        <h2 className="lpb-component-library-title">Thư viện</h2>
                        <button onClick={handleToggle} className="lpb-component-library-toggle">
                            <i className="fas fa-chevron-left"></i>
                        </button>
                    </div>
                    {categories.map((cat) => (
                        <button
                            key={cat.id}
                            onClick={() => handleCategoryChange(cat.id)}
                            className={`lpb-component-library-category-btn ${activeCategory === cat.id ? 'lpb-component-library-category-btn-active' : ''}`}
                        >
                            {cat.name}
                        </button>
                    ))}
                </div>
                <div className="lpb-component-library-subcategory-nav">
                    {EXTENDED_LIBRARY[activeCategory]?.subCategories.map((sub) => (
                        <button
                            key={sub.id}
                            onClick={() => handleSubCategoryChange(sub.id)}
                            className={`lpb-component-library-subcategory-btn ${activeSubCategory === sub.id ? 'lpb-component-library-subcategory-btn-active' : ''}`}
                        >
                            {sub.name}
                        </button>
                    ))}
                </div>
                <div className="lpb-component-library-template-column">
                    {filteredItems.length > 0 ? (
                        <div className="lpb-component-library-template-list" style={{ display: 'grid', gap: '10px' }}>
                            {filteredItems.map((item) => (
                                <DraggableComponent
                                    key={item.id}
                                    item={item}
                                    isSection={activeCategory === 'sections' || activeCategory === 'templates'}
                                    onDrag={() => setIsDragging(true)}
                                    onDragEnd={() => setIsDragging(false)}
                                />
                            ))}
                        </div>
                    ) : (
                        <div className="lpb-component-library-empty">Chọn một danh mục con để xem templates</div>
                    )}
                </div>
            </div>
        </div>
    );
});

export default ComponentLibrary;
