// F:\landinghub-iconic\apps\web\src\pages\Users.js
import React, { useState, useEffect } from 'react';
import api from '@landinghub/api';

const Users = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const res = await api.get('/api/users');
                setUsers(res.data);
            } catch (err) {
                console.error('Error fetching users:', err);
            } finally {
                setLoading(false);
            }
        };
        fetchData();
    }, []);

    if (loading) return <div>Đang tải...</div>;

    return (
        <div className="user-content">
            <h1>Quản lý Tài khoản</h1>
            <table>
                <thead>
                <tr>
                    <th>Email</th>
                    <th>Tên</th>
                    <th>Role</th>
                    <th>Subscription</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {users.map((user) => (
                    <tr key={user._id}>
                        <td>{user.email}</td>
                        <td>{user.name}</td>
                        <td>{user.role}</td>
                        <td>{user.subscription}</td>
                        <td>
                            <button onClick={() => alert(`Chỉnh sửa ${user.email}`)}>Edit</button>
                            <button onClick={() => alert(`Xóa ${user.email}`)}>Delete</button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default React.memo(Users);
