                      }) => {
    const [collapsedSections, setCollapsedSections] = useState(new Set());

    // Tổ chức layers: popups trước, sections sau
    const organizedLayers = useMemo(() => {
        const sections = pageData.elements
            .filter(el => el.type === 'section' || el.isSection)
            .sort((a, b) => (a.position?.desktop?.y || 0) - (b.position?.desktop?.y || 0));

        const popups = pageData.elements
            .filter(el => el.type === 'popup')
            .sort((a, b) => (b.styles?.zIndex || 0) - (a.styles?.zIndex || 0));

        return { sections, popups };
    }, [pageData.elements]);

    const toggleSection = (sectionId) => {
        const newCollapsed = new Set(collapsedSections);
        if (newCollapsed.has(sectionId)) {
            newCollapsed.delete(sectionId);
        } else {
            newCollapsed.add(sectionId);
        }
        setCollapsedSections(newCollapsed);
    };

    const handleSelectLayer = (elementId, childId = null) => {
        if (childId) {
            console.log('Select child:', childId, 'in parent:', elementId);
        }
        onSelectElement([elementId]);
    };

    const handleToggleVisibility = (elementId, childId = null, e) => {
        if (e) e.stopPropagation();
        if (onToggleVisibility) {
            onToggleVisibility(elementId, childId);
        }
    };

    const handleToggleLock = (elementId, childId = null, e) => {
        if (e) e.stopPropagation();
        if (onToggleLock) {
            onToggleLock(elementId, childId);
        }
    };

    const handleDelete = (elementId, e) => {
        if (e) e.stopPropagation();
        if (window.confirm('Xóa layer này?')) {
            onDeleteElement(elementId);
        }
    };

    const renderChildLayers = (element) => {
        if (!element.children || element.children.length === 0) return null;

        const isCollapsed = collapsedSections.has(element.id);

        return (
            <div className="layer-children">
                {!isCollapsed && element.children.map((child) => (
                    <div
                        key={child.id}
                        className={`layer-item layer-child ${selectedIds.includes(child.id) ? 'layer-selected' : ''} ${child.visible === false ? 'layer-hidden' : ''}`}
                        onClick={(e) => {
                            e.stopPropagation();
                            handleSelectLayer(element.id, child.id);
                        }}
                    >
                        <div className="layer-indent" />
                        <i className="fas fa-cube layer-icon"></i>
                        <span className="layer-name" title={child.id}>
              {child.componentData?.content || child.type || 'Element'}
            </span>
                        <div className="layer-actions">
                            <button
                                className="layer-action-btn"
                                onClick={(e) => handleToggleVisibility(element.id, child.id, e)}
                                title={child.visible === false ? "Hiện" : "Ẩn"}
                            >
                                <i className={`fas ${child.visible === false ? 'fa-eye-slash' : 'fa-eye'}`}></i>
                            </button>
                            <button
                                className="layer-action-btn"
                                onClick={(e) => handleToggleLock(element.id, child.id, e)}
                                title={child.locked ? "Mở khóa" : "Khóa"}
                            >
                                <i className={`fas ${child.locked ? 'fa-lock' : 'fa-unlock'}`}></i>
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        );
    };

    const renderLayer = (element, type = 'section') => {
        const isSelected = selectedIds.includes(element.id);
        const hasChildren = element.children && element.children.length > 0;
        const isCollapsed = collapsedSections.has(element.id);
        const icon = type === 'popup' ? 'fa-window-restore' : 'fa-layer-group';

        return (
            <div key={element.id} className="layer-group">
                <div
                    className={`layer-item ${isSelected ? 'layer-selected' : ''} ${element.visible === false ? 'layer-hidden' : ''}`}
                    onClick={() => handleSelectLayer(element.id)}
                >
                    {hasChildren ? (
                        <button
                            className="layer-toggle"
                            onClick={(e) => {
                                e.stopPropagation();
                                toggleSection(element.id);
                            }}
                        >
                            <i className={`fas ${isCollapsed ? 'fa-chevron-right' : 'fa-chevron-down'}`}></i>
                        </button>
                    ) : (
                        <div className="layer-toggle-spacer" />
                    )}

                    <i className={`fas ${icon} layer-icon`}></i>

                    <span className="layer-name" title={element.id}>
            {type === 'popup'
                ? (element.componentData?.title || 'Popup')
                : (element.componentData?.name || `Section ${element.id.slice(-4)}`)}
          </span>

                    {hasChildren && (
                        <span className="layer-child-count">
              {element.children.length}
            </span>
                    )}

                    <div className="layer-actions">
                        <button
                            className="layer-action-btn"
                            onClick={(e) => handleToggleVisibility(element.id, null, e)}
                            title={element.visible === false ? "Hiện" : "Ẩn"}
                        >
                            <i className={`fas ${element.visible === false ? 'fa-eye-slash' : 'fa-eye'}`}></i>
                        </button>
                        <button
                            className="layer-action-btn"
                            onClick={(e) => handleToggleLock(element.id, null, e)}
                            title={element.locked ? "Mở khóa" : "Khóa"}
                        >
                            <i className={`fas ${element.locked ? 'fa-lock' : 'fa-unlock'}`}></i>
                        </button>
                        <button
                            className="layer-action-btn layer-delete"
                            onClick={(e) => handleDelete(element.id, e)}
                            title="Xóa"
                        >
                            <i className="fas fa-trash"></i>
                        </button>
                    </div>
                </div>

                {hasChildren && renderChildLayers(element)}
            </div>
        );
    };

    return (
        <div className="lpb-layers-panel-modern">
            <div className="layer-panel-header">
                <div className="layer-panel-title">
                    <i className="fas fa-layer-group"></i>
                    <h4>Danh sách Layers</h4>
                </div>
                <button className="layer-panel-close" onClick={onClose}>
                    <i className="fas fa-times"></i>
                </button>
            </div>

            <div className="layer-list">
                {/* Popups Section */}
                {organizedLayers.popups.length > 0 && (
                    <div className="layer-category">
                        <div className="layer-category-header">
                            <i className="fas fa-window-restore"></i>
                            <span>Popups</span>
                            <span className="layer-category-badge">{organizedLayers.popups.length}</span>
                        </div>
                        {organizedLayers.popups.map(popup => renderLayer(popup, 'popup'))}
                    </div>
                )}

                {/* Sections */}
                {organizedLayers.sections.length > 0 && (
                    <div className="layer-category">
                        <div className="layer-category-header">
                            <i className="fas fa-layer-group"></i>
                            <span>Sections</span>
                            <span className="layer-category-badge">{organizedLayers.sections.length}</span>
                        </div>
                        {organizedLayers.sections.map(section => renderLayer(section, 'section'))}
                    </div>
                )}

                {/* Empty State */}
                {organizedLayers.sections.length === 0 && organizedLayers.popups.length === 0 && (
                    <div className="layer-empty">
                        <i className="fas fa-layer-group" style={{ fontSize: '48px', opacity: 0.3 }}></i>
                        <p>Chưa có layer nào</p>
                        <small>Thêm section hoặc popup từ thư viện</small>
                    </div>
                )}
            </div>

            <style>{`
        .lpb-layers-panel-modern {
          position: absolute;
          top: 60px;
          right: 10px;
          width: 340px;
          max-height: 520px;
          background: #ffffff;
          border-radius: 10px;
          box-shadow: 0 8px 24px rgba(0, 0, 0, 0.12), 0 2px 8px rgba(0, 0, 0, 0.08);
          z-index: 10000;
          display: flex;
          flex-direction: column;
          font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
          overflow: hidden;
          animation: slideDown 0.2s ease-out;
        }
        
        @keyframes slideDown {
          from {
            opacity: 0;
            transform: translateY(-10px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        
        .layer-panel-header {
          display: flex;
          align-items: center;
          justify-content: space-between;
          padding: 14px 16px;
          border-bottom: 1px solid #e5e7eb;
          background: linear-gradient(to bottom, #f9fafb, #ffffff);
        }
        
        .layer-panel-title {
          display: flex;
          align-items: center;
          gap: 10px;
        }
        
        .layer-panel-title i {
          color: #2563eb;
          font-size: 16px;
        }
        
        .layer-panel-title h4 {
          margin: 0;
          font-size: 15px;
          font-weight: 600;
          color: #111827;
        }
        
        .layer-panel-close {
          width: 30px;
          height: 30px;
          display: flex;
          align-items: center;
          justify-content: center;
          border: none;
          background: transparent;
          cursor: pointer;
          border-radius: 6px;
          color: #6b7280;
          transition: all 0.15s ease;
        }
        
        .layer-panel-close:hover {
          background: #f3f4f6;
          color: #111827;
        }
        
        .layer-list {
          flex: 1;
          overflow-y: auto;
          padding: 10px;
        }
        
        .layer-category {
          margin-bottom: 14px;
        }
        
        .layer-category-header {
          display: flex;
          align-items: center;
          gap: 8px;
          padding: 8px 12px;
          font-size: 12px;
          font-weight: 600;
          color: #374151;
          text-transform: uppercase;
          letter-spacing: 0.5px;
          background: #f3f4f6;
          border-radius: 6px;
          margin-bottom: 6px;
        }
        
        .layer-category-header i {
          font-size: 11px;
          color: #6b7280;
        }
        
        .layer-category-badge {
          margin-left: auto;
          background: #e5e7eb;
          color: #374151;
          padding: 2px 8px;
          border-radius: 12px;
          font-size: 11px;
          font-weight: 600;
        }
        
        .layer-group {
          margin-bottom: 2px;
        }
        
        .layer-item {
          display: flex;
          align-items: center;
          gap: 8px;
          padding: 9px 10px 9px 8px;
          border-radius: 6px;
          cursor: pointer;
          transition: all 0.15s ease;
          user-select: none;
          position: relative;
          font-size: 13px;
          border: 1px solid transparent;
        }
        
        .layer-item:hover {
          background: #f9fafb;
          border-color: #e5e7eb;
        }
        
        .layer-item.layer-selected {
          background: #dbeafe;
          border-color: #3b82f6;
          box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
          font-weight: 500;
        }
        
        .layer-item.layer-hidden {
          opacity: 0.5;
        }
        
        .layer-item.layer-hidden .layer-name {
          text-decoration: line-through;
        }
        
        .layer-child {
          margin-left: 24px;
          font-size: 12px;
        }
        
        .layer-indent {
          width: 20px;
        }
        
        .layer-toggle {
          width: 20px;
          height: 20px;
          display: flex;
          align-items: center;
          justify-content: center;
          border: none;
          background: transparent;
          cursor: pointer;
          border-radius: 4px;
          color: #6b7280;
          padding: 0;
          flex-shrink: 0;
          font-size: 10px;
          transition: all 0.15s ease;
        }
        
        .layer-toggle:hover {
          background: #e5e7eb;
          color: #111827;
        }
        
        .layer-toggle-spacer {
          width: 20px;
          flex-shrink: 0;
        }
        
        .layer-icon {
          flex-shrink: 0;
          color: #6b7280;
          font-size: 14px;
        }
        
        .layer-name {
          flex: 1;
          color: #374151;
          overflow: hidden;
          text-overflow: ellipsis;
          white-space: nowrap;
          font-weight: 500;
        }
        
        .layer-child-count {
          background: #e5e7eb;
          color: #6b7280;
          padding: 2px 6px;
          border-radius: 10px;
          font-size: 10px;
          font-weight: 600;
        }
        
        .layer-actions {
          display: flex;
          gap: 2px;
          opacity: 0;
          transition: opacity 0.15s ease;
        }
        
        .layer-item:hover .layer-actions,
        .layer-item.layer-selected .layer-actions {
          opacity: 1;
        }
        
        .layer-action-btn {
          width: 26px;
          height: 26px;
          display: flex;
          align-items: center;
          justify-content: center;
          border: none;
          background: transparent;
          cursor: pointer;
          border-radius: 4px;
          color: #6b7280;
          padding: 0;
          transition: all 0.15s ease;
          font-size: 12px;
        }
        
        .layer-action-btn:hover {
          background: #e5e7eb;
          color: #111827;
        }
        
        .layer-action-btn.layer-delete:hover {
          background: #fee2e2;
          color: #dc2626;
        }
        
        .layer-children {
          margin-top: 2px;
        }
        
        .layer-empty {
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: center;
          padding: 60px 24px;
          text-align: center;
          color: #9ca3af;
        }
        
        .layer-empty p {
          margin: 12px 0 4px;
          font-size: 14px;
          font-weight: 500;
          color: #6b7280;
        }
        
        .layer-empty small {
          font-size: 12px;
          color: #9ca3af;
        }
        
        /* Scrollbar styling */
        .layer-list::-webkit-scrollbar {
          width: 6px;
        }
        
        .layer-list::-webkit-scrollbar-track {
          background: transparent;
        }
        
        .layer-list::-webkit-scrollbar-thumb {
          background: #d1d5db;
          border-radius: 3px;
        }
        
        .layer-list::-webkit-scrollbar-thumb:hover {
          background: #9ca3af;
        }
        
        /* Dark mode support (optional) */
        @media (prefers-color-scheme: dark) {
          .lpb-layers-panel-modern {
            background: #1f2937;
            box-shadow: 0 8px 24px rgba(0, 0, 0, 0.4);
          }
          
          .layer-panel-header {
            border-bottom-color: #374151;
            background: linear-gradient(to bottom, #1f2937, #111827);
          }
          
          .layer-panel-title h4 {
            color: #f3f4f6;
          }
          
          .layer-category-header {
            background: #374151;
            color: #e5e7eb;
          }
          
          .layer-item {
            color: #e5e7eb;
          }
          
          .layer-item:hover {
            background: #374151;
            border-color: #4b5563;
          }
          
          .layer-item.layer-selected {
            background: #1e40af;
            border-color: #3b82f6;
          }
          
          .layer-name {
            color: #e5e7eb;
          }
        }
      `}</style>
        </div>
    );
};

export default LayerManager;
