import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import api from "@landinghub/api";
import DogLoader from "../components/DogLoader";
import grapesjs from "grapesjs";
import "grapesjs/dist/css/grapes.min.css";
import "grapesjs-blocks-basic"; // plugin block ƒë·∫πp

const CreateLandingPage = () => {
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [pageDescription, setPageDescription] = useState("");
    const [saveTime, setSaveTime] = useState(null);
    const editorRef = useRef(null);
    const editorContainerRef = useRef(null);
    const navigate = useNavigate();

    // --- Auth check ---
    useEffect(() => {
        const token = localStorage.getItem("token");
        if (!token) {
            navigate("/auth");
            return;
        }
        try {
            const decoded = jwtDecode(token);
            if (!decoded.userId) throw new Error("Invalid token");
            api.defaults.headers.common["Authorization"] = `Bearer ${token}`;
        } catch {
            navigate("/auth");
        }
    }, [navigate]);

    // --- Init GrapesJS ---
    useEffect(() => {
        if (!editorRef.current) {
            editorRef.current = grapesjs.init({
                container: editorContainerRef.current,
                height: "80vh",
                fromElement: false,
                storageManager: false,
                plugins: ["gjs-blocks-basic"],
                pluginsOpts: {
                    "gjs-blocks-basic": {},
                },
            });

            // --- Auto save m·ªói khi thay ƒë·ªïi ---
            editorRef.current.on("update", () => handleAutoSave());
            editorRef.current.on("component:add", () => handleAutoSave());
            editorRef.current.on("component:remove", () => handleAutoSave());
        }
    }, []);

    // --- Auto save function ---
    const handleAutoSave = async () => {
        if (!editorRef.current) return;
        const html = editorRef.current.getHtml();
        const css = editorRef.current.getCss();
        try {
            await api.post("/api/pages/auto-save", { html, css });
            setSaveTime(new Date().toLocaleTimeString());
        } catch (err) {
            console.error("Auto save error:", err);
        }
    };

    // --- Reload editor ---
    const handleReloadEditor = () => {
        if (editorRef.current) {
            editorRef.current.destroy();
            editorRef.current = null;
            window.location.reload();
        }
    };

    // --- Export HTML ---
    const handleExportHTML = () => {
        if (!editorRef.current) return;
        const html = editorRef.current.getHtml();
        const css = editorRef.current.getCss();
        const fullHtml = `
      <html>
      <head>
      <style>${css}</style>
      </head>
      <body>${html}</body>
      </html>
    `;
        const blob = new Blob([fullHtml], { type: "text/html" });
        const url = URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = "landing-page.html";
        a.click();
        URL.revokeObjectURL(url);
    };

    if (loading) return <DogLoader />;

    return (
        <div className="p-4 max-w-7xl mx-auto">
            <div className="flex justify-between items-center mb-4">
                <button onClick={() => navigate("/pages")} className="btn-secondary">
                    ‚Üê Quay l·∫°i
                </button>
                <h1 className="text-2xl font-bold">T·∫°o Landing Page N√¢ng Cao</h1>
            </div>

            {error && (
                <div className="error">
                    {error}
                    <button onClick={() => setError(null)}>ƒê√≥ng</button>
                </div>
            )}

            <div className="flex flex-col gap-4">
                <div className="flex flex-col gap-2">
                    <label>M√¥ t·∫£ Landing Page</label>
                    <textarea
                        value={pageDescription}
                        onChange={(e) => setPageDescription(e.target.value)}
                        rows="4"
                        className="textarea"
                        placeholder="M√¥ t·∫£ chi ti·∫øt..."
                    />
                </div>

                <div className="flex gap-2">
                    <button onClick={handleExportHTML} className="btn-primary">
                        üíø Xu·∫•t HTML
                    </button>
                    <button onClick={handleReloadEditor} className="btn-danger">
                        üîÅ Reload Editor
                    </button>
                    {saveTime && <span>‚è∞ L·∫ßn l∆∞u cu·ªëi: {saveTime}</span>}
                </div>

                <div
                    ref={editorContainerRef}
                    className="editor-section border rounded mt-2"
                ></div>
            </div>
        </div>
    );
};

export default CreateLandingPage;
