import React, { useRef, useCallback, useState, useEffect } from 'react';
import { useDrop, useDrag } from 'react-dnd';
import { getEmptyImage } from 'react-dnd-html5-backend';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import { sections } from '../../constants/sections';
import Guidelines from './Guidelines';
import SelectionOverlay from './SelectionOverlay';
import QuickEditToolkit from './QuickEditToolkit';

const ItemTypes = {
    ELEMENT: 'element',
    EXISTING_ELEMENT: 'existingElement',
    CHILD_ELEMENT: 'childElement',
};

// Calculate precise coordinates within canvas or section
const getCanvasPosition = (mouseX, mouseY, containerElement, zoomLevel) => {
    if (!containerElement) {
        console.error('Container element is null in getCanvasPosition');
        return { x: 0, y: 0 };
    }

    const rect = containerElement.getBoundingClientRect();
    const scrollX = containerElement.scrollLeft || 0;
    const scrollY = containerElement.scrollTop || 0;

    const rawX = (mouseX - rect.left + scrollX) / (zoomLevel / 100);
    const rawY = (mouseY - rect.top + scrollY) / (zoomLevel / 100);

    return { x: Math.max(0, rawX), y: Math.max(0, rawY) };
};

// Snap to grid with snap points
const snapToGrid = (x, y, gridSize, snapPoints = []) => {
    let snappedX = Math.round(x / gridSize) * gridSize;
    let snappedY = Math.round(y / gridSize) * gridSize;

    snapPoints.forEach((point) => {
        if (Math.abs(x - point.x) < 15) snappedX = point.x;
        if (Math.abs(y - point.y) < 15) snappedY = point.y;
    });

    return { x: snappedX, y: snappedY };
};

// Calculate element bounds
const getElementBounds = (element) => ({
    left: element.position.desktop?.x || 0,
    top: element.position.desktop?.y || 0,
    right: (element.position.desktop?.x || 0) + (element.size?.width || 200),
    bottom: (element.position.desktop?.y || 0) + (element.size?.height || 50),
    centerX: (element.position.desktop?.x || 0) + (element.size?.width || 200) / 2,
    centerY: (element.position.desktop?.y || 0) + (element.size?.height || 50) / 2,
});

// Render component content
const renderComponentContent = (type, componentData = {}, styles = {}, children = []) => {
    if (!type || typeof type !== 'string') {
        return (
            <div className="text-red-500 p-2 border border-dashed border-red-500 rounded" style={styles}>
                Unknown Component: {type || 'Không xác định'}
            </div>
        );
    }

    if (type === 'section' && componentData.structure === 'ladi-standard') {
        return (
            <div className="w-full h-full relative" style={styles}>
                <div className="absolute inset-0 bg-cover bg-center z-0" style={{
                    backgroundColor: componentData.backgroundColor || 'transparent',
                    backgroundImage: componentData.backgroundImage ? `url(${componentData.backgroundImage})` : 'none',
                }}></div>
                <div className="absolute inset-0 z-0" style={{
                    backgroundColor: componentData.overlayColor || 'transparent',
                    opacity: componentData.overlayOpacity || 0,
                }}></div>
                <div className="relative z-10 p-5 w-full h-full">
                    {children.map((child, index) => (
                        <div key={child.id || index} className="mb-2">
                            {renderComponentContent(child.type, child.componentData || {}, child.styles || {})}
                        </div>
                    ))}
                </div>
            </div>
        );
    }

    switch (type) {
        case 'heading':
            const headingLevel = componentData.level && ['h1', 'h2', 'h3', 'h4', 'h5', 'h6'].includes(componentData.level)
                ? componentData.level
                : 'h2';
            const HeadingTag = headingLevel;
            return (
                <HeadingTag className={`text-${componentData.fontSize || '2xl'} text-${componentData.color || 'gray-800'} font-${componentData.fontWeight || 'bold'} text-${componentData.textAlign || 'left'}`} style={styles}>
                    {componentData.content || 'Tiêu đề'}
                </HeadingTag>
            );
        case 'paragraph':
            return (
                <p className={`text-${componentData.fontSize || 'base'} text-${componentData.color || 'gray-800'} font-${componentData.fontWeight || 'normal'} leading-${componentData.lineHeight || 'normal'} text-${componentData.textAlign || 'left'}`} style={styles}>
                    {componentData.content || 'Đoạn văn'}
                </p>
            );
        case 'button':
            return (
                <button className={`px-${componentData.padding || '4'} py-2 rounded-${componentData.borderRadius || 'lg'} bg-${componentData.background || 'blue-600'} text-${componentData.color || 'white'} font-${componentData.fontWeight || 'semibold'} text-${componentData.fontSize || 'base'} text-${componentData.textAlign || 'center'}`} style={styles}>
                    {componentData.content || 'Nút'}
                </button>
            );
        case 'image':
            return (
                <img
                    src={componentData.src || 'https://via.placeholder.com/150?text=Image'}
                    alt={componentData.alt || 'Hình ảnh'}
                    className={`max-w-${componentData.maxWidth || 'full'} h-${componentData.height || 'auto'} rounded-${componentData.borderRadius || 'none'}`}
                    style={styles}
                    draggable={componentData.draggable ?? false}
                />
            );
        case 'form':
            return (
                <div className={`flex flex-${componentData.direction || 'col'} gap-${componentData.gap || '2'}`} style={styles}>
                    {componentData.title && !children.some(child => child?.type === 'heading') && (
                        <h3 className={`text-${componentData.titleFontSize || 'lg'} text-${componentData.titleColor || 'gray-800'} font-${componentData.titleFontWeight || 'semibold'}`}>
                            {componentData.title}
                        </h3>
                    )}
                    {Array.isArray(componentData.fields) && componentData.fields.length > 0 ? (
                        componentData.fields.map((field, index) => (
                            <input
                                key={index}
                                type={field.type || 'text'}
                                placeholder={field.placeholder || field.label || 'Nhập...'}
                                className={`p-${field.padding || '2'} rounded-${field.borderRadius || 'lg'} border border-${field.border || 'gray-300'} text-${field.fontSize || 'base'}`}
                            />
                        ))
                    ) : (
                        !children.some(child => child?.type === 'input') && (
                            <input
                                type={componentData.inputType || 'text'}
                                placeholder={componentData.placeholder || 'Nhập...'}
                                className={`p-${componentData.inputPadding || '2'} rounded-${componentData.inputBorderRadius || 'lg'} border border-${componentData.inputBorder || 'gray-300'} text-${componentData.inputFontSize || 'base'}`}
                            />
                        )
                    )}
                    {!children.some(child => child?.type === 'button') && (
                        <button
                            type="submit"
                            className={`bg-${componentData.buttonBackground || 'blue-600'} text-${componentData.buttonColor || 'white'} p-${componentData.buttonPadding || '2 4'} rounded-${componentData.buttonBorderRadius || 'lg'} font-${componentData.buttonFontWeight || 'semibold'} text-${componentData.buttonFontSize || 'base'}`}
                        >
                            {componentData.buttonText || 'Gửi'}
                        </button>
                    )}
                    {Array.isArray(children) && children.length > 0 && children.map((child, index) => (
                        <div key={child.id || index} className="mb-2">
                            {renderComponentContent(child.type, child.componentData || {}, child.styles || {})}
                        </div>
                    ))}
                </div>
            );
        case 'section':
        case 'container':
            return (
                <div className={`flex flex-${componentData.direction || 'col'} gap-${componentData.gap || '2'} p-${componentData.padding || '0'} bg-${componentData.background || 'transparent'}`} style={styles}>
                    {componentData.title && !children.some(child => child?.type === 'heading') && (
                        <h2 className={`text-${componentData.titleFontSize || '2xl'} text-${componentData.titleColor || 'gray-800'} font-${componentData.titleFontWeight || 'bold'}`}>
                            {componentData.title}
                        </h2>
                    )}
                    {componentData.subtitle && !children.some(child => child?.type === 'paragraph') && (
                        <p className={`text-${componentData.subtitleFontSize || 'lg'} text-${componentData.subtitleColor || 'gray-600'}`}>
                            {componentData.subtitle}
                        </p>
                    )}
                    {componentData.content && !children.some(child => child?.type === 'paragraph') && (
                        <p className={`text-${componentData.contentFontSize || 'base'} text-${componentData.contentColor || 'gray-600'}`}>
                            {componentData.content}
                        </p>
                    )}
                    {Array.isArray(componentData.features) && componentData.features.length > 0 && !children.some(child => child?.type === 'list') && (
                        <ul className={`list-none p-${componentData.featuresPadding || '0'} flex flex-${componentData.featuresDirection || 'col'} gap-${componentData.featuresGap || '2'}`}>
                            {componentData.features.map((feature, index) => (
                                <li key={index} className={`text-${componentData.featureFontSize || 'base'} text-${componentData.featureColor || 'gray-800'}`}>
                                    {feature}
                                </li>
                            ))}
                        </ul>
                    )}
                    {componentData.ctaText && !children.some(child => child?.type === 'button') && (
                        <button className={`p-${componentData.ctaPadding || '2 5'} rounded-${componentData.ctaBorderRadius || 'lg'} bg-${componentData.ctaBackground || 'blue-600'} text-${componentData.ctaColor || 'white'} font-${componentData.ctaFontWeight || 'semibold'} text-${componentData.ctaFontSize || 'base'}`}>
                            {componentData.ctaText}
                        </button>
                    )}
                    {componentData.contact && !children.some(child => child?.type === 'paragraph') && (
                        <p className={`text-${componentData.contactFontSize || 'sm'} text-${componentData.contactColor || 'gray-600'}`}>
                            {componentData.contact}
                        </p>
                    )}
                    {Array.isArray(children) && children.length > 0 && children.map((child, index) => (
                        <div key={child.id || index} className="mb-2">
                            {renderComponentContent(child.type, child.componentData || {}, child.styles || {})}
                        </div>
                    ))}
                </div>
            );
        case 'divider':
            return (
                <hr className={`border-none bg-${componentData.color || 'gray-200'} h-${componentData.thickness || '0.5'} w-${componentData.width || 'full'}`} style={styles} />
            );
        case 'spacer':
            return (
                <div className={`bg-${componentData.background || 'transparent'} h-${componentData.height || '10'} w-${componentData.width || 'full'}`} style={styles} />
            );
        case 'popup':
            return (
                <div className={`flex flex-${componentData.direction || 'col'} gap-${componentData.gap || '2'} text-${componentData.textAlign || 'center'} p-${componentData.padding || '5'} bg-${componentData.background || 'white/90'} rounded-${componentData.borderRadius || '3xl'}`} style={styles}>
                    {componentData.title && !children.some(child => child?.type === 'heading') && (
                        <h3 className={`text-${componentData.titleFontSize || 'xl'} text-${componentData.titleColor || 'gray-800'} font-${componentData.titleFontWeight || 'semibold'}`}>
                            {componentData.title}
                        </h3>
                    )}
                    {componentData.content && !children.some(child => child?.type === 'paragraph') && (
                        <p className={`text-${componentData.contentFontSize || 'base'} text-${componentData.contentColor || 'gray-600'}`}>
                            {componentData.content}
                        </p>
                    )}
                    {componentData.buttonText && !children.some(child => child?.type === 'button') && (
                        <button className={`p-${componentData.buttonPadding || '2 5'} rounded-${componentData.buttonBorderRadius || 'lg'} bg-${componentData.buttonBackground || 'blue-600'} text-${componentData.buttonColor || 'white'} font-${componentData.buttonFontWeight || 'semibold'} text-${componentData.buttonFontSize || 'base'}`}>
                            {componentData.buttonText}
                        </button>
                    )}
                    {Array.isArray(children) && children.length > 0 && children.map((child, index) => (
                        <div key={child.id || index} className="mb-2">
                            {renderComponentContent(child.type, child.componentData || {}, child.styles || {})}
                        </div>
                    ))}
                </div>
            );
        case 'modal':
            return (
                <div className={`flex flex-${componentData.direction || 'col'} gap-${componentData.gap || '2'} p-${componentData.padding || '5'} bg-${componentData.background || 'white/90'} rounded-${componentData.borderRadius || '3xl'}`} style={styles}>
                    {componentData.title && !children.some(child => child?.type === 'heading') && (
                        <h3 className={`text-${componentData.titleFontSize || 'xl'} text-${componentData.titleColor || 'gray-800'} font-${componentData.titleFontWeight || 'semibold'}`}>
                            {componentData.title}
                        </h3>
                    )}
                    {componentData.content && !children.some(child => child?.type === 'paragraph') && (
                        <p className={`text-${componentData.contentFontSize || 'base'} text-${componentData.contentColor || 'gray-600'}`}>
                            {componentData.content}
                        </p>
                    )}
                    {componentData.closeButton && !children.some(child => child?.type === 'button') && (
                        <button className={`p-${componentData.buttonPadding || '2 4'} rounded-${componentData.buttonBorderRadius || 'lg'} bg-${componentData.buttonBackground || 'gray-500'} text-${componentData.buttonColor || 'white'} font-${componentData.buttonFontWeight || 'semibold'} text-${componentData.buttonFontSize || 'base'} self-end`}>
                            {componentData.buttonText || 'Đóng'}
                        </button>
                    )}
                    {Array.isArray(children) && children.length > 0 && children.map((child, index) => (
                        <div key={child.id || index} className="mb-2">
                            {renderComponentContent(child.type, child.componentData || {}, child.styles || {})}
                        </div>
                    ))}
                </div>
            );
        case 'card':
            return (
                <div className={`p-${componentData.padding || '5'} bg-${componentData.background || 'white'} rounded-${componentData.borderRadius || 'lg'} shadow-${componentData.boxShadow || 'md'}`} style={styles}>
                    {componentData.title && (
                        <h3 className={`text-${componentData.titleFontSize || 'lg'} text-${componentData.titleColor || 'gray-800'}`}>
                            {componentData.title}
                        </h3>
                    )}
                    {componentData.content && (
                        <p className={`text-${componentData.contentFontSize || 'base'} text-${componentData.contentColor || 'gray-600'} mt-${componentData.contentMargin || '2'}`}>
                            {componentData.content}
                        </p>
                    )}
                </div>
            );
        case 'testimonial':
            return (
                <div className={`p-${componentData.padding || '5'} bg-${componentData.background || 'gray-50'} rounded-${componentData.borderRadius || 'lg'}`} style={styles}>
                    {componentData.quote && (
                        <p className={`text-${componentData.quoteFontSize || 'base'} text-${componentData.quoteColor || 'gray-800'} italic`}>
                            "{componentData.quote}"
                        </p>
                    )}
                    {componentData.author && (
                        <p className={`text-${componentData.authorFontSize || 'sm'} text-${componentData.authorColor || 'gray-600'} mt-${componentData.authorMargin || '2'} text-right`}>
                            - {componentData.author}
                        </p>
                    )}
                </div>
            );
        case 'menu':
            return (
                <div className={`flex gap-${componentData.gap || '5'}`} style={styles}>
                    {Array.isArray(componentData.items) && componentData.items.map((item, index) => (
                        <a
                            key={index}
                            href={item.url || '#'}
                            className={`text-${componentData.color || 'white'} no-underline text-${componentData.fontSize || 'base'}`}
                        >
                            {item.label}
                        </a>
                    ))}
                </div>
            );
        case 'grid':
            return (
                <div className={`grid grid-cols-${componentData.columns || '2'} gap-${componentData.gap || '5'}`} style={styles}>
                    {Array.isArray(componentData.items) && componentData.items.map((item, index) => (
                        <div key={index} className={`bg-${componentData.itemBackground || 'white'} rounded-${componentData.itemBorderRadius || 'lg'} p-${componentData.itemPadding || '2'}`}>
                            {item.image && (
                                <img
                                    src={item.image}
                                    alt={item.title || 'Product'}
                                    className="w-full h-auto"
                                />
                            )}
                            {item.title && (
                                <h4 className={`text-${componentData.titleFontSize || 'base'} text-${componentData.titleColor || 'gray-800'} mt-${componentData.titleMargin || '2'}`}>
                                    {item.title}
                                </h4>
                            )}
                            {item.price && (
                                <p className={`text-${componentData.priceFontSize || 'sm'} text-${componentData.priceColor || 'gray-600'} mt-${componentData.priceMargin || '1'}`}>
                                    {item.price}
                                </p>
                            )}
                        </div>
                    ))}
                </div>
            );
        case 'list':
            return (
                <ul className={`list-${componentData.listStyleType || 'none'} p-${componentData.padding || '0'}`} style={styles}>
                    {Array.isArray(componentData.items) && componentData.items.map((item, index) => (
                        <li key={index} className={`text-${componentData.itemFontSize || 'base'} text-${componentData.itemColor || 'gray-800'} m-${componentData.itemMargin || '1 0'}`}>
                            {item.title || item}
                        </li>
                    ))}
                </ul>
            );
        default:
            return (
                <div className={`text-${componentData.errorColor || 'red-500'} p-2 border border-dashed border-red-500 rounded`} style={styles}>
                    Unknown Component: {type}
                </div>
            );
    }
};

// ChildElement Component
const ChildElement = React.memo(
    ({
         parentId,
         id,
         type,
         componentData = {},
         styles = {},
         isSelected,
         onSelectChild,
         zoomLevel,
         gridSize,
         showGrid,
         onUpdateChildPosition,
         onMoveChild,
         position,
         size,
         viewMode,
         setDragPreview,
     }) => {
        const dragRef = useRef(null);

        const [{ isDragging }, drag] = useDrag(() => ({
            type: ItemTypes.CHILD_ELEMENT,
            item: () => {
                console.log('ChildElement drag started:', { childId: id, parentId });
                return { childId: id, parentId, isExisting: true, position, size };
            },
            end: (item, monitor) => {
                if (!monitor.didDrop()) {
                    console.log('ChildElement drag ended without drop, clearing preview');
                    setDragPreview(null);
                }
            },
            collect: (monitor) => ({
                isDragging: monitor.isDragging(),
            }),
        }));

        const handleClick = (e) => {
            e.stopPropagation();
            console.log('ChildElement clicked:', { parentId, id, isSelected });
            if (typeof onSelectChild === 'function') {
                onSelectChild(parentId, id);
            } else {
                console.warn('onSelectChild is not a function');
            }
        };

        useEffect(() => {
            drag(dragRef);
        }, [drag]);

        return (
            <div
                className={`absolute ${isSelected ? 'border-2 border-blue-500' : ''} ${isDragging ? 'opacity-50' : 'opacity-100'}`}
                style={{
                    left: position[viewMode]?.x || 0,
                    top: position[viewMode]?.y || 0,
                    width: size?.width || 200,
                    height: size?.height || 50,
                    zIndex: isDragging ? 1000 : (position.z || 10),
                    cursor: isDragging ? 'grabbing' : 'grab',
                    ...styles,
                }}
                onClick={handleClick}
            >
                <div ref={dragRef}>
                    {renderComponentContent(type, componentData, styles)}
                </div>
                {isSelected && (
                    <QuickEditToolkit
                        element={{ id, type, styles }}
                        position={{ x: 0, y: -40 }}
                        onEdit={() => console.log('Edit child:', id)}
                        onDuplicate={() => console.log('Duplicate child:', id)}
                        onDelete={() => console.log('Delete child:', id)}
                        onClose={() => onSelectChild(parentId, null)}
                    />
                )}
            </div>
        );
    }
);

// Element Component
const Element = React.memo(
    ({
         element,
         isSelected,
         onSelectElement,
         onUpdatePosition,
         viewMode,
         zoomLevel,
         gridSize,
         onContextMenu,
         canvasBounds,
         canvasRef,
         onSelectChild,
         selectedChildId,
         onAddChild,
         onUpdateChildPosition,
         onMoveChild,
         showGrid,
         setDragPreview,
     }) => {
        const { id, type, componentData = {}, position = {}, size = { width: viewMode === 'mobile' ? 375 : 1200, height: 400 }, styles = {}, children = [] } = element;
        const elementRef = useRef(null);
        const containerRef = useRef(null);

        const handleMouseDown = useCallback(
            (e) => {
                e.stopPropagation();
                onSelectElement(id, e.ctrlKey);
                onSelectChild(id, null);
            },
            [id, onSelectElement, onSelectChild]
        );

        const handleContextMenu = useCallback(
            (e) => {
                e.preventDefault();
                e.stopPropagation();
                onContextMenu(id, { x: e.clientX, y: e.clientY });
            },
            [id, onContextMenu]
        );

        const snapPoints = [
            { x: 0, y: 0 },
            { x: size.width, y: size.height },
            { x: size.width / 2, y: size.height / 2 },
        ];
        children.forEach((child) => {
            const bounds = getElementBounds(child);
            snapPoints.push(
                { x: bounds.left, y: bounds.top },
                { x: bounds.right, y: bounds.bottom },
                { x: bounds.centerX, y: bounds.centerY },
                { x: bounds.left, y: bounds.centerY },
                { x: bounds.right, y: bounds.centerY },
                { x: bounds.centerX, y: bounds.top },
                { x: bounds.centerX, y: bounds.bottom }
            );
        });

        if (type === 'section' && componentData.structure === 'ladi-standard') {
            const [{ isOverContainer, canDropContainer }, drop] = useDrop(() => ({
                accept: [ItemTypes.ELEMENT, ItemTypes.CHILD_ELEMENT],
                canDrop: (item, monitor) => {
                    if (!type || type !== 'section' || componentData?.structure !== 'ladi-standard') {
                        return false;
                    }
                    if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT && item.parentId && item.parentId !== id) {
                        return false;
                    }
                    return true;
                },
                drop: (item, monitor) => {
                    if (!monitor.canDrop()) {
                        toast.error('Không thể thả vào container này!');
                        setDragPreview(null);
                        return { moved: false };
                    }
                    const clientOffset = monitor.getClientOffset();
                    if (!clientOffset || !containerRef.current) {
                        toast.error('Không thể xác định vị trí thả!');
                        setDragPreview(null);
                        return { moved: false };
                    }

                    const pos = getCanvasPosition(clientOffset.x, clientOffset.y, containerRef.current, zoomLevel);
                    const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                    if (monitor.getItemType() === ItemTypes.ELEMENT) {
                        const newId = `${item.id}-${Date.now()}`;
                        const newChild = {
                            id: newId,
                            type: item.json.type,
                            componentData: item.json.componentData || {},
                            position: {
                                [viewMode]: snapped,
                                desktop: snapped,
                                tablet: snapped,
                                mobile: snapped,
                            },
                            size: item.json.size || { width: 200, height: 50 },
                            styles: item.json.styles || {},
                            children: [],
                        };
                        onAddChild(id, newChild);
                        toast.success('Đã thêm thành phần con!');
                        setDragPreview(null);
                        return { moved: true, newPosition: snapped };
                    } else if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT) {
                        if (item.parentId === id) {
                            onUpdateChildPosition(id, item.childId, snapped);
                            toast.success('Đã di chuyển thành phần con trong section!');
                        } else {
                            onMoveChild(item.parentId, item.childId, id, snapped);
                            toast.success('Đã di chuyển thành phần con sang section khác!');
                        }
                        setDragPreview(null);
                        return { moved: true, newPosition: snapped };
                    }
                    setDragPreview(null);
                    return { moved: false };
                },
                collect: (monitor) => ({
                    isOverContainer: monitor.isOver(),
                    canDropContainer: monitor.canDrop(),
                }),
            }), [id, type, componentData, viewMode, zoomLevel, gridSize, showGrid, onAddChild, onUpdateChildPosition, onMoveChild]);

            useEffect(() => {
                drop(containerRef);
            }, [drop]);

            return (
                <div
                    ref={elementRef}
                    className={`absolute ${isSelected ? 'border-2 border-blue-500' : ''}`}
                    style={{
                        top: position[viewMode]?.y || 0,
                        width: viewMode === 'mobile' ? '375px' : '1200px',
                        height: size.height,
                        left: '50%',
                        transform: 'translateX(-50%)',
                        zIndex: position.z || 1,
                        userSelect: 'none',
                    }}
                    onMouseDown={handleMouseDown}
                    onContextMenu={handleContextMenu}
                >
                    <div className="w-full h-full relative">
                        <div className="absolute inset-0 bg-cover bg-center z-0" style={{
                            backgroundColor: componentData.backgroundColor || 'transparent',
                            backgroundImage: componentData.backgroundImage ? `url(${componentData.backgroundImage})` : 'none',
                        }}></div>
                        <div className="absolute inset-0 z-0" style={{
                            backgroundColor: componentData.overlayColor || 'transparent',
                            opacity: componentData.overlayOpacity || 0,
                        }}></div>
                        <div
                            ref={containerRef}
                            className={`relative z-10 p-5 w-full h-full ${isOverContainer && canDropContainer ? 'border-2 border-dashed border-blue-500' : ''}`}
                        >
                            {children.map((child) => (
                                <ChildElement
                                    key={child.id}
                                    parentId={id}
                                    id={child.id}
                                    type={child.type}
                                    componentData={child.componentData || {}}
                                    styles={child.styles || {}}
                                    position={child.position || {}}
                                    size={child.size || { width: 200, height: 50 }}
                                    isSelected={selectedChildId === child.id}
                                    onSelectChild={onSelectChild}
                                    zoomLevel={zoomLevel}
                                    gridSize={gridSize}
                                    showGrid={showGrid}
                                    onUpdateChildPosition={onUpdateChildPosition}
                                    onMoveChild={onMoveChild}
                                    viewMode={viewMode}
                                    setDragPreview={setDragPreview}
                                />
                            ))}
                        </div>
                    </div>
                </div>
            );
        }

        const [{ isDragging }, drag, preview] = useDrag(() => ({
            type: ItemTypes.EXISTING_ELEMENT,
            item: () => {
                console.log('Element drag started:', { id, position: position[viewMode] });
                return {
                    id,
                    elementSize: size,
                    elementPosition: position[viewMode] || { x: 0, y: 0 },
                    isExisting: true,
                };
            },
            end: (item, monitor) => {
                if (!monitor.didDrop()) {
                    console.log('Element drag ended without drop, clearing preview');
                    setDragPreview(null);
                }
            },
            collect: (monitor) => ({
                isDragging: monitor.isDragging(),
            }),
        }), [id, size, position, viewMode]);

        useEffect(() => {
            preview(getEmptyImage(), { captureDraggingState: true });
        }, [preview]);

        return (
            <div
                ref={(node) => {
                    elementRef.current = node;
                    drag(node);
                }}
                className={`absolute ${isSelected ? 'border-2 border-blue-500' : ''} ${isDragging ? 'opacity-50' : 'opacity-100'}`}
                style={{
                    top: position[viewMode]?.y || 0,
                    width: viewMode === 'mobile' ? '375px' : '1200px',
                    height: size.height,
                    left: '50%',
                    transform: 'translateX(-50%)',
                    zIndex: position.z || 1,
                    cursor: isDragging ? 'grabbing' : 'grab',
                    userSelect: 'none',
                    ...styles,
                }}
                onMouseDown={handleMouseDown}
                onContextMenu={handleContextMenu}
            >
                {renderComponentContent(type, componentData, styles, children)}
            </div>
        );
    }
);

// Canvas Component
const Canvas = React.memo(
    ({
         pageData,
         selectedIds,
         onSelectElement,
         onUpdatePosition,
         onUpdateSize,
         onDeleteElement,
         onAddElement,
         onGroupElements,
         viewMode,
         zoomLevel,
         gridSize,
         showGrid,
         onEditElement,
         onDuplicateElement,
         onMoveElementUp,
         onMoveElementDown,
         onSelectChild,
         selectedChildId,
         onAddChild,
         onUpdateChildPosition,
         onMoveChild,
         guideLine,
         onShowAddSectionPopup,
         onUpdateCanvasHeight,
     }) => {
        const canvasRef = useRef(null);
        const [guidelines, setGuidelines] = useState([]);
        const [contextMenu, setContextMenu] = useState(null);
        const [dragPreview, setDragPreview] = useState(null);
        const [showPopup, setShowPopup] = useState(false);
        const [guideLinePosition, setGuideLinePosition] = useState(guideLine?.y || 0);
        const [popupPosition, setPopupPosition] = useState({ x: 0, y: 0 });

        const canvasBounds = canvasRef.current
            ? {
                width: canvasRef.current.offsetWidth / (zoomLevel / 100),
                height: canvasRef.current.offsetHeight / (zoomLevel / 100),
            }
            : { width: 1200, height: 1000 };

        const updateCanvasHeight = useCallback(() => {
            const lastSection = pageData.elements
                .filter((el) => el.type === 'section')
                .sort((a, b) => (b.position?.[viewMode]?.y || 0) - (a.position?.[viewMode]?.y || 0))[0];
            const newHeight = lastSection
                ? (lastSection.position?.[viewMode]?.y || 0) + (lastSection.size?.height || 400) + 50
                : 1000;
            if (newHeight !== pageData.canvas.height) {
                onUpdateCanvasHeight(newHeight);
            }
            setGuideLinePosition(lastSection ? (lastSection.position?.[viewMode]?.y || 0) + (lastSection.size?.height || 400) + 10 : 0);
        }, [pageData.elements, viewMode, onUpdateCanvasHeight]);

        useEffect(() => {
            updateCanvasHeight();
        }, [pageData.elements, updateCanvasHeight]);

        const getSnapPoints = useCallback(() => {
            const points = [
                { x: 0, y: 0 },
                { x: viewMode === 'mobile' ? 375 : 1200, y: canvasBounds.height },
                { x: (viewMode === 'mobile' ? 375 : 1200) / 2, y: canvasBounds.height / 2 },
            ];

            pageData.elements.forEach((el) => {
                const bounds = getElementBounds(el);
                points.push(
                    { x: bounds.left, y: bounds.top },
                    { x: bounds.right, y: bounds.bottom },
                    { x: bounds.centerX, y: bounds.centerY },
                    { x: bounds.left, y: bounds.centerY },
                    { x: bounds.right, y: bounds.centerY },
                    { x: bounds.centerX, y: bounds.top },
                    { x: bounds.centerX, y: bounds.bottom }
                );
                el.children?.forEach((child) => {
                    const childBounds = getElementBounds(child);
                    points.push(
                        { x: childBounds.left, y: childBounds.top },
                        { x: childBounds.right, y: childBounds.bottom },
                        { x: childBounds.centerX, y: childBounds.centerY },
                        { x: childBounds.left, y: childBounds.centerY },
                        { x: childBounds.right, y: childBounds.centerY },
                        { x: childBounds.centerX, y: childBounds.top },
                        { x: childBounds.centerX, y: childBounds.bottom }
                    );
                });
            });

            return points;
        }, [pageData.elements, canvasBounds, viewMode]);

        const [{ isOver, dragItem }, drop] = useDrop(() => ({
            accept: [ItemTypes.ELEMENT, ItemTypes.CHILD_ELEMENT],
            hover: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) {
                    setDragPreview(null);
                    return;
                }

                const pos = getCanvasPosition(clientOffset.x, clientOffset.y, canvasRef.current, zoomLevel);
                const snapPoints = getSnapPoints();
                const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                if (monitor.getItemType() === ItemTypes.ELEMENT && item.json) {
                    setDragPreview({
                        id: 'preview',
                        x: snapped.x,
                        y: snapped.y,
                        size: item.json.size || { width: viewMode === 'mobile' ? 375 : 1200, height: 400 },
                        type: item.json.type,
                        componentData: item.json.componentData || { structure: 'ladi-standard' },
                        styles: item.json.styles,
                    });
                } else if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT) {
                    setDragPreview({
                        id: item.childId,
                        x: snapped.x,
                        y: snapped.y,
                        size: item.size || { width: 200, height: 50 },
                        type: pageData.elements
                            .find((el) => el.id === item.parentId)
                            ?.children.find((child) => child.id === item.childId)?.type,
                        componentData: pageData.elements
                            .find((el) => el.id === item.parentId)
                            ?.children.find((child) => child.id === item.childId)?.componentData,
                        styles: pageData.elements
                            .find((el) => el.id === item.parentId)
                            ?.children.find((child) => child.id === item.childId)?.styles,
                    });
                }

                const newGuidelines = [];
                pageData.elements.forEach((el) => {
                    if (el.id === item.id) return;
                    const bounds = getElementBounds(el);
                    if (Math.abs(snapped.x - bounds.left) < 15) newGuidelines.push({ vertical: true, position: bounds.left });
                    if (Math.abs(snapped.x - bounds.right) < 15) newGuidelines.push({ vertical: true, position: bounds.right });
                    if (Math.abs(snapped.x - bounds.centerX) < 15) newGuidelines.push({ vertical: true, position: bounds.centerX });
                    if (Math.abs(snapped.y - bounds.top) < 15) newGuidelines.push({ vertical: false, position: bounds.top });
                    if (Math.abs(snapped.y - bounds.bottom) < 15) newGuidelines.push({ vertical: false, position: bounds.bottom });
                    if (Math.abs(snapped.y - bounds.centerY) < 15) newGuidelines.push({ vertical: false, position: bounds.centerY });
                    el.children?.forEach((child) => {
                        const childBounds = getElementBounds(child);
                        if (Math.abs(snapped.x - childBounds.left) < 15) newGuidelines.push({ vertical: true, position: childBounds.left });
                        if (Math.abs(snapped.x - childBounds.right) < 15) newGuidelines.push({ vertical: true, position: childBounds.right });
                        if (Math.abs(snapped.x - childBounds.centerX) < 15) newGuidelines.push({ vertical: true, position: childBounds.centerX });
                        if (Math.abs(snapped.y - childBounds.top) < 15) newGuidelines.push({ vertical: false, position: childBounds.top });
                        if (Math.abs(snapped.y - childBounds.bottom) < 15) newGuidelines.push({ vertical: false, position: childBounds.bottom });
                        if (Math.abs(snapped.y - childBounds.centerY) < 15) newGuidelines.push({ vertical: false, position: childBounds.centerY });
                    });
                });

                if (Math.abs(snapped.x) < 15) newGuidelines.push({ vertical: true, position: 0 });
                if (Math.abs(snapped.x - (viewMode === 'mobile' ? 375 : 1200)) < 15) newGuidelines.push({ vertical: true, position: viewMode === 'mobile' ? 375 : 1200 });
                if (Math.abs(snapped.x - (viewMode === 'mobile' ? 375 : 1200) / 2) < 15) newGuidelines.push({ vertical: true, position: (viewMode === 'mobile' ? 375 : 1200) / 2 });
                if (Math.abs(snapped.y) < 15) newGuidelines.push({ vertical: false, position: 0 });
                if (Math.abs(snapped.y - canvasBounds.height) < 15) newGuidelines.push({ vertical: false, position: canvasBounds.height });
                if (Math.abs(snapped.y - canvasBounds.height / 2) < 15) newGuidelines.push({ vertical: false, position: canvasBounds.height / 2 });

                setGuidelines(newGuidelines);
            },
            drop: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) {
                    toast.error('Không thể xác định vị trí thả!');
                    setDragPreview(null);
                    setGuidelines([]);
                    return { moved: false };
                }

                const pos = getCanvasPosition(clientOffset.x, clientOffset.y, canvasRef.current, zoomLevel);
                const snapPoints = getSnapPoints();
                const snapped = snapToGrid(pos.x, pos.y, showGrid ? gridSize : Infinity, snapPoints);

                if (monitor.getItemType() === ItemTypes.CHILD_ELEMENT) {
                    const sourceSection = pageData.elements.find((el) => el.id === item.parentId);
                    if (!sourceSection) {
                        console.error('Source section not found:', item.parentId);
                        setDragPreview(null);
                        setGuidelines([]);
                        return { moved: false };
                    }
                    onMoveChild(item.parentId, item.childId, null, snapped);
                    toast.success('Đã di chuyển thành phần con ra canvas!');
                    setDragPreview(null);
                    setGuidelines([]);
                    return { moved: true, newPosition: snapped };
                } else if (monitor.getItemType() === ItemTypes.ELEMENT) {
                    if (item.json.type !== 'section') {
                        toast.error('Chỉ có thể thả section lên canvas!');
                        setDragPreview(null);
                        setGuidelines([]);
                        return { moved: false };
                    }
                    const lastSectionY = pageData.elements
                        .filter((el) => el.type === 'section')
                        .sort((a, b) => (b.position?.[viewMode]?.y || 0) - (a.position?.[viewMode]?.y || 0))
                        .reduce((maxY, el) => {
                            const y = el.position?.[viewMode]?.y || 0;
                            const height = el.size?.height || 400;
                            return Math.max(maxY, y + height);
                        }, 0);
                    const newElement = {
                        id: `${item.id}-${Date.now()}`,
                        type: item.json.type,
                        componentData: JSON.parse(JSON.stringify(item.json.componentData || { structure: 'ladi-standard' })),
                        position: {
                            [viewMode]: { x: 0, y: lastSectionY + 10 },
                            desktop: { x: 0, y: lastSectionY + 10 },
                            tablet: { x: 0, y: lastSectionY + 10 },
                            mobile: { x: 0, y: lastSectionY + 10 },
                        },
                        size: { ...item.json.size, width: viewMode === 'mobile' ? 375 : 1200 },
                        styles: JSON.parse(JSON.stringify(item.json.styles || {})),
                        children: JSON.parse(JSON.stringify(item.json.children || []))
                    };
                    onAddElement(newElement);
                    setGuideLinePosition(lastSectionY + 10 + (item.json.size?.height || 400));
                    updateCanvasHeight();
                    toast.success('Đã thêm section mới!');
                    setDragPreview(null);
                    setGuidelines([]);
                    return { moved: true, newPosition: { x: 0, y: lastSectionY + 10 } };
                }

                setDragPreview(null);
                setGuidelines([]);
                return { moved: false };
            },
            collect: (monitor) => ({
                isOver: monitor.isOver(),
                dragItem: monitor.getItem(),
            }),
        }), [pageData.elements, viewMode, zoomLevel, gridSize, showGrid, canvasBounds, onAddElement, onMoveChild, updateCanvasHeight]);

        const handleContextMenu = useCallback((id, position) => {
            setContextMenu({ id, position });
        }, []);

        const handleCanvasClick = useCallback(
            (e) => {
                if (e.target === canvasRef.current) {
                    onSelectElement([]);
                    onSelectChild(null, null);
                    setContextMenu(null);
                    setDragPreview(null);
                }
            },
            [onSelectElement, onSelectChild]
        );

        const handleKeyDown = useCallback(
            (e) => {
                const ids = Array.isArray(selectedIds) ? selectedIds : [];
                if (!ids.length) return;

                e.preventDefault();
                e.stopPropagation();

                const step = e.shiftKey ? 10 : 1;

                ids.forEach((id) => {
                    const element = pageData.elements.find((el) => el.id === id);
                    if (element && element.type !== 'section') {
                        const newY = e.key === 'ArrowUp' ? Math.max(0, (element.position[viewMode]?.y || 0) - step) : (element.position[viewMode]?.y || 0) + step;
                        const newX = e.key === 'ArrowLeft' ? Math.max(0, (element.position[viewMode]?.x || 0) - step) : (element.position[viewMode]?.x || 0) + step;
                        switch (e.key) {
                            case 'ArrowUp':
                            case 'ArrowDown':
                                onUpdatePosition(id, { [viewMode]: { x: element.position[viewMode]?.x || 0, y: newY } }, 'absolute');
                                break;
                            case 'ArrowLeft':
                            case 'ArrowRight':
                                onUpdatePosition(id, { [viewMode]: { x: newX, y: element.position[viewMode]?.y || 0 } }, 'absolute');
                                break;
                            case 'Delete':
                            case 'Backspace':
                                onDeleteElement(id);
                                break;
                            case 'Escape':
                                onSelectElement([]);
                                onSelectChild(null, null);
                                setContextMenu(null);
                                setDragPreview(null);
                                setShowPopup(false);
                                break;
                            default:
                                break;
                        }
                    }
                });
            },
            [selectedIds, onUpdatePosition, onDeleteElement, onSelectElement, onSelectChild, viewMode, pageData.elements]
        );

        useHotkeys('ctrl+a', (e) => {
            e.preventDefault();
            onSelectElement(pageData.elements.map((el) => el.id));
        }, [pageData.elements, onSelectElement]);

        useHotkeys('ctrl+g', (e) => {
            e.preventDefault();
            const ids = Array.isArray(selectedIds) ? selectedIds : [];
            if (ids.length > 1) {
                onGroupElements(ids);
            }
        }, [selectedIds, onGroupElements]);

        useEffect(() => {
            const handleClickOutside = () => {
                setContextMenu(null);
                setDragPreview(null);
                setShowPopup(false);
            };
            if (contextMenu || showPopup) {
                document.addEventListener('click', handleClickOutside);
                return () => document.removeEventListener('click', handleClickOutside);
            }
        }, [contextMenu, showPopup]);

        const handleAddSection = (section) => {
            const lastSectionY = pageData.elements
                .filter((el) => el.type === 'section')
                .sort((a, b) => (b.position?.[viewMode]?.y || 0) - (a.position?.[viewMode]?.y || 0))
                .reduce((maxY, el) => {
                    const y = el.position?.[viewMode]?.y || 0;
                    const height = el.size?.height || 400;
                    return Math.max(maxY, y + height);
                }, 0);

            const newElement = {
                id: `${section.id}-${Date.now()}`,
                type: section.json.type,
                componentData: JSON.parse(JSON.stringify(section.json.componentData || { structure: 'ladi-standard' })),
                position: {
                    [viewMode]: { x: 0, y: lastSectionY + 10 },
                    desktop: { x: 0, y: lastSectionY + 10 },
                    tablet: { x: 0, y: lastSectionY + 10 },
                    mobile: { x: 0, y: lastSectionY + 10 },
                },
                size: { ...section.json.size, width: viewMode === 'mobile' ? 375 : 1200 },
                styles: JSON.parse(JSON.stringify(section.json.styles || {})),
                children: JSON.parse(JSON.stringify(section.json.children || []))
            };
            onAddElement(newElement);
            setGuideLinePosition(lastSectionY + 10 + (section.json.size?.height || 400));
            updateCanvasHeight();
            toast.success('Đã thêm section mới!');
            setShowPopup(false);
        };

        const renderDragPreview = () => {
            if (!dragPreview) return null;

            const { x, y, size, type, componentData, styles } = dragPreview;
            return (
                <div
                    className="absolute border-2 border-dashed border-blue-500 bg-blue-500/10 pointer-events-none z-[1000]"
                    style={{
                        transform: `translate(${x}px, ${y}px)`,
                        width: size.width,
                        height: size.height,
                        opacity: 0.5,
                        ...styles,
                    }}
                >
                    {renderComponentContent(type, componentData || {}, {})}
                </div>
            );
        };

        const renderAddSectionButton = () => {
            if (pageData.elements.length > 0) return null;
            return (
                <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 z-[1000]">
                    <button
                        onClick={(e) => {
                            e.stopPropagation();
                            setPopupPosition({ x: e.clientX, y: e.clientY });
                            setShowPopup(true);
                        }}
                        className="px-6 py-3 bg-blue-600 text-white rounded-lg font-semibold text-base"
                    >
                        Thêm Section
                    </button>
                </div>
            );
        };

        const renderSectionPopup = () => {
            if (!showPopup) return null;

            const popupWidth = 600;
            const popupHeight = window.innerHeight * 0.8;
            const offset = 20;
            let adjustedX = popupPosition.x + offset;
            let adjustedY = popupPosition.y + offset;

            if (adjustedX + popupWidth > window.innerWidth) {
                adjustedX = window.innerWidth - popupWidth - 10;
            }
            if (adjustedY + popupHeight > window.innerHeight) {
                adjustedY = window.innerHeight - popupHeight - 10;
            }
            if (adjustedX < 10) adjustedX = 10;
            if (adjustedY < 10) adjustedY = 10;

            return (
                <>
                    <div
                        className="fixed inset-0 bg-black/50 z-[9999]"
                        onClick={() => setShowPopup(false)}
                    />
                    <div
                        className="fixed bg-gray-800 text-white rounded-2xl shadow-2xl w-[600px] max-h-[80vh] overflow-y-auto p-6"
                        style={{ left: adjustedX, top: adjustedY, zIndex: 10000 }}
                        onClick={(e) => e.stopPropagation()}
                    >
                        <h2 className="text-xl mb-5 text-center font-semibold">Chọn Section</h2>
                        {sections.subCategories.map((category) => (
                            <div key={category.id} className="mb-5">
                                <h3 className="text-lg mb-2 text-gray-200">{category.name}</h3>
                                <div className="grid grid-cols-[repeat(auto-fill,minmax(250px,1fr))] gap-5">
                                    {category.templates.map((template) => (
                                        <div
                                            key={template.id}
                                            className="border border-gray-600 rounded-lg p-2 text-center bg-gray-700 hover:bg-gray-600 hover:scale-102 transition-all duration-200 cursor-pointer"
                                            onClick={() => handleAddSection(template)}
                                        >
                                            <img
                                                src={template.previewImage}
                                                alt={template.name}
                                                className="w-full h-[150px] object-cover rounded mb-2"
                                            />
                                            <h4 className="text-base mb-1 text-white">{template.name}</h4>
                                            <p className="text-sm text-gray-300 mb-0">{template.description}</p>
                                            <button
                                                className="mt-2 px-4 py-2 bg-blue-600 text-white rounded font-semibold"
                                            >
                                                Thêm
                                            </button>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        ))}
                        <button
                            onClick={() => setShowPopup(false)}
                            className="absolute top-4 right-4 px-3 py-2 bg-gray-500 text-white rounded-lg font-semibold"
                        >
                            Đóng
                        </button>
                    </div>
                </>
            );
        };

        return (
            <div className="outline-none" onKeyDown={handleKeyDown} tabIndex={0}>
                <div
                    ref={(node) => {
                        canvasRef.current = node;
                        drop(node);
                    }}
                    className={`relative ${viewMode === 'mobile' ? 'w-[375px]' : viewMode === 'tablet' ? 'w-[768px]' : 'w-[1200px]'} mx-auto`}
                    onClick={handleCanvasClick}
                    style={{
                        height: pageData.canvas.height ? `${pageData.canvas.height}px` : 'auto',
                        background: pageData.canvas.background,
                        transform: `scale(${zoomLevel / 100})`,
                        transformOrigin: 'top center',
                    }}
                >
                    <Guidelines guidelines={guidelines} zoomLevel={zoomLevel} />
                    {pageData.elements.map((element) => (
                        <Element
                            key={element.id}
                            element={element}
                            isSelected={selectedIds.includes(element.id)}
                            onSelectElement={onSelectElement}
                            onUpdatePosition={onUpdatePosition}
                            viewMode={viewMode}
                            zoomLevel={zoomLevel}
                            gridSize={gridSize}
                            onContextMenu={handleContextMenu}
                            canvasBounds={canvasBounds}
                            canvasRef={canvasRef}
                            onSelectChild={onSelectChild}
                            selectedChildId={selectedChildId}
                            onAddChild={onAddChild}
                            onUpdateChildPosition={onUpdateChildPosition}
                            onMoveChild={onMoveChild}
                            showGrid={showGrid}
                            setDragPreview={setDragPreview}
                        />
                    ))}
                    {renderDragPreview()}
                    {guideLine?.show && (
                        <>
                            <div
                                className="absolute left-1/2 transform -translate-x-1/2 border-t border-dashed border-blue-500 opacity-50 z-[1001]"
                                style={{ top: guideLinePosition, width: viewMode === 'mobile' ? '375px' : '1200px' }}
                            />
                            {pageData.elements.length > 0 && (
                                <button
                                    onClick={(e) => {
                                        e.stopPropagation();
                                        setPopupPosition({ x: e.clientX, y: e.clientY });
                                        setShowPopup(true);
                                    }}
                                    className="absolute left-1/2 transform -translate-x-1/2 px-6 py-3 bg-blue-600 text-white rounded-lg font-semibold text-base z-[1002]"
                                    style={{ top: guideLinePosition - 20 }}
                                >
                                    Thêm Section
                                </button>
                            )}
                            <div
                                className="absolute top-0 border-l border-dashed border-blue-500 opacity-50 z-[1001]"
                                style={{
                                    left: viewMode === 'mobile' ? 'calc(50% - 187.5px)' : 'calc(50% - 600px)',
                                    height: pageData.canvas.height ? `${pageData.canvas.height}px` : '100%',
                                }}
                            />
                            <div
                                className="absolute top-0 border-l border-dashed border-blue-500 opacity-50 z-[1001]"
                                style={{
                                    left: viewMode === 'mobile' ? 'calc(50% + 187.5px)' : 'calc(50% + 600px)',
                                    height: pageData.canvas.height ? `${pageData.canvas.height}px` : '100%',
                                }}
                            />
                        </>
                    )}
                    <SelectionOverlay
                        selectedElements={pageData.elements.filter((el) => Array.isArray(selectedIds) && selectedIds.includes(el.id))}
                        onResize={onUpdateSize}
                        zoomLevel={zoomLevel}
                    />
                    {contextMenu && (
                        <div
                            className="fixed bg-white border border-gray-300 rounded shadow-md min-w-[120px] z-[10000]"
                            style={{ left: contextMenu.position.x, top: contextMenu.position.y }}
                        >
                            <div
                                className="px-3 py-2 cursor-pointer hover:bg-gray-100"
                                onClick={() => {
                                    onDeleteElement(contextMenu.id);
                                    setContextMenu(null);
                                }}
                            >
                                Xóa
                            </div>
                            <div
                                className="px-3 py-2 cursor-pointer hover:bg-gray-100"
                                onClick={() => {
                                    onGroupElements([contextMenu.id]);
                                    setContextMenu(null);
                                }}
                            >
                                Nhóm
                            </div>
                        </div>
                    )}
                    {renderAddSectionButton()}
                    {renderSectionPopup()}
                </div>
            </div>
        );
    }
);

export default Canvas;
