import React, { useRef, useCallback, useState } from 'react';
import { useDrop, useDrag } from 'react-dnd';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import Guidelines from './Guidelines';
import Rulers from './Rulers';
import SelectionOverlay from './SelectionOverlay';
import '../../styles/Canvas.css';

const ItemTypes = {
    ELEMENT: 'element',
    EXISTING_ELEMENT: 'existingElement',
};

// Hàm tính tọa độ chung
const getCanvasPosition = (mouseX, mouseY, canvasRef, zoomLevel, scrollX = 0, scrollY = 0) => {
    const canvasRect = canvasRef.current?.getBoundingClientRect();
    if (!canvasRect) return { x: 0, y: 0 };
    const x = (mouseX - canvasRect.left + scrollX) / (zoomLevel / 100);
    const y = (mouseY - canvasRect.top + scrollY) / (zoomLevel / 100);
    return { x, y };
};

const snapToGrid = (x, y, gridSize) => ({
    x: Math.round(x / gridSize) * gridSize,
    y: Math.round(y / gridSize) * gridSize,
});

const getElementBounds = (element) => ({
    left: element.position.desktop.x,
    top: element.position.desktop.y,
    right: element.position.desktop.x + element.size.width,
    bottom: element.position.desktop.y + element.size.height,
});

const Element = React.memo(
    ({
         element,
         isSelected,
         onSelectElement,
         onUpdatePosition,
         viewMode,
         zoomLevel,
         gridSize,
         onContextMenu,
     }) => {
        const { id, type, componentData = {}, position = {}, size = { width: 200, height: 50 }, styles = {} } = element;
        const ref = useRef(null);
        const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });

        const [{ isDragging }, drag] = useDrag(() => ({
            type: ItemTypes.EXISTING_ELEMENT,
            item: { id },
            end: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (clientOffset && ref.current) {
                    const canvasRef = ref.current.closest('.lpb-canvas');
                    const pos = getCanvasPosition(
                        clientOffset.x,
                        clientOffset.y,
                        { current: canvasRef },
                        zoomLevel,
                        canvasRef.scrollLeft,
                        canvasRef.scrollTop
                    );
                    const snapped = snapToGrid(pos.x - dragOffset.x, pos.y - dragOffset.y, gridSize);
                    console.group('Drag End Debug');
                    console.log('Client Offset:', clientOffset);
                    console.log('Canvas Rect:', canvasRef.getBoundingClientRect());
                    console.log('Scroll:', { scrollX: canvasRef.scrollLeft, scrollY: canvasRef.scrollTop });
                    console.log('Canvas Position:', pos);
                    console.log('Snapped Position:', snapped);
                    console.groupEnd();
                    onUpdatePosition(id, { [viewMode]: snapped });
                }
            },
            collect: (monitor) => ({
                isDragging: !!monitor.isDragging(),
            }),
        }));

        const handleMouseDown = (e) => {
            e.stopPropagation();
            const elementRect = ref.current.getBoundingClientRect();
            const canvasRef = ref.current.closest('.lpb-canvas');
            const canvasRect = canvasRef.getBoundingClientRect();
            const offsetX = (e.clientX - elementRect.left + canvasRef.scrollLeft) / (zoomLevel / 100);
            const offsetY = (e.clientY - elementRect.top + canvasRef.scrollTop) / (zoomLevel / 100);
            setDragOffset({ x: offsetX, y: offsetY });
            console.log('Mouse Down Offset:', { offsetX, offsetY });
            onSelectElement(id, e.ctrlKey);
        };

        const handleContextMenu = (e) => {
            e.preventDefault();
            e.stopPropagation();
            onContextMenu(id, { x: e.clientX, y: e.clientY });
        };

        const renderContent = () => {
            switch (type) {
                case 'heading':
                    return <h2 style={{ fontSize: componentData.fontSize || '1.5rem', color: componentData.color || '#1f2937' }}>{componentData.content || 'Tiêu đề'}</h2>;
                case 'paragraph':
                    return <p style={{ fontSize: componentData.fontSize || '1rem', color: componentData.color || '#1f2937' }}>{componentData.content || 'Đoạn văn'}</p>;
                case 'button':
                    return <button style={{ padding: '8px 16px', borderRadius: '8px', background: componentData.background || '#2563eb', color: componentData.color || '#fff' }}>{componentData.content || 'Nút'}</button>;
                case 'image':
                    return <img src={componentData.src || 'https://via.placeholder.com/150'} alt="Image" style={{ maxWidth: '100%', height: 'auto' }} />;
                case 'form':
                    return (
                        <form style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                            <input type="text" placeholder="Input" style={{ padding: '8px', borderRadius: '8px' }} />
                            <button type="submit" style={{ background: '#2563eb', color: '#fff', padding: '8px 16px', borderRadius: '8px' }}>Submit</button>
                        </form>
                    );
                default:
                    return <div>{componentData.content || 'Unknown'}</div>;
            }
        };

        return (
            <div
                ref={drag}
                className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${isDragging ? 'lpb-canvas-element-dragging' : ''}`}
                style={{
                    transform: `translate(${position[viewMode]?.x || 0}px, ${position[viewMode]?.y || 0}px)`,
                    width: size.width,
                    height: size.height,
                    zIndex: position.z || 1,
                    ...styles,
                }}
                onMouseDown={handleMouseDown}
                onContextMenu={handleContextMenu}
            >
                {renderContent()}
            </div>
        );
    }
);

const Canvas = React.memo(
    ({
         pageData,
         selectedIds,
         onSelectElement,
         onUpdatePosition,
         onUpdateSize,
         onDeleteElement,
         onAddElement,
         onGroupElements,
         viewMode,
         zoomLevel,
         gridSize,
         showGrid,
     }) => {
        const canvasRef = useRef(null);
        const [guidelines, setGuidelines] = useState([]);
        const [contextMenu, setContextMenu] = useState(null);

        const [{ isOver }, drop] = useDrop(() => ({
            accept: [ItemTypes.ELEMENT, ItemTypes.EXISTING_ELEMENT],
            hover: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (clientOffset && canvasRef.current) {
                    const pos = getCanvasPosition(
                        clientOffset.x,
                        clientOffset.y,
                        canvasRef,
                        zoomLevel,
                        canvasRef.current.scrollLeft,
                        canvasRef.current.scrollTop
                    );
                    const snapped = snapToGrid(pos.x, pos.y, gridSize);
                    const newGuidelines = [];
                    pageData.elements.forEach((el) => {
                        const bounds = getElementBounds(el);
                        if (Math.abs(snapped.x - bounds.left) < 5) newGuidelines.push({ vertical: true, position: bounds.left });
                        if (Math.abs(snapped.x - bounds.right) < 5) newGuidelines.push({ vertical: true, position: bounds.right });
                        if (Math.abs(snapped.y - bounds.top) < 5) newGuidelines.push({ vertical: false, position: bounds.top });
                        if (Math.abs(snapped.y - bounds.bottom) < 5) newGuidelines.push({ vertical: false, position: bounds.bottom });
                    });
                    setGuidelines(newGuidelines);
                    console.group('Hover Debug');
                    console.log('Hover - Client Offset:', clientOffset);
                    console.log('Hover - Canvas Rect:', canvasRef.current.getBoundingClientRect());
                    console.log('Hover - Scroll:', { scrollX: canvasRef.current.scrollLeft, scrollY: canvasRef.current.scrollTop });
                    console.log('Hover - Canvas Position:', pos);
                    console.log('Hover - Snapped:', snapped);
                    console.groupEnd();
                }
            },
            drop: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) {
                    toast.error('Không thể xác định vị trí thả!');
                    return;
                }
                const pos = getCanvasPosition(
                    clientOffset.x,
                    clientOffset.y,
                    canvasRef,
                    zoomLevel,
                    canvasRef.current.scrollLeft,
                    canvasRef.current.scrollTop
                );
                const snapped = snapToGrid(pos.x, pos.y, gridSize);
                console.group('Drop Debug');
                console.log('Drop - Client Offset:', clientOffset);
                console.log('Drop - Canvas Rect:', canvasRef.current.getBoundingClientRect());
                console.log('Drop - Scroll:', { scrollX: canvasRef.current.scrollLeft, scrollY: canvasRef.current.scrollTop });
                console.log('Drop - Canvas Position:', pos);
                console.log('Drop - Snapped Position:', snapped);
                console.groupEnd();
                if (item.json) {
                    onAddElement({
                        id: `${item.id}-${Date.now()}`,
                        type: item.json.type,
                        componentData: item.json.componentData,
                        position: { [viewMode]: snapped, desktop: snapped, tablet: snapped, mobile: snapped },
                        size: item.json.size || { width: 200, height: 50 },
                        styles: item.json.styles || {},
                    });
                    toast.success('Đã thêm phần tử!');
                } else if (item.id) {
                    onUpdatePosition(item.id, { [viewMode]: snapped });
                    toast.success('Đã di chuyển phần tử!');
                }
                setGuidelines([]);
            },
            collect: (monitor) => ({
                isOver: !!monitor.isOver(),
            }),
        }));

        const handleContextMenu = (id, position) => {
            setContextMenu({ id, position });
        };

        const handleKeyDown = useCallback((e) => {
            e.stopPropagation();
            if (!selectedIds.length) return;
            if (e.key === 'ArrowUp') {
                onUpdatePosition(selectedIds, { [viewMode]: { y: -1 * (e.shiftKey ? 10 : 1) } });
            } else if (e.key === 'ArrowDown') {
                onUpdatePosition(selectedIds, { [viewMode]: { y: 1 * (e.shiftKey ? 10 : 1) } });
            } else if (e.key === 'ArrowLeft') {
                onUpdatePosition(selectedIds, { [viewMode]: { x: -1 * (e.shiftKey ? 10 : 1) } });
            } else if (e.key === 'ArrowRight') {
                onUpdatePosition(selectedIds, { [viewMode]: { x: 1 * (e.shiftKey ? 10 : 1) } });
            } else if (e.key === 'Delete') {
                selectedIds.forEach((id) => onDeleteElement(id));
            } else if (e.key === 'Escape') {
                onSelectElement([]);
            }
        }, [selectedIds, onUpdatePosition, onDeleteElement, onSelectElement, viewMode]);

        useHotkeys('ctrl+a', () => onSelectElement(pageData.elements.map((el) => el.id)), [pageData.elements, onSelectElement]);
        useHotkeys('ctrl+g', () => onGroupElements(selectedIds), [selectedIds, onGroupElements]);

        return (
            <div className="lpb-canvas-wrapper" onKeyDown={handleKeyDown} tabIndex={0}>
                <div
                    ref={(node) => {
                        canvasRef.current = node;
                        drop(node);
                    }}
                    className={`lpb-canvas ${viewMode === 'mobile' ? 'lpb-canvas-mobile' : viewMode === 'tablet' ? 'lpb-canvas-tablet' : ''}`}
                    style={{
                        minHeight: '100%',
                        background: pageData.canvas.background,
                        transform: `scale(${zoomLevel / 100})`,
                        transformOrigin: 'top left',
                    }}
                >
                    <Rulers canvasWidth={pageData.canvas.width} canvasHeight={1000} zoomLevel={zoomLevel} />
                    <Guidelines guidelines={guidelines} />
                    {pageData.elements.map((element) => (
                        <Element
                            key={element.id}
                            element={element}
                            isSelected={selectedIds.includes(element.id)}
                            onSelectElement={onSelectElement}
                            onUpdatePosition={onUpdatePosition}
                            onUpdateSize={onUpdateSize}
                            viewMode={viewMode}
                            zoomLevel={zoomLevel}
                            gridSize={showGrid ? gridSize : Infinity}
                            onContextMenu={handleContextMenu}
                        />
                    ))}
                    <SelectionOverlay
                        selectedElements={pageData.elements.filter((el) => selectedIds.includes(el.id))}
                        onResize={onUpdateSize}
                        zoomLevel={zoomLevel}
                    />
                    {contextMenu && (
                        <div
                            className="lpb-context-menu"
                            style={{ left: contextMenu.position.x, top: contextMenu.position.y }}
                        >
                            <div className="lpb-context-menu-item" onClick={() => onDeleteElement(contextMenu.id)}>Delete</div>
                            <div className="lpb-context-menu-item" onClick={() => onGroupElements([contextMenu.id])}>Group</div>
                        </div>
                    )}
                </div>
                {/* Visual Debug */}
                <div
                    style={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        border: '2px solid red',
                        pointerEvents: 'none',
                        zIndex: 9999,
                    }}
                />
            </div>
        );
    }
);

export default Canvas;
