import React, { useState } from 'react';
import api from '@landinghub/api';
import { useNavigate } from 'react-router-dom';
import '../styles/register.css'
const Register = () => {
    const [form, setForm] = useState({ name: '', email: '', password: '' });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleRegister = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);
        try {
            const res = await api.post('/api/auth/register', form);
            localStorage.setItem('token', res.data.token);
            navigate('/dashboard');
        } catch (err) {
            setError(err.response?.data?.msg || 'Register failed');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="register-container" >
            <form className="form" onSubmit={handleRegister}>
                <h2 style={{ textAlign: 'center', marginBottom: 20 }}>Đăng Ký</h2>

                {error && (
                    <div style={{ color: 'red', marginBottom: 12, textAlign: 'center' }}>
                        {error}
                    </div>
                )}

                <div className="flex-column">
                    <label>Tên</label>
                    <div className="inputForm">
                        <input
                            type="text"
                            name="name"
                            value={form.name}
                            onChange={handleChange}
                            placeholder="Enter your Name"
                            className="input"
                            required
                        />
                    </div>
                </div>

                <div className="flex-column">
                    <label>Email</label>
                    <div className="inputForm">
                        <input
                            type="email"
                            name="email"
                            value={form.email}
                            onChange={handleChange}
                            placeholder="Enter your Email"
                            className="input"
                            required
                        />
                    </div>
                </div>

                <div className="flex-column">
                    <label>Password</label>
                    <div className="inputForm">
                        <input
                            type="password"
                            name="password"
                            value={form.password}
                            onChange={handleChange}
                            placeholder="Enter your Password"
                            className="input"
                            required
                        />
                    </div>
                </div>

                <button type="submit" className="button-submit" disabled={loading}>
                    {loading ? 'Đang đăng ký...' : 'Đăng Ký'}
                </button>

                <p className="p" style={{ textAlign: 'center', marginTop: 12 }}>
                    Already have an account? <span className="span" style={{ cursor: 'pointer' }} onClick={() => navigate('/login')}>Sign In</span>
                </p>
            </form>
        </div>
    );
};

export default Register;
