import React, { useState, useRef, useCallback } from 'react';
import '../../styles/Toolbar.css'
const QuickEditToolkit = ({ element, position, onEdit, onDuplicate, onDelete, onMoveUp, onMoveDown, onClose }) => {
    if (!element || !position) return null;

    return (
        <div
            className="absolute bg-white shadow-lg border border-gray-200 rounded-lg p-2 flex gap-1 z-50"
            style={{
                left: position.x,
                top: position.y - 50,
                minWidth: '200px'
            }}
        >
            <button
                onClick={onEdit}
                className="p-2 hover:bg-gray-100 rounded text-gray-600"
                title="Chỉnh sửa"
            >
                <i className="fas fa-edit"></i>
            </button>
            <button
                onClick={onDuplicate}
                className="p-2 hover:bg-gray-100 rounded text-gray-600"
                title="Nhân bản"
            >
                <i className="fas fa-copy"></i>
            </button>
            <button
                onClick={onMoveUp}
                className="p-2 hover:bg-gray-100 rounded text-gray-600"
                title="Di chuyển lên"
            >
                <i className="fas fa-arrow-up"></i>
            </button>
            <button
                onClick={onMoveDown}
                className="p-2 hover:bg-gray-100 rounded text-gray-600"
                title="Di chuyển xuống"
            >
                <i className="fas fa-arrow-down"></i>
            </button>
            <div className="w-px bg-gray-300 mx-1"></div>
            <button
                onClick={onDelete}
                className="p-2 hover:bg-red-100 rounded text-red-600"
                title="Xóa"
            >
                <i className="fas fa-trash"></i>
            </button>
            <button
                onClick={onClose}
                className="p-2 hover:bg-gray-100 rounded text-gray-600 ml-auto"
                title="Đóng"
            >
                <i className="fas fa-times"></i>
            </button>
        </div>
    );
};
export default QuickEditToolkit;
