import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles/LandingList.css';

const LandingList = ({
                         landingPages,
                         filteredPages,
                         menuOpen,
                         setMenuOpen,
                         handlePreviewPage,
                         handleEditPage,
                         handlePublishPage,
                         handleDeletePage,
                         formatDate,
                     }) => {
    const navigate = useNavigate();

    const handlePageClick = (page) => {
        navigate(`/pages/create?id=${page.id}`);
    };

    const getStatusInfo = (status) => {
        switch (status) {
            case 'ƒê√É XU·∫§T B·∫¢N':
                return { color: '#10b981', bg: '#d1fae5', text: 'ƒê√£ xu·∫•t b·∫£n' };
            case 'CH∆ØA XU·∫§T B·∫¢N':
                return { color: '#6b7280', bg: '#f3f4f6', text: 'Ch∆∞a xu·∫•t b·∫£n' };
            case 'ARCHIVED':
                return { color: '#3b82f6', bg: '#dbeafe', text: 'ƒê√£ l∆∞u tr·ªØ' };
            default:
                return { color: '#ef4444', bg: '#fee2e2', text: 'L·ªói' };
        }
    };

    return (
        <div className="landing-list">
            {filteredPages.length === 0 ? (
                <div className="empty-state">
                    <div className="empty-icon">üìÑ</div>
                    <h3>Ch∆∞a c√≥ landing page n√†o</h3>
                    <p>T·∫°o landing page ƒë·∫ßu ti√™n c·ªßa b·∫°n ƒë·ªÉ b·∫Øt ƒë·∫ßu</p>
                </div>
            ) : (
                filteredPages.map((page) => {
                    const statusInfo = getStatusInfo(page.status);
                    return (
                        <div
                            key={page.id || page._id}
                            className="page-card"
                            onClick={() => handlePageClick(page)}
                        >
                            {/* Thumbnail Preview */}
                            <div className="page-thumbnail">
                                {page.screenshot_url ? (
                                    <img
                                        src={page.screenshot_url}
                                        alt={page.name}
                                        loading="lazy"
                                        onError={(e) => {
                                            e.target.style.display = 'none';
                                            e.target.nextSibling.style.display = 'flex';
                                        }}
                                    />
                                ) : null}
                                <div className="thumbnail-placeholder" style={{display: page.screenshot_url ? 'none' : 'flex'}}>
                                    <div className="placeholder-icon">üé®</div>
                                    <span>ƒêang t·∫°o preview...</span>
                                </div>

                                {/* Status Badge */}
                                <div
                                    className="status-badge"
                                    style={{
                                        backgroundColor: statusInfo.bg,
                                        color: statusInfo.color
                                    }}
                                >
                                    <span className="status-dot" style={{ backgroundColor: statusInfo.color }}></span>
                                    {statusInfo.text}
                                </div>
                            </div>

                            {/* Page Info */}
                            <div className="page-content">
                                <div className="page-header">
                                    <h3 className="page-title" title={page.name}>
                                        {page.name}
                                    </h3>
                                    <div className="page-meta">
                                        <span className="meta-item">
                                            <svg width="12" height="12" viewBox="0 0 24 24" fill="currentColor">
                                                <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z"/>
                                            </svg>
                                            T·∫°o: {formatDate(page.created_at)}
                                        </span>
                                        <span className="meta-item">
                                            <svg width="12" height="12" viewBox="0 0 24 24" fill="currentColor">
                                                <path d="M9 11H7v6h2v-6zm4 0h-2v6h2v-6zm4 0h-2v6h2v-6zm2-7H5v2h4v1H3v14h18V7h-6V6h4V4zM7 19V9h10v10H7z"/>
                                            </svg>
                                            C·∫≠p nh·∫≠t: {formatDate(page.updated_at)}
                                        </span>
                                    </div>
                                </div>

                                {/* Page Stats */}
                                <div className="page-stats">
                                    <div className="stat-item">
                                        <span className="stat-number">{page.views || 0}</span>
                                        <span className="stat-label">L∆∞·ª£t xem</span>
                                    </div>
                                    <div className="stat-item">
                                        <span className="stat-number">{page.conversions || 0}</span>
                                        <span className="stat-label">Chuy·ªÉn ƒë·ªïi</span>
                                    </div>
                                    <div className="stat-item">
                                        <span className="stat-number">{page.revenue || '0ƒë'}</span>
                                        <span className="stat-label">Doanh thu</span>
                                    </div>
                                </div>

                                {/* Actions */}
                                <div className="page-actions" onClick={(e) => e.stopPropagation()}>
                                    <button
                                        className="action-btn edit-btn"
                                        onClick={() => handleEditPage(page)}
                                        title="Ch·ªânh s·ª≠a"
                                    >
                                        <svg width="14" height="14" viewBox="0 0 24 24" fill="currentColor">
                                            <path d="M3 17.25V21h3.75L17.81 9.94l-3.75-3.75L3 17.25zM20.71 7.04c.39-.39.39-1.02 0-1.41l-2.34-2.34c-.39-.39-1.02-.39-1.41 0l-1.83 1.83 3.75 3.75 1.83-1.83z"/>
                                        </svg>
                                        S·ª≠a
                                    </button>

                                    <button
                                        className="action-btn preview-btn"
                                        onClick={() => handlePreviewPage(page)}
                                        title="Xem tr∆∞·ªõc"
                                    >
                                        <svg width="14" height="14" viewBox="0 0 24 24" fill="currentColor">
                                            <path d="M12 4.5C7 4.5 2.73 7.61 1 12c1.73 4.39 6 7.5 11 7.5s9.27-3.11 11-7.5c-1.73-4.39-6-7.5-11-7.5zM12 17c-2.76 0-5-2.24-5-5s2.24-5 5-5 5 2.24 5 5-2.24 5-5 5zm0-8c-1.66 0-3 1.34-3 3s1.34 3 3 3 3-1.34 3-3-1.34-3-3-3z"/>
                                        </svg>
                                        Xem
                                    </button>

                                    <button
                                        className="action-btn publish-btn"
                                        onClick={() => handlePublishPage(page.id || page._id)}
                                        title="Tri·ªÉn khai"
                                    >
                                        <svg width="14" height="14" viewBox="0 0 24 24" fill="currentColor">
                                            <path d="M5 4v3h5.5v12h3V7H19V4z"/>
                                        </svg>
                                        Xu·∫•t b·∫£n
                                    </button>

                                    <button
                                        className="action-btn delete-btn"
                                        onClick={() => handleDeletePage(page.id || page._id)}
                                        title="X√≥a"
                                    >
                                        <svg width="14" height="14" viewBox="0 0 24 24" fill="currentColor">
                                            <path d="M6 19c0 1.1.9 2 2 2h8c1.1 0 2-.9 2-2V7H6v12zM19 4h-3.5l-1-1h-5l-1 1H5v2h14V4z"/>
                                        </svg>
                                        X√≥a
                                    </button>
                                </div>
                            </div>
                        </div>
                    );
                })
            )}
        </div>
    );
};

export default LandingList;
