import React, { useState, useRef, useCallback } from 'react';
import COMPONENT_LIBRARY from '../constants/componentLibrary';


const ComponentLibrary = ({ onDragStart }) => {
    const [activeCategory, setActiveCategory] = useState('text');

    const categories = [
        { id: 'text', name: 'Văn bản', icon: 'fas fa-font' },
        { id: 'buttons', name: 'Nút bấm', icon: 'fas fa-mouse-pointer' },
        { id: 'sections', name: 'Section', icon: 'fas fa-layer-group' },
        { id: 'media', name: 'Media', icon: 'fas fa-image' },
        { id: 'forms', name: 'Form', icon: 'fas fa-wpforms' }
    ];

    return (
        <div className="w-64 bg-white border-r border-gray-200 h-full overflow-y-auto">
            <div className="p-4 border-b">
                <h2 className="font-semibold text-gray-800">Phần tử</h2>
            </div>

            {/* Category Tabs */}
            <div className="border-b">
                {categories.map(cat => (
                    <button
                        key={cat.id}
                        onClick={() => setActiveCategory(cat.id)}
                        className={`w-full p-3 text-left flex items-center gap-2 hover:bg-gray-50 ${
                            activeCategory === cat.id ? 'bg-blue-50 border-r-2 border-blue-500 text-blue-600' : 'text-gray-600'
                        }`}
                    >
                        <i className={cat.icon}></i>
                        <span className="text-sm font-medium">{cat.name}</span>
                    </button>
                ))}
            </div>

            {/* Component List */}
            <div className="p-2">
                {COMPONENT_LIBRARY[activeCategory]?.map(component => (
                    <div
                        key={component.id}
                        draggable
                        onDragStart={(e) => onDragStart(e, component)}
                        className="p-3 mb-2 border border-gray-200 rounded-lg cursor-move hover:border-blue-300 hover:shadow-sm transition-all"
                    >
                        <div className="flex items-center gap-3">
                            <i className={`${component.icon} text-gray-500`}></i>
                            <div>
                                <div className="font-medium text-sm text-gray-800">{component.name}</div>
                                <div className="text-xs text-gray-500">Kéo để thêm</div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

// Main Canvas Area
const Canvas = ({ pageStructure, onDrop, selectedElement, onSelectElement }) => {
    const canvasRef = useRef(null);

    const handleDrop = (e) => {
        e.preventDefault();
        const componentData = JSON.parse(e.dataTransfer.getData('component'));
        const rect = canvasRef.current.getBoundingClientRect();
        const position = {
            x: e.clientX - rect.left,
            y: e.clientY - rect.top
        };
        onDrop(componentData, position);
    };

    const handleDragOver = (e) => {
        e.preventDefault();
    };

    const renderComponent = (component, index) => {
        const isSelected = selectedElement?.id === component.id;

        switch (component.json.type) {
            case 'heading':
                return (
                    <div
                        key={component.id || index}
                        className={`relative ${isSelected ? 'ring-2 ring-blue-500' : ''}`}
                        onClick={() => onSelectElement(component)}
                        style={component.json.styles}
                    >
                        <h1>{component.json.content}</h1>
                        {isSelected && (
                            <div className="absolute -top-6 left-0 bg-blue-500 text-white text-xs px-2 py-1 rounded">
                                Tiêu đề H1
                            </div>
                        )}
                    </div>
                );

            case 'paragraph':
                return (
                    <div
                        key={component.id || index}
                        className={`relative ${isSelected ? 'ring-2 ring-blue-500' : ''}`}
                        onClick={() => onSelectElement(component)}
                        style={component.json.styles}
                    >
                        <p>{component.json.content}</p>
                        {isSelected && (
                            <div className="absolute -top-6 left-0 bg-blue-500 text-white text-xs px-2 py-1 rounded">
                                Đoạn văn
                            </div>
                        )}
                    </div>
                );

            case 'button':
                return (
                    <div
                        key={component.id || index}
                        className={`relative inline-block ${isSelected ? 'ring-2 ring-blue-500' : ''}`}
                        onClick={() => onSelectElement(component)}
                    >
                        <button style={component.json.styles}>
                            {component.json.content}
                        </button>
                        {isSelected && (
                            <div className="absolute -top-6 left-0 bg-blue-500 text-white text-xs px-2 py-1 rounded">
                                Nút bấm
                            </div>
                        )}
                    </div>
                );

            case 'section':
                return (
                    <section
                        key={component.id || index}
                        className={`relative ${isSelected ? 'ring-2 ring-blue-500' : ''}`}
                        onClick={() => onSelectElement(component)}
                        style={{
                            ...component.json.styles,
                            background: component.json.background?.value
                        }}
                    >
                        {component.json.layout === 'hero' && (
                            <div className="container mx-auto">
                                <h1 className="text-4xl font-bold mb-4">{component.json.content.title}</h1>
                                <p className="text-xl mb-8">{component.json.content.subtitle}</p>
                                <div className="flex gap-4 justify-center">
                                    {component.json.content.buttons?.map((btn, idx) => (
                                        <button
                                            key={idx}
                                            className={`px-6 py-3 rounded-lg font-semibold ${
                                                btn.type === 'primary'
                                                    ? 'bg-white text-blue-600'
                                                    : 'border-2 border-white text-white bg-transparent'
                                            }`}
                                        >
                                            {btn.text}
                                        </button>
                                    ))}
                                </div>
                            </div>
                        )}

                        {component.json.layout === 'feature-grid' && (
                            <div className="container mx-auto">
                                <div className="text-center mb-12">
                                    <h2 className="text-3xl font-bold mb-4 text-gray-800">{component.json.content.title}</h2>
                                    <p className="text-gray-600">{component.json.content.subtitle}</p>
                                </div>
                                <div className="grid md:grid-cols-3 gap-8">
                                    {component.json.content.features?.map((feature, idx) => (
                                        <div key={idx} className="text-center p-6 bg-white rounded-lg shadow-sm">
                                            <i className={`${feature.icon} text-3xl text-blue-600 mb-4`}></i>
                                            <h3 className="text-xl font-semibold mb-3">{feature.title}</h3>
                                            <p className="text-gray-600">{feature.description}</p>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}

                        {isSelected && (
                            <div className="absolute top-2 left-2 bg-blue-500 text-white text-xs px-2 py-1 rounded">
                                {component.json.layout === 'hero' ? 'Hero Section' : 'Feature Grid'}
                            </div>
                        )}
                    </section>
                );

            default:
                return null;
        }
    };

    return (
        <div
            ref={canvasRef}
            className="flex-1 bg-white overflow-auto"
            onDrop={handleDrop}
            onDragOver={handleDragOver}
        >
            <div className="min-h-full">
                {pageStructure.length === 0 ? (
                    <div className="h-full flex items-center justify-center text-gray-400">
                        <div className="text-center">
                            <i className="fas fa-mouse-pointer text-6xl mb-4"></i>
                            <p className="text-xl">Kéo thả component vào đây để bắt đầu</p>
                        </div>
                    </div>
                ) : (
                    pageStructure.map((component, index) => renderComponent(component, index))
                )}
            </div>
        </div>
    );
};

// Right Sidebar - Properties Panel
const PropertiesPanel = ({ selectedElement, onUpdateElement }) => {
    if (!selectedElement) {
        return (
            <div className="w-80 bg-white border-l border-gray-200 p-4">
                <div className="text-center text-gray-500">
                    <i className="fas fa-mouse-pointer text-4xl mb-4"></i>
                    <p>Chọn một element để chỉnh sửa thuộc tính</p>
                </div>
            </div>
        );
    }

    const handleStyleChange = (property, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                styles: {
                    ...selectedElement.json.styles,
                    [property]: value
                }
            }
        };
        onUpdateElement(updated);
    };

    const handleContentChange = (value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                content: value
            }
        };
        onUpdateElement(updated);
    };

    return (
        <div className="w-80 bg-white border-l border-gray-200 h-full overflow-y-auto">
            <div className="p-4 border-b">
                <h3 className="font-semibold text-gray-800">Thuộc tính</h3>
            </div>

            <div className="p-4 space-y-6">
                {/* Content Section */}
                {(selectedElement.json.type === 'heading' ||
                    selectedElement.json.type === 'paragraph' ||
                    selectedElement.json.type === 'button') && (
                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">
                            Nội dung
                        </label>
                        <textarea
                            value={selectedElement.json.content}
                            onChange={(e) => handleContentChange(e.target.value)}
                            className="w-full p-2 border border-gray-300 rounded-md resize-none"
                            rows={3}
                        />
                    </div>
                )}

                {/* Typography */}
                <div>
                    <h4 className="font-medium text-gray-700 mb-3">Typography</h4>
                    <div className="space-y-3">
                        <div>
                            <label className="block text-sm text-gray-600 mb-1">Font Size</label>
                            <input
                                type="text"
                                value={selectedElement.json.styles?.fontSize || '16px'}
                                onChange={(e) => handleStyleChange('fontSize', e.target.value)}
                                className="w-full p-2 border border-gray-300 rounded-md text-sm"
                            />
                        </div>
                        <div>
                            <label className="block text-sm text-gray-600 mb-1">Font Weight</label>
                            <select
                                value={selectedElement.json.styles?.fontWeight || 'normal'}
                                onChange={(e) => handleStyleChange('fontWeight', e.target.value)}
                                className="w-full p-2 border border-gray-300 rounded-md text-sm"
                            >
                                <option value="normal">Normal</option>
                                <option value="bold">Bold</option>
                                <option value="600">Semi Bold</option>
                                <option value="300">Light</option>
                            </select>
                        </div>
                        <div>
                            <label className="block text-sm text-gray-600 mb-1">Color</label>
                            <input
                                type="color"
                                value={selectedElement.json.styles?.color || '#000000'}
                                onChange={(e) => handleStyleChange('color', e.target.value)}
                                className="w-full h-10 border border-gray-300 rounded-md"
                            />
                        </div>
                    </div>
                </div>

                {/* Background */}
                {selectedElement.json.type === 'button' && (
                    <div>
                        <h4 className="font-medium text-gray-700 mb-3">Background</h4>
                        <input
                            type="color"
                            value={selectedElement.json.styles?.backgroundColor || '#007bff'}
                            onChange={(e) => handleStyleChange('backgroundColor', e.target.value)}
                            className="w-full h-10 border border-gray-300 rounded-md"
                        />
                    </div>
                )}

                {/* Spacing */}
                <div>
                    <h4 className="font-medium text-gray-700 mb-3">Spacing</h4>
                    <div className="space-y-3">
                        <div>
                            <label className="block text-sm text-gray-600 mb-1">Margin Bottom</label>
                            <input
                                type="text"
                                value={selectedElement.json.styles?.marginBottom || '0px'}
                                onChange={(e) => handleStyleChange('marginBottom', e.target.value)}
                                className="w-full p-2 border border-gray-300 rounded-md text-sm"
                            />
                        </div>
                        {selectedElement.json.type === 'button' && (
                            <div>
                                <label className="block text-sm text-gray-600 mb-1">Padding</label>
                                <input
                                    type="text"
                                    value={selectedElement.json.styles?.padding || '12px 30px'}
                                    onChange={(e) => handleStyleChange('padding', e.target.value)}
                                    className="w-full p-2 border border-gray-300 rounded-md text-sm"
                                />
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

// Top Toolbar
const Toolbar = ({ onSave, onPreview, onExport, onGenerateCode }) => {
    return (
        <div className="h-14 bg-white border-b border-gray-200 flex items-center justify-between px-4">
            <div className="flex items-center gap-2">
                <button className="p-2 text-gray-600 hover:bg-gray-100 rounded-md">
                    <i className="fas fa-bars"></i>
                </button>
                <h1 className="text-lg font-semibold text-gray-800">Landing Builder</h1>
            </div>

            <div className="flex items-center gap-2">
                <button
                    onClick={onPreview}
                    className="px-4 py-2 text-gray-600 hover:bg-gray-100 rounded-md flex items-center gap-2"
                >
                    <i className="fas fa-eye"></i>
                    Preview
                </button>
                <button
                    onClick={onSave}
                    className="px-4 py-2 text-gray-600 hover:bg-gray-100 rounded-md flex items-center gap-2"
                >
                    <i className="fas fa-save"></i>
                    Lưu
                </button>
                <button
                    onClick={onGenerateCode}
                    className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 flex items-center gap-2"
                >
                    <i className="fas fa-code"></i>
                    Tạo Code
                </button>
            </div>
        </div>
    );
};

// Main App
const CreateLandingPage = () => {
    const [pageStructure, setPageStructure] = useState([]);
    const [selectedElement, setSelectedElement] = useState(null);

    const handleDragStart = (e, component) => {
        e.dataTransfer.setData('component', JSON.stringify(component));
    };

    const handleDrop = (componentData, position) => {
        const newComponent = {
            ...componentData,
            id: `${componentData.id}-${Date.now()}`
        };
        setPageStructure(prev => [...prev, newComponent]);
    };

    const handleSelectElement = (element) => {
        setSelectedElement(element);
    };

    const handleUpdateElement = (updatedElement) => {
        setPageStructure(prev =>
            prev.map(el => el.id === updatedElement.id ? updatedElement : el)
        );
        setSelectedElement(updatedElement);
    };

    const handleGenerateCode = async () => {
        const pageConfig = {
            components: pageStructure.map(comp => comp.json),
            meta: {
                title: "Generated Landing Page",
                description: "Page generated from JSON"
            }
        };

        try {
            // Call backend API
            const response = await fetch('/api/generate-html', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(pageConfig)
            });

            if (response.ok) {
                const result = await response.json();
                console.log('Generated code:', result);
                // Handle success - maybe show modal with generated code
            }
        } catch (error) {
            console.error('Error generating code:', error);
        }
    };

    const handlePreview = () => {
        // Open preview in new window
        window.open('/preview', '_blank');
    };

    const handleSave = () => {
        localStorage.setItem('page-structure', JSON.stringify(pageStructure));
        alert('Đã lưu trang!');
    };

    return (
        <div className="h-screen flex flex-col bg-gray-50">
            <Toolbar
                onSave={handleSave}
                onPreview={handlePreview}
                onGenerateCode={handleGenerateCode}
            />

            <div className="flex-1 flex overflow-hidden">
                <ComponentLibrary onDragStart={handleDragStart} />
                <Canvas
                    pageStructure={pageStructure}
                    onDrop={handleDrop}
                    selectedElement={selectedElement}
                    onSelectElement={handleSelectElement}
                />
                <PropertiesPanel
                    selectedElement={selectedElement}
                    onUpdateElement={handleUpdateElement}
                />
            </div>
        </div>
    );
};

export default CreateLandingPage;
