import React, { useState, useCallback, useRef, useEffect } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { useHotkeys } from 'react-hotkeys-hook';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Toolbar from './create-page/Toolbar';
import ComponentLibrary from './create-page/ComponentLibrary';
import Canvas from './create-page/Canvas';
import PropertiesPanel from './create-page/PropertiesPanel';
import '../styles/CreateLanding.css';

const CreateLandingPage = () => {
    const [pageData, setPageData] = useState({
        canvas: { width: 1200, height: 'auto', background: '#ffffff' },
        elements: [],
    });
    const [selectedIds, setSelectedIds] = useState([]);
    const [history, setHistory] = useState([pageData]);
    const [historyIndex, setHistoryIndex] = useState(0);
    const [viewMode, setViewMode] = useState('desktop');
    const [zoomLevel, setZoomLevel] = useState(100);
    const [leftSidebarCollapsed, setLeftSidebarCollapsed] = useState(false); // Sửa để hiển thị mặc định
    const [rightSidebarCollapsed, setRightSidebarCollapsed] = useState(false); // Sửa để hiển thị mặc định
    const fileInputRef = useRef(null);

    const updatePosition = useCallback((id, position) => {
        setPageData((prev) => {
            const newData = {
                ...prev,
                elements: prev.elements.map((el) =>
                    el.id === id ? { ...el, position: { ...el.position, ...position } } : el
                ),
            };
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newData]);
            setHistoryIndex((prev) => prev + 1);
            return newData;
        });
        toast.success('Đã cập nhật vị trí!');
    }, [historyIndex]);

    const updateSize = useCallback((id, size) => {
        setPageData((prev) => {
            const newData = {
                ...prev,
                elements: prev.elements.map((el) =>
                    el.id === id ? { ...el, size: { ...el.size, ...size } } : el
                ),
            };
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newData]);
            setHistoryIndex((prev) => prev + 1);
            return newData;
        });
        toast.success('Đã cập nhật kích thước!');
    }, [historyIndex]);

    const addElement = useCallback((element) => {
        setPageData((prev) => {
            const newData = {
                ...prev,
                elements: [...prev.elements, element],
            };
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newData]);
            setHistoryIndex((prev) => prev + 1);
            return newData;
        });
        setSelectedIds([element.id]);
    }, [historyIndex]);

    const deleteElement = useCallback((id) => {
        setPageData((prev) => {
            const newData = {
                ...prev,
                elements: prev.elements.filter((el) => el.id !== id),
            };
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newData]);
            setHistoryIndex((prev) => prev + 1);
            return newData;
        });
        setSelectedIds((prev) => prev.filter((sid) => sid !== id));
        toast.success('Đã xóa phần tử!');
    }, [historyIndex]);

    const updateElement = useCallback((id, updates) => {
        setPageData((prev) => {
            const newData = {
                ...prev,
                elements: prev.elements.map((el) =>
                    el.id === id ? { ...el, ...updates } : el
                ),
            };
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newData]);
            setHistoryIndex((prev) => prev + 1);
            return newData;
        });
        toast.success('Đã cập nhật phần tử!');
    }, [historyIndex]);

    const selectElement = useCallback((id, multi = false) => {
        setSelectedIds((prev) => {
            if (multi) {
                return prev.includes(id) ? prev.filter((sid) => sid !== id) : [...prev, id];
            }
            return [id];
        });
    }, []);

    const undo = useCallback(() => {
        if (historyIndex > 0) {
            setHistoryIndex((prev) => prev - 1);
            setPageData(history[historyIndex - 1]);
            toast.info('Đã hoàn tác!');
        }
    }, [history, historyIndex]);

    const redo = useCallback(() => {
        if (historyIndex < history.length - 1) {
            setHistoryIndex((prev) => prev + 1);
            setPageData(history[historyIndex + 1]);
            toast.info('Đã khôi phục!');
        }
    }, [history, historyIndex]);

    const handleZoom = useCallback((direction) => {
        setZoomLevel((prev) => direction === 'in' ? Math.min(200, prev + 10) : Math.max(50, prev - 10));
    }, []);

    const handleSave = useCallback(() => {
        localStorage.setItem('landing-page-builder', JSON.stringify({ pageData, viewMode, zoomLevel }));
        toast.success('Đã lưu trang!');
    }, [pageData, viewMode, zoomLevel]);

    useHotkeys('ctrl+s', handleSave, [handleSave]);
    useHotkeys('ctrl+z', undo, [undo]);
    useHotkeys('ctrl+shift+z', redo, [redo]);

    useEffect(() => {
        const autoSave = setTimeout(() => {
            if (pageData.elements.length > 0) {
                localStorage.setItem('landing-page-builder-autosave', JSON.stringify({ pageData, viewMode, zoomLevel }));
                toast.info('Đã tự động lưu!');
            }
        }, 30000);
        return () => clearTimeout(autoSave);
    }, [pageData, viewMode, zoomLevel]);

    return (
        <DndProvider backend={HTML5Backend}>
            <div className="lpb-container">
                <link
                    rel="stylesheet"
                    href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.1/css/all.min.css"
                    crossOrigin="anonymous"
                    referrerPolicy="no-referrer"
                />
                <ToastContainer position="top-right" autoClose={3000} hideProgressBar />
                <Toolbar
                    onSave={handleSave}
                    onUndo={undo}
                    onRedo={redo}
                    canUndo={historyIndex > 0}
                    canRedo={historyIndex < history.length - 1}
                    viewMode={viewMode}
                    onViewModeChange={setViewMode}
                    onZoom={handleZoom}
                />
                <div className="lpb-main-content">
                    <div className={`lpb-sidebar-left ${leftSidebarCollapsed ? 'lpb-sidebar-collapsed' : ''}`}>
                        <ComponentLibrary
                            isCollapsed={leftSidebarCollapsed}
                            onToggle={() => setLeftSidebarCollapsed(!leftSidebarCollapsed)}
                            onAddElement={addElement}
                        />
                    </div>
                    <div className="lpb-canvas-container">
                        <Canvas
                            pageData={pageData}
                            selectedIds={selectedIds}
                            onSelectElement={selectElement}
                            onUpdatePosition={updatePosition}
                            onUpdateSize={updateSize}
                            onDeleteElement={deleteElement}
                            onAddElement={addElement}
                            viewMode={viewMode}
                            zoomLevel={zoomLevel}
                        />
                    </div>
                    <div className={`lpb-sidebar-right ${rightSidebarCollapsed ? 'lpb-sidebar-collapsed' : ''}`}>
                        <PropertiesPanel
                            selectedElement={pageData.elements.find((el) => selectedIds.includes(el.id))}
                            onUpdateElement={updateElement}
                            isCollapsed={rightSidebarCollapsed}
                            onToggle={() => setRightSidebarCollapsed(!rightSidebarCollapsed)}
                        />
                    </div>
                </div>
            </div>
        </DndProvider>
    );
};

export default CreateLandingPage;
