                  children: popupChildren
                });
                break;
              case 'html':
                children.push({
                  id: childId,
                  type: 'html',
                  componentData: {
                    content: child.innerHTML || '<p>Custom HTML content</p>',
                    ...childComponentData
                  },
                  styles: childStyles,
                  position: childPosition,
                  size: childSize
                });
                break;
              default:
                console.warn(`Unknown child type: ${childType}`, child);
                break;
            }
          });
        }
      }

      // Tạo element chính
      elements.push({
        id,
        type,
        componentData: {
          ...componentData,
          backgroundColor: styles.backgroundColor || componentData.backgroundColor || '#ffffff'
        },
        position,
        size,
        styles: {
          ...styles,
          borderRadius: styles.borderRadius || CSS_VARIABLES['--border-radius'] || '8px',
          boxShadow: styles.boxShadow || CSS_VARIABLES['--shadow'] || 'none',
          zIndex: styles.zIndex || (type === 'section' ? 1 : 10)
        },
        children
      });
    });
    const pageData = {
      canvas: canvasData,
      elements
    };
    console.log('Parsed pageData:', JSON.stringify(pageData, null, 2));
    return pageData;
  } catch (error) {
    console.error('Error parsing HTML:', error);
    toast.error('Lỗi khi phân tích nội dung trang: ' + error.message);
    return {
      canvas: {
        width: parseInt(CSS_VARIABLES['--canvas-desktop']) || 1200,
        background: CSS_VARIABLES['--white'] || '#ffffff'
      },
      elements: []
    };
  }
};

// Note: Hàm parseDataAttribute không có trong mã gốc, giả sử nó được định nghĩa ở nơi khác hoặc bỏ qua nếu không cần.
function parseDataAttribute(node, attr) {
  // Giả sử hàm này tồn tại ở mã gốc, nếu không thì cần định nghĩa hoặc điều chỉnh.
  return JSON.parse(node.getAttribute(attr) || '{}');
}

