import React, { useState, useEffect } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { toast } from 'react-toastify';
import { parse } from 'node-html-parser';
import Canvas from '../components/create-page/Canvas';
import ComponentLibrary from '../components/create-page/ComponentLibrary';
import PropertiesPanel from '../components/create-page/PropertiesPanel';
import Toolbar from './create-page/Toolbar';
import api from '@landinghub/api';
import '../styles/CreateLanding.css';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

// Định nghĩa các biến CSS tương ứng với CreateLanding.css
const CSS_VARIABLES = {
    '--primary': '#6366f1',
    '--primary-dark': '#4f46e5',
    '--primary-light': '#a5b4fc',
    '--secondary': '#f1f5f9',
    '--accent': '#06b6d4',
    '--success': '#10b981',
    '--warning': '#f59e0b',
    '--danger': '#ef4444',
    '--dark': '#1e293b',
    '--light': '#f8fafc',
    '--white': '#ffffff',
    '--text-primary': '#334155',
    '--text-secondary': '#64748b',
    '--text-light': '#94a3b8',
    '--text-inverse': '#ffffff',
    '--border-color': '#e2e8f0',
    '--border-radius': '8px',
    '--border-radius-lg': '12px',
    '--border-radius-xl': '16px',
    '--shadow-sm': '0 1px 2px 0 rgba(0, 0, 0, 0.05)',
    '--shadow': '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
    '--shadow-lg': '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
    '--shadow-xl': '0 20px 25px -5px rgba(0, 0, 0, 0.1)',
    '--canvas-desktop': '1200px',
};

// Hàm parse styles từ chuỗi style
const parseStyles = (styleString) => {
    if (!styleString) return {};
    return styleString.split(';').reduce((acc, style) => {
        const [key, value] = style.split(':').map(s => s.trim());
        if (key && value) acc[key] = value;
        return acc;
    }, {});
};

// Hàm parse HTML từ S3 thành pageData
const parseHTMLToPageData = (html) => {
    try {
        console.log('Parsing HTML from S3:', html);
        const root = parse(html, { blockTextElements: { style: true } });
        const pageContainer = root.querySelector('.page-container');
        if (!pageContainer) {
            console.warn('No .page-container found in HTML');
            toast.warn('HTML không chứa .page-container. Khởi tạo trang trống.');
            return { canvas: { width: 1200, background: CSS_VARIABLES['--white'] }, elements: [] };
        }

        const elements = [];
        pageContainer.childNodes.forEach((node, index) => {
            if (node.nodeType === 1 && node.tagName === 'DIV') { // Only process element nodes that are DIVs
                const styles = node.getAttribute('style') || '';
                const styleObj = parseStyles(styles);

                const position = {
                    desktop: { x: parseFloat(styleObj.left) || 0, y: parseFloat(styleObj.top) || 0 },
                    tablet: { x: parseFloat(styleObj.left) || 0, y: parseFloat(styleObj.top) || 0 },
                    mobile: { x: parseFloat(styleObj.left) || 0, y: parseFloat(styleObj.top) || 0 },
                };
                const size = {
                    width: parseFloat(styleObj.width) || 100,
                    height: parseFloat(styleObj.height) || 100,
                };

                let type = node.classList.contains('ladi-section') ? 'section' : 'container';
                let children = [];

                node.childNodes.forEach((child, childIndex) => {
                    if (child.nodeType !== 1) return; // Skip non-element nodes (e.g., text, comments)
                    const childStyles = child.getAttribute('style') ? parseStyles(child.getAttribute('style')) : {};
                    if (child.tagName === 'H2') {
                        children.push({
                            id: `child-${index}-${childIndex}-${Date.now()}`,
                            type: 'heading',
                            componentData: { content: child.textContent.trim() || 'Tiêu đề' },
                            styles: { ...childStyles, 'font-size': childStyles['font-size'] || '18px', color: childStyles.color || CSS_VARIABLES['--text-primary'] },
                            position: { desktop: { x: 0, y: 0 }, tablet: { x: 0, y: 0 }, mobile: { x: 0, y: 0 } },
                            size: { width: 200, height: 50 },
                        });
                    } else if (child.tagName === 'P') {
                        children.push({
                            id: `child-${index}-${childIndex}-${Date.now()}`,
                            type: 'paragraph',
                            componentData: { content: child.textContent.trim() || 'Đoạn văn' },
                            styles: { ...childStyles, 'font-size': childStyles['font-size'] || '14px', color: childStyles.color || CSS_VARIABLES['--text-secondary'] },
                            position: { desktop: { x: 0, y: 0 }, tablet: { x: 0, y: 0 }, mobile: { x: 0, y: 0 } },
                            size: { width: 200, height: 50 },
                        });
                    } else if (child.tagName === 'BUTTON') {
                        children.push({
                            id: `child-${index}-${childIndex}-${Date.now()}`,
                            type: 'button',
                            componentData: { content: child.textContent.trim() || 'Nút' },
                            styles: {
                                ...childStyles,
                                'background-color': childStyles['background-color'] || CSS_VARIABLES['--primary'],
                                color: childStyles.color || CSS_VARIABLES['--text-inverse'],
                                'border-radius': childStyles['border-radius'] || CSS_VARIABLES['--border-radius'],
                                padding: childStyles.padding || '8px 16px',
                            },
                            position: { desktop: { x: 0, y: 0 }, tablet: { x: 0, y: 0 }, mobile: { x: 0, y: 0 } },
                            size: { width: 100, height: 40 },
                        });
                    } else if (child.tagName === 'IMG') {
                        children.push({
                            id: `child-${index}-${childIndex}-${Date.now()}`,
                            type: 'image',
                            componentData: { src: child.getAttribute('src') || '' },
                            styles: { ...childStyles, width: childStyles.width || '100px', height: childStyles.height || 'auto' },
                            position: { desktop: { x: 0, y: 0 }, tablet: { x: 0, y: 0 }, mobile: { x: 0, y: 0 } },
                            size: { width: 100, height: 100 },
                        });
                    }
                });

                elements.push({
                    id: `element-${index}-${Date.now()}`,
                    type,
                    componentData: node.classList.contains('ladi-section') ? { structure: 'ladi-standard' } : {},
                    position,
                    size,
                    styles: {
                        ...styleObj,
                        'border-radius': styleObj['border-radius'] || CSS_VARIABLES['--border-radius'],
                        'background-color': styleObj['background-color'] || CSS_VARIABLES['--white'],
                        'box-shadow': styleObj['box-shadow'] || CSS_VARIABLES['--shadow'],
                    },
                    children: children.length > 0 ? children : undefined,
                });
            }
        });

        const pageData = {
            canvas: { width: parseInt(CSS_VARIABLES['--canvas-desktop']), background: CSS_VARIABLES['--white'] },
            elements,
        };
        console.log('Parsed pageData:', JSON.stringify(pageData, null, 2));
        return pageData;
    } catch (error) {
        console.error('Error parsing HTML:', error);
        toast.error('Lỗi khi phân tích nội dung trang. Khởi tạo trang trống.');
        return { canvas: { width: parseInt(CSS_VARIABLES['--canvas-desktop']), background: CSS_VARIABLES['--white'] }, elements: [] };
    }
};

