import React, { useMemo } from 'react';
import { Eye, EyeOff, Lock, Unlock, Trash2 } from 'lucide-react';

const LayerManager = ({
                          pageData,
                          selectedIds = [],
                          onSelectElement,
                          onToggleVisibility,
                          onToggleLock,
                          onDeleteElement,
                          onDeleteChild,
                          viewMode = 'desktop',
                      }) => {
    // Lấy danh sách layers và sắp xếp theo vị trí Y
    const layers = useMemo(() => {
        return pageData.elements
            .filter(el => el.type === 'section' || el.type === 'popup' || el.isSection)
            .sort((a, b) => (a.position?.desktop?.y || 0) - (b.position?.desktop?.y || 0));
    }, [pageData.elements]);

    // Chọn layer và scroll đến vị trí
    const selectLayer = (layer, e) => {
        e?.stopPropagation();

        const canvas = document.querySelector('.lpb-canvas-scrollable');
        if (canvas) {
            const targetY = layer.position?.[viewMode]?.y || layer.position?.desktop?.y || 0;
            canvas.scrollTo({ top: targetY - 100, behavior: 'smooth' });
        }

        onSelectElement?.([layer.id]);
    };

    // Toggle hiển thị
    const toggleVisibility = (layerId, childId, e) => {
        e?.stopPropagation();
        onToggleVisibility?.(layerId, childId);
    };

    // Toggle khóa
    const toggleLock = (layerId, childId, e) => {
        e?.stopPropagation();
        onToggleLock?.(layerId, childId);
    };

    // Xóa layer
    const deleteLayer = (layerId, childId, e) => {
        e?.stopPropagation();
        if (!window.confirm('Bạn có chắc muốn xóa layer này?')) return;

        if (childId) {
            onDeleteChild?.(layerId, childId);
        } else {
            onDeleteElement?.(layerId);
        }
    };

    // Tạo tên layer
    const getLayerName = (layer, index) => {
        const type = layer.type === 'popup' ? 'Popup' : 'Section';
        const name = layer.type === 'popup'
            ? layer.componentData?.title
            : layer.componentData?.name;

        return name && name !== 'Section' && name !== 'Popup'
            ? `${index + 1}. ${name}`
            : `${index + 1}. ${type}`;
    };

    return (
        <div className="layer-toolbar">
            <div className="layer-scroll">
                <div className="layer-tabs">
                    {layers.length > 0 ? (
                        layers.map((layer, idx) => {
                            const isActive = selectedIds.includes(layer.id);
                            const isHidden = layer.visible === false;
                            const name = getLayerName(layer, idx);

                            return (
                                <div
                                    key={layer.id}
                                    className={`layer-tab ${isActive ? 'active' : ''} ${isHidden ? 'hidden' : ''}`}
                                    onClick={(e) => selectLayer(layer, e)}
                                    title={name}
                                >
                                    <div className="layer-info">
                                        <span className="layer-num">{idx + 1}</span>
                                        <span className="layer-name">{name}</span>
                                    </div>

                                    <div className="layer-actions">
                                        <button
                                            onClick={(e) => toggleVisibility(layer.id, null, e)}
                                            title={isHidden ? 'Hiện' : 'Ẩn'}
                                        >
                                            {isHidden ? <EyeOff size={16} /> : <Eye size={16} />}
                                        </button>
                                        <button
                                            onClick={(e) => toggleLock(layer.id, null, e)}
                                            title={layer.locked ? 'Mở khóa' : 'Khóa'}
                                        >
                                            {layer.locked ? <Lock size={16} /> : <Unlock size={16} />}
                                        </button>
                                        <button
                                            className="delete"
                                            onClick={(e) => deleteLayer(layer.id, null, e)}
                                            title="Xóa"
                                        >
                                            <Trash2 size={16} />
                                        </button>
                                    </div>
                                </div>
                            );
                        })
                    ) : (
                        <div className="empty">Chưa có layer</div>
                    )}
                </div>
            </div>

            <style>{`
        .layer-toolbar {
          position: absolute;
          bottom: 0px;
          left: 280px;
          right: 320px;
          height: 60px;
          z-index: 999;
          background-color: white;
          
          
        }

        .layer-scroll {
          width: 100%;
          height: 100%;
          overflow-x: auto;
          overflow-y: hidden;
          padding: 0 16px;
        }

        .layer-scroll::-webkit-scrollbar {
          height: 4px;
        }

        .layer-scroll::-webkit-scrollbar-thumb {
          background: #cbd5e1;
          border-radius: 2px;
        }

        .layer-tabs {
          display: inline-flex;
          gap: 6px;
          padding: 8px 10px;
          background: linear-gradient(135deg, #fefefe 0%, #f9fafb 100%);
          border-radius: 14px;
          box-shadow: 0 2px 16px rgba(0, 0, 0, 0.06), 0 0 0 1px rgba(0, 0, 0, 0.04);
          min-width: min-content;
        }

        .layer-tab {
          display: flex;
          align-items: center;
          gap: 8px;
          padding: 8px 12px;
          background: linear-gradient(135deg, #f0f4ff 0%, #e0e7ff 100%);
          border-radius: 10px;
          cursor: pointer;
          transition: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);
          white-space: nowrap;
          border: 1px solid rgba(99, 102, 241, 0.1);
        }

        .layer-tab:hover {
          background: linear-gradient(135deg, #dfe6ff 0%, #d4ddff 100%);
          transform: translateY(-2px);
          border-color: rgba(99, 102, 241, 0.2);
          box-shadow: 0 4px 12px rgba(99, 102, 241, 0.15);
        }

        .layer-tab.active {
          background: linear-gradient(135deg, #6366f1 0%, #4f46e5 100%);
          box-shadow: 0 4px 16px rgba(99, 102, 241, 0.35), 0 0 0 2px rgba(255, 255, 255, 0.5);
          border-color: transparent;
        }

        .layer-tab.hidden {
          opacity: 0.5;
        }

        .layer-info {
          display: flex;
          align-items: center;
          gap: 10px;
        }

        .layer-num {
          display: flex;
          align-items: center;
          justify-content: center;
          width: 24px;
          height: 24px;
          background: #e2e8f0;
          border-radius: 6px;
          font-size: 12px;
          font-weight: 600;
          color: #64748b;
        }

        .layer-tab.active .layer-num {
          background: rgba(255, 255, 255, 0.2);
          color: white;
        }

        .layer-name {
          font-size: 14px;
          font-weight: 500;
          color: #334155;
          max-width: 150px;
          overflow: hidden;
          text-overflow: ellipsis;
        }

        .layer-tab.active .layer-name {
          color: white;
        }

        .layer-tab.hidden .layer-name {
          text-decoration: line-through;
        }

        .layer-actions {
          display: flex;
          gap: 4px;
          opacity: 0;
          transition: opacity 0.2s;
        }

        .layer-tab:hover .layer-actions,
        .layer-tab.active .layer-actions {
          opacity: 1;
        }

        .layer-actions button {
          width: 28px;
          height: 28px;
          display: flex;
          align-items: center;
          justify-content: center;
          border: none;
          background: white;
          border-radius: 6px;
          cursor: pointer;
          color: #64748b;
          transition: all 0.15s;
        }

        .layer-actions button:hover {
          background: #3b82f6;
          color: white;
          transform: scale(1.1);
        }

        .layer-actions button.delete:hover {
          background: #ef4444;
        }

        .layer-tab.active .layer-actions button {
          background: rgba(255, 255, 255, 0.2);
          color: white;
        }

        .empty {
          padding: 12px 20px;
          color: #94a3b8;
          font-size: 14px;
        }

        @media (max-width: 1400px) {
          .layer-toolbar {
            left: 60px;
            right: 60px;
          }
        }

        @media (max-width: 768px) {
          .layer-toolbar {
            height: 50px;
          }

          .layer-tabs {
            gap: 6px;
            padding: 8px 10px;
          }

          .layer-tab {
            padding: 8px 12px;
            gap: 8px;
          }

          .layer-num {
            width: 20px;
            height: 20px;
            font-size: 11px;
          }

          .layer-name {
            font-size: 13px;
            max-width: 100px;
          }

          .layer-actions button {
            width: 24px;
            height: 24px;
          }

          .layer-actions button svg {
            width: 14px;
            height: 14px;
          }
        }
      `}</style>
        </div>
    );
};

export default LayerManager;
