import React, { useState } from 'react';
import { useDrag } from 'react-dnd';
import EXTENDED_LIBRARY from './EXTENDED_LIBRARY';
import '../../styles/CreateLanding.css';

const ComponentLibrary = ({ isCollapsed, onToggle, addSection, addElement }) => {
    const ItemTypes = {
        SECTION: 'section',
        ELEMENT: 'element',
    };

    const [selectedCategory, setSelectedCategory] = useState(Object.keys(EXTENDED_LIBRARY)[0]);
    const [selectedSubCategory, setSelectedSubCategory] = useState(
        EXTENDED_LIBRARY[Object.keys(EXTENDED_LIBRARY)[0]].subCategories[0]?.id || null
    );

    const DraggableTemplate = ({ template, type }) => {
        const [{ isDragging }, drag] = useDrag(() => ({
            type: type === 'section' || type === 'popup' || type === 'template' ? ItemTypes.SECTION : ItemTypes.ELEMENT,
            item: {
                id: template.id,
                json: template.json,
            },
            collect: (monitor) => ({
                isDragging: !!monitor.isDragging(),
            }),
        }));

        const renderTemplatePreview = () => {
            const { type, componentData = {}, styles = { desktop: {} }, children = [] } = template.json || {};
            const previewStyle = {
                ...styles.desktop,
                position: 'relative',
                width: '100%',
                minHeight: type === 'form' ? '180px' : type === 'gallery' || type === 'carousel' ? '200px' : '150px',
                background: styles.desktop.background || '#fff',
                borderRadius: '8px',
                padding: '12px',
                boxSizing: 'border-box',
            };

            switch (type) {
                case 'section':
                case 'popup':
                case 'template':
                    return (
                        <div style={previewStyle} className={`lpb-template-item ${type}`}>
                            <h3 style={{ margin: 0, fontSize: '1rem', color: '#1f2937' }}>
                                {componentData.title || 'Section'}
                            </h3>
                            {children.length > 0 && (
                                <div style={{ display: 'flex', flexDirection: 'column', gap: '10px', marginTop: '10px' }}>
                                    {children.map((child) => (
                                        <div key={child.id} style={{ ...child.styles.desktop, fontSize: '0.875rem' }}>
                                            {child.type === 'image' ? (
                                                <img
                                                    src={child.componentData.src || 'https://via.placeholder.com/150'}
                                                    alt={child.componentData.alt}
                                                    style={{ maxWidth: '100%', height: 'auto' }}
                                                />
                                            ) : (
                                                <span>{child.componentData.content || child.componentData.label}</span>
                                            )}
                                        </div>
                                    ))}
                                </div>
                            )}
                        </div>
                    );
                case 'form':
                    return (
                        <div style={previewStyle} className="lpb-template-item lpb-template-form">
                            <form style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                                {children.map((child) => (
                                    <div key={child.id}>
                                        {child.type === 'input' ? (
                                            <input
                                                type={child.componentData.type || 'text'}
                                                placeholder={child.componentData.label || 'Input'}
                                                style={{ ...child.styles.desktop, width: '100%', padding: '8px', borderRadius: '4px' }}
                                            />
                                        ) : (
                                            <textarea
                                                placeholder={child.componentData.label || 'Textarea'}
                                                style={{ ...child.styles.desktop, width: '100%', padding: '8px', borderRadius: '4px' }}
                                            />
                                        )}
                                    </div>
                                ))}
                                <button style={{ padding: '8px', borderRadius: '4px', background: '#f97316', color: '#fff' }}>
                                    Submit
                                </button>
                            </form>
                        </div>
                    );
                case 'button-group':
                    return (
                        <div style={previewStyle} className="lpb-template-item">
                            <div style={{ display: 'flex', gap: '10px' }}>
                                {children.map((child) => (
                                    <button key={child.id} style={{ ...child.styles.desktop, padding: '8px 12px', borderRadius: '4px' }}>
                                        {child.componentData.content || 'Button'}
                                    </button>
                                ))}
                            </div>
                        </div>
                    );
                case 'gallery':
                case 'carousel':
                    return (
                        <div style={previewStyle} className={`lpb-template-item lpb-template-${type}`}>
                            <div style={{ display: type === 'carousel' ? 'flex' : 'flex', flexWrap: type === 'gallery' ? 'wrap' : 'nowrap', gap: '10px', overflowX: type === 'carousel' ? 'auto' : 'visible' }}>
                                {children.map((child) => (
                                    <img
                                        key={child.id}
                                        src={child.componentData.src || 'https://via.placeholder.com/100'}
                                        alt={child.componentData.alt || 'Image'}
                                        style={{ ...child.styles.desktop, width: '100px', height: '100px', objectFit: 'cover' }}
                                    />
                                ))}
                            </div>
                        </div>
                    );
                case 'heading':
                    return (
                        <h2 style={{ ...previewStyle, fontSize: '1.25rem' }} className="lpb-template-item">
                            {componentData.content || 'Tiêu đề'}
                        </h2>
                    );
                case 'paragraph':
                    return (
                        <p style={{ ...previewStyle, fontSize: '0.875rem' }} className="lpb-template-item">
                            {componentData.content || 'Đoạn văn'}
                        </p>
                    );
                case 'button':
                    return (
                        <button style={{ ...previewStyle, padding: '8px 16px', borderRadius: '4px' }} className="lpb-template-item">
                            {componentData.content || 'Nút'}
                        </button>
                    );
                case 'image':
                    return (
                        <img
                            src={componentData.src || componentData.images?.[0]?.src || 'https://via.placeholder.com/150'}
                            alt={componentData.alt || 'Image'}
                            style={{ ...previewStyle, maxWidth: '100%', height: 'auto' }}
                            className="lpb-template-item"
                        />
                    );
                default:
                    return (
                        <div style={previewStyle} className="lpb-template-item">
                            {componentData.title || componentData.content || 'Unknown'}
                        </div>
                    );
            }
        };

        return (
            <div
                ref={drag}
                className={`lpb-template-item ${isDragging ? 'lpb-template-item-dragging' : ''} ${template.json.type}`}
            >
                {renderTemplatePreview()}
            </div>
        );
    };

    return (
        <div className={`lpb-component-library ${isCollapsed ? 'lpb-component-library-collapsed' : ''}`}>
            <div className="lpb-component-library-header">
                <h2 className="lpb-component-library-title">Thư viện thành phần</h2>
                <button onClick={onToggle} className="lpb-component-library-toggle">
                    <i className={isCollapsed ? 'fas fa-chevron-right' : 'fas fa-chevron-left'}></i>
                </button>
            </div>
            {!isCollapsed && (
                <div className="lpb-component-library-wrapper">
                    <div className="lpb-component-library-category-nav">
                        {Object.entries(EXTENDED_LIBRARY).map(([categoryKey, category]) => (
                            <div key={categoryKey}>
                                <button
                                    className={`lpb-component-library-category-btn ${selectedCategory === categoryKey ? 'lpb-component-library-category-btn-active' : ''}`}
                                    onClick={() => {
                                        setSelectedCategory(categoryKey);
                                        setSelectedSubCategory(category.subCategories[0]?.id || null);
                                    }}
                                >
                                    <i className={category.icon}></i> {category.name}
                                </button>
                                {selectedCategory === categoryKey && (
                                    <div className="lpb-component-library-subcategory-nav" style={{ marginLeft: '1rem' }}>
                                        {category.subCategories.map((subCategory) => (
                                            <button
                                                key={subCategory.id}
                                                className={`lpb-component-library-subcategory-btn ${selectedSubCategory === subCategory.id ? 'lpb-component-library-subcategory-btn-active' : ''}`}
                                                onClick={() => setSelectedSubCategory(subCategory.id)}
                                            >
                                                {subCategory.name}
                                            </button>
                                        ))}
                                    </div>
                                )}
                            </div>
                        ))}
                    </div>
                    <div className="lpb-component-library-template-column">
                        <div className="lpb-component-library-template-list">
                            {selectedSubCategory && EXTENDED_LIBRARY[selectedCategory]?.subCategories
                                .find((sub) => sub.id === selectedSubCategory)
                                ?.templates.map((template) => (
                                    <DraggableTemplate
                                        key={template.id}
                                        template={template}
                                        type={selectedCategory === 'elements' ? 'element' : 'section'}
                                    />
                                ))}
                            {!selectedSubCategory && (
                                <div className="lpb-component-library-empty">
                                    Vui lòng chọn danh mục con
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            )}
            {isCollapsed && (
                <div className="lpb-component-library-category-nav">
                    {Object.entries(EXTENDED_LIBRARY).map(([categoryKey, category]) => (
                        <button
                            key={categoryKey}
                            className="lpb-component-library-icon-collapsed"
                            title={category.name}
                            onClick={() => {
                                setSelectedCategory(categoryKey);
                                setSelectedSubCategory(category.subCategories[0]?.id || null);
                                onToggle();
                            }}
                        >
                            <i className={category.icon}></i>
                        </button>
                    ))}
                </div>
            )}
        </div>
    );
};

export default ComponentLibrary;
