import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles/LandingList.css';

const LandingList = ({
    landingPages,
    filteredPages,
    menuOpen,
    setMenuOpen,
    handlePreviewPage,
    handleEditPage,
    handlePublishPage,
    handleDeletePage,
    formatDate,
}) => {
    const navigate = useNavigate();

    const handlePageClick = (page) => {
        navigate(`/pages/create?id=${page.id || page._id}`);
    };

    return (
        <div className="landing-list">
            {filteredPages.length === 0 ? (
                <p>Không có landing page nào phù hợp.</p>
            ) : (
                filteredPages.map((page) => (
                    <div
                        key={page.id || page._id}
                        className="page-item"
                        onClick={() => handlePageClick(page)}
                        style={{ cursor: 'pointer' }
                    }

                    >
                        <h3>{page.name}</h3>
                        <p>Trạng thái: {page.status}</p>
                        <p>Ngày tạo: {formatDate(page.created_at)}</p>
                        <p>Ngày cập nhật: {formatDate(page.updated_at)}</p>
                        <div className="page-actions" onClick={(e) => e.stopPropagation()}>
                            <button onClick={() => handleEditPage(page)}>Chỉnh sửa</button>
                            <button onClick={() => handlePreviewPage(page)}>Xem trước</button>
                            <button onClick={() => handlePublishPage(page.id || page._id)}>
                                Triển khai
                            </button>
                            <button onClick={() => handleDeletePage(page.id || page._id)}>Xóa</button>
                        </div>
                    </div>
                ))
            )}
        </div>
    );
};

export default LandingList;
