export const popups = {
    name: 'Popups',
    subCategories: [
        {
            id: 'basic-popups',
            name: 'Basic Popups',
            templates: [
                {
                    id: 'popup-1',
                    name: 'Simple Popup',
                    icon: 'fa-window-restore',
                    description: 'A minimal popup with title and content',
                    previewImage: 'https://res.cloudinary.com/your_cloud_name/image/upload/v1234567890/popup.jpg',
                    json: {
                        type: 'popup',
                        componentData: {
                            title: 'Welcome!',
                            content: 'This is a simple popup for announcements.',
                            buttonText: 'Close',
                            buttonAction: { type: 'closePopup' }
                        },
                        position: {
                            desktop: { x: 0, y: 0 },
                            tablet: { x: 0, y: 0 },
                            mobile: { x: 0, y: 0 }
                        },
                        size: { width: 600, height: 400 },
                        styles: {
                            backgroundColor: 'rgba(255, 255, 255, 0.9)',
                            borderRadius: '12px',
                            boxShadow: '0 4px 16px rgba(0, 0, 0, 0.2)',
                            zIndex: 10000
                        },
                        children: [
                            {
                                id: 'popup-heading-1',
                                type: 'heading',
                                componentData: { content: 'Welcome!' },
                                position: { desktop: { x: 20, y: 20 }, tablet: { x: 20, y: 20 }, mobile: { x: 20, y: 20 } },
                                size: { width: 560, height: 40 },
                                styles: { fontSize: '1.5rem', color: '#1f2937', textAlign: 'center' }
                            },
                            {
                                id: 'popup-button-1',
                                type: 'button',
                                componentData: { content: 'Close', action: { type: 'closePopup' } },
                                position: { desktop: { x: 240, y: 320 }, tablet: { x: 240, y: 320 }, mobile: { x: 240, y: 320 } },
                                size: { width: 120, height: 40 },
                                styles: { background: '#2563eb', color: '#fff', borderRadius: '8px' }
                            }
                        ]
                    }
                },
                {
                    id: 'promo-popup',
                    name: 'Promo Popup',
                    icon: 'fa-gift',
                    description: 'A promotional popup with a subscription form',
                    previewImage: 'https://res.cloudinary.com/your_cloud_name/image/upload/v1234567890/promo-popup.jpg',
                    json: {
                        type: 'popup',
                        componentData: {
                            title: 'Special Offer!',
                            content: 'Sign up for our newsletter to get 20% off.',
                            fields: [
                                { type: 'email', placeholder: 'Enter your email', label: 'Email' }
                            ],
                            buttonText: 'Claim Offer',
                            dataSource: { type: 'static' },
                            events: {
                                onSubmit: { type: 'submitForm', apiUrl: '/api/newsletter' }
                            }
                        },
                        position: {
                            desktop: { x: 0, y: 0 },
                            tablet: { x: 0, y: 0 },
                            mobile: { x: 0, y: 0 }
                        },
                        size: { width: 600, height: 400 },
                        styles: {
                            backgroundColor: 'rgba(255, 255, 255, 0.9)',
                            borderRadius: '12px',
                            boxShadow: '0 4px 16px rgba(0, 0, 0, 0.2)',
                            zIndex: 10000
                        },
                        children: [
                            {
                                id: 'promo-heading-1',
                                type: 'heading',
                                componentData: { content: 'Special Offer!' },
                                position: { desktop: { x: 20, y: 20 }, tablet: { x: 20, y: 20 }, mobile: { x: 20, y: 20 } },
                                size: { width: 560, height: 40 },
                                styles: { fontSize: '1.5rem', color: '#1f2937', textAlign: 'center' }
                            },
                            {
                                id: 'promo-form-1',
                                type: 'form',
                                componentData: {
                                    fields: [{ type: 'email', placeholder: 'Enter your email', label: 'Email' }],
                                    buttonText: 'Claim Offer'
                                },
                                position: { desktop: { x: 20, y: 100 }, tablet: { x: 20, y: 100 }, mobile: { x: 20, y: 100 } },
                                size: { width: 560, height: 120 },
                                styles: { display: 'flex', flexDirection: 'column', gap: '10px' }
                            }
                        ]
                    }
                }
            ]
        }
    ]
};
