import React, { useRef, useCallback, useState, useEffect } from 'react';
import { useDrop, useDrag } from 'react-dnd';
import { getEmptyImage } from 'react-dnd-html5-backend';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import Guidelines from './Guidelines';
import SelectionOverlay from './SelectionOverlay';
import QuickEditToolkit from './QuickEditToolkit';
import '../../styles/CreateLanding.css';

const ItemTypes = {
    ELEMENT: 'element',
    EXISTING_ELEMENT: 'existingElement',
    CHILD_ELEMENT: 'childElement',
};

const CSS_VARIABLES = {
    '--primary': '#6366f1',
    '--primary-dark': '#4f46e5',
    '--primary-light': '#a5b4fc',
    '--secondary': '#f1f5f9',
    '--accent': '#06b6d4',
    '--success': '#10b981',
    '--warning': '#f59e0b',
    '--danger': '#ef4444',
    '--dark': '#1e293b',
    '--light': '#f8fafc',
    '--white': '#ffffff',
    '--text-primary': '#334155',
    '--text-secondary': '#64748b',
    '--text-light': '#94a3b8',
    '--text-inverse': '#ffffff',
    '--border-color': '#e2e8f0',
    '--border-radius': '8px',
    '--border-radius-lg': '12px',
    '--border-radius-xl': '16px',
    '--shadow-sm': '0 1px 2px 0 rgba(0, 0, 0, 0.05)',
    '--shadow': '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
    '--shadow-lg': '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
    '--shadow-xl': '0 20px 25px -5px rgba(0, 0, 0, 0.1)',
    '--canvas-desktop': '1200px',
    '--canvas-tablet': '768px',
    '--canvas-mobile': '375px',
};

const getCanvasPosition = (mouseX, mouseY, canvasElement, zoomLevel) => {
    if (!canvasElement) {
        console.error('Canvas element is null in getCanvasPosition');
        return { x: 0, y: 0 };
    }
    const canvasRect = canvasElement.getBoundingClientRect();
    const scrollX = canvasElement.scrollLeft || 0;
    const scrollY = canvasElement.scrollTop || 0;
    const rawX = (mouseX - canvasRect.left + scrollX) / (zoomLevel / 100);
    const rawY = (mouseY - canvasRect.top + scrollY) / (zoomLevel / 100);
    return { x: Math.max(0, rawX), y: Math.max(0, rawY) };
};

const snapToGrid = (x, y, gridSize, snapPoints = []) => {
    let snappedX = Math.round(x / gridSize) * gridSize;
    let snappedY = Math.round(y / gridSize) * gridSize;
    snapPoints.forEach((point) => {
        if (Math.abs(x - point.x) < 15) snappedX = point.x;
        if (Math.abs(y - point.y) < 15) snappedY = point.y;
    });
    return { x: snappedX, y: snappedY };
};

const getElementBounds = (element) => ({
    left: element.position.desktop?.x || 0,
    top: element.position.desktop?.y || 0,
    right: (element.position.desktop?.x || 0) + (element.size?.width || 200),
    bottom: (element.position.desktop?.y || 0) + (element.size?.height || 50),
    centerX: (element.position.desktop?.x || 0) + (element.size?.width || 200) / 2,
    centerY: (element.position.desktop?.y || 0) + (element.size?.height || 50) / 2,
});

const renderComponentContent = (type, componentData = {}, styles = {}, children = []) => {
    console.log('renderComponentContent:', { type, componentData, styles, children });
    switch (type) {
        case 'heading':
            return (
                <h2 style={{ fontSize: componentData.fontSize || '1.5rem', color: componentData.color || CSS_VARIABLES['--text-primary'], margin: 0, padding: 0, ...styles }}>
                    {componentData.content || 'Tiêu đề'}
                </h2>
            );
        case 'paragraph':
            return (
                <p style={{ fontSize: componentData.fontSize || '1rem', color: componentData.color || CSS_VARIABLES['--text-secondary'], margin: 0, padding: 0, ...styles }}>
                    {componentData.content || 'Đoạn văn'}
                </p>
            );
        case 'button':
            return (
                <button style={{ padding: '8px 16px', borderRadius: CSS_VARIABLES['--border-radius'], background: componentData.background || CSS_VARIABLES['--primary'], color: componentData.color || CSS_VARIABLES['--text-inverse'], border: 'none', cursor: 'pointer', ...styles }}>
                    {componentData.content || 'Nút'}
                </button>
            );
        case 'image':
            return (
                <img
                    src={componentData.src || componentData.content || 'https://via.placeholder.com/150'}
                    alt={componentData.alt || 'Image'}
                    style={{ maxWidth: '100%', height: 'auto', ...styles }}
                    draggable={false}
                />
            );
        case 'input':
            return (
                <input
                    type={componentData.type || 'text'}
                    placeholder={componentData.placeholder || 'Input'}
                    style={{ padding: '8px', borderRadius: CSS_VARIABLES['--border-radius'], border: `1px solid ${CSS_VARIABLES['--border-color']}`, ...styles }}
                />
            );
        case 'list-item':
            return (
                <li style={{ fontSize: '1rem', margin: 0, padding: '4px 0', ...styles }}>
                    {componentData.content || 'List item'}
                </li>
            );
        case 'form':
            return (
                <form style={{ display: 'flex', flexDirection: 'column', gap: '10px', ...styles }}>
                    {componentData.title && !children.some(child => child.type === 'heading') && (
                        <h3 style={{ margin: 0, fontSize: '1.2rem' }}>{componentData.title}</h3>
                    )}
                    {componentData.fields?.map((field, index) => (
                        <input
                            key={index}
                            type="text"
                            placeholder={field}
                            style={{ padding: '8px', borderRadius: CSS_VARIABLES['--border-radius'], border: `1px solid ${CSS_VARIABLES['--border-color']}` }}
                        />
                    ))}
                    {!children.some(child => child.type === 'button') && (
                        <button style={{ padding: '8px 16px', borderRadius: CSS_VARIABLES['--border-radius'], background: CSS_VARIABLES['--primary'], color: CSS_VARIABLES['--text-inverse'], border: 'none', cursor: 'pointer' }}>
                            {componentData.buttonText || 'Submit'}
                        </button>
                    )}
                    {children.map((child, index) => (
                        <ChildElement
                            key={child.id}
                            parentId={child.parentId}
                            id={child.id}
                            type={child.type}
                            content={child.content}
                            componentData={child.componentData || {}}
                            styles={child.styles || {}}
                            index={index}
                        />
                    ))}
                </form>
            );
        case 'section':
        case 'container':
        case 'group':
            return (
                <div style={{ display: 'flex', flexDirection: 'column', gap: '10px', minHeight: '100px', padding: '20px', background: CSS_VARIABLES['--white'], border: `2px dashed ${CSS_VARIABLES['--primary']}`, borderRadius: CSS_VARIABLES['--border-radius'], ...styles }}>
                    {componentData.title && !children.some(child => child.type === 'heading') && (
                        <h2 style={{ fontSize: '1.8rem', margin: 0, color: CSS_VARIABLES['--text-primary'] }}>{componentData.title}</h2>
                    )}
                    {componentData.subtitle && !children.some(child => child.type === 'paragraph') && (
                        <p style={{ fontSize: '1.2rem', margin: 0, color: CSS_VARIABLES['--text-secondary'] }}>{componentData.subtitle}</p>
                    )}
                    {componentData.content && !children.some(child => child.type === 'paragraph') && (
                        <p style={{ fontSize: '1rem', margin: 0, color: CSS_VARIABLES['--text-secondary'] }}>{componentData.content}</p>
                    )}
                    {componentData.features && !children.some(child => child.type === 'list-item') && (
                        <ul style={{ listStyle: 'none', padding: 0, margin: 0, display: 'flex', flexDirection: 'column', gap: '8px' }}>
                            {componentData.features.map((feature, index) => (
                                <li key={index} style={{ fontSize: '1rem', color: CSS_VARIABLES['--text-secondary'] }}>{feature}</li>
                            ))}
                        </ul>
                    )}
                    {componentData.ctaText && !children.some(child => child.type === 'button') && (
                        <button style={{ padding: '10px 20px', borderRadius: CSS_VARIABLES['--border-radius'], background: CSS_VARIABLES['--primary'], color: CSS_VARIABLES['--text-inverse'], border: 'none', cursor: 'pointer' }}>
                            {componentData.ctaText}
                        </button>
                    )}
                    {children.map((child, index) => (
                        <ChildElement
                            key={child.id}
                            parentId={child.parentId}
                            id={child.id}
                            type={child.type}
                            content={child.content}
                            componentData={child.componentData || {}}
                            styles={child.styles || {}}
                            index={index}
                        />
                    ))}
                </div>
            );
        case 'divider':
            return <hr style={{ border: 'none', background: CSS_VARIABLES['--border-color'], height: '2px', ...styles }} />;
        case 'spacer':
            return <div style={{ background: 'transparent', height: componentData.height || '20px', ...styles }} />;
        case 'popup':
            return (
                <div style={{ display: 'flex', flexDirection: 'column', gap: '10px', textAlign: 'center', background: CSS_VARIABLES['--white'], padding: '20px', borderRadius: CSS_VARIABLES['--border-radius'], boxShadow: CSS_VARIABLES['--shadow'], ...styles }}>
                    {componentData.title && !children.some(child => child.type === 'heading') && (
                        <h3 style={{ fontSize: '1.5rem', margin: 0, color: CSS_VARIABLES['--text-primary'] }}>{componentData.title}</h3>
                    )}
                    {componentData.content && !children.some(child => child.type === 'paragraph') && (
                        <p style={{ fontSize: '1rem', margin: 0, color: CSS_VARIABLES['--text-secondary'] }}>{componentData.content}</p>
                    )}
                    {componentData.buttonText && !children.some(child => child.type === 'button') && (
                        <button style={{ padding: '10px 20px', borderRadius: CSS_VARIABLES['--border-radius'], background: CSS_VARIABLES['--primary'], color: CSS_VARIABLES['--text-inverse'], border: 'none', cursor: 'pointer' }}>
                            {componentData.buttonText}
                        </button>
                    )}
                    {children.map((child, index) => (
                        <ChildElement
                            key={child.id}
                            parentId={child.parentId}
                            id={child.id}
                            type={child.type}
                            content={child.content}
                            componentData={child.componentData || {}}
                            styles={child.styles || {}}
                            index={index}
                        />
                    ))}
                </div>
            );
        case 'modal':
            return (
                <div style={{ display: 'flex', flexDirection: 'column', gap: '10px', background: CSS_VARIABLES['--white'], padding: '20px', borderRadius: CSS_VARIABLES['--border-radius'], boxShadow: CSS_VARIABLES['--shadow-lg'], ...styles }}>
                    {componentData.title && !children.some(child => child.type === 'heading') && (
                        <h3 style={{ fontSize: '1.5rem', margin: 0, color: CSS_VARIABLES['--text-primary'] }}>{componentData.title}</h3>
                    )}
                    {componentData.content && !children.some(child => child.type === 'paragraph') && (
                        <p style={{ fontSize: '1rem', margin: 0, color: CSS_VARIABLES['--text-secondary'] }}>{componentData.content}</p>
                    )}
                    {componentData.closeButton && !children.some(child => child.type === 'button') && (
                        <button style={{ padding: '8px 16px', borderRadius: CSS_VARIABLES['--border-radius'], background: CSS_VARIABLES['--dark'], color: CSS_VARIABLES['--text-inverse'], border: 'none', cursor: 'pointer', alignSelf: 'flex-end' }}>
                            Close
                        </button>
                    )}
                    {children.map((child, index) => (
                        <ChildElement
                            key={child.id}
                            parentId={child.parentId}
                            id={child.id}
                            type={child.type}
                            content={child.content}
                            componentData={child.componentData || {}}
                            styles={child.styles || {}}
                            index={index}
                        />
                    ))}
                </div>
            );
        default:
            return <div style={{ color: CSS_VARIABLES['--danger'], ...styles }}>Unknown Component: {type}</div>;
    }
};

const ChildElement = React.memo(
    ({
         parentId,
         id,
         type,
         content,
         componentData = {},
         styles = {},
         index,
         isSelected,
         onSelectChild,
         onUpdateChildren,
         zoomLevel,
         gridSize,
         showGrid,
     }) => {
        const dragRef = useRef(null);
        const dropRef = useRef(null);

        const [{ isDragging }, drag] = useDrag(() => ({
            type: ItemTypes.CHILD_ELEMENT,
            item: () => {
                console.log('ChildElement drag started:', { childId: id, parentId, index });
                return { childId: id, parentId, index, isExisting: true };
            },
            collect: (monitor) => ({
                isDragging: monitor.isDragging(),
            }),
        }));

        const [{ isOver, canDrop }, drop] = useDrop(() => ({
            accept: ItemTypes.CHILD_ELEMENT,
            canDrop: (item) => item.parentId === parentId,
            drop: (item, monitor) => {
                if (!monitor.canDrop()) {
                    console.warn('Drop not allowed: Different parent or same position');
                    toast.error('Không thể di chuyển thành phần con sang section khác!');
                    return { moved: false };
                }
                console.log('ChildElement drop:', { childId: item.childId, parentId, dragIndex: item.index, dropIndex: index });
                if (item.index !== index) {
                    try {
                        onUpdateChildren(parentId, item.index, index);
                        toast.success('Đã di chuyển thành phần con!');
                        return { moved: true, parentId, dropIndex: index };
                    } catch (error) {
                        console.error('Error in onUpdateChildren:', error);
                        toast.error('Lỗi khi di chuyển thành phần con!');
                        return { moved: false };
                    }
                }
                return { moved: false };
            },
            collect: (monitor) => ({
                isOver: monitor.isOver(),
                canDrop: monitor.canDrop(),
            }),
        }));

        const handleClick = (e) => {
            e.stopPropagation();
            console.log('ChildElement clicked:', { parentId, id, isSelected });
            if (typeof onSelectChild === 'function') {
                onSelectChild(parentId, id);
            } else {
                console.warn('onSelectChild is not a function');
            }
        };

        useEffect(() => {
            drag(dragRef);
            drop(dropRef);
        }, [drag, drop]);

        return (
            <div
                ref={dropRef}
                className={`lpb-child-element ${isSelected ? 'lpb-child-element-selected' : ''} ${isDragging ? 'lpb-child-element-dragging' : ''} ${isOver && canDrop ? 'lpb-child-element-hover' : ''}`}
                style={{
                    position: 'relative',
                    margin: '5px 0',
                    padding: '5px',
                    cursor: isDragging ? 'grabbing' : 'grab',
                    opacity: isDragging ? 0.5 : 1,
                    borderBottom: isOver && canDrop ? '2px solid #2563eb' : 'none',
                    ...styles,
                }}
                onClick={handleClick}
            >
                <div ref={dragRef}>
                    {renderComponentContent(type, { content, ...componentData }, styles)}
                </div>
                {isSelected && (
                    <QuickEditToolkit
                        element={{ id, type, content, componentData, styles }}
                        position={{ x: 0, y: -40 }}
                        onEdit={() => console.log('Edit child:', id)}
                        onDuplicate={() => console.log('Duplicate child:', id)}
                        onDelete={() => console.log('Delete child:', id)}
                        onMoveUp={() => console.log('Move up child:', id)}
                        onMoveDown={() => console.log('Move down child:', id)}
                        onClose={() => onSelectChild(parentId, null)}
                    />
                )}
            </div>
        );
    }
);

const Element = React.memo(
    ({
         element,
         isSelected,
         onSelectElement,
         onUpdatePosition,
         viewMode,
         zoomLevel,
         gridSize,
         onContextMenu,
         canvasBounds,
         canvasRef,
         onSelectChild,
         selectedChildId,
         onUpdateChildren,
         showGrid,
     }) => {
        const { id, type, componentData = {}, position = {}, size = { width: 200, height: 50 }, styles = {}, children = [] } = element;
        const elementRef = useRef(null);
        const [isDragStarted, setIsDragStarted] = useState(false);

        const [{ isDragging }, drag, preview] = useDrag(() => ({
            type: ItemTypes.EXISTING_ELEMENT,
            item: () => {
                setIsDragStarted(true);
                console.log('Element drag started:', { id, position: position[viewMode], size });
                return {
                    id,
                    elementSize: size,
                    elementPosition: position[viewMode] || { x: 0, y: 0 },
                    isExisting: true,
                };
            },
            end: (item, monitor) => {
                setIsDragStarted(false);
                const dropResult = monitor.getDropResult();
                if (!dropResult || !dropResult.moved) {
                    console.log('Drag cancelled or invalid drop for element:', id);
                    return;
                }
                console.log('Drag ended successfully for element:', id, 'New position:', dropResult.newPosition);
            },
            collect: (monitor) => ({
                isDragging: monitor.isDragging(),
            }),
        }), [id, size, position, viewMode]);

        useEffect(() => {
            preview(getEmptyImage(), { captureDraggingState: true });
        }, [preview]);

        const handleMouseDown = useCallback(
            (e) => {
                e.stopPropagation();
                onSelectElement(id, e.ctrlKey);
                onSelectChild(id, null);
            },
            [id, onSelectElement, onSelectChild]
        );
        const handleContextMenu = useCallback(
            (e) => {
                e.preventDefault();
                e.stopPropagation();
                onContextMenu(id, { x: e.clientX, y: e.clientY });
            },
            [id, onContextMenu]
        );

        return (
            <div
                ref={(node) => {
                    elementRef.current = node;
                    drag(node);
                }}
                className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${isDragging ? 'lpb-canvas-element-dragging' : ''}`}
                style={{
                    position: 'absolute',
                    transform: `translate(${position[viewMode]?.x || 0}px, ${position[viewMode]?.y || 0}px)`,
                    width: size.width,
                    height: size.height,
                    zIndex: position.z || 10,
                    opacity: isDragging ? 0.5 : 1,
                    cursor: isDragging ? 'grabbing' : 'grab',
                    userSelect: 'none',
                    ...styles,
                }}
                onMouseDown={handleMouseDown}
                onContextMenu={handleContextMenu}
                data-type={type}
                data-element-id={id}
            >
                {renderComponentContent(type, componentData, styles, children)}
                {children.map((child, index) => (
                    <ChildElement
                        key={child.id}
                        parentId={id}
                        id={child.id}
                        type={child.type}
                        content={child.content}
                        componentData={child.componentData || {}}
                        styles={child.styles || {}}
                        index={index}
                        isSelected={selectedChildId === child.id}
                        onSelectChild={onSelectChild}
                        onUpdateChildren={onUpdateChildren}
                        zoomLevel={zoomLevel}
                        gridSize={gridSize}
                        showGrid={showGrid}
                    />
                ))}
                {isSelected && (
                    <SelectionOverlay
                        element={{ id, type, position: position[viewMode], size }}
                        canvasBounds={canvasBounds}
                        onResize={(newSize) => onUpdateSize(id, newSize)}
                        zoomLevel={zoomLevel}
                    />
                )}
            </div>
        );
    }
);

const Canvas = React.memo(
    ({
         pageData,
         selectedIds,
         onSelectElement,
         onUpdatePosition,
         onUpdateSize,
         onDeleteElement,
         onAddElement,
         onGroupElements,
         viewMode,
         zoomLevel,
         gridSize,
         showGrid,
         onEditElement,
         onDuplicateElement,
         onMoveElementUp,
         onMoveElementDown,
         onSelectChild,
         selectedChildId,
         onUpdateChildren,
     }) => {
        const canvasRef = useRef(null);
        const [guidelines, setGuidelines] = useState([]);
        const [contextMenu, setContextMenu] = useState(null);
        const [dragPreview, setDragPreview] = useState(null);

        const canvasBounds = canvasRef.current
            ? {
                width: canvasRef.current.offsetWidth / (zoomLevel / 100),
                height: canvasRef.current.offsetHeight / (zoomLevel / 100),
            }
            : { width: parseInt(CSS_VARIABLES['--canvas-desktop']), height: 1000 };

        const getSnapPoints = useCallback(() => {
            const points = [
                { x: 0, y: 0 },
                { x: canvasBounds.width, y: canvasBounds.height },
                { x: canvasBounds.width / 2, y: canvasBounds.height / 2 },
            ];

            pageData.elements.forEach((el) => {
                const bounds = getElementBounds(el);
                points.push(
                    { x: bounds.left, y: bounds.top },
                    { x: bounds.right, y: bounds.bottom },
                    { x: bounds.centerX, y: bounds.centerY },
                    { x: bounds.left, y: bounds.centerY },
                    { x: bounds.right, y: bounds.centerY },
                    { x: bounds.centerX, y: bounds.top },
                    { x: bounds.centerX, y: bounds.bottom }
                );
            });

            return points;
        }, [pageData.elements, canvasBounds]);

        const [{ isOver, dragItem }, drop] = useDrop(() => ({
            accept: [ItemTypes.ELEMENT, ItemTypes.EXISTING_ELEMENT],
            hover: (item, monitor) => {
                if (!canvasRef.current) return;
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset) return;

                const canvasPos = getCanvasPosition(clientOffset.x, clientOffset.y, canvasRef.current, zoomLevel);
                const snapPoints = getSnapPoints();
                const snappedPos = snapToGrid(canvasPos.x, canvasPos.y, gridSize, snapPoints);

                const guidelines = [];
                snapPoints.forEach((point) => {
                    if (Math.abs(snappedPos.x - point.x) < 5) {
                        guidelines.push({ type: 'vertical', position: point.x });
                    }
                    if (Math.abs(snappedPos.y - point.y) < 5) {
                        guidelines.push({ type: 'horizontal', position: point.y });
                    }
                });
                setGuidelines(guidelines);

                if (!item.isExisting) {
                    setDragPreview({
                        type: item.json?.type || 'container', // Sử dụng item.json.type thay vì item.type
                        componentData: item.json?.componentData || {},
                        styles: item.json?.styles || {},
                        position: snappedPos,
                        size: item.json?.size || { width: 200, height: 50 },
                    });
                }
            },
            drop: (item, monitor) => {
                if (!canvasRef.current) {
                    console.warn('Canvas ref is null during drop');
                    return { moved: false };
                }
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset) {
                    console.warn('Client offset is null during drop');
                    return { moved: false };
                }

                const canvasPos = getCanvasPosition(clientOffset.x, clientOffset.y, canvasRef.current, zoomLevel);
                const snapPoints = getSnapPoints();
                const snappedPos = snapToGrid(canvasPos.x, canvasPos.y, gridSize, snapPoints);

                setGuidelines([]);
                setDragPreview(null);

                if (item.isExisting) {
                    console.log('Dropping existing element:', item.id, 'to position:', snappedPos);
                    onUpdatePosition(item.id, { [viewMode]: snappedPos }, 'replace');
                    return { moved: true, newPosition: snappedPos };
                } else {
                    if (!item.json || !item.json.type) {
                        console.error('Invalid item structure: item.json or item.json.type is missing', item);
                        toast.error('Không thể thêm phần tử: Dữ liệu không hợp lệ!');
                        return { moved: false };
                    }
                    console.log('Dropping new element:', item.json.type, 'to position:', snappedPos);
                    const newElement = {
                        id: `element-${Date.now()}`,
                        type: item.json.type,
                        position: {
                            desktop: snappedPos,
                            tablet: snappedPos,
                            mobile: snappedPos,
                        },
                        size: item.json.size || { width: 200, height: 50 },
                        styles: {
                            'border-radius': item.json.styles?.['border-radius'] || CSS_VARIABLES['--border-radius'],
                            'background-color': item.json.type === 'button' ? CSS_VARIABLES['--primary'] : item.json.styles?.['background-color'] || CSS_VARIABLES['--white'],
                            'box-shadow': item.json.styles?.['box-shadow'] || CSS_VARIABLES['--shadow'],
                            ...item.json.styles,
                        },
                        componentData: item.json.componentData || { content: item.json.type.charAt(0).toUpperCase() + item.json.type.slice(1) },
                        children: item.json.children || [],
                    };
                    onAddElement(newElement);
                    toast.success(`Đã thêm ${item.json.type}!`);
                    return { moved: true, newPosition: snappedPos };
                }
            },
            collect: (monitor) => ({
                isOver: monitor.isOver(),
                dragItem: monitor.getItem(),
            }),
        }), [onAddElement, onUpdatePosition, viewMode, zoomLevel, gridSize, getSnapPoints]);

        const handleCanvasClick = useCallback(
            (e) => {
                e.stopPropagation();
                onSelectElement([]);
                onSelectChild(null, null);
                setContextMenu(null);
            },
            [onSelectElement, onSelectChild]
        );

        const handleContextMenu = useCallback(
            (id, position) => {
                setContextMenu({ id, position });
            },
            []
        );

        const handleCloseContextMenu = useCallback(() => {
            setContextMenu(null);
        }, []);

        const handleContextMenuAction = useCallback(
            (action) => {
                if (!contextMenu) return;
                const { id } = contextMenu;
                switch (action) {
                    case 'edit':
                        onEditElement(id);
                        break;
                    case 'duplicate':
                        onDuplicateElement(id);
                        toast.success('Đã sao chép phần tử!');
                        break;
                    case 'delete':
                        onDeleteElement(id);
                        toast.success('Đã xóa phần tử!');
                        break;
                    case 'moveUp':
                        onMoveElementUp(id);
                        toast.success('Đã di chuyển phần tử lên!');
                        break;
                    case 'moveDown':
                        onMoveElementDown(id);
                        toast.success('Đã di chuyển phần tử xuống!');
                        break;
                    case 'group':
                        if (selectedIds.length > 1) {
                            onGroupElements(selectedIds);
                        } else {
                            toast.error('Vui lòng chọn ít nhất 2 phần tử để nhóm!');
                        }
                        break;
                    default:
                        console.warn('Unknown context menu action:', action);
                }
                setContextMenu(null);
            },
            [contextMenu, onEditElement, onDuplicateElement, onDeleteElement, onMoveElementUp, onMoveElementDown, onGroupElements, selectedIds]
        );

        useHotkeys('delete', () => {
            if (selectedIds.length > 0) {
                selectedIds.forEach((id) => onDeleteElement(id));
                toast.success('Đã xóa các phần tử được chọn!');
            }
        }, [selectedIds, onDeleteElement]);

        useHotkeys('ctrl+d, cmd+d', (e) => {
            e.preventDefault();
            if (selectedIds.length > 0) {
                selectedIds.forEach((id) => onDuplicateElement(id));
                toast.success('Đã sao chép các phần tử được chọn!');
            }
        }, [selectedIds, onDuplicateElement]);

        useHotkeys('ctrl+g, cmd+g', (e) => {
            e.preventDefault();
            if (selectedIds.length > 1) {
                onGroupElements(selectedIds);
            } else {
                toast.error('Vui lòng chọn ít nhất 2 phần tử để nhóm!');
            }
        }, [selectedIds, onGroupElements]);

        return (
            <div
                ref={drop(canvasRef)}
                className="lpb-canvas"
                style={{
                    width: viewMode === 'desktop' ? CSS_VARIABLES['--canvas-desktop'] : viewMode === 'tablet' ? CSS_VARIABLES['--canvas-tablet'] : CSS_VARIABLES['--canvas-mobile'],
                    height: '100%',
                    position: 'relative',
                    background: pageData.canvas?.background || CSS_VARIABLES['--white'],
                    transform: `scale(${zoomLevel / 100})`,
                    transformOrigin: 'top left',
                    overflow: 'auto',
                    border: `1px solid ${CSS_VARIABLES['--border-color']}`,
                    boxShadow: CSS_VARIABLES['--shadow'],
                    backgroundImage: showGrid
                        ? `linear-gradient(to right, ${CSS_VARIABLES['--border-color']} 1px, transparent 1px), linear-gradient(to bottom, ${CSS_VARIABLES['--border-color']} 1px, transparent 1px)`
                        : 'none',
                    backgroundSize: `${gridSize}px ${gridSize}px`,
                }}
                onClick={handleCanvasClick}
            >
                {pageData.elements.map((element) => (
                    <Element
                        key={element.id}
                        element={element}
                        isSelected={selectedIds.includes(element.id)}
                        onSelectElement={onSelectElement}
                        onUpdatePosition={onUpdatePosition}
                        viewMode={viewMode}
                        zoomLevel={zoomLevel}
                        gridSize={gridSize}
                        onContextMenu={handleContextMenu}
                        canvasBounds={canvasBounds}
                        canvasRef={canvasRef}
                        onSelectChild={onSelectChild}
                        selectedChildId={selectedChildId}
                        onUpdateChildren={onUpdateChildren}
                        showGrid={showGrid}
                    />
                ))}
                {dragPreview && (
                    <div
                        style={{
                            position: 'absolute',
                            transform: `translate(${dragPreview.position.x}px, ${dragPreview.position.y}px)`,
                            width: dragPreview.size.width,
                            height: dragPreview.size.height,
                            background: 'rgba(99, 102, 241, 0.2)',
                            border: `2px dashed ${CSS_VARIABLES['--primary']}`,
                            pointerEvents: 'none',
                            zIndex: 1000,
                        }}
                    >
                        {renderComponentContent(dragPreview.type, dragPreview.componentData || {}, dragPreview.styles || {})}
                    </div>
                )}
                <Guidelines guidelines={guidelines} zoomLevel={zoomLevel} />
                {contextMenu && (
                    <div
                        className="lpb-context-menu"
                        style={{
                            position: 'fixed',
                            top: contextMenu.position.y,
                            left: contextMenu.position.x,
                            background: CSS_VARIABLES['--white'],
                            border: `1px solid ${CSS_VARIABLES['--border-color']}`,
                            boxShadow: CSS_VARIABLES['--shadow-lg'],
                            borderRadius: CSS_VARIABLES['--border-radius'],
                            zIndex: 1000,
                            padding: '8px 0',
                        }}
                        onClick={(e) => e.stopPropagation()}
                    >
                        <div className="lpb-context-menu-item" onClick={() => handleContextMenuAction('edit')}>
                            Chỉnh sửa
                        </div>
                        <div className="lpb-context-menu-item" onClick={() => handleContextMenuAction('duplicate')}>
                            Sao chép
                        </div>
                        <div className="lpb-context-menu-item" onClick={() => handleContextMenuAction('delete')}>
                            Xóa
                        </div>
                        <div className="lpb-context-menu-item" onClick={() => handleContextMenuAction('moveUp')}>
                            Di chuyển lên
                        </div>
                        <div className="lpb-context-menu-item" onClick={() => handleContextMenuAction('moveDown')}>
                            Di chuyển xuống
                        </div>
                        {selectedIds.length > 1 && (
                            <div className="lpb-context-menu-item" onClick={() => handleContextMenuAction('group')}>
                                Nhóm
                            </div>
                        )}
                    </div>
                )}
            </div>
        );
    }
);

export default Canvas;
