import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import api from "@landinghub/api";
import DogLoader from "../components/DogLoader";

import grapesjs from "grapesjs";
import "grapesjs/dist/css/grapes.min.css";
import "grapesjs-blocks-basic";

const CreateLandingPage = () => {
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [pageDescription, setPageDescription] = useState("");
    const [saveTime, setSaveTime] = useState(null);
    const editorRef = useRef(null);
    const editorContainerRef = useRef(null);
    const navigate = useNavigate();

    // --- Check auth token ---
    useEffect(() => {
        const token = localStorage.getItem("token");
        if (!token) {
            navigate("/auth");
            return;
        }
        try {
            const decoded = jwtDecode(token);
            if (!decoded.userId) throw new Error("Invalid token");
            api.defaults.headers.common["Authorization"] = `Bearer ${token}`;
        } catch {
            navigate("/auth");
        }
    }, [navigate]);

    // --- Init GrapesJS ---
    useEffect(() => {
        if (!editorRef.current) {
            editorRef.current = grapesjs.init({
                container: editorContainerRef.current,
                height: "80vh",
                fromElement: false,
                storageManager: false,
                plugins: ["gjs-blocks-basic"],
                pluginsOpts: { "gjs-blocks-basic": {} },
                blockManager: {
                    appendTo: "#blocks-panel", // Block panel b√™n tr√°i
                },
                panels: {
                    defaults: [
                        {
                            id: "panel-top",
                            el: ".panel__top",
                            buttons: []
                        }
                    ]
                }
            });

            // Auto save khi update ho·∫∑c add/remove component
            ["update", "component:add", "component:remove"].forEach(event => {
                editorRef.current.on(event, handleAutoSave);
            });
        }
    }, []);

    // --- Auto save ---
    const handleAutoSave = async () => {
        if (!editorRef.current) return;
        const html = editorRef.current.getHtml();
        const css = editorRef.current.getCss();
        try {
            await api.post("/api/pages/auto-save", { html, css });
            setSaveTime(new Date().toLocaleTimeString());
        } catch (err) {
            console.error("Auto save error:", err);
        }
    };

    // --- Reload editor ---
    const handleReloadEditor = () => {
        if (editorRef.current) {
            editorRef.current.destroy();
            editorRef.current = null;
            window.location.reload();
        }
    };

    // --- Export HTML ---
    const handleExportHTML = () => {
        if (!editorRef.current) return;
        const html = editorRef.current.getHtml();
        const css = editorRef.current.getCss();
        const fullHtml = `<html><head><style>${css}</style></head><body>${html}</body></html>`;
        const blob = new Blob([fullHtml], { type: "text/html" });
        const url = URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = "landing-page.html";
        a.click();
        URL.revokeObjectURL(url);
    };

    if (loading) return <DogLoader />;

    return (
        <div className="create-landing-page flex gap-4 p-4 max-w-7xl mx-auto">
            {/* Block panel b√™n tr√°i */}
            <div id="blocks-panel" className="border p-2 rounded w-60 bg-white"></div>

            {/* Main content */}
            <div className="flex-1 flex flex-col gap-4">
                <div className="flex justify-between items-center mb-2">
                    <button onClick={() => navigate("/pages")} className="btn-secondary">
                        ‚Üê Quay l·∫°i
                    </button>
                    <h1 className="text-2xl font-bold">T·∫°o Landing Page</h1>
                </div>

                {error && (
                    <div className="error">
                        {error} <button onClick={() => setError(null)}>ƒê√≥ng</button>
                    </div>
                )}

                {/* Description + Actions */}
                <div className="flex flex-col gap-2">
          <textarea
              value={pageDescription}
              onChange={(e) => setPageDescription(e.target.value)}
              rows="4"
              placeholder="M√¥ t·∫£ chi ti·∫øt..."
              className="textarea"
          />
                    <div className="flex gap-2 items-center">
                        <button onClick={handleExportHTML} className="btn-primary">
                            üíø Xu·∫•t HTML
                        </button>
                        <button onClick={handleReloadEditor} className="btn-danger">
                            üîÅ Reload Editor
                        </button>
                        {saveTime && <span>‚è∞ L·∫ßn l∆∞u cu·ªëi: {saveTime}</span>}
                    </div>
                </div>

                {/* GrapesJS editor */}
                <div ref={editorContainerRef} className="editor-section border rounded"></div>
            </div>
        </div>
    );
};

export default CreateLandingPage;
