import { toast } from 'react-toastify';
import { sections } from '../../constants/sections';

export const ItemTypes = {
    ELEMENT: 'element',
    EXISTING_ELEMENT: 'existingElement',
    CHILD_ELEMENT: 'childElement',
};

// Calculate precise coordinates within canvas or section
export const getCanvasPosition = (mouseX, mouseY, containerElement, zoomLevel) => {
    if (!containerElement) {
        console.error('Container element is null in getCanvasPosition');
        return { x: 0, y: 0 };
    }

    const rect = containerElement.getBoundingClientRect();
    const scrollX = containerElement.scrollLeft || 0;
    const scrollY = containerElement.scrollTop || 0;

    const rawX = (mouseX - rect.left + scrollX) / (zoomLevel / 100);
    const rawY = (mouseY - rect.top + scrollY) / (zoomLevel / 100);

    return { x: Math.max(0, rawX), y: Math.max(0, rawY) };
};

// Snap to grid with snap points
export const snapToGrid = (x, y, gridSize, snapPoints = []) => {
    let snappedX = Math.round(x / gridSize) * gridSize;
    let snappedY = Math.round(y / gridSize) * gridSize;

    snapPoints.forEach((point) => {
        if (Math.abs(x - point.x) < 15) snappedX = point.x;
        if (Math.abs(y - point.y) < 15) snappedY = point.y;
    });

    return { x: snappedX, y: snappedY };
};

// Calculate element bounds
export const getElementBounds = (element) => ({
    left: element.position.desktop?.x || 0,
    top: element.position.desktop?.y || 0,
    right: (element.position.desktop?.x || 0) + (element.size?.width || 200),
    bottom: (element.position.desktop?.y || 0) + (element.size?.height || 50),
    centerX: (element.position.desktop?.x || 0) + (element.size?.width || 200) / 2,
    centerY: (element.position.desktop?.y || 0) + (element.size?.height || 50) / 2,
});

// Process styles, including pseudo-classes and keyframes
const processStyles = (styles, className) => {
    let cssString = '';
    const keyframes = {};

    const toKebabCase = (str) => str.replace(/[A-Z]/g, (m) => `-${m.toLowerCase()}`);

    Object.entries(styles).forEach(([key, value]) => {
        if (key.startsWith(':')) {
            // Handle pseudo-classes (:hover, :active)
            cssString += `.${className}${key} { `;
            Object.entries(value).forEach(([prop, val]) => {
                if (prop.startsWith('::')) {
                    // Handle pseudo-elements (::before, ::after)
                    cssString += `.${className}${prop} { `;
                    Object.entries(val).forEach(([p, v]) => {
                        cssString += `${toKebabCase(p)}: ${v}; `;
                    });
                    cssString += '} ';
                } else {
                    cssString += `${toKebabCase(prop)}: ${val}; `;
                }
            });
            cssString += '} ';
        } else if (key.startsWith('@keyframes')) {
            // Handle keyframes
            keyframes[key] = value;
        } else {
            cssString += `${toKebabCase(key)}: ${value}; `;
        }
    });

    // Convert keyframes to CSS
    Object.entries(keyframes).forEach(([key, frames]) => {
        cssString += `${key} { `;
        Object.entries(frames).forEach(([percentage, props]) => {
            cssString += `${percentage} { `;
            Object.entries(props).forEach(([prop, val]) => {
                cssString += `${toKebabCase(prop)}: ${val}; `;
            });
            cssString += '} ';
        });
        cssString += '} ';
    });

    return cssString;
};

// Render component content
export const renderComponentContent = (
    type,
    componentData = {},
    styles = {},
    children = [],
    isCanvas = false,
    onSelectChild = null,
    parentId = null,
    childId = null
) => {
    if (!type || typeof type !== 'string') {
        return (
            <div style={{ color: '#ff0000', padding: '10px', border: '1px dashed #ff0000', borderRadius: '4px', ...styles }}>
                Unknown Component: {type || 'Không xác định'}
            </div>
        );
    }
    const getCleanTextStyles = (styles) => {
        const { background, border, ...cleanStyles } = styles;
        return cleanStyles;
    };
    // const getCleanTextStyles = (originalStyles) => {
    //     const cleaned = { ...originalStyles };
    //     // Remove properties that shouldn't be on text elements
    //     delete cleaned.border;
    //     delete cleaned.borderRadius;
    //     delete cleaned.boxShadow;
    //     delete cleaned.backgroundColor;
    //     delete cleaned.background;
    //     delete cleaned.backgroundImage;
    //     delete cleaned.backgroundSize;
    //     delete cleaned.backgroundPosition;
    //     delete cleaned.padding;
    //     delete cleaned.paddingLeft;
    //     delete cleaned.paddingRight;
    //     delete cleaned.paddingTop;
    //     delete cleaned.paddingBottom;
    //
    //     // Ensure basic properties
    //     cleaned.margin = cleaned.margin || '0';
    //     cleaned.boxSizing = 'border-box';
    //     cleaned.display = cleaned.display || 'block';
    //     cleaned.width = cleaned.width || '100%';
    //     cleaned.height = cleaned.height || 'auto';
    //
    //     return cleaned;
    // };

    if (type === 'section' && componentData.structure === 'ladi-standard') {
        return (
            <div className="ladi-section" style={{ ...styles, width: '100%', height: '100%' }}>
                <div
                    className="ladi-section-background"
                    style={{
                        backgroundColor: componentData.backgroundColor || 'transparent',
                        backgroundImage: componentData.backgroundImage ? `url(${componentData.backgroundImage})` : 'none',
                        backgroundSize: 'cover',
                        backgroundPosition: 'center',
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        zIndex: 0,
                    }}
                ></div>
                <div
                    className="ladi-overlay"
                    style={{
                        backgroundColor: componentData.overlayColor || 'transparent',
                        opacity: componentData.overlayOpacity || 0,
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        zIndex: 0,
                    }}
                ></div>
                <div
                    className="ladi-container"
                    style={{
                        position: 'relative',
                        zIndex: 1,
                        padding: componentData.padding || '20px',
                        width: '100%',
                        height: '100%',
                    }}
                >
                    {children.map((child, index) => (
                        <div key={child.id || index} style={{ marginBottom: child.marginBottom || '10px' }}>
                            {renderComponentContent(
                                child.type,
                                child.componentData || {},
                                child.styles || {},
                                child.children || [],
                                isCanvas,
                                onSelectChild,
                                parentId,
                                child.id
                            )}
                        </div>
                    ))}
                </div>
            </div>
        );
    }

    switch (type) {


        case 'icon': {
            const { icon, events = {}, title, imageUrl } = componentData; // Thêm imageUrl
            const uniqueClass = `icon-${parentId || 'root'}-${childId || Date.now()}`;
            const isSvg = icon && icon.startsWith('<svg');

            // Clone styles to avoid mutation
            const iconStyles = { ...styles };

            // Xử lý pseudo-classes và keyframes
            let hoverStyles = {};
            let activeStyles = {};
            let keyframesCSS = '';

            if (iconStyles[':hover']) {
                hoverStyles = iconStyles[':hover'];
                delete iconStyles[':hover'];
            }

            if (iconStyles[':active']) {
                activeStyles = iconStyles[':active'];
                delete iconStyles[':active'];
            }

            // Xử lý keyframes
            Object.keys(iconStyles).forEach((key) => {
                if (key.startsWith('@keyframes')) {
                    const animationName = key.replace('@keyframes ', '').trim();
                    const frames = iconStyles[key];
                    keyframesCSS += `@keyframes ${animationName} {`;
                    Object.entries(frames).forEach(([percentage, props]) => {
                        keyframesCSS += `${percentage} {`;
                        Object.entries(props).forEach(([prop, val]) => {
                            const kebabProp = prop.replace(/[A-Z]/g, (m) => `-${m.toLowerCase()}`);
                            keyframesCSS += `${kebabProp}: ${val};`;
                        });
                        keyframesCSS += '}';
                    });
                    keyframesCSS += '}';
                    delete iconStyles[key];
                }
            });

            // Generate CSS cho hover và active
            const toKebabCase = (str) => str.replace(/[A-Z]/g, (m) => `-${m.toLowerCase()}`);

            let hoverCSS = '';
            if (Object.keys(hoverStyles).length > 0) {
                hoverCSS = `.${uniqueClass}:hover {`;
                Object.entries(hoverStyles).forEach(([key, value]) => {
                    hoverCSS += `${toKebabCase(key)}: ${value};`;
                });
                hoverCSS += '}';
            }

            let activeCSS = '';
            if (Object.keys(activeStyles).length > 0) {
                activeCSS = `.${uniqueClass}:active {`;
                Object.entries(activeStyles).forEach(([key, value]) => {
                    activeCSS += `${toKebabCase(key)}: ${value};`;
                });
                activeCSS += '}';
            }

            // Thêm CSS cho img
            const imgCSS = `
        .${uniqueClass} img {
            width: 100%;
            height: 100%;
            object-fit: contain;
        }
    `;

            const handleClick = (e) => {
                e.stopPropagation();
                if (isCanvas) {
                    if (typeof onSelectChild === 'function' && parentId && childId) {
                        onSelectChild(parentId, childId);
                    }
                    if (events.onClick) {
                        if (events.onClick.type === 'navigate') {
                            toast.info(`Mô phỏng: Điều hướng đến ${events.onClick.url}`, {
                                position: 'bottom-right',
                                autoClose: 2000,
                            });
                        } else if (events.onClick.type === 'triggerApi') {
                            toast.info(`Mô phỏng: Gọi API ${events.onClick.apiUrl}`, {
                                position: 'bottom-right',
                                autoClose: 2000,
                            });
                        }
                    }
                } else {
                    if (events.onClick?.type === 'navigate') {
                        window.location.href = events.onClick.url;
                    } else if (events.onClick?.type === 'triggerApi') {
                        console.log(`Trigger API: ${events.onClick.apiUrl}`);
                    }
                }
            };

            return (
                <>
                    {(hoverCSS || activeCSS || keyframesCSS || imageUrl) && (
                        <style>
                            {`
                        .${uniqueClass} {
                            position: absolute;
                            z-index: ${iconStyles.zIndex || 10};
                            isolation: ${iconStyles.isolation || 'isolate'};
                        }
                        .${uniqueClass} svg {
                            width: 100%;
                            height: 100%;
                            fill: none;
                            stroke: currentColor;
                        }
                        ${imgCSS}
                        ${hoverCSS}
                        ${activeCSS}
                        ${keyframesCSS}
                    `}
                        </style>
                    )}
                    <div
                        className={`lpb-icon ${uniqueClass}`}
                        style={{
                            ...iconStyles,
                            width: componentData.size?.width || iconStyles.width || '50px',
                            height: componentData.size?.height || iconStyles.height || '50px',
                            display: iconStyles.display || 'flex',
                            alignItems: iconStyles.alignItems || 'center',
                            justifyContent: iconStyles.justifyContent || 'center',
                            cursor: events.onClick ? 'pointer' : 'default',
                            position: iconStyles.position || 'absolute',
                        }}
                        onClick={handleClick}
                        title={title || ''}
                    >
                        {imageUrl ? (
                            <img
                                src={imageUrl}
                                alt={title || 'Icon'}
                                style={{
                                    width: '100%',
                                    height: '100%',
                                    objectFit: 'contain',
                                }}
                            />
                        ) : isSvg ? (
                            <div
                                dangerouslySetInnerHTML={{ __html: icon }}
                                style={{
                                    width: componentData.size?.width || iconStyles.width || '50px',
                                    height: componentData.size?.height || iconStyles.height || '50px',
                                    display: 'flex',
                                    alignItems: 'center',
                                    justifyContent: 'center',
                                }}
                            />
                        ) : (
                            icon && <i className={icon} style={{ fontSize: 'inherit', color: 'inherit' }}></i>
                        )}
                    </div>
                </>
            );
        }

        case 'heading':
            const headingLevel = componentData.level && ['h1', 'h2', 'h3', 'h4', 'h5', 'h6'].includes(componentData.level)
                ? componentData.level
                : 'h2';
            const HeadingTag = headingLevel;
            return (
                <HeadingTag
                    style={{
                        ...getCleanTextStyles(styles),
                        fontSize: componentData.fontSize || '1.5rem',
                        color: componentData.color || '#1f2937',
                        margin: componentData.margin || '0',
                        fontWeight: componentData.fontWeight || '700',
                        textAlign: componentData.textAlign || 'left',
                        lineHeight: componentData.lineHeight || '1.4',
                    }}
                >
                    {componentData.content || 'Tiêu đề'}
                </HeadingTag>
            );

        case 'paragraph':
            return (
                <p
                    style={{
                        ...getCleanTextStyles(styles),
                        fontSize: componentData.fontSize || '1rem',
                        color: componentData.color || '#1f2937',
                        margin: componentData.margin || '0',
                        fontWeight: componentData.fontWeight || '400',
                        lineHeight: componentData.lineHeight || '1.6',
                        textAlign: componentData.textAlign || 'left',
                    }}
                >
                    {componentData.content || 'Đoạn văn'}
                </p>
            );

        case 'button': {
            const buttonClass = `button-${componentData.id || Math.random().toString(36).substr(2, 9)}`;
            const buttonStyles = { ...styles };
            const pseudoStyles = {};
            const keyframes = {};

            if (buttonStyles[':hover']) {
                pseudoStyles[':hover'] = buttonStyles[':hover'];
                delete buttonStyles[':hover'];
            }
            if (buttonStyles[':active']) {
                pseudoStyles[':active'] = buttonStyles[':active'];
                delete buttonStyles[':active'];
            }

            Object.keys(buttonStyles).forEach((key) => {
                if (key.startsWith('@keyframes')) {
                    const animationName = key.replace('@keyframes ', '').trim();
                    keyframes[animationName] = buttonStyles[key];
                    delete buttonStyles[key];
                }
            });

            const toKebabCase = (str) => str.replace(/[A-Z]/g, (m) => `-${m.toLowerCase()}`);

            const cssString = Object.entries(pseudoStyles).reduce((acc, [pseudo, props]) => {
                const propsString = Object.entries(props).map(([key, value]) => {
                    if (key.startsWith('::')) {
                        const subPropsString = Object.entries(value).map(([k, v]) => `${toKebabCase(k)}: ${v};`).join(' ');
                        return `.${buttonClass}${key} { ${subPropsString} }`;
                    }
                    return `${toKebabCase(key)}: ${value};`;
                }).join(' ');
                return `${acc}.${buttonClass}${pseudo} { ${propsString} } `;
            }, '');

            const keyframesString = Object.entries(keyframes).reduce((acc, [name, frames]) => {
                const framesString = Object.entries(frames).map(([frame, props]) => {
                    const propsString = Object.entries(props).map(([key, value]) => {
                        return `${toKebabCase(key)}: ${value};`;
                    }).join(' ');
                    return `${frame} { ${propsString} }`;
                }).join(' ');
                return `${acc} @keyframes ${name} { ${framesString} }`;
            }, '');

            return (
                <>
                    {(cssString || keyframesString) && <style>{cssString + keyframesString}</style>}
                    <button
                        className={buttonClass}
                        style={{
                            ...buttonStyles,
                            padding: buttonStyles.padding || '10px 20px',
                            borderRadius: buttonStyles.borderRadius || componentData.borderRadius || '8px',
                            background: buttonStyles.background || componentData.background || '#2563eb',
                            color: buttonStyles.color || componentData.color || '#fff',
                            border: buttonStyles.border || componentData.border || 'none',
                            cursor: buttonStyles.cursor || 'pointer',
                            fontSize: buttonStyles.fontSize || componentData.fontSize || '1rem',
                            fontWeight: buttonStyles.fontWeight || componentData.fontWeight || '600',
                            textAlign: buttonStyles.textAlign || componentData.textAlign || 'center',
                            display: buttonStyles.display || 'inline-flex',
                            alignItems: buttonStyles.alignItems || 'center',
                            justifyContent: buttonStyles.justifyContent || 'center',
                            transition: buttonStyles.transition || 'all 0.3s ease',
                            boxSizing: 'border-box',
                            width: buttonStyles.width || 'auto',
                            minWidth: buttonStyles.minWidth || 'fit-content',
                            whiteSpace: 'nowrap',
                        }}
                        onClick={
                            componentData.events?.onClick
                                ? (e) => {
                                    e.stopPropagation();
                                    if (isCanvas) {
                                        if (typeof onSelectChild === 'function' && parentId && childId) {
                                            onSelectChild(parentId, childId);
                                        }
                                        const event = componentData.events.onClick;
                                        if (event.type === 'submitForm') {
                                            toast.info(`Mô phỏng: Gửi form đến ${event.apiUrl}`, {
                                                position: 'bottom-right',
                                                autoClose: 2000,
                                            });
                                        } else if (event.type === 'navigate') {
                                            toast.info(`Mô phỏng: Điều hướng đến ${event.url}`, {
                                                position: 'bottom-right',
                                                autoClose: 2000,
                                            });
                                        } else if (event.type === 'triggerApi') {
                                            toast.info(`Mô phỏng: Gọi API ${event.apiUrl}`, {
                                                position: 'bottom-right',
                                                autoClose: 2000,
                                            });
                                        }
                                    } else {
                                        const event = componentData.events.onClick;
                                        if (event.type === 'submitForm') {
                                            console.log(`Submitting form to ${event.apiUrl}`);
                                        } else if (event.type === 'navigate') {
                                            window.location.href = event.url;
                                        } else if (event.type === 'triggerApi') {
                                            console.log(`Trigger API: ${event.apiUrl}`);
                                        }
                                    }
                                }
                                : (e) => {
                                    e.stopPropagation();
                                    if (isCanvas && typeof onSelectChild === 'function' && parentId && childId) {
                                        onSelectChild(parentId, childId);
                                    }
                                }
                        }
                    >
                        {componentData.content || 'Nút'}
                    </button>
                </>
            );
        }

        case 'image':
            return (
                <img
                    src={componentData.src || 'https://via.placeholder.com/150?text=Image'}
                    alt={componentData.alt || 'Hình ảnh'}
                    style={{
                        maxWidth: componentData.maxWidth || '100%',
                        height: componentData.height || 'auto',
                        borderRadius: componentData.borderRadius || '0',
                        ...styles,
                    }}
                    draggable={componentData.draggable ?? false}
                />
            );

        case 'form':
            return (
                <div
                    style={{
                        display: 'flex',
                        flexDirection: componentData.direction || 'column',
                        gap: componentData.gap || '10px',
                        ...styles,
                    }}
                >
                    {componentData.title && !children.some((child) => child?.type === 'heading') && (
                        <h3
                            style={{
                                ...getCleanTextStyles(styles),
                                margin: componentData.titleMargin || '0',
                                fontSize: componentData.titleFontSize || '1.2rem',
                                color: componentData.titleColor || '#1f2937',
                                fontWeight: componentData.titleFontWeight || '600',
                            }}
                        >
                            {componentData.title}
                        </h3>
                    )}
                    {Array.isArray(componentData.fields) && componentData.fields.length > 0 ? (
                        componentData.fields.map((field, index) => (
                            <input
                                key={index}
                                type={field.type || 'text'}
                                placeholder={field.placeholder || field.label || 'Nhập...'}
                                style={{
                                    padding: field.padding || '8px',
                                    borderRadius: field.borderRadius || '8px',
                                    border: field.border || '1px solid #ccc',
                                    fontSize: field.fontSize || '1rem',
                                }}
                            />
                        ))
                    ) : (
                        !children.some((child) => child?.type === 'input') && (
                            <input
                                type={componentData.inputType || 'text'}
                                placeholder={componentData.placeholder || 'Nhập...'}
                                style={{
                                    padding: componentData.inputPadding || '8px',
                                    borderRadius: componentData.inputBorderRadius || '8px',
                                    border: componentData.inputBorder || '1px solid #ccc',
                                    fontSize: componentData.inputFontSize || '1rem',
                                }}
                            />
                        )
                    )}
                    {!children.some((child) => child?.type === 'button') && (
                        <button
                            type="submit"
                            style={{
                                background: componentData.buttonBackground || '#2563eb',
                                color: componentData.buttonColor || '#fff',
                                padding: componentData.buttonPadding || '8px 16px',
                                borderRadius: componentData.buttonBorderRadius || '8px',
                                border: componentData.buttonBorder || 'none',
                                cursor: 'pointer',
                                fontSize: componentData.buttonFontSize || '1rem',
                                fontWeight: componentData.buttonFontWeight || '600',
                            }}
                        >
                            {componentData.buttonText || 'Gửi'}
                        </button>
                    )}
                    {Array.isArray(children) &&
                        children.length > 0 &&
                        children.map((child, index) => (
                            <div key={child.id || index} style={{ marginBottom: child.marginBottom || '10px' }}>
                                {renderComponentContent(
                                    child.type,
                                    child.componentData || {},
                                    child.styles || {},
                                    child.children || [],
                                    isCanvas,
                                    onSelectChild,
                                    parentId,
                                    child.id
                                )}
                            </div>
                        ))}
                </div>
            );

        case 'section':
        case 'container':
            return (
                <div
                    style={{
                        display: componentData.layoutType || 'flex',
                        flexDirection: componentData.direction || 'column',
                        gap: componentData.gap || '10px',
                        padding: componentData.padding || '0',
                        background: componentData.background || 'transparent',
                        ...styles,
                    }}
                >
                    {componentData.title && !children.some((child) => child?.type === 'heading') && (
                        <h2
                            style={{
                                ...getCleanTextStyles(styles),
                                fontSize: componentData.titleFontSize || '1.8rem',
                                margin: componentData.titleMargin || '0',
                                color: componentData.titleColor || '#1f2937',
                                fontWeight: componentData.titleFontWeight || '700',
                            }}
                        >
                            {componentData.title}
                        </h2>
                    )}
                    {componentData.subtitle && !children.some((child) => child?.type === 'paragraph') && (
                        <p
                            style={{
                                ...getCleanTextStyles(styles),
                                fontSize: componentData.subtitleFontSize || '1.2rem',
                                margin: componentData.subtitleMargin || '0',
                                color: componentData.subtitleColor || '#4b5563',
                            }}
                        >
                            {componentData.subtitle}
                        </p>
                    )}
                    {componentData.content && !children.some((child) => child?.type === 'paragraph') && (
                        <p
                            style={{
                                ...getCleanTextStyles(styles),
                                fontSize: componentData.contentFontSize || '1rem',
                                margin: componentData.contentMargin || '0',
                                color: componentData.contentColor || '#4b5563',
                            }}
                        >
                            {componentData.content}
                        </p>
                    )}
                    {Array.isArray(componentData.features) &&
                        componentData.features.length > 0 &&
                        !children.some((child) => child?.type === 'list') && (
                            <ul
                                style={{
                                    listStyle: 'none',
                                    padding: componentData.featuresPadding || 0,
                                    margin: componentData.featuresMargin || '0',
                                    display: 'flex',
                                    flexDirection: componentData.featuresDirection || 'column',
                                    gap: componentData.featuresGap || '8px',
                                }}
                            >
                                {componentData.features.map((feature, index) => (
                                    <li
                                        key={index}
                                        style={{
                                            fontSize: componentData.featureFontSize || '1rem',
                                            color: componentData.featureColor || '#1f2937',
                                        }}
                                    >
                                        {feature}
                                    </li>
                                ))}
                            </ul>
                        )}
                    {componentData.ctaText && !children.some((child) => child?.type === 'button') && (
                        <button
                            style={{
                                padding: componentData.ctaPadding || '10px 20px',
                                borderRadius: componentData.ctaBorderRadius || '8px',
                                background: componentData.ctaBackground || '#2563eb',
                                color: componentData.ctaColor || '#fff',
                                border: componentData.ctaBorder || 'none',
                                cursor: 'pointer',
                                fontSize: componentData.ctaFontSize || '1rem',
                                fontWeight: componentData.ctaFontWeight || '600',
                            }}
                        >
                            {componentData.ctaText}
                        </button>
                    )}
                    {componentData.contact && !children.some((child) => child?.type === 'paragraph') && (
                        <p
                            style={{
                                ...getCleanTextStyles(styles),
                                fontSize: componentData.contactFontSize || '0.9rem',
                                margin: componentData.contactMargin || '0',
                                color: componentData.contactColor || '#4b5563',
                            }}
                        >
                            {componentData.contact}
                        </p>
                    )}
                    {Array.isArray(children) &&
                        children.length > 0 &&
                        children.map((child, index) => (
                            <div key={child.id || index} style={{ marginBottom: child.marginBottom || '10px' }}>
                                {renderComponentContent(
                                    child.type,
                                    child.componentData || {},
                                    child.styles || {},
                                    child.children || [],
                                    isCanvas,
                                    onSelectChild,
                                    parentId,
                                    child.id
                                )}
                            </div>
                        ))}
                </div>
            );

        case 'divider':
            return (
                <hr
                    style={{
                        border: 'none',
                        background: componentData.color || '#e5e7eb',
                        height: componentData.thickness || '2px',
                        width: componentData.width || '100%',
                        margin: componentData.margin || '0',
                        ...styles,
                    }}
                />
            );

        case 'spacer':
            return (
                <div
                    style={{
                        background: componentData.background || 'transparent',
                        height: componentData.height || '40px',
                        width: componentData.width || '100%',
                        ...styles,
                    }}
                />
            );

        case 'popup':
            return (
                <div
                    style={{
                        display: 'flex',
                        flexDirection: componentData.direction || 'column',
                        gap: componentData.gap || '10px',
                        textAlign: componentData.textAlign || 'center',
                        padding: componentData.padding || '20px',
                        background: componentData.background || 'rgba(255, 255, 255, 0.9)',
                        borderRadius: componentData.borderRadius || '12px',
                        ...styles,
                    }}
                >
                    {componentData.title && !children.some((child) => child?.type === 'heading') && (
                        <h3
                            style={{
                                ...getCleanTextStyles(styles),
                                fontSize: componentData.titleFontSize || '1.5rem',
                                margin: componentData.titleMargin || '0',
                                color: componentData.titleColor || '#1f2937',
                                fontWeight: componentData.titleFontWeight || '600',
                            }}
                        >
                            {componentData.title}
                        </h3>
                    )}
                    {componentData.content && !children.some((child) => child?.type === 'paragraph') && (
                        <p
                            style={{
                                ...getCleanTextStyles(styles),
                                fontSize: componentData.contentFontSize || '1rem',
                                margin: componentData.contentMargin || '0',
                                color: componentData.contentColor || '#4b5563',
                            }}
                        >
                            {componentData.content}
                        </p>
                    )}
                    {componentData.buttonText && !children.some((child) => child?.type === 'button') && (
                        <button
                            style={{
                                padding: componentData.buttonPadding || '10px 20px',
                                borderRadius: componentData.buttonBorderRadius || '8px',
                                background: componentData.buttonBackground || '#2563eb',
                                color: componentData.buttonColor || '#fff',
                                border: componentData.buttonBorder || 'none',
                                cursor: 'pointer',
                                fontSize: componentData.buttonFontSize || '1rem',
                                fontWeight: componentData.buttonFontWeight || '600',
                            }}
                        >
                            {componentData.buttonText}
                        </button>
                    )}
                    {Array.isArray(children) &&
                        children.length > 0 &&
                        children.map((child, index) => (
                            <div key={child.id || index} style={{ marginBottom: child.marginBottom || '10px' }}>
                                {renderComponentContent(
                                    child.type,
                                    child.componentData || {},
                                    child.children || [],
                                    isCanvas,
                                    onSelectChild,
                                    parentId,
                                    child.id
                                )}
                            </div>
                        ))}
                </div>
            );

        case 'modal':
            return (
                <div
                    style={{
                        display: 'flex',
                        flexDirection: componentData.direction || 'column',
                        gap: componentData.gap || '10px',
                        padding: componentData.padding || '20px',
                        background: componentData.background || 'rgba(255, 255, 255, 0.9)',
                        borderRadius: componentData.borderRadius || '12px',
                        ...styles,
                    }}
                >
                    {componentData.title && !children.some((child) => child?.type === 'heading') && (
                        <h3
                            style={{
                                ...getCleanTextStyles(styles),
                                fontSize: componentData.titleFontSize || '1.5rem',
                                margin: componentData.titleMargin || '0',
                                color: componentData.titleColor || '#1f2937',
                                fontWeight: componentData.titleFontWeight || '600',
                            }}
                        >
                            {componentData.title}
                        </h3>
                    )}
                    {componentData.content && !children.some((child) => child?.type === 'paragraph') && (
                        <p
                            style={{
                                ...getCleanTextStyles(styles),
                                fontSize: componentData.contentFontSize || '1rem',
                                margin: componentData.contentMargin || '0',
                                color: componentData.contentColor || '#4b5563',
                            }}
                        >
                            {componentData.content}
                        </p>
                    )}
                    {componentData.closeButton && !children.some((child) => child?.type === 'button') && (
                        <button
                            style={{
                                padding: componentData.buttonPadding || '8px 16px',
                                borderRadius: componentData.buttonBorderRadius || '8px',
                                background: componentData.buttonBackground || '#6b7280',
                                color: componentData.buttonColor || '#fff',
                                border: componentData.buttonBorder || 'none',
                                cursor: 'pointer',
                                alignSelf: 'flex-end',
                                fontSize: componentData.buttonFontSize || '1rem',
                                fontWeight: componentData.buttonFontWeight || '600',
                            }}
                        >
                            {componentData.buttonText || 'Đóng'}
                        </button>
                    )}
                    {Array.isArray(children) &&
                        children.length > 0 &&
                        children.map((child, index) => (
                            <div key={child.id || index} style={{ marginBottom: child.marginBottom || '10px' }}>
                                {renderComponentContent(
                                    child.type,
                                    child.componentData || {},
                                    child.children || [],
                                    isCanvas,
                                    onSelectChild,
                                    parentId,
                                    child.id
                                )}
                            </div>
                        ))}
                </div>
            );

        case 'card':
            return (
                <div
                    style={{
                        padding: componentData.padding || '20px',
                        background: componentData.background || '#ffffff',
                        borderRadius: componentData.borderRadius || '8px',
                        boxShadow: componentData.boxShadow || '0 2px 8px rgba(0,0,0,0.1)',
                        ...styles,
                    }}
                >
                    {componentData.title && (
                        <h3
                            style={{
                                ...getCleanTextStyles(styles),
                                fontSize: componentData.titleFontSize || '1.2rem',
                                margin: componentData.titleMargin || '0',
                                color: componentData.titleColor || '#1f2937',
                            }}
                        >
                            {componentData.title}
                        </h3>
                    )}
                    {componentData.content && (
                        <p
                            style={{
                                ...getCleanTextStyles(styles),
                                fontSize: componentData.contentFontSize || '1rem',
                                margin: componentData.contentMargin || '10px 0 0',
                                color: componentData.contentColor || '#4b5563',
                            }}
                        >
                            {componentData.content}
                        </p>
                    )}
                </div>
            );



        case 'grid':
            return (
                <div
                    style={{
                        display: 'grid',
                        gridTemplateColumns: componentData.columns ? `repeat(${componentData.columns}, 1fr)` : 'repeat(2, 1fr)',
                        gap: componentData.gap || '20px',
                        ...styles,
                    }}
                >
                    {Array.isArray(componentData.items) &&
                        componentData.items.map((item, index) => (
                            <div
                                key={index}
                                style={{
                                    background: componentData.itemBackground || '#ffffff',
                                    borderRadius: componentData.itemBorderRadius || '8px',
                                    padding: componentData.itemPadding || '10px',
                                }}
                            >
                                {item.image && (
                                    <img
                                        src={item.image}
                                        alt={item.title || 'Product'}
                                        style={{ maxWidth: '100%', height: 'auto' }}
                                    />
                                )}
                                {item.title && (
                                    <h4
                                        style={{
                                            ...getCleanTextStyles(styles),
                                            fontSize: componentData.titleFontSize || '1rem',
                                            margin: componentData.titleMargin || '10px 0 0',
                                            color: componentData.titleColor || '#1f2937',
                                        }}
                                    >
                                        {item.title}
                                    </h4>
                                )}
                                {item.price && (
                                    <p
                                        style={{
                                            ...getCleanTextStyles(styles),
                                            fontSize: componentData.priceFontSize || '0.9rem',
                                            margin: componentData.priceMargin || '5px 0 0',
                                            color: componentData.priceColor || '#4b5563',
                                        }}
                                    >
                                        {item.price}
                                    </p>
                                )}
                            </div>
                        ))}
                </div>
            );

        case 'list':
            return (
                <ul
                    style={{
                        listStyleType: componentData.listStyleType || 'none',
                        padding: componentData.padding || '0',
                        ...styles,
                    }}
                >
                    {Array.isArray(componentData.items) &&
                        componentData.items.map((item, index) => (
                            <li
                                key={index}
                                style={{
                                    ...getCleanTextStyles(styles),
                                    fontSize: componentData.itemFontSize || '1rem',
                                    margin: componentData.itemMargin || '5px 0',
                                    color: componentData.itemColor || '#1f2937',
                                }}
                            >
                                {item.title || item}
                            </li>
                        ))}
                </ul>
            );

        case 'gallery': {
            const { title, images = [], animation = {}, events = {} } = componentData;
            const galleryClass = `gallery-${parentId || 'root'}-${childId || Date.now()}`;

            // Xử lý animation
            let animationCSS = '';
            if (animation.type) {
                const animationName = `${animation.type}-${galleryClass}`;
                animationCSS = `
                    @keyframes ${animationName} {
                        ${animation.type === 'fadeIn' ? `
                            from { opacity: 0; }
                            to { opacity: 1; }
                        ` : animation.type === 'fadeInUp' ? `
                            from { opacity: 0; transform: translateY(20px); }
                            to { opacity: 1; transform: translateY(0); }
                        ` : animation.type === 'slideInRight' ? `
                            from { transform: translateX(100%); }
                            to { transform: translateX(0); }
                        ` : animation.type === 'bounceIn' ? `
                            0% { opacity: 0; transform: scale(0.3); }
                            50% { opacity: 1; transform: scale(1.05); }
                            70% { transform: scale(0.9); }
                            100% { transform: scale(1); }
                        ` : ''}
                    }
                    .${galleryClass} {
                        animation: ${animationName} ${animation.duration || 800}ms ease-out ${animation.delay || 0}ms;
                    }
                `;
            }

            const handleImageClick = (e, imageUrl, index) => {
                e.stopPropagation();
                if (isCanvas) {
                    if (typeof onSelectChild === 'function' && parentId && childId) {
                        onSelectChild(parentId, childId);
                    }
                    toast.info(`Mô phỏng: Mở lightbox cho ảnh ${index + 1}`, {
                        position: 'bottom-right',
                        autoClose: 2000,
                    });
                } else {
                    console.log(`Open image: ${imageUrl}`);
                }
            };

            return (
                <>
                    {animationCSS && <style>{animationCSS}</style>}
                    <div
                        className={`lpb-gallery ${galleryClass}`}
                        style={{
                            ...styles,
                            cursor: events?.onClick ? 'pointer' : 'default',
                        }}
                        onClick={(e) => {
                            e.stopPropagation();
                            if (isCanvas && typeof onSelectChild === 'function' && parentId && childId) {
                                onSelectChild(parentId, childId);
                            }
                        }}
                    >
                        {/* Chỉ render title nếu không ở chế độ template và không có heading trong children */}
                        {title && !isTemplateMode && !children.some((child) => child?.type === 'heading') && (
                            <h3
                                style={{
                                    fontSize: componentData.titleFontSize || '1.2rem',
                                    margin: componentData.titleMargin || '0 0 15px 0',
                                    color: componentData.titleColor || '#1f2937',
                                    fontWeight: componentData.titleFontWeight || '600',
                                    ...getCleanTextStyles(styles),
                                }}
                            >
                                {title}
                            </h3>
                        )}
                        <div
                            style={{
                                display: styles.display || 'grid',
                                gridTemplateColumns: styles.gridTemplateColumns || 'repeat(auto-fill, minmax(150px, 1fr))',
                                gap: styles.gap || '10px',
                                width: '100%',
                            }}
                        >
                            {images.map((imageUrl, index) => (
                                <div
                                    key={index}
                                    style={{
                                        position: 'relative',
                                        overflow: 'hidden',
                                        borderRadius: styles.borderRadius || '8px',
                                        aspectRatio: '1 / 1',
                                        cursor: 'pointer',
                                        transition: 'transform 0.3s ease',
                                    }}
                                    onClick={(e) => handleImageClick(e, imageUrl, index)}
                                    onMouseEnter={(e) => {
                                        if (events?.onHover?.type === 'zoomIn') {
                                            e.currentTarget.style.transform = 'scale(1.05)';
                                        }
                                    }}
                                    onMouseLeave={(e) => {
                                        if (events?.onHover?.type === 'zoomIn') {
                                            e.currentTarget.style.transform = 'scale(1)';
                                        }
                                    }}
                                >
                                    <img
                                        src={imageUrl}
                                        alt={`Gallery ${index + 1}`}
                                        style={{
                                            width: '100%',
                                            height: '100%',
                                            objectFit: 'cover',
                                        }}
                                        loading="lazy"
                                    />
                                </div>
                            ))}
                        </div>
                        {/* Render children nếu có */}
                        {Array.isArray(children) &&
                            children.length > 0 &&
                            children.map((child, index) => (
                                <div key={child.id || index} style={{ marginBottom: child.marginBottom || '10px' }}>
                                    {renderComponentContent(
                                        child.type,
                                        child.componentData || {},
                                        child.styles || {},
                                        child.children || [],
                                        isCanvas,
                                        onSelectChild,
                                        parentId,
                                        child.id,
                                        isTemplateMode // Truyền isTemplateMode cho children
                                    )}
                                </div>
                            ))}
                    </div>
                </>
            );
        }

        case 'menu': {
            const { title, items = [], animation = {}, events = {} } = componentData;
            const menuClass = `menu-${parentId || 'root'}-${childId || Date.now()}`;

            // Xử lý animation
            let animationCSS = '';
            if (animation.type) {
                const animationName = `${animation.type}-${menuClass}`;
                animationCSS = `
            @keyframes ${animationName} {
                ${animation.type === 'fadeIn' ? `
                    from { opacity: 0; }
                    to { opacity: 1; }
                ` : animation.type === 'slideInLeft' ? `
                    from { transform: translateX(-100%); }
                    to { transform: translateX(0); }
                ` : animation.type === 'fadeInDown' ? `
                    from { opacity: 0; transform: translateY(-20px); }
                    to { opacity: 1; transform: translateY(0); }
                ` : animation.type === 'zoomIn' ? `
                    from { opacity: 0; transform: scale(0.8); }
                    to { opacity: 1; transform: scale(1); }
                ` : ''}
            }
            .${menuClass} {
                animation: ${animationName} ${animation.duration || 600}ms ease-out ${animation.delay || 0}ms;
            }
        `;
            }

            const handleMenuClick = (e, item) => {
                e.stopPropagation();
                if (isCanvas) {
                    if (typeof onSelectChild === 'function' && parentId && childId) {
                        onSelectChild(parentId, childId);
                    }
                    toast.info(`Mô phỏng: Điều hướng đến ${item.url || '#'}`, {
                        position: 'bottom-right',
                        autoClose: 2000,
                    });
                } else {
                    if (item.url && item.url !== '#') {
                        window.location.href = item.url;
                    }
                }
            };

            const renderMenuItem = (item, index, level = 0) => {
                const hasSubmenu = item.submenu && item.submenu.length > 0;
                const hasMegaMenu = item.megaMenu && item.megaMenu.columns;

                return (
                    <div
                        key={index}
                        style={{
                            position: level === 0 ? 'relative' : 'static',
                        }}
                    >
                        <a
                            href={item.url || '#'}
                            style={{
                                display: 'flex',
                                alignItems: 'center',
                                gap: '8px',
                                color: styles.color || '#1f2937',
                                textDecoration: 'none',
                                fontSize: styles.fontSize || '14px',
                                fontWeight: level === 0 ? '500' : '400',
                                padding: level === 0 ? '8px 12px' : '6px 10px',
                                borderRadius: '6px',
                                transition: 'all 0.2s ease',
                                cursor: 'pointer',
                            }}
                            onClick={(e) => {
                                if (!hasSubmenu && !hasMegaMenu) {
                                    handleMenuClick(e, item);
                                } else {
                                    e.preventDefault();
                                }
                            }}
                            onMouseEnter={(e) => {
                                if (events.onHover?.type === 'underline') {
                                    e.currentTarget.style.textDecoration = 'underline';
                                }
                                e.currentTarget.style.backgroundColor = 'rgba(0, 0, 0, 0.05)';
                            }}
                            onMouseLeave={(e) => {
                                if (events.onHover?.type === 'underline') {
                                    e.currentTarget.style.textDecoration = 'none';
                                }
                                e.currentTarget.style.backgroundColor = 'transparent';
                            }}
                        >
                            {item.icon && <i className={`lucide lucide-${item.icon}`} style={{ width: '16px', height: '16px' }}></i>}
                            <span>{item.label}</span>
                            {(hasSubmenu || hasMegaMenu) && (
                                <i className="fas fa-chevron-down" style={{ fontSize: '10px', marginLeft: '4px' }}></i>
                            )}
                        </a>

                        {/* Submenu dropdown */}
                        {hasSubmenu && (
                            <div
                                style={{
                                    position: 'absolute',
                                    top: level === 0 ? '100%' : '0',
                                    left: level === 0 ? '0' : '100%',
                                    background: '#ffffff',
                                    boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
                                    borderRadius: '8px',
                                    padding: '8px',
                                    minWidth: '200px',
                                    opacity: 0,
                                    visibility: 'hidden',
                                    transition: 'all 0.2s ease',
                                    zIndex: 1000,
                                }}
                                className={`submenu-${index}`}
                            >
                                {item.submenu.map((subItem, subIndex) => renderMenuItem(subItem, subIndex, level + 1))}
                            </div>
                        )}

                        {/* Mega menu */}
                        {hasMegaMenu && (
                            <div
                                style={{
                                    position: 'absolute',
                                    top: '100%',
                                    left: '0',
                                    background: '#ffffff',
                                    boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
                                    borderRadius: '8px',
                                    padding: '20px',
                                    minWidth: '400px',
                                    opacity: 0,
                                    visibility: 'hidden',
                                    transition: 'all 0.2s ease',
                                    zIndex: 1000,
                                }}
                                className={`megamenu-${index}`}
                            >
                                <div
                                    style={{
                                        display: 'grid',
                                        gridTemplateColumns: `repeat(${item.megaMenu.columns.length}, 1fr)`,
                                        gap: '20px',
                                    }}
                                >
                                    {item.megaMenu.columns.map((column, colIndex) => (
                                        <div key={colIndex}>
                                            {column.title && (
                                                <h4
                                                    style={{
                                                        fontSize: '14px',
                                                        fontWeight: '600',
                                                        marginBottom: '10px',
                                                        color: '#1f2937',
                                                    }}
                                                >
                                                    {column.title}
                                                </h4>
                                            )}
                                            {column.items.map((colItem, itemIndex) => (
                                                <a
                                                    key={itemIndex}
                                                    href={colItem.url || '#'}
                                                    style={{
                                                        display: 'block',
                                                        padding: '6px 0',
                                                        color: '#4b5563',
                                                        textDecoration: 'none',
                                                        fontSize: '13px',
                                                        transition: 'color 0.2s ease',
                                                    }}
                                                    onClick={(e) => handleMenuClick(e, colItem)}
                                                    onMouseEnter={(e) => {
                                                        e.currentTarget.style.color = '#2563eb';
                                                    }}
                                                    onMouseLeave={(e) => {
                                                        e.currentTarget.style.color = '#4b5563';
                                                    }}
                                                >
                                                    {colItem.label}
                                                </a>
                                            ))}
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}

                        <style>{`
                    .menu-item-${index}:hover .submenu-${index},
                    .menu-item-${index}:hover .megamenu-${index} {
                        opacity: 1 !important;
                        visibility: visible !important;
                    }
                `}</style>
                    </div>
                );
            };

            return (
                <>
                    {animationCSS && <style>{animationCSS}</style>}
                    <nav
                        className={`lpb-menu ${menuClass}`}
                        style={{
                            ...styles,
                        }}
                        onClick={(e) => {
                            e.stopPropagation();
                            if (isCanvas && typeof onSelectChild === 'function' && parentId && childId) {
                                onSelectChild(parentId, childId);
                            }
                        }}
                    >
                        {/* Chỉ render title nếu không có heading trong children */}
                        {title && !children.some((child) => child?.type === 'heading') && (
                            <h3
                                style={{
                                    fontSize: componentData.titleFontSize || '1rem',
                                    margin: componentData.titleMargin || '0 0 10px 0',
                                    color: componentData.titleColor || '#1f2937',
                                    fontWeight: componentData.titleFontWeight || '600',
                                    ...getCleanTextStyles(styles),
                                }}
                            >
                                {title}
                            </h3>
                        )}
                        <div
                            style={{
                                display: styles.display || 'flex',
                                flexDirection: styles.flexDirection || 'row',
                                gap: styles.gap || '10px',
                                alignItems: 'center',
                            }}
                        >
                            {items.map((item, index) => (
                                <div key={index} className={`menu-item-${index}`}>
                                    {renderMenuItem(item, index)}
                                </div>
                            ))}
                        </div>
                        {/* Render children nếu có */}
                        {Array.isArray(children) &&
                            children.length > 0 &&
                            children.map((child, index) => (
                                <div key={child.id || index} style={{ marginBottom: child.marginBottom || '10px' }}>
                                    {renderComponentContent(
                                        child.type,
                                        child.componentData || {},
                                        child.styles || {},
                                        child.children || [],
                                        isCanvas,
                                        onSelectChild,
                                        parentId,
                                        child.id
                                    )}
                                </div>
                            ))}
                    </nav>
                </>
            );
        }
        default:
            return (
                <div
                    style={{
                        color: componentData.errorColor || '#ff0000',
                        padding: '10px',
                        border: '1px dashed #ff0000',
                        borderRadius: '4px',
                        ...styles,
                    }}
                >
                    Unknown Component: {type}
                </div>
            );
    }
};
