import React, { useState } from 'react';
import { useDrag } from 'react-dnd';
import COMPONENT_LIBRARY from "../../constants/componentLibrary";

const ItemTypes = {
    COMPONENT: 'component',
};

const ComponentLibrary = ({ isCollapsed, onToggle }) => {
    const [activeCategory, setActiveCategory] = useState('text');

    const categories = [
        { id: 'text', name: 'Văn bản', icon: 'fas fa-font' },
        { id: 'buttons', name: 'Nút bấm', icon: 'fas fa-mouse-pointer' },
        { id: 'sections', name: 'Section', icon: 'fas fa-layer-group' },
        { id: 'media', name: 'Media', icon: 'fas fa-image' },
        { id: 'forms', name: 'Form', icon: 'fas fa-wpforms' },
    ];

    if (isCollapsed) {
        return (
            <div className="w-12 bg-white border-r border-gray-200 h-full flex flex-col items-center py-2">
                <button onClick={onToggle} className="p-2 hover:bg-gray-100 rounded mb-4">
                    <i className="fas fa-chevron-right"></i>
                </button>
                {categories.map((cat) => (
                    <button key={cat.id} className="p-2 hover:bg-gray-100 rounded mb-1" title={cat.name}>
                        <i className={cat.icon}></i>
                    </button>
                ))}
            </div>
        );
    }

    const DraggableComponent = ({ component }) => {
        const [{ isDragging }, drag] = useDrag(() => ({
            type: ItemTypes.COMPONENT,
            item: { componentData: component },
            collect: (monitor) => ({
                isDragging: !!monitor.isDragging(),
            }),
        }));

        return (
            <div
                ref={drag}
                className="p-3 mb-2 border border-gray-200 rounded-lg cursor-move hover:border-blue-300 hover:shadow-sm transition-all"
                style={{ opacity: isDragging ? 0.5 : 1 }}
            >
                <div className="flex items-center gap-3">
                    <i className={`${component.icon} text-gray-500`}></i>
                    <div>
                        <div className="font-medium text-sm text-gray-800">{component.name}</div>
                        <div className="text-xs text-gray-500">Kéo để thêm</div>
                    </div>
                </div>
            </div>
        );
    };

    return (
        <div className="w-64 bg-white border-r border-gray-200 h-full overflow-y-auto">
            <div className="p-4 border-b flex items-center justify-between">
                <h2 className="font-semibold text-gray-800">Phần tử</h2>
                <button onClick={onToggle} className="p-1 hover:bg-gray-100 rounded">
                    <i className="fas fa-chevron-left"></i>
                </button>
            </div>

            <div className="border-b">
                {categories.map((cat) => (
                    <button
                        key={cat.id}
                        onClick={() => setActiveCategory(cat.id)}
                        className={`w-full p-3 text-left flex items-center gap-2 hover:bg-gray-50 ${
                            activeCategory === cat.id ? 'bg-blue-50 border-r-2 border-blue-500 text-blue-600' : 'text-gray-600'
                        }`}
                    >
                        <i className={cat.icon}></i>
                        <span className="text-sm font-medium">{cat.name}</span>
                    </button>
                ))}
            </div>

            <div className="p-2">
                {COMPONENT_LIBRARY[activeCategory]?.map((component) => (
                    <DraggableComponent key={component.id} component={component} />
                ))}
            </div>
        </div>
    );
};

export default ComponentLibrary;
