export const CSS_VARIABLES = {
    '--primary': '#6366f1',
    '--primary-dark': '#4f46e5',
    '--primary-light': '#a5b4fc',
    '--secondary': '#f1f5f9',
    '--accent': '#06b6d4',
    '--success': '#10b981',
    '--warning': '#f59e0b',
    '--danger': '#ef4444',
    '--dark': '#1e293b',
    '--light': '#f8fafc',
    '--white': '#ffffff',
    '--text-primary': '#334155',
    '--text-secondary': '#64748b',
    '--text-light': '#94a3b8',
    '--text-inverse': '#ffffff',
    '--border-color': '#e2e8f0',
    '--border-radius': '8px',
    '--border-radius-lg': '12px',
    '--border-radius-xl': '16px',
    '--shadow-sm': '0 1px 2px 0 rgba(0, 0, 0, 0.05)',
    '--shadow': '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
    '--shadow-lg': '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
    '--shadow-xl': '0 20px 25px -5px rgba(0, 0, 0, 0.1)',
    '--canvas-desktop': '1200px',
};

export const renderStaticHTML = (pageData) => {
    console.log('Rendering static HTML with pageData:', JSON.stringify(pageData, null, 2));
    let html = `
<!DOCTYPE html>
<html lang="vi">
    <head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Landing Page</title>
<style>
    :root {
    --primary: ${CSS_VARIABLES['--primary']};
    --primary-dark: ${CSS_VARIABLES['--primary-dark']};
    --primary-light: ${CSS_VARIABLES['--primary-light']};
    --secondary: ${CSS_VARIABLES['--secondary']};
    --accent: ${CSS_VARIABLES['--accent']};
    --success: ${CSS_VARIABLES['--success']};
    --warning: ${CSS_VARIABLES['--warning']};
    --danger: ${CSS_VARIABLES['--danger']};
    --dark: ${CSS_VARIABLES['--dark']};
    --light: ${CSS_VARIABLES['--light']};
    --white: ${CSS_VARIABLES['--white']};
    --text-primary: ${CSS_VARIABLES['--text-primary']};
    --text-secondary: ${CSS_VARIABLES['--text-secondary']};
    --text-light: ${CSS_VARIABLES['--text-light']};
    --text-inverse: ${CSS_VARIABLES['--text-inverse']};
    --border-color: ${CSS_VARIABLES['--border-color']};
    --border-radius: ${CSS_VARIABLES['--border-radius']};
    --shadow: ${CSS_VARIABLES['--shadow']};
}
    body {
    font-family: 'Inter', Arial, sans-serif;
    margin: 0;
    padding: 0;
    background: ${pageData.canvas.background};
}
    .page-container {
    position: relative;
    width: ${pageData.canvas.width || '1200px'};
    min-height: 100vh;
    margin: 0 auto;
    background: ${pageData.canvas.background};
}

    .ladi-section {
    width: 420px;
    min-width: 420px;
    margin: 0 auto;
    position: relative;
    overflow: hidden;
}

    .ladi-section-background {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 0;
}

    .ladi-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 1;
    pointer-events: none;
}

    .ladi-container {
    position: relative;
    z-index: 2;
    padding: 20px;
    min-height: 100%;
    box-sizing: border-box;
}

    @media (max-width: 768px) {
    .ladi-section {
    width: 100%;
    min-width: auto;
}
}
</style>
</head>
<body>
<div class="page-container">
    `;

    pageData.elements.forEach((element) => {
        const { id, type, componentData = {}, position = {}, size = {}, styles = {}, children = [] } = element;
        const positionStyles = position.desktop ? `position: absolute; left: ${position.desktop.x}px; top: ${position.desktop.y}px;` : '';
        const sizeStyles = size ? `width: ${size.width}px; height: ${size.height}px;` : '';
        const cssStyles = styles ? Object.entries(styles).map(([key, value]) => `${key}: ${value};`).join(' ') : '';

        if (type === 'section' && componentData.structure === 'ladi-standard') {
            html += `
                <div id="${id}" class="ladi-section" style="${positionStyles} ${sizeStyles} ${cssStyles}" data-type="${type}">
                    <div class="ladi-section-background" style="background-color: ${componentData.backgroundColor || 'transparent'}; background-image: ${componentData.backgroundImage || 'none'}; background-size: cover; background-position: center;"></div>
                    <div class="ladi-overlay" style="background-color: ${componentData.overlayColor || 'transparent'}; opacity: ${componentData.overlayOpacity || 0};"></div>
                    <div class="ladi-container" style="padding: ${componentData.padding || '20px'};">
            `;

            children.forEach((child) => {
                const childPosition = child.position?.desktop ? `position: absolute; left: ${child.position.desktop.x}px; top: ${child.position.desktop.y}px;` : '';
                const childSize = child.size ? `width: ${child.size.width}px; height: ${child.size.height}px;` : '';
                const childStyles = child.styles ? Object.entries(child.styles).map(([key, value]) => `${key}: ${value};`).join(' ') : '';
                if (child.type === 'heading') {
                    html += `<h2 class="lpb-child-element" style="${childPosition} ${childSize} ${childStyles}">${child.componentData?.content || 'Tiêu đề'}</h2>`;
                } else if (child.type === 'paragraph') {
                    html += `<p class="lpb-child-element" style="${childPosition} ${childSize} ${childStyles}">${child.componentData?.content || 'Đoạn văn'}</p>`;
                } else if (child.type === 'button') {
                    html += `<button class="lpb-child-element" style="${childPosition} ${childSize} ${childStyles}">${child.componentData?.content || 'Nút'}</button>`;
                } else if (child.type === 'image') {
                    html += `<img class="lpb-child-element" src="${child.componentData?.src || ''}" style="${childPosition} ${childSize} ${childStyles}" />`;
                }
            });

            html += `</div></div>`;
            return;
        }

        let elementHtml = `<div id="${id}" class="lpb-canvas-element" style="${positionStyles} ${sizeStyles} ${cssStyles}" data-type="${type}">`;

        if (type === 'container' || type === 'group') {
            children.forEach((child) => {
                const childPosition = child.position?.desktop ? `position: absolute; left: ${child.position.desktop.x}px; top: ${child.position.desktop.y}px;` : '';
                const childSize = child.size ? `width: ${child.size.width}px; height: ${child.size.height}px;` : '';
                const childStyles = child.styles ? Object.entries(child.styles).map(([key, value]) => `${key}: ${value};`).join(' ') : '';
                if (child.type === 'heading') {
                    elementHtml += `<h2 class="lpb-child-element" style="${childPosition} ${childSize} ${childStyles}">${child.componentData?.content || 'Tiêu đề'}</h2>`;
                } else if (child.type === 'paragraph') {
                    elementHtml += `<p class="lpb-child-element" style="${childPosition} ${childSize} ${childStyles}">${child.componentData?.content || 'Đoạn văn'}</p>`;
                } else if (child.type === 'button') {
                    elementHtml += `<button class="lpb-child-element" style="${childPosition} ${childSize} ${childStyles}">${child.componentData?.content || 'Nút'}</button>`;
                } else if (child.type === 'image') {
                    elementHtml += `<img class="lpb-child-element" src="${child.componentData?.src || ''}" style="${childPosition} ${childSize} ${childStyles}" />`;
                }
            });
        } else if (type === 'heading') {
            elementHtml += `<h2 class="lpb-child-element" style="${cssStyles}">${componentData?.content || 'Tiêu đề'}</h2>`;
        } else if (type === 'paragraph') {
            elementHtml += `<p class="lpb-child-element" style="${cssStyles}">${componentData?.content || 'Đoạn văn'}</p>`;
        } else if (type === 'button') {
            elementHtml += `<button class="lpb-child-element" style="${cssStyles}">${componentData?.content || 'Nút'}</button>`;
        } else if (type === 'image') {
            elementHtml += `<img class="lpb-child-element" src="${componentData?.src || ''}" style="${cssStyles}" />`;
        } else if (type === 'form') {
            elementHtml += `<form style="${cssStyles}">`;
            if (componentData?.title) {
                elementHtml += `<h3 style="margin: 0 0 15px 0; font-size: 1.2rem;">${componentData.title}</h3>`;
            }
            if (componentData?.fields) {
                componentData.fields.forEach(field => {
                    elementHtml += `<input type="text" placeholder="${field}" style="padding: 8px; border-radius: 8px; border: 1px solid #ccc; margin-bottom: 10px; width: 100%; box-sizing: border-box;" />`;
                });
            } else {
                elementHtml += `<input type="text" placeholder="${componentData?.placeholder || 'Input'}" style="padding: 8px; border-radius: 8px; border: 1px solid #ccc; margin-bottom: 10px; width: 100%; box-sizing: border-box;" />`;
            }
            elementHtml += `<button type="submit" style="background: #2563eb; color: #fff; padding: 8px 16px; border-radius: 8px; border: none; cursor: pointer;">${componentData?.buttonText || 'Submit'}</button>`;
            elementHtml += `</form>`;
        }
        elementHtml += `</div>`;
        html += elementHtml;
    });

    html += `
</div>
</body>
</html>
`;
    return html;
};
