import React, { useState, useEffect, useContext } from "react"
import { UserContext } from "../context/UserContext"
import Header from "../components/Header"
import Sidebar from "../components/Sidebar"
import { jwtDecode } from "jwt-decode"
import { useNavigate, useParams } from "react-router-dom"
import axios from "axios"
import AOS from "aos"
import "aos/dist/aos.css"
import "../styles/MarketplaceDetail.css"
import DogLoader from "../components/Loader"
import { toast } from "react-toastify"
import {
    FiEye, FiShoppingCart, FiStar, FiUser, FiHeart, FiExternalLink,
    FiDownload, FiCheckCircle, FiShield, FiZap, FiSmartphone,
    FiSliders, FiFileText, FiPackage, FiTag, FiCreditCard, FiLayers,
    FiXCircle, FiRefreshCw
} from "react-icons/fi"

const MarketplaceDetail = () => {
    const { user } = useContext(UserContext)
    const { id } = useParams()
    const navigate = useNavigate()
    const API_BASE_URL = process.env.REACT_APP_API_URL || "http://localhost:5000"

    // ----- STATE -----
    const [loading, setLoading] = useState(true)
    const [userRole, setUserRole] = useState(null)
    const [page, setPage] = useState(null)
    const [order, setOrder] = useState(null)
    const [error, setError] = useState("")
    const [purchasing, setPurchasing] = useState(false)
    const [selectedPaymentMethod, setSelectedPaymentMethod] = useState("SANDBOX")
    const [isLiked, setIsLiked] = useState(false)
    const [currentImageIndex, setCurrentImageIndex] = useState(0)
    const [hasPurchased, setHasPurchased] = useState(false)
    const [isSeller, setIsSeller] = useState(false)
    const [downloading, setDownloading] = useState(false)
    const [showOrderModal, setShowOrderModal] = useState(false)
    const [refunding, setRefunding] = useState(false)
    const [rating, setRating] = useState(0)
    const [comment, setComment] = useState("")

    // ----- CONST -----
    const paymentMethods = [
        { value: "SANDBOX", label: "Sandbox (Test)", description: "Môi trường test thanh toán an toàn." },
        { value: "MOMO", label: "Ví điện tử MOMO", description: "Thanh toán nhanh chóng qua ví MOMO." },
        { value: "VNPAY", label: "Cổng thanh toán VNPay", description: "Hỗ trợ nhiều ngân hàng và thẻ quốc tế." },
    ]

    // ----- EFFECT -----
    useEffect(() => {
        const initAuth = async () => {
            const token = localStorage.getItem("token")
            if (!token) { navigate("/auth"); setLoading(false); return }
            try {
                const decoded = jwtDecode(token)
                setUserRole(decoded.role)
            } catch { navigate("/auth") }
        }
        if (user?.role) setUserRole(user.role)
        else initAuth()
    }, [user, navigate])

    useEffect(() => {
        AOS.init({ duration: 600, once: true })
        if (id) loadPageDetail()
    }, [id])

    // ----- API -----
    const loadPageDetail = async () => {
        try {
            setLoading(true);
            const token = localStorage.getItem("token");
            const res = await axios.get(
                `${API_BASE_URL}/api/marketplace/${id}/detail`,
                { headers: token ? { Authorization: `Bearer ${token}` } : {} }
            );

            // 1. Xem thực tế
            console.log("Server trả về", res);

            // 2. Lấy payload
            const payload = res.data?.data || res.data;
            const p = payload?.page || payload;

            if (!p || !p._id) throw new Error("Không có dữ liệu page");

            setPage(p);
            setOrder(res.data.order || null);
            setIsSeller(!!res.data.isSeller);
            setIsLiked(!!res.data.liked);
            setHasPurchased(!!res.data.order);
        } catch (err) {
            console.error(err);
            setError(err.response?.data?.message || err.message || "Lỗi tải chi tiết");
        } finally {
            setLoading(false);
        }
    };

    // ----- ACTION -----
    const handlePurchase = async () => {
        try {
            setPurchasing(true)
            const token = localStorage.getItem("token")
            if (!token) { toast.warn("Vui lòng đăng nhập"); navigate("/auth"); return }
            const { data: res } = await axios.post(
                `${API_BASE_URL}/api/payment/create-transaction`,
                { marketplace_page_id: id, payment_method: selectedPaymentMethod },
                { headers: { Authorization: `Bearer ${token}` } }
            )
            if (res.success) {
                const { payment_url, transaction_id } = res.data
                selectedPaymentMethod === "SANDBOX"
                    ? navigate(`/payment/sandbox?transaction_id=${transaction_id}`)
                    : window.location.href = payment_url
            } else toast.error(res.message)
        } catch (err) {
            toast.error(err.response?.data?.message || "Không thể tạo giao dịch")
        } finally {
            setPurchasing(false)
        }
    }

    const handleLike = async () => {
        try {
            const token = localStorage.getItem("token")
            if (!token) { toast.warn("Vui lòng đăng nhập"); navigate("/auth"); return }
            const { data: res } = await axios.post(
                `${API_BASE_URL}/api/marketplace/${id}/like`,
                {},
                { headers: { Authorization: `Bearer ${token}` } }
            )
            if (res.success) {
                setIsLiked(res.data.liked)
                setPage(p => ({ ...p, likes: res.data.likes }))
            }
        } catch {
            toast.error("Không thể cập nhật like")
        }
    }

    const handleDownload = async (format) => {
        try {
            setDownloading(true)
            const token = localStorage.getItem("token")
            const res = await axios.get(`${API_BASE_URL}/api/marketplace/${id}/download/${format}`, {
                headers: { Authorization: `Bearer ${token}` },
                responseType: "blob"
            })
            const url = window.URL.createObjectURL(new Blob([res.data]))
            const link = document.createElement("a")
            link.href = url
            link.download = `${page.title}.${format === "html" ? "zip" : "iuhpage"}`
            link.click()
            window.URL.revokeObjectURL(url)
            toast.success(`Đã tải xuống ${format.toUpperCase()}`)
        } catch {
            toast.error("Không thể tải xuống")
        } finally {
            setDownloading(false)
        }
    }

    const handleRequestRefund = async () => {
        if (!order) return
        const reason = prompt("Lý do hoàn tiền:")
        if (!reason) return
        try {
            setRefunding(true)
            await axios.patch(
                `${API_BASE_URL}/api/orders/${order._id}/refund`,
                { reason },
                { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
            )
            toast.success("Đã gửi yêu cầu hoàn tiền")
            loadPageDetail() // reload
        } catch (err) {
            toast.error(err.response?.data?.message || "Không thể gửi yêu cầu")
        } finally {
            setRefunding(false)
        }
    }

    const handleSubmitReview = async () => {
        if (!order || rating === 0) { toast.warn("Vui lòng chọn sao"); return }
        try {
            await axios.post(
                `${API_BASE_URL}/api/marketplace/${id}/reviews`,
                { rating, comment },
                { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
            )
            toast.success("Cảm ơn bạn đã đánh giá!")
            setRating(0)
            setComment("")
            loadPageDetail()
        } catch {
            toast.error("Không thể gửi đánh giá")
        }
    }

    // ----- RENDER -----
    if (loading) return <DogLoader />
    if (error) return (
        <div className="marketplace-detail-container">
            <Header /><Sidebar userRole={userRole} />
            <div className="marketplace-detail-main">
                <div className="error-container">
                    <h2>Lỗi</h2>
                    <p>{error}</p>
                    <button onClick={() => navigate("/marketplace")}>Quay lại</button>
                </div>
            </div>
        </div>
    )
    if (!page) return null

    const discount = page.original_price && page.original_price > page.price
        ? Math.round(((page.original_price - page.price) / page.original_price) * 100)
        : 0

    return (
        <div className="marketplace-detail-container">
            <Sidebar userRole={userRole} />
            <div className="marketplace-detail-main">
                <Header />
                <div className="marketplace-detail-content">
                    {/* Breadcrumb */}
                    <div className="breadcrumb">
                        <span onClick={() => navigate("/marketplace")}>Marketplace</span>
                        <span className="separator">/</span>
                        <span className="current">{page.title}</span>
                    </div>

                    <div className="detail-grid">
                        {/* Left: ảnh, mô tả */}
                        <div className="detail-left">
                            <img
                                src={(page.screenshots?.[currentImageIndex]) || page.main_screenshot || "/placeholder.png"}
                                alt={page.title}
                                className="main-image"
                            />
                            {page.screenshots?.length > 1 && (
                                <div className="thumbnail-gallery">
                                    {page.screenshots.map((s, i) => (
                                        <img
                                            key={i}
                                            src={s}
                                            alt={`thumb${i}`}
                                            className={i === currentImageIndex ? "active" : ""}
                                            onClick={() => setCurrentImageIndex(i)}
                                        />
                                    ))}
                                </div>
                            )}
                            <div className="info-section">
                                <h3><FiFileText /> Mô tả</h3>
                                <p>{page.description}</p>
                            </div>
                        </div>

                        {/* Right: thông tin, mua, tải */}
                        <div className="detail-right">
                            <div className="detail-header">
                                <div className="category-badge">{page.category}</div>
                                <h1>{page.title}</h1>
                                <div className="meta-info">
                                    <span><FiEye /> {page.views}</span>
                                    <span><FiShoppingCart /> {page.sold_count}</span>
                                    <span><FiStar /> {page.rating?.toFixed(1)}</span>
                                </div>
                                <div className="seller-info">
                                    <span><FiUser /> Bán bởi: <strong>{page.seller_id?.name || "Ẩn danh"}</strong></span>
                                </div>
                            </div>

                            {/* Giá */}
                            <div className="price-section">
                                <div className="current-price">
                                    {page.price === 0 ? "Miễn phí" : new Intl.NumberFormat("vi-VN", {
                                        style: "currency", currency: "VND"
                                    }).format(page.price)}
                                </div>
                                {page.original_price > page.price && (
                                    <>
                                        <div className="original-price">
                                            {new Intl.NumberFormat("vi-VN", {
                                                style: "currency", currency: "VND"
                                            }).format(page.original_price)}
                                        </div>
                                        <div className="discount-badge">-{discount}%</div>
                                    </>
                                )}
                            </div>

                            {/* Demo & Like */}
                            <div className="action-buttons-secondary">
                                {page.demo_url && (
                                    <a href={page.demo_url} target="_blank" rel="noopener noreferrer" className="demo-btn">
                                        <FiExternalLink /> Xem Demo
                                    </a>
                                )}
                                <button className={`like-btn ${isLiked ? "liked" : ""}`} onClick={handleLike}>
                                    <FiHeart style={{ fill: isLiked ? "#ff4d4f" : "none" }} /> {page.likes}
                                </button>
                            </div>

                            {/* Tags */}
                            {page.tags?.length > 0 && (
                                <div className="info-section">
                                    <h3><FiTag /> Tags</h3>
                                    <div className="tags">
                                        {page.tags.map(t => <span key={t} className="tag">{t}</span>)}
                                    </div>
                                </div>
                            )}

                            {/* DOWNLOAD / PURCHASE / ORDER INFO */}
                            {(hasPurchased || isSeller) && (
                                <div className="purchase-info-box">
                                    <h3><FiPackage /> Quyền của bạn</h3>
                                    {isSeller && <p>Bạn là người bán – có thể tải bản gốc.</p>}
                                    {hasPurchased && (
                                        <>
                                            <p>Đã mua ngày: {new Date(order.createdAt).toLocaleDateString("vi-VN")}</p>
                                            <button className="btn-secondary" onClick={() => setShowOrderModal(true)}>
                                                <FiFileText /> Xem đơn hàng
                                            </button>
                                            {order.status === "delivered" && (
                                                <button className="btn-danger" onClick={handleRequestRefund} disabled={refunding}>
                                                    <FiRefreshCw /> Yêu cầu hoàn tiền
                                                </button>
                                            )}
                                            <div className="download-section">
                                                <h4><FiDownload /> Tải xuống</h4>
                                                <button onClick={() => handleDownload("html")} disabled={downloading}>
                                                    Tải .ZIP
                                                </button>
                                                <button onClick={() => handleDownload("iuhpage")} disabled={downloading}>
                                                    Tải .iuhpage
                                                </button>
                                            </div>
                                        </>
                                    )}
                                </div>
                            )}

                            {/* FORM MUA */}
                            {!(hasPurchased || isSeller) && page.price > 0 && (
                                <div className="purchase-flow-section">
                                    <h3><FiCreditCard /> Thanh toán</h3>
                                    {paymentMethods.map(m => (
                                        <div
                                            key={m.value}
                                            className={`payment-method ${selectedPaymentMethod === m.value ? "selected" : ""}`}
                                            onClick={() => setSelectedPaymentMethod(m.value)}
                                        >
                                            <div className="method-label">{m.label}</div>
                                            <div className="method-desc">{m.description}</div>
                                            {selectedPaymentMethod === m.value && <FiCheckCircle />}
                                        </div>
                                    ))}
                                    <button className="purchase-btn" onClick={handlePurchase} disabled={purchasing}>
                                        <FiShoppingCart /> {purchasing ? "Đang xử lý..." : "Thanh toán ngay"}
                                    </button>
                                </div>
                            )}

                            {/* ĐÁNH GIÁ */}
                            {hasPurchased && order.status === "delivered" && (
                                <div className="review-section">
                                    <h3>Đánh giá của bạn</h3>
                                    <div className="stars">
                                        {[1, 2, 3, 4, 5].map(s => (
                                            <FiStar
                                                key={s}
                                                className={s <= rating ? "filled" : ""}
                                                onClick={() => setRating(s)}
                                            />
                                        ))}
                                    </div>
                                    <textarea
                                        placeholder="Nhận xét..."
                                        value={comment}
                                        onChange={e => setComment(e.target.value)}
                                        rows={3}
                                    />
                                    <button onClick={handleSubmitReview}>Gửi đánh giá</button>
                                </div>
                            )}
                        </div>
                    </div>

                    {/* POPUP ĐƠN HÀNG */}
                    {showOrderModal && order && (
                        <div className="modal-overlay" onClick={() => setShowOrderModal(false)}>
                            <div className="modal-content" onClick={e => e.stopPropagation()}>
                                <div className="modal-header">
                                    <h3>Chi tiết đơn hàng</h3>
                                    <span className="close" onClick={() => setShowOrderModal(false)}><FiXCircle /></span>
                                </div>
                                <div className="modal-body">
                                    <p><strong>Mã đơn:</strong> {order.orderId}</p>
                                    <p><strong>Ngày mua:</strong> {new Date(order.createdAt).toLocaleDateString("vi-VN")}</p>
                                    <p><strong>Trạng thái:</strong> <span className={`status ${order.status}`}>{order.status}</span></p>
                                    <p><strong>Tổng tiền:</strong> {new Intl.NumberFormat("vi-VN", {
                                        style: "currency", currency: "VND"
                                    }).format(order.price)}</p>
                                    {order.status === "delivered" && (
                                        <button className="btn-danger" onClick={handleRequestRefund} disabled={refunding}>
                                            <FiRefreshCw /> Yêu cầu hoàn tiền
                                        </button>
                                    )}
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    )
}

export default MarketplaceDetail
