import React, { useState, useEffect } from 'react';
import api from '@landinghub/api';

const UserDashboard = () => {
    const [pages, setPages] = useState([]);
    const [leads, setLeads] = useState([]);
    const [reports, setReports] = useState({});
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const pagesRes = await api.get('/api/pages');
                const leadsRes = await api.get('/api/leads');
                const reportsRes = await api.get('/api/reports');
                setPages(pagesRes.data);
                setLeads(leadsRes.data);
                setReports(reportsRes.data);
            } catch (err) {
                console.error('Error fetching user data:', err);
            } finally {
                setLoading(false);
            }
        };
        fetchData();
    }, []);

    if (loading) return <div>Đang tải...</div>;

    return (
        <div className="user-dashboard">
            <h1>User Dashboard</h1>
            <section>
                <h2>Tổng quan</h2>
                <p>Số Landing Page: {pages.length}</p>
                <p>Số Leads: {leads.length}</p>
                <p>View Count: {reports.view_count || 0}</p>
            </section>
        </div>
    );
};

export default React.memo(UserDashboard);
