import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    Filler,
} from 'chart.js';
import api from '@landinghub/api';
import {
    Box,
    Grid,
    Card,
    CardContent,
    Typography,
    CircularProgress,
    Alert,
    Paper,
    Divider,
    Avatar,
    List,
    ListItem,
    ListItemAvatar,
    ListItemText,
} from '@mui/material';
import {
    Web as WebIcon,
    Visibility as ViewsIcon,
    TrendingUp as ConversionIcon,
    MonetizationOn as RevenueIcon,
} from '@mui/icons-material';
import { styled } from '@mui/material/styles';

// Đăng ký các thành phần Chart.js
ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    Filler
);

// Styled components
const StyledCard = styled(Card)(({ theme }) => ({
    borderRadius: '12px',
    boxShadow: '0 4px 20px rgba(0, 0, 0, 0.1)',
    transition: 'transform 0.3s ease',
    '&:hover': {
        transform: 'translateY(-5px)',
    },
}));

const StatItem = styled(Box)(({ theme }) => ({
    display: 'flex',
    alignItems: 'center',
    gap: theme.spacing(2),
    padding: theme.spacing(2),
    background: theme.palette.background.paper,
    borderRadius: '8px',
}));

const UserDashboard = () => {
    const [overview, setOverview] = useState(null);
    const [chartData, setChartData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Lấy dữ liệu thống kê khi component mount
    useEffect(() => {
        const fetchData = async () => {
            try {
                setLoading(true);
                // Lấy dữ liệu tổng quan
                const overviewResponse = await api.get('/analytics/overview');
                setOverview(overviewResponse.data.overview);

                // Lấy dữ liệu biểu đồ
                const chartResponse = await api.get('/analytics/chart');
                setChartData(chartResponse.data.chart);
            } catch (err) {
                setError(err.response?.data?.error || 'Lỗi khi tải dữ liệu thống kê');
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    // Xử lý khi đang loading
    if (loading) {
        return (
            <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
                <CircularProgress />
            </Box>
        );
    }

    // Xử lý khi có lỗi
    if (error) {
        return (
            <Box m={4}>
                <Alert severity="error">{error}</Alert>
            </Box>
        );
    }

    return (
        <Box m={4}>
            {/* Header */}
            <Typography variant="h4" gutterBottom fontWeight="bold" color="primary">
                Dashboard Thống Kê
            </Typography>
            <Typography variant="subtitle1" color="textSecondary" gutterBottom>
                Tổng quan hiệu suất các Landing Page của bạn
            </Typography>

            <Divider sx={{ my: 3 }} />

            {/* Thống kê tổng quan */}
            <Grid container spacing={3}>
                <Grid item xs={12} sm={6} md={3}>
                    <StyledCard>
                        <CardContent>
                            <StatItem>
                                <WebIcon color="primary" fontSize="large" />
                                <Box>
                                    <Typography variant="h6" color="textSecondary">
                                        Tổng số trang
                                    </Typography>
                                    <Typography variant="h4" fontWeight="bold">
                                        {overview?.totalPages || 0}
                                    </Typography>
                                    <Typography variant="body2" color="textSecondary">
                                        ({overview?.publishedPages || 0} đã xuất bản)
                                    </Typography>
                                </Box>
                            </StatItem>
                        </CardContent>
                    </StyledCard>
                </Grid>

                <Grid item xs={12} sm={6} md={3}>
                    <StyledCard>
                        <CardContent>
                            <StatItem>
                                <ViewsIcon color="primary" fontSize="large" />
                                <Box>
                                    <Typography variant="h6" color="textSecondary">
                                        Lượt xem
                                    </Typography>
                                    <Typography variant="h4" fontWeight="bold">
                                        {overview?.totalViews || 0}
                                    </Typography>
                                </Box>
                            </StatItem>
                        </CardContent>
                    </StyledCard>
                </Grid>

                <Grid item xs={12} sm={6} md={3}>
                    <StyledCard>
                        <CardContent>
                            <StatItem>
                                <ConversionIcon color="primary" fontSize="large" />
                                <Box>
                                    <Typography variant="h6" color="textSecondary">
                                        Chuyển đổi
                                    </Typography>
                                    <Typography variant="h4" fontWeight="bold">
                                        {overview?.totalConversions || 0}
                                    </Typography>
                                    <Typography variant="body2" color="textSecondary">
                                        ({overview?.conversionRate || '0%'})
                                    </Typography>
                                </Box>
                            </StatItem>
                        </CardContent>
                    </StyledCard>
                </Grid>

                <Grid item xs={12} sm={6} md={3}>
                    <StyledCard>
                        <CardContent>
                            <StatItem>
                                <RevenueIcon color="primary" fontSize="large" />
                                <Box>
                                    <Typography variant="h6" color="textSecondary">
                                        Doanh thu
                                    </Typography>
                                    <Typography variant="h4" fontWeight="bold">
                                        {overview?.totalRevenue || '0đ'}
                                    </Typography>
                                </Box>
                            </StatItem>
                        </CardContent>
                    </StyledCard>
                </Grid>
            </Grid>

            {/* Biểu đồ hiệu suất */}
            <Box mt={4}>
                <StyledCard>
                    <CardContent>
                        <Typography variant="h6" gutterBottom fontWeight="bold">
                            Biểu đồ hiệu suất
                        </Typography>
                        {chartData && (
                            <Box sx={{ height: '400px' }}>
                                <Line data={chartData.data} options={chartData.options} />
                            </Box>
                        )}
                    </CardContent>
                </StyledCard>
            </Box>

            {/* Top Pages */}
            <Box mt={4}>
                <StyledCard>
                    <CardContent>
                        <Typography variant="h6" gutterBottom fontWeight="bold">
                            Top Landing Pages
                        </Typography>
                        <List>
                            {overview?.topPagesByViews?.map((page) => (
                                <ListItem key={page.id} sx={{ py: 2 }}>
                                    <ListItemAvatar>
                                        <Avatar
                                            src={page.screenshot_url}
                                            alt={page.name}
                                            variant="rounded"
                                            sx={{ width: 60, height: 60, mr: 2 }}
                                        />
                                    </ListItemAvatar>
                                    <ListItemText
                                        primary={
                                            <Typography variant="subtitle1" fontWeight="bold">
                                                {page.name}
                                            </Typography>
                                        }
                                        secondary={
                                            <Box>
                                                <Typography variant="body2" color="textSecondary">
                                                    Lượt xem: {page.views}
                                                </Typography>
                                                <Typography variant="body2" color="textSecondary">
                                                    Chuyển đổi: {page.conversions} ({page.conversionRate}%)
                                                </Typography>
                                                <Typography variant="body2" color="textSecondary">
                                                    Doanh thu: {page.revenue}
                                                </Typography>
                                            </Box>
                                        }
                                    />
                                </ListItem>
                            ))}
                            {(!overview?.topPagesByViews || overview.topPagesByViews.length === 0) && (
                                <Typography color="textSecondary">Chưa có dữ liệu trang nổi bật.</Typography>
                            )}
                        </List>
                    </CardContent>
                </StyledCard>
            </Box>
        </Box>
    );
};

export default React.memo(UserDashboard);
