import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import api from "@landinghub/api";
import DogLoader from "../components/DogLoader";
import "grapesjs/dist/css/grapes.min.css";
import grapesjs from "grapesjs";
import '../styles/CreateLanding.css'
const CreateLandingPage = () => {
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [pageDescription, setPageDescription] = useState("");
    const [aiGeneratedContent, setAiGeneratedContent] = useState("");
    const [showConfirmAI, setShowConfirmAI] = useState(false);
    const editorRef = useRef(null);
    const grapesRef = useRef(null);
    const navigate = useNavigate();

    useEffect(() => {
        // check token
        const token = localStorage.getItem("token");
        if (!token) {
            navigate("/auth");
            return;
        }
        try {
            const decoded = jwtDecode(token);
            if (!decoded.userId) throw new Error("Invalid token");
            api.defaults.headers.common["Authorization"] = `Bearer ${token}`;
        } catch (err) {
            navigate("/auth");
        }
    }, [navigate]);

    useEffect(() => {
        if (!grapesRef.current) {
            grapesRef.current = grapesjs.init({
                container: editorRef.current,
                height: "100%",
                fromElement: false,
                storageManager: false,
                plugins: ["gjs-preset-webpage"],
            });
        }
    }, []);

    const handleCreatePage = async () => {
        if (!pageDescription) {
            setError("Vui lòng nhập mô tả landing page");
            return;
        }
        try {
            setLoading(true);
            const htmlContent = grapesRef.current.getHtml();
            const cssContent = grapesRef.current.getCss();

            // gửi dữ liệu + description cho AI
            const response = await api.post("/api/pages", {
                description: pageDescription,
                html: htmlContent,
                css: cssContent,
            });

            setAiGeneratedContent(response.data.html || "");
            setShowConfirmAI(true);
        } catch (err) {
            setError(
                "Lỗi khi tạo landing page: " + (err.response?.data?.msg || err.message)
            );
        } finally {
            setLoading(false);
        }
    };

    const handleConfirmAI = () => {
        navigate(`/pages/edit/${Date.now()}`, {
            state: {
                html: aiGeneratedContent,
                isNew: true,
            },
        });
    };

    if (loading) return <DogLoader />;

    
};

export default CreateLandingPage;
