import React from 'react';

const PropertiesPanel = ({ selectedElement, onUpdateElement, isCollapsed, onToggle }) => {
    if (isCollapsed) {
        return (
            <div className="w-12 bg-white border-l border-gray-200 h-full flex flex-col items-center py-2 shadow-sm">
                <button onClick={onToggle} className="p-2 hover:bg-gray-100 rounded">
                    <i className="fas fa-chevron-left"></i>
                </button>
            </div>
        );
    }

    if (!selectedElement) {
        return (
            <div className="w-80 bg-white border-l border-gray-200 flex flex-col shadow-lg">
                <div className="flex items-center justify-between border-b px-4 py-3 bg-gray-50">
                    <h3 className="font-semibold text-gray-800 text-sm uppercase tracking-wide">
                        Thuộc tính
                    </h3>
                    <button onClick={onToggle} className="p-1 hover:bg-gray-200 rounded">
                        <i className="fas fa-chevron-right"></i>
                    </button>
                </div>
                <div className="flex-1 flex flex-col items-center justify-center text-gray-500 p-6">
                    <i className="fas fa-mouse-pointer text-4xl mb-3 opacity-70"></i>
                    <p className="text-sm text-center leading-relaxed">
                        Chọn một element để chỉnh sửa thuộc tính
                    </p>
                </div>
            </div>
        );
    }

    const handleStyleChange = (property, value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                styles: {
                    ...selectedElement.json.styles,
                    [property]: value,
                },
            },
        };
        onUpdateElement(updated);
    };

    const handleContentChange = (value) => {
        const updated = {
            ...selectedElement,
            json: {
                ...selectedElement.json,
                content: value,
            },
        };
        onUpdateElement(updated);
    };

    const isSection = selectedElement.json.type === 'section';

    return (
        <div className="w-80 bg-white border-l border-gray-200 h-full flex flex-col shadow-lg">
            <div className="flex items-center justify-between border-b px-4 py-3 bg-gray-50">
                <h3 className="font-semibold text-gray-800 text-sm uppercase tracking-wide">
                    Thuộc tính {isSection ? 'Section' : 'Element'}
                </h3>
                <button onClick={onToggle} className="p-1 hover:bg-gray-200 rounded">
                    <i className="fas fa-chevron-right"></i>
                </button>
            </div>
            <div className="flex-1 overflow-y-auto p-4 space-y-6">
                {/* Kích thước Section */}
                {isSection && (
                    <div className="bg-gray-50 rounded-lg p-3 border">
                        <h4 className="text-xs font-semibold text-gray-700 mb-3 uppercase tracking-wide">
                            Kích thước
                        </h4>
                        <div className="space-y-3">
                            <div>
                                <label className="block text-xs text-gray-500 mb-1">Chiều rộng</label>
                                <input
                                    type="text"
                                    value={selectedElement.json.styles?.width || '1200px'}
                                    onChange={(e) => handleStyleChange('width', e.target.value)}
                                    className="w-full p-2 border border-gray-300 rounded-md text-sm focus:ring-2 focus:ring-blue-500"
                                    placeholder="e.g., 1200px, 100%"
                                />
                            </div>
                            <div>
                                <label className="block text-xs text-gray-500 mb-1">Chiều cao</label>
                                <input
                                    type="text"
                                    value={selectedElement.json.styles?.height || 'auto'}
                                    onChange={(e) => handleStyleChange('height', e.target.value)}
                                    className="w-full p-2 border border-gray-300 rounded-md text-sm focus:ring-2 focus:ring-blue-500"
                                    placeholder="e.g., auto, 400px"
                                />
                            </div>
                        </div>
                    </div>
                )}

                {/* Content Section */}
                {(selectedElement.json.type === 'heading' ||
                    selectedElement.json.type === 'paragraph' ||
                    selectedElement.json.type === 'button') && (
                    <div className="bg-gray-50 rounded-lg p-3 border">
                        <label className="block text-xs font-medium text-gray-600 mb-2 uppercase tracking-wide">
                            Nội dung
                        </label>
                        <textarea
                            value={selectedElement.json.content}
                            onChange={(e) => handleContentChange(e.target.value)}
                            className="w-full p-2 border border-gray-300 rounded-md resize-none text-sm focus:ring-2 focus:ring-blue-500"
                            rows={3}
                        />
                    </div>
                )}

                {/* Typography Section */}
                {!isSection && (
                    <div className="bg-gray-50 rounded-lg p-3 border">
                        <h4 className="text-xs font-semibold text-gray-700 mb-3 uppercase tracking-wide">
                            Typography
                        </h4>
                        <div className="space-y-3">
                            <div>
                                <label className="block text-xs text-gray-500 mb-1">Font Size</label>
                                <input
                                    type="text"
                                    value={selectedElement.json.styles?.fontSize || '16px'}
                                    onChange={(e) => handleStyleChange('fontSize', e.target.value)}
                                    className="w-full p-2 border border-gray-300 rounded-md text-sm focus:ring-2 focus:ring-blue-500"
                                />
                            </div>
                            <div>
                                <label className="block text-xs text-gray-500 mb-1">Font Weight</label>
                                <select
                                    value={selectedElement.json.styles?.fontWeight || 'normal'}
                                    onChange={(e) => handleStyleChange('fontWeight', e.target.value)}
                                    className="w-full p-2 border border-gray-300 rounded-md text-sm focus:ring-2 focus:ring-blue-500"
                                >
                                    <option value="normal">Normal</option>
                                    <option value="bold">Bold</option>
                                    <option value="600">Semi Bold</option>
                                    <option value="300">Light</option>
                                </select>
                            </div>
                            <div>
                                <label className="block text-xs text-gray-500 mb-1">Text Color</label>
                                <input
                                    type="color"
                                    value={selectedElement.json.styles?.color || '#000000'}
                                    onChange={(e) => handleStyleChange('color', e.target.value)}
                                    className="w-12 h-8 border border-gray-300 rounded-md cursor-pointer"
                                />
                            </div>
                        </div>
                    </div>
                )}

                {/* Background Section (only for button) */}
                {selectedElement.json.type === 'button' && (
                    <div className="bg-gray-50 rounded-lg p-3 border">
                        <h4 className="text-xs font-semibold text-gray-700 mb-3 uppercase tracking-wide">
                            Background
                        </h4>
                        <input
                            type="color"
                            value={selectedElement.json.styles?.backgroundColor || '#007bff'}
                            onChange={(e) => handleStyleChange('backgroundColor', e.target.value)}
                            className="w-12 h-8 border border-gray-300 rounded-md cursor-pointer"
                        />
                    </div>
                )}
            </div>
        </div>
    );
};

export default PropertiesPanel;
