// src/components/create-page/hooks.js
import { useState, useEffect, useCallback } from 'react';
import { toast } from 'react-toastify';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import api from '@landinghub/api';
import { parseHTMLToPageData, renderStaticHTML, CSS_VARIABLES } from './helpersPr';

export const usePageState = (pageId, navigate) => {
    const [pageData, setPageData] = useState({
        canvas: { width: parseInt(CSS_VARIABLES['--canvas-desktop']), background: CSS_VARIABLES['--white'] },
        elements: [],
    });
    const [selectedIds, setSelectedIds] = useState([]);
    const [selectedChildId, setSelectedChildId] = useState(null);
    const [history, setHistory] = useState([]);
    const [historyIndex, setHistoryIndex] = useState(-1);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        if (!pageId) {
            toast.error('Không tìm thấy ID trang. Vui lòng tạo trang mới từ dashboard.');
            navigate('/pages');
            return;
        }

        if (!pageId.match(/^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i)) {
            toast.error('ID trang không hợp lệ. Vui lòng kiểm tra lại.');
            navigate('/pages');
            return;
        }

        const fetchPageContent = async () => {
            setIsLoading(true);
            try {
                console.log('Fetching content for pageId:', pageId);
                const response = await api.get(`/api/pages/${pageId}/content`);
                console.log('Page content response:', response.data);
                if (response.data.html) {
                    const parsedPageData = parseHTMLToPageData(response.data.html);
                    setPageData(parsedPageData);
                    setHistory([parsedPageData]);
                    setHistoryIndex(0);
                } else {
                    console.warn('No HTML content found in response');
                    toast.warn('Không tìm thấy nội dung trang. Khởi tạo trang trống.');
                    setPageData({ canvas: { width: parseInt(CSS_VARIABLES['--canvas-desktop']), background: CSS_VARIABLES['--white'] }, elements: [] });
                }
            } catch (error) {
                console.error('Lỗi khi lấy nội dung trang:', error);
                const errorMessage = error.response?.data?.error || error.message;
                toast.error('Lỗi khi lấy nội dung trang: ' + errorMessage);
                if (error.response?.status === 401) {
                    localStorage.removeItem('token');
                    navigate('/auth');
                } else if (error.response?.status === 400 && errorMessage.includes('Invalid page ID')) {
                    toast.error('ID trang không hợp lệ. Vui lòng kiểm tra lại.');
                    navigate('/pages');
                } else {
                    navigate('/pages');
                }
            } finally {
                setIsLoading(false);
            }
        };
        fetchPageContent();
    }, [pageId, navigate]);

    const handleSelectElement = useCallback((ids, append = false) => {
        if (Array.isArray(ids)) {
            setSelectedIds(append ? [...new Set([...selectedIds, ...ids])] : ids);
        } else {
            setSelectedIds(append ? [...new Set([...selectedIds, ids])] : [ids]);
        }
        setSelectedChildId(null);
    }, [selectedIds]);

    const handleSelectChild = useCallback((parentId, childId) => {
        if (!parentId) {
            setSelectedIds([]);
            setSelectedChildId(null);
            return;
        }
        if (!pageData.elements.find((el) => el.id === parentId)) {
            console.warn(`Parent element with id ${parentId} not found`);
            setSelectedIds([]);
            setSelectedChildId(null);
            return;
        }
        setSelectedIds([parentId]);
        setSelectedChildId(childId);
    }, [pageData.elements]);

    const handleAddElement = useCallback((element) => {
        console.log('Adding new element:', element);
        setPageData((prev) => {
            const newElement = {
                ...element,
                id: `${element.id}-${Date.now()}`,
                position: {
                    desktop: element.position?.desktop || { x: 0, y: 0 },
                    tablet: element.position?.tablet || { x: 0, y: 0 },
                    mobile: element.position?.mobile || { x: 0, y: 0 },
                },
                styles: {
                    ...element.styles,
                    borderRadius: element.styles?.borderRadius || CSS_VARIABLES['--border-radius'],
                    backgroundColor: element.styles?.backgroundColor || CSS_VARIABLES['--white'],
                    boxShadow: element.styles?.boxShadow || CSS_VARIABLES['--shadow'],
                    zIndex: element.type === 'section' ? 1 : 10,
                },
                children: element.children || [],
            };
            const newPageData = {
                ...prev,
                elements: [...prev.elements, newElement],
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã thêm phần tử!');
    }, [historyIndex, history]);

    const handleAddChild = useCallback((parentId, newChild) => {
        console.log('Adding child to parent:', { parentId, newChild });
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) => {
                    if (el.id === parentId) {
                        return {
                            ...el,
                            children: [...(el.children || []), {
                                ...newChild,
                                position: {
                                    desktop: newChild.position?.desktop || { x: 0, y: 0 },
                                    tablet: newChild.position?.tablet || { x: 0, y: 0 },
                                    mobile: newChild.position?.mobile || { x: 0, y: 0 },
                                },
                                styles: {
                                    ...newChild.styles,
                                    zIndex: 2,
                                },
                            }],
                        };
                    }
                    return el;
                }),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã thêm thành phần con!');
    }, [historyIndex, history]);

    const handleUpdatePosition = useCallback((id, position, mode) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) =>
                    el.id === id
                        ? {
                            ...el,
                            position: mode === 'replace' ? position : { ...el.position, ...position },
                        }
                        : el
                ),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
    }, [historyIndex, history]);

    const handleUpdateSize = useCallback((id, size) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) =>
                    el.id === id ? { ...el, size } : el
                ),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
    }, [historyIndex, history]);

    const handleUpdateCanvas = useCallback((canvasUpdates) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                canvas: {
                    ...prev.canvas,
                    ...canvasUpdates,
                },
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã cập nhật cài đặt canvas!');
    }, [historyIndex, history]);

    const handleDeleteElement = useCallback((id) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.filter((el) => el.id !== id),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        setSelectedIds((prev) => prev.filter((selId) => selId !== id));
        setSelectedChildId(null);
        toast.success('Đã xóa phần tử!');
    }, [historyIndex, history]);

    const handleEditElement = useCallback((updatedElement) => {
        console.log('Updating element:', updatedElement);
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) =>
                    el.id === selectedIds[0]
                        ? updatedElement.isChild
                            ? {
                                ...el,
                                children: el.children.map((child) =>
                                    child.id === selectedChildId
                                        ? { ...child, ...updatedElement.json }
                                        : child
                                ),
                            }
                            : { ...el, ...updatedElement.json }
                        : el
                ),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã cập nhật phần tử!');
    }, [historyIndex, history, selectedIds, selectedChildId]);

    const handleDuplicateElement = useCallback((id) => {
        const element = pageData.elements.find((el) => el.id === id);
        if (element) {
            const newElement = {
                ...element,
                id: `${element.id}-${Date.now()}`,
                position: {
                    desktop: {
                        x: (element.position.desktop?.x || 0) + 10,
                        y: (element.position.desktop?.y || 0) + 10,
                    },
                    tablet: {
                        x: (element.position.tablet?.x || 0) + 10,
                        y: (element.position.tablet?.y || 0) + 10,
                    },
                    mobile: {
                        x: (element.position.mobile?.x || 0) + 10,
                        y: (element.position.mobile?.y || 0) + 10,
                    },
                },
            };
            setPageData((prev) => {
                const newPageData = {
                    ...prev,
                    elements: [...prev.elements, newElement],
                };
                setHistory([...history.slice(0, historyIndex + 1), newPageData]);
                setHistoryIndex(historyIndex + 1);
                return newPageData;
            });
            toast.success('Đã sao chép phần tử!');
        }
    }, [historyIndex, history, pageData.elements]);

    const handleMoveElementUp = useCallback((id) => {
        setPageData((prev) => {
            const index = prev.elements.findIndex((el) => el.id === id);
            if (index <= 0) return prev;
            const newElements = [...prev.elements];
            [newElements[index - 1], newElements[index]] = [newElements[index], newElements[index - 1]];
            const newPageData = { ...prev, elements: newElements };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã di chuyển phần tử lên!');
    }, [historyIndex, history]);

    const handleMoveElementDown = useCallback((id) => {
        setPageData((prev) => {
            const index = prev.elements.findIndex((el) => el.id === id);
            if (index >= prev.elements.length - 1) return prev;
            const newElements = [...prev.elements];
            [newElements[index], newElements[index + 1]] = [newElements[index + 1], newElements[index]];
            const newPageData = { ...prev, elements: newElements };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã di chuyển phần tử xuống!');
    }, [historyIndex, history]);

    const handleGroupElements = useCallback((ids) => {
        const groupId = `group-${Date.now()}`;
        setPageData((prev) => {
            const selectedElements = prev.elements.filter((el) => ids.includes(el.id));
            if (selectedElements.length === 0) return prev;
            const newPageData = {
                ...prev,
                elements: [
                    ...prev.elements.filter((el) => !ids.includes(el.id)),
                    {
                        id: groupId,
                        type: 'group',
                        position: {
                            desktop: { x: 0, y: 0 },
                            tablet: { x: 0, y: 0 },
                            mobile: { x: 0, y: 0 },
                        },
                        size: { width: 300, height: 200 },
                        styles: {
                            borderRadius: CSS_VARIABLES['--border-radius'],
                            backgroundColor: CSS_VARIABLES['--white'],
                            boxShadow: CSS_VARIABLES['--shadow'],
                            zIndex: 10,
                        },
                        children: selectedElements.map((el) => ({
                            ...el,
                            position: {
                                desktop: { x: 0, y: 0 },
                                tablet: { x: 0, y: 0 },
                                mobile: { x: 0, y: 0 },
                            },
                        })),
                    },
                ],
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        setSelectedIds([groupId]);
        toast.success('Đã nhóm các phần tử!');
    }, [historyIndex, history]);

    const handleUpdateChildPosition = useCallback((parentId, childId, newPosition, viewMode) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) => {
                    if (el.id === parentId) {
                        return {
                            ...el,
                            children: el.children.map((child) =>
                                child.id === childId
                                    ? { ...child, position: { ...child.position, [viewMode]: newPosition } }
                                    : child
                            ),
                        };
                    }
                    return el;
                }),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã cập nhật vị trí thành phần con!');
    }, [historyIndex, history]);

    const handleMoveChild = useCallback((sourceParentId, childId, targetParentId, newElementOrPosition, viewMode) => {
        setPageData((prev) => {
            let newElements = [...prev.elements];
            let childElement;

            if (sourceParentId) {
                newElements = newElements.map((el) => {
                    if (el.id === sourceParentId) {
                        const childIndex = el.children.findIndex((child) => child.id === childId);
                        if (childIndex !== -1) {
                            childElement = {
                                ...el.children[childIndex],
                                position: {
                                    ...el.children[childIndex].position,
                                    [viewMode]:
                                        typeof newElementOrPosition === 'object' && newElementOrPosition.position
                                            ? newElementOrPosition.position[viewMode]
                                            : newElementOrPosition,
                                },
                            };
                            return { ...el, children: el.children.filter((child) => child.id !== childId) };
                        }
                    }
                    return el;
                });
            } else {
                const childIndex = newElements.findIndex((el) => el.id === childId);
                if (childIndex !== -1) {
                    childElement = {
                        ...newElements[childIndex],
                        position: {
                            ...newElements[childIndex].position,
                            [viewMode]:
                                typeof newElementOrPosition === 'object' && newElementOrPosition.position
                                    ? newElementOrPosition.position[viewMode]
                                    : newElementOrPosition,
                        },
                    };
                    newElements.splice(childIndex, 1);
                }
            }

            if (targetParentId) {
                newElements = newElements.map((el) => {
                    if (el.id === targetParentId) {
                        return { ...el, children: [...(el.children || []), childElement] };
                    }
                    return el;
                });
            } else {
                newElements.push(
                    typeof newElementOrPosition === 'object' && newElementOrPosition.id
                        ? newElementOrPosition
                        : childElement
                );
            }

            const newPageData = { ...prev, elements: newElements };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success(targetParentId ? 'Đã di chuyển thành phần con!' : 'Đã chuyển thành phần con thành phần tử độc lập!');
    }, [historyIndex, history]);

    const handleUpdateChildren = useCallback((parentId, dragIndex, dropIndex) => {
        setPageData((prev) => {
            const newPageData = {
                ...prev,
                elements: prev.elements.map((el) => {
                    if (el.id === parentId) {
                        const newChildren = [...(el.children || [])];
                        if (dragIndex >= 0 && dragIndex < newChildren.length && dropIndex >= 0 && dropIndex < newChildren.length) {
                            const [movedChild] = newChildren.splice(dragIndex, 1);
                            newChildren.splice(dropIndex, 0, movedChild);
                            return { ...el, children: newChildren };
                        }
                    }
                    return el;
                }),
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.success('Đã sắp xếp lại thành phần con!');
    }, [historyIndex, history]);

    const handleUndo = useCallback(() => {
        if (historyIndex >= 0) {
            setPageData(history[historyIndex]);
            setHistoryIndex(historyIndex - 1);
            toast.info('Đã hoàn tác!');
        }
    }, [historyIndex, history]);

    const handleRedo = useCallback(() => {
        if (historyIndex < history.length - 1) {
            setPageData(history[historyIndex + 1]);
            setHistoryIndex(historyIndex + 1);
            toast.info('Đã khôi phục!');
        }
    }, [historyIndex, history]);

    const handleUpdateCanvasHeight = useCallback((newHeight, viewMode) => {
        setPageData((prev) => {
            const totalHeight = prev.elements
                .filter((el) => el.type === 'section')
                .reduce((maxY, el) => {
                    const y = el.position?.[viewMode]?.y || 0;
                    const height = el.size?.height || 400;
                    return Math.max(maxY, y + height);
                }, 0);
            return {
                ...prev,
                canvas: {
                    ...prev.canvas,
                    height: totalHeight + 100,
                },
            };
        });
    }, []);

    return {
        pageData,
        setPageData,
        selectedIds,
        selectedChildId,
        history,
        historyIndex,
        isLoading,
        handleSelectElement,
        handleSelectChild,
        handleAddElement,
        handleAddChild,
        handleUpdatePosition,
        handleUpdateSize,
        handleUpdateCanvas,
        handleDeleteElement,
        handleEditElement,
        handleDuplicateElement,
        handleMoveElementUp,
        handleMoveElementDown,
        handleGroupElements,
        handleUpdateChildPosition,
        handleMoveChild,
        handleUpdateChildren,
        handleUndo,
        handleRedo,
        handleUpdateCanvasHeight,
    };
};

export const useViewControls = (pageData, history, historyIndex, setPageData, setHistory, setHistoryIndex) => {
    const [viewMode, setViewMode] = useState('desktop');
    const [zoomLevel, setZoomLevel] = useState(100);
    const [gridSize, setGridSize] = useState(10);
    const [showGrid, setShowGrid] = useState(true);
    const [guideLine, setGuideLine] = useState({ show: false, y: 0 });

    const handleViewModeChange = useCallback((mode) => {
        console.log('Changing view mode to:', mode);
        setViewMode(mode);
        setPageData((prev) => {
            const canvasWidth = mode === 'desktop' ? 1200 : 375;
            const newElements = prev.elements.map((element) => ({
                ...element,
                size: {
                    ...element.size,
                    width: mode === 'mobile' ? '100%' : element.size.width || 1200,
                },
                position: {
                    ...element.position,
                    [mode]: {
                        ...element.position[mode],
                        x: mode === 'mobile' ? 0 : element.position[mode]?.x || 0,
                    },
                },
            }));
            const newPageData = {
                ...prev,
                canvas: {
                    ...prev.canvas,
                    width: canvasWidth,
                },
                elements: newElements,
            };
            setHistory([...history.slice(0, historyIndex + 1), newPageData]);
            setHistoryIndex(historyIndex + 1);
            return newPageData;
        });
        toast.info(`Đã chuyển sang chế độ ${mode === 'desktop' ? 'Desktop' : 'Mobile'}`);
    }, [history, historyIndex, setPageData, setHistory, setHistoryIndex]);

    const handleShowAddSectionGuide = useCallback((show) => {
        console.log('Toggling add section guide:', show);
        if (show) {
            const lastSectionY = pageData.elements
                .filter((el) => el.type === 'section')
                .reduce((maxY, el) => {
                    const y = el.position?.[viewMode]?.y || 0;
                    const height = el.size?.height || 400;
                    return Math.max(maxY, y + height);
                }, 0);
            setGuideLine({ show: true, y: lastSectionY + 10 });
        } else {
            setGuideLine({ show: false, y: 0 });
        }
    }, [pageData.elements, viewMode]);

    return {
        viewMode,
        setViewMode,
        zoomLevel,
        setZoomLevel,
        gridSize,
        setGridSize,
        showGrid,
        setShowGrid,
        guideLine,
        handleViewModeChange,
        handleShowAddSectionGuide,
    };
};

export const useApiHandlers = (pageId, navigate, pageData) => {
    const handleSave = useCallback(async () => {
        if (!pageId) {
            toast.error('Không tìm thấy ID trang. Vui lòng tạo trang mới từ dashboard.');
            return;
        }

        try {
            // Kiểm tra tính hợp lệ của pageData
            if (!pageData.elements.length) {
                toast.warn('Trang hiện tại trống. Vui lòng thêm nội dung trước khi lưu.');
                return;
            }

            const htmlContent = renderStaticHTML(pageData);
            console.log('Saving page with pageId:', pageId, 'Elements count:', pageData.elements.length, 'Canvas settings:', pageData.canvas);

            // Gửi sự kiện phân tích (nếu có, ví dụ Google Analytics)
            if (window.gtag) {
                window.gtag('event', 'page_save', {
                    event_category: 'Page Editor',
                    event_label: `Page ${pageId}`,
                    value: pageData.elements.length,
                });
            }

            const response = await api.put(`/api/pages/${pageId}`, { html: htmlContent });
            if (response.data.success) {
                toast.success(`Lưu trang thành công! (${pageData.elements.length} phần tử)`);
                alert('✅ Lưu trang thành công!');
            } else {
                toast.error('Lưu trang thất bại: ' + (response.data.error || 'Lỗi không xác định'));
            }
        } catch (error) {
            console.error('Lỗi khi lưu trang:', error);
            const errorMessage = error.response?.data?.error || error.message;
            toast.error('Lỗi khi lưu trang: ' + errorMessage);
            if (error.response?.status === 401) {
                localStorage.removeItem('token');
                navigate('/auth');
            }
        }
    }, [pageId, navigate, pageData]);

    const handlePreview = useCallback(() => {
        const htmlContent = renderStaticHTML(pageData);
        const newWindow = window.open('', '_blank');
        newWindow.document.write(htmlContent);
        newWindow.document.close();
    }, [pageData]);

    const handleGenerateCode = useCallback(async () => {
        if (!pageId) {
            toast.error('Không tìm thấy ID trang để triển khai.');
            return;
        }

        try {
            const htmlContent = renderStaticHTML(pageData);
            console.log('Saving before publish, sending PUT request:', { html: htmlContent });
            await api.put(`/api/pages/${pageId}`, { html: htmlContent });
            console.log('Sending POST request to /api/pages/:id/publish');
            const response = await api.post(`/api/pages/${pageId}/publish`);
            console.log('Publish response:', response.data);
            if (response.data.success && response.data.page?.url) {
                toast.success(`Triển khai thành công! Truy cập tại: ${response.data.page.url}`);
            } else {
                toast.error('Triển khai thất bại: ' + (response.data.error || 'Lỗi không xác định'));
            }
        } catch (error) {
            console.error('Lỗi khi triển khai:', error);
            const errorMessage = error.response?.data?.error || error.message;
            toast.error('Lỗi khi triển khai trang: ' + errorMessage);
            if (error.response?.status === 401) {
                localStorage.removeItem('token');
                navigate('/auth');
            }
        }
    }, [pageId, navigate, pageData]);

    useEffect(() => {
        const initializeAuth = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                console.error('No token found in localStorage');
                toast.error('Vui lòng đăng nhập để tiếp tục');
                navigate('/auth');
                return;
            }

            let decoded;
            try {
                decoded = jwtDecode(token);
                const currentTime = Math.floor(Date.now() / 1000);
                if (decoded.exp && decoded.exp < currentTime) {
                    console.error('Token has expired');
                    localStorage.removeItem('token');
                    toast.error('Phiên đăng nhập đã hết hạn. Vui lòng đăng nhập lại.');
                    navigate('/auth');
                    return;
                }
                api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            } catch (err) {
                console.error('Error decoding token:', err);
                toast.error('Phiên đăng nhập không hợp lệ');
                navigate('/auth');
                return;
            }
        };
        initializeAuth();
    }, [navigate]);

    return {
        handleSave,
        handlePreview,
        handleGenerateCode,
    };
};

