import React, { useState, useCallback, useMemo, useEffect, useRef } from 'react';
import { Search, ChevronDown, ChevronRight, ChevronLeft, Grid, List, Zap, Star } from 'lucide-react';

const ItemTypes = {
    COMPONENT: 'component',
    TEMPLATE: 'template',
};

const EXTENDED_LIBRARY = {
    elements: {
        name: 'Ph·∫ßn t·ª≠',
        icon: Grid,
        color: '#3b82f6',
        subCategories: [
            {
                id: 'text',
                name: 'VƒÉn b·∫£n',
                templates: [
                    {
                        id: 'text-basic',
                        name: 'C∆° b·∫£n',
                        icon: 'üìù',
                        description: 'VƒÉn b·∫£n ƒë∆°n gi·∫£n, d·ªÖ ch·ªânh s·ª≠a',
                        level: 'basic',
                        popularity: 95,
                        json: {
                            type: 'heading',
                            content: 'Ti√™u ƒë·ªÅ m·∫´u',
                            styles: {color: '#333', fontSize: '24px'},
                        },
                    },
                    {
                        id: 'text-advanced',
                        name: 'N√¢ng cao',
                        icon: '‚ú®',
                        description: 'VƒÉn b·∫£n v·ªõi hi·ªáu ·ª©ng ƒë·ªông v√† t√πy ch·ªânh',
                        level: 'advanced',
                        popularity: 78,
                        variants: ['bold', 'italic', 'animated'],
                        json: {
                            type: 'paragraph',
                            content: 'VƒÉn b·∫£n n√¢ng cao v·ªõi hi·ªáu ·ª©ng',
                            styles: {color: '#555', fontStyle: 'italic', animation: 'fadeIn 1s'},
                        },
                    },
                ],
            },
            {
                id: 'buttons',
                name: 'N√∫t b·∫•m',
                templates: [
                    {
                        id: 'button-basic',
                        name: 'N√∫t c∆° b·∫£n',
                        icon: 'üîò',
                        description: 'N√∫t h√†nh ƒë·ªông ƒë∆°n gi·∫£n, d·ªÖ s·ª≠ d·ª•ng',
                        level: 'basic',
                        popularity: 92,
                        json: {
                            type: 'button',
                            content: 'Nh·∫•n v√†o',
                            styles: {background: '#3b82f6', color: '#fff', padding: '8px 16px', borderRadius: '6px'},
                        },
                    },
                    {
                        id: 'button-group',
                        name: 'Nh√≥m n√∫t',
                        icon: 'üéõÔ∏è',
                        description: 'Nh√≥m n√∫t li√™n k·∫øt v·ªõi b·ªë c·ª•c linh ho·∫°t',
                        level: 'advanced',
                        popularity: 65,
                        json: {
                            type: 'button-group',
                            content: ['N√∫t 1', 'N√∫t 2'],
                            styles: {gap: '10px'},
                        },
                    },
                ],
            },
        ],
    },
    sections: {
        name: 'Section',
        icon: List,
        color: '#10b981',
        subCategories: [
            {
                id: 'images',
                name: '·∫¢nh',
                templates: [
                    {
                        id: 'image-basic',
                        name: '·∫¢nh ƒë∆°n',
                        icon: 'üñºÔ∏è',
                        description: 'H√¨nh ·∫£nh c∆° b·∫£n, d·ªÖ ch√®n',
                        level: 'basic',
                        popularity: 88,
                        json: {
                            type: 'image',
                            content: 'https://via.placeholder.com/300x200',
                            styles: {width: '300px', borderRadius: '8px'},
                        },
                    },
                    {
                        id: 'image-gallery',
                        name: 'H√¨nh h·ªôp',
                        icon: 'üé¨',
                        description: 'Gallery h√¨nh ·∫£nh v·ªõi b·ªë c·ª•c l∆∞·ªõi',
                        level: 'advanced',
                        popularity: 72,
                        json: {
                            type: 'gallery',
                            content: ['https://via.placeholder.com/150x150', 'https://via.placeholder.com/150x150'],
                            styles: {gap: '10px'},
                        },
                    },
                ],
            },
        ],
    },
    templates: {
        name: 'Templates',
        icon: Zap,
        color: '#f59e0b',
        subCategories: [
            {
                id: 'landing',
                name: 'Landing Page',
                templates: [
                    {
                        id: 'lp-education',
                        name: 'Gi√°o d·ª•c',
                        icon: 'üéì',
                        description: 'Template cho kh√≥a h·ªçc tr·ª±c tuy·∫øn',
                        level: 'basic',
                        popularity: 85,
                        structure: [
                            {
                                id: 'header-edu',
                                json: {
                                    type: 'section',
                                    content: {title: 'Kh√≥a h·ªçc tr·ª±c tuy·∫øn', subtitle: 'H·ªçc m·ªçi l√∫c, m·ªçi n∆°i'},
                                    styles: {background: '#e0f2fe'},
                                },
                            },
                        ],
                    },
                ],
            },
        ],
    },
};

const DraggableComponent = React.memo(({ item, isTemplate = false, isDragging, onDragStart, onDragEnd }) => {
    const dragRef = useRef(null);
    const [isHovered, setIsHovered] = useState(false);

    const handleMouseDown = useCallback((e) => {
        onDragStart();
        // Simulate drag behavior
        const rect = e.currentTarget.getBoundingClientRect();
        const offsetX = e.clientX - rect.left;
        const offsetY = e.clientY - rect.top;

        const handleMouseMove = (e) => {
            // Update drag preview position
        };

        const handleMouseUp = () => {
            onDragEnd();
            document.removeEventListener('mousemove', handleMouseMove);
            document.removeEventListener('mouseup', handleMouseUp);
        };

        document.addEventListener('mousemove', handleMouseMove);
        document.addEventListener('mouseup', handleMouseUp);
    }, [onDragStart, onDragEnd]);

    const getPopularityColor = (popularity) => {
        if (popularity >= 90) return 'text-green-600';
        if (popularity >= 70) return 'text-yellow-600';
        return 'text-gray-500';
    };

    const getLevelBadge = (level) => {
        const colors = {
            basic: 'bg-blue-100 text-blue-700',
            advanced: 'bg-purple-100 text-purple-700',
            expert: 'bg-red-100 text-red-700'
        };
        return colors[level] || colors.basic;
    };

    return (
        <div
            ref={dragRef}
            className={`group relative bg-white rounded-lg border-2 transition-all duration-200 cursor-move select-none
                ${isDragging ? 'border-blue-400 shadow-lg scale-105 opacity-80' : 'border-gray-200'}
                ${isHovered ? 'border-blue-300 shadow-md' : ''}
                hover:border-blue-300 hover:shadow-md`}
            onMouseDown={handleMouseDown}
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
        >
            <div className="p-3 space-y-2">
                <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-2">
                        <span className="text-lg">{item.icon}</span>
                        <span className="font-medium text-gray-800 text-sm truncate flex-1">
                            {item.name}
                        </span>
                    </div>
                    {item.popularity && (
                        <div className={`flex items-center space-x-1 ${getPopularityColor(item.popularity)}`}>
                            <Star className="w-3 h-3 fill-current" />
                            <span className="text-xs font-medium">{item.popularity}</span>
                        </div>
                    )}
                </div>

                <p className="text-xs text-gray-600 line-clamp-2">{item.description}</p>

                <div className="flex items-center justify-between">
                    {item.level && (
                        <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getLevelBadge(item.level)}`}>
                            {item.level.toUpperCase()}
                        </span>
                    )}
                    <div className="flex items-center space-x-1 text-gray-400 group-hover:text-blue-500">
                        <div className="w-2 h-2 bg-current rounded-full"></div>
                        <div className="w-2 h-2 bg-current rounded-full"></div>
                        <div className="w-2 h-2 bg-current rounded-full"></div>
                    </div>
                </div>
            </div>

            {/* Quick preview on hover */}
            {isHovered && (
                <div className="absolute -top-1 -right-1 bg-blue-500 text-white rounded-full w-5 h-5 flex items-center justify-center text-xs font-bold">
                    +
                </div>
            )}
        </div>
    );
});

const ComponentLibrary = React.memo(({ isCollapsed: initialCollapsed, onToggle }) => {
    const [isCollapsed, setIsCollapsed] = useState(initialCollapsed);
    const [activeCategory, setActiveCategory] = useState('elements');
    const [searchQuery, setSearchQuery] = useState('');
    const [expandedSubs, setExpandedSubs] = useState({ elements: { text: true, buttons: true } });
    const [viewMode, setViewMode] = useState('grid'); // 'grid' or 'list'
    const [sortBy, setSortBy] = useState('popularity'); // 'name', 'popularity', 'level'
    const [dragState, setDragState] = useState({ isDragging: false, draggedItem: null });

    const categories = Object.keys(EXTENDED_LIBRARY).map((key) => ({
        id: key,
        ...EXTENDED_LIBRARY[key],
    }));

    const handleToggle = useCallback(() => {
        setIsCollapsed((prev) => !prev);
        onToggle?.();
    }, [onToggle]);

    const toggleSub = useCallback((catId, subId) => {
        setExpandedSubs((prev) => ({
            ...prev,
            [catId]: { ...prev[catId], [subId]: !prev[catId]?.[subId] },
        }));
    }, []);

    const filteredItems = useMemo(() => {
        const categoryData = EXTENDED_LIBRARY[activeCategory];
        if (!categoryData) return [];

        let items = [];
        categoryData.subCategories.forEach((sub) => {
            if (expandedSubs[activeCategory]?.[sub.id]) {
                items = [...items, ...sub.templates];
            }
        });

        // Filter by search
        if (searchQuery) {
            items = items.filter((item) =>
                item.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                item.description.toLowerCase().includes(searchQuery.toLowerCase())
            );
        }

        // Sort items
        items.sort((a, b) => {
            switch (sortBy) {
                case 'popularity':
                    return (b.popularity || 0) - (a.popularity || 0);
                case 'name':
                    return a.name.localeCompare(b.name);
                case 'level':
                    const levelOrder = { basic: 1, advanced: 2, expert: 3 };
                    return (levelOrder[a.level] || 0) - (levelOrder[b.level] || 0);
                default:
                    return 0;
            }
        });

        return items;
    }, [activeCategory, searchQuery, expandedSubs, sortBy]);

    const handleCategoryChange = useCallback((categoryId) => {
        setActiveCategory(categoryId);
        setSearchQuery('');
    }, []);

    const handleDragStart = useCallback((item) => {
        setDragState({ isDragging: true, draggedItem: item });
    }, []);

    const handleDragEnd = useCallback(() => {
        setDragState({ isDragging: false, draggedItem: null });
    }, []);

    // Keyboard shortcuts
    useEffect(() => {
        const handleKeyPress = (e) => {
            if (e.ctrlKey || e.metaKey) {
                const num = parseInt(e.key);
                if (num >= 1 && num <= categories.length) {
                    handleCategoryChange(categories[num - 1].id);
                }
            }
        };

        window.addEventListener('keydown', handleKeyPress);
        return () => window.removeEventListener('keydown', handleKeyPress);
    }, [categories, handleCategoryChange]);

    if (isCollapsed) {
        return (
            <div className="w-16 bg-white border-r border-gray-200 h-full flex flex-col items-center py-4 space-y-3">
                <button
                    onClick={handleToggle}
                    className="p-2 rounded-lg hover:bg-gray-100 transition-colors"
                    title="M·ªü th∆∞ vi·ªán"
                >
                    <ChevronRight className="w-5 h-5 text-gray-600" />
                </button>

                {categories.map((cat) => {
                    const IconComponent = cat.icon;
                    return (
                        <button
                            key={cat.id}
                            className={`p-2 rounded-lg transition-colors ${
                                activeCategory === cat.id
                                    ? 'bg-blue-100 text-blue-600'
                                    : 'text-gray-600 hover:bg-gray-100'
                            }`}
                            title={cat.name}
                            onClick={() => {
                                handleCategoryChange(cat.id);
                                setIsCollapsed(false);
                                onToggle?.();
                            }}
                        >
                            <IconComponent className="w-5 h-5" />
                        </button>
                    );
                })}
            </div>
        );
    }

    return (
        <div className="w-80 bg-white border-r border-gray-200 h-full flex flex-col">
            {/* Header */}
            <div className="flex items-center justify-between p-4 border-b border-gray-200">
                <h2 className="text-lg font-semibold text-gray-800">Th∆∞ vi·ªán</h2>
                <button
                    onClick={handleToggle}
                    className="p-1.5 rounded-lg hover:bg-gray-100 transition-colors"
                    title="Thu g·ªçn th∆∞ vi·ªán"
                >
                    <ChevronLeft className="w-4 h-4 text-gray-600" />
                </button>
            </div>

            {/* Search and Controls */}
            <div className="p-4 border-b border-gray-200 space-y-3">
                <div className="relative">
                    <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
                    <input
                        type="text"
                        placeholder="T√¨m ki·∫øm th√†nh ph·∫ßn..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    />
                </div>

                <div className="flex items-center justify-between text-xs">
                    <select
                        value={sortBy}
                        onChange={(e) => setSortBy(e.target.value)}
                        className="px-2 py-1 border border-gray-300 rounded text-xs focus:ring-1 focus:ring-blue-500"
                    >
                        <option value="popularity">Ph·ªï bi·∫øn</option>
                        <option value="name">T√™n A-Z</option>
                        <option value="level">ƒê·ªô kh√≥</option>
                    </select>

                    <div className="flex border border-gray-300 rounded overflow-hidden">
                        <button
                            className={`px-2 py-1 text-xs ${viewMode === 'grid' ? 'bg-blue-500 text-white' : 'bg-white text-gray-600'}`}
                            onClick={() => setViewMode('grid')}
                        >
                            Grid
                        </button>
                        <button
                            className={`px-2 py-1 text-xs ${viewMode === 'list' ? 'bg-blue-500 text-white' : 'bg-white text-gray-600'}`}
                            onClick={() => setViewMode('list')}
                        >
                            List
                        </button>
                    </div>
                </div>
            </div>

            {/* Categories */}
            <div className="flex-1 overflow-y-auto">
                <div className="p-2">
                    <div className="flex space-x-1 mb-4">
                        {categories.map((cat) => {
                            const IconComponent = cat.icon;
                            return (
                                <button
                                    key={cat.id}
                                    onClick={() => handleCategoryChange(cat.id)}
                                    className={`flex items-center space-x-2 px-3 py-2 rounded-lg text-sm font-medium transition-colors flex-1 justify-center ${
                                        activeCategory === cat.id
                                            ? 'bg-blue-100 text-blue-700 shadow-sm'
                                            : 'text-gray-600 hover:bg-gray-100'
                                    }`}
                                    title={`${cat.name} (Ctrl+${categories.indexOf(cat) + 1})`}
                                >
                                    <IconComponent className="w-4 h-4" />
                                    <span className="hidden sm:block">{cat.name}</span>
                                </button>
                            );
                        })}
                    </div>
                </div>

                {/* Subcategories and Items */}
                <div className="px-2 pb-4">
                    {EXTENDED_LIBRARY[activeCategory]?.subCategories.map((sub) => (
                        <div key={sub.id} className="mb-4">
                            <button
                                onClick={() => toggleSub(activeCategory, sub.id)}
                                className="w-full flex items-center justify-between p-3 text-left bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors mb-2"
                            >
                                <span className="font-medium text-gray-700">{sub.name}</span>
                                {expandedSubs[activeCategory]?.[sub.id] ? (
                                    <ChevronDown className="w-4 h-4 text-gray-500" />
                                ) : (
                                    <ChevronRight className="w-4 h-4 text-gray-500" />
                                )}
                            </button>

                            {expandedSubs[activeCategory]?.[sub.id] && (
                                <div className={`space-y-3 ${viewMode === 'grid' ? 'grid grid-cols-1 gap-3' : 'space-y-2'}`}>
                                    {sub.templates
                                        .filter(item => !searchQuery ||
