                                            <DraggableComponent
                                                key={item.id}
                                                item={item}
                                                isTemplate={activeCategory === 'templates'}
                                                isDragging={dragState.draggedItem?.id === item.id}
                                                onDragStart={() => handleDragStart(item)}
                                                onDragEnd={handleDragEnd}
                                            />
                                        ))}
