import React, { useState, useMemo, useCallback, useEffect, useRef } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { useHotkeys } from 'react-hotkeys-hook';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Toolbar from './create-page/Toolbar';
import ComponentLibrary from './create-page/ComponentLibrary';
import Canvas from './create-page/Canvas';
import PropertiesPanel from './create-page/PropertiesPanel';
import '../styles/CreateLanding.css';

const ItemTypes = {
    SECTION: 'section',
    ELEMENT: 'element',
    EXISTING_ELEMENT: 'existingElement',
};

const CreateLandingPage = () => {
    const [pageStructure, setPageStructure] = useState({
        id: 'page-1',
        type: 'page',
        componentData: { title: 'My Page' },
        styles: { desktop: { minHeight: '100vh', background: '#fff' }, mobile: { minHeight: '100vh', background: '#fff' } },
        children: [],
    });
    const [selectedElement, setSelectedElement] = useState(null);
    const [leftSidebarCollapsed, setLeftSidebarCollapsed] = useState(true);
    const [rightSidebarCollapsed, setRightSidebarCollapsed] = useState(false);
    const [quickEditElement, setQuickEditElement] = useState(null);
    const [quickEditPosition, setQuickEditPosition] = useState(null);
    const [viewMode, setViewMode] = useState('desktop');
    const [zoomLevel, setZoomLevel] = useState(100);
    const [history, setHistory] = useState([{
        id: 'page-1',
        type: 'page',
        componentData: { title: 'My Page' },
        styles: { desktop: { minHeight: '100vh', background: '#fff' }, mobile: { minHeight: '100vh', background: '#fff' } },
        children: [],
    }]);
    const [historyIndex, setHistoryIndex] = useState(0);
    const [copiedSection, setCopiedSection] = useState(null);
    const fileInputRef = useRef(null);

    const memoizedPageStructure = useMemo(() => pageStructure, [pageStructure]);

    const updatePosition = useCallback((elementId, sectionId, newPosition) => {
        setPageStructure((prev) => {
            const newStructure = {
                ...prev,
                children: prev.children.map((section) => {
                    if (section.id === sectionId) {
                        return {
                            ...section,
                            elements: section.elements.map((el) =>
                                el.id === elementId
                                    ? {
                                        ...el,
                                        json: {
                                            ...el.json,
                                            styles: {
                                                ...el.json.styles,
                                                desktop: { ...el.json.styles.desktop, ...newPosition.desktop },
                                                mobile: { ...el.json.styles.mobile, ...newPosition.mobile },
                                            },
                                        },
                                    }
                                    : el
                            ),
                        };
                    }
                    return section;
                }),
            };
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
            setHistoryIndex((prev) => prev + 1);
            return newStructure;
        });
        toast.success('Đã cập nhật vị trí phần tử!');
    }, [historyIndex]);

    const addSection = useCallback((sectionData, index) => {
        const newSection = {
            ...sectionData,
            id: `${sectionData.id}-${Date.now()}`,
            elements: [],
            json: {
                ...sectionData.json,
                styles: {
                    desktop: { ...sectionData.json.styles.desktop, width: '100%', maxWidth: '1200px', minHeight: '200px' },
                    mobile: { ...sectionData.json.styles.mobile, width: '100%', maxWidth: '100%', minHeight: '150px' },
                },
            },
        };
        setPageStructure((prev) => {
            const newStructure = {
                ...prev,
                children: [...prev.children.slice(0, index), newSection, ...prev.children.slice(index)],
            };
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
            setHistoryIndex((prev) => prev + 1);
            return newStructure;
        });
        toast.success('Đã thêm section!');
    }, [historyIndex]);

    const addElementToSection = useCallback((sectionId, elementData, index) => {
        const newElement = {
            ...elementData,
            id: `${elementData.id}-${Date.now()}`,
            json: {
                ...elementData.json,
                styles: {
                    desktop: { ...elementData.json.styles.desktop, left: 50, top: index * 100 + 50 },
                    mobile: { ...elementData.json.styles.mobile, left: 25, top: index * 50 + 25 },
                },
            },
        };
        setPageStructure((prev) => {
            const newStructure = {
                ...prev,
                children: prev.children.map((section) =>
                    section.id === sectionId
                        ? {
                            ...section,
                            elements: [...section.elements.slice(0, index), newElement, ...section.elements.slice(index)],
                        }
                        : section
                ),
            };
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
            setHistoryIndex((prev) => prev + 1);
            return newStructure;
        });
        toast.success('Đã thêm phần tử vào section!');
    }, [historyIndex]);

    const moveElement = useCallback((sectionId, elementId, newIndex) => {
        setPageStructure((prev) => {
            const newStructure = {
                ...prev,
                children: prev.children.map((section) => {
                    if (section.id === sectionId) {
                        const currentIndex = section.elements.findIndex((el) => el.id === elementId);
                        if (currentIndex === -1 || currentIndex === newIndex) return section;
                        const newElements = [...section.elements];
                        const [movedElement] = newElements.splice(currentIndex, 1);
                        newElements.splice(newIndex, 0, {
                            ...movedElement,
                            json: {
                                ...movedElement.json,
                                styles: {
                                    ...movedElement.json.styles,
                                    desktop: { ...movedElement.json.styles.desktop, top: newIndex * 100 + 50 },
                                    mobile: { ...movedElement.json.styles.mobile, top: newIndex * 50 + 25 },
                                },
                            },
                        });
                        return { ...section, elements: newElements };
                    }
                    return section;
                }),
            };
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
            setHistoryIndex((prev) => prev + 1);
            return newStructure;
        });
        toast.info('Đã di chuyển phần tử!');
    }, [historyIndex]);

    const handleInlineEdit = useCallback((elementId, sectionId, newContent) => {
        setPageStructure((prev) => {
            const newStructure = {
                ...prev,
                children: prev.children.map((section) => {
                    if (section.id === sectionId) {
                        return {
                            ...section,
                            elements: section.elements.map((el) =>
                                el.id === elementId
                                    ? {
                                        ...el,
                                        json: {
                                            ...el.json,
                                            componentData: { ...el.json.componentData, content: newContent },
                                        },
                                    }
                                    : el
                            ),
                        };
                    }
                    return section;
                }),
            };
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
            setHistoryIndex((prev) => prev + 1);
            return newStructure;
        });
        toast.success('Đã cập nhật nội dung!');
    }, [historyIndex]);

    const handleCopySection = useCallback((sectionId) => {
        const section = pageStructure.children.find((s) => s.id === sectionId);
        if (section) {
            setCopiedSection({ ...section, id: `${section.id}-copy-${Date.now()}` });
            toast.success('Đã sao chép section!');
        }
    }, [pageStructure]);

    const handlePasteSection = useCallback((index) => {
        if (!copiedSection) {
            toast.error('Không có section nào được sao chép!');
            return;
        }
        setPageStructure((prev) => {
            const newStructure = {
                ...prev,
                children: [
                    ...prev.children.slice(0, index),
                    {
                        ...copiedSection,
                        id: `${copiedSection.id}-${Date.now()}`,
                        elements: copiedSection.elements.map((el) => ({
                            ...el,
                            id: `${el.id}-copy-${Date.now()}`,
                        })),
                    },
                    ...prev.children.slice(index),
                ],
            };
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
            setHistoryIndex((prev) => prev + 1);
            return newStructure;
        });
        toast.success('Đã dán section!');
    }, [copiedSection, historyIndex]);

    const handleExportPage = useCallback(() => {
        const exportData = {
            pageStructure,
            viewMode,
            zoomLevel,
            exportedAt: new Date().toISOString(),
        };
        const blob = new Blob([JSON.stringify(exportData, null, 2)], { type: 'application/json' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `landing-page-${Date.now()}.json`;
        a.click();
        URL.revokeObjectURL(url);
        toast.success('Đã xuất trang thành công!');
    }, [pageStructure, viewMode, zoomLevel]);

    const handleImportPage = useCallback((event) => {
        const file = event.target.files[0];
        if (!file) return;
        const reader = new FileReader();
        reader.onload = (e) => {
            try {
                const data = JSON.parse(e.target.result);
                setPageStructure(data.pageStructure || {
                    id: 'page-1',
                    type: 'page',
                    componentData: { title: 'My Page' },
                    styles: { desktop: { minHeight: '100vh', background: '#fff' }, mobile: { minHeight: '100vh', background: '#fff' } },
                    children: [],
                });
                setViewMode(data.viewMode || 'desktop');
                setZoomLevel(data.zoomLevel || 100);
                setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), data.pageStructure || []]);
                setHistoryIndex((prev) => prev + 1);
                toast.success('Đã nhập trang thành công!');
            } catch (error) {
                toast.error('Lỗi khi nhập trang: ' + error.message);
            }
        };
        reader.readAsText(file);
        event.target.value = null;
    }, [historyIndex]);

    const undo = useCallback(() => {
        if (historyIndex > 0) {
            setHistoryIndex((prev) => prev - 1);
            setPageStructure(history[historyIndex - 1]);
            toast.info('Đã hoàn tác!');
        }
    }, [history, historyIndex]);

    const redo = useCallback(() => {
        if (historyIndex < history.length - 1) {
            setHistoryIndex((prev) => prev + 1);
            setPageStructure(history[historyIndex + 1]);
            toast.info('Đã khôi phục!');
        }
    }, [history, historyIndex]);

    const handleSelectElement = useCallback((element) => {
        setSelectedElement(element);
        setQuickEditElement(null);
        setQuickEditPosition(null);
    }, []);

    const handleUpdateElement = useCallback((updatedElement) => {
        setPageStructure((prev) => {
            const newStructure = {
                ...prev,
                children: prev.children.map((section) => {
                    if (section.id === updatedElement.id) {
                        return updatedElement;
                    }
                    return {
                        ...section,
                        elements: section.elements.map((el) => (el.id === updatedElement.id ? updatedElement : el)),
                    };
                }),
            };
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
            setHistoryIndex((prev) => prev + 1);
            return newStructure;
        });
        setSelectedElement(updatedElement);
        toast.success('Đã cập nhật phần tử!');
    }, [historyIndex]);

    const handleQuickEdit = useCallback((element, position) => {
        setQuickEditElement(element);
        setQuickEditPosition(position);
    }, []);

    const handleCloseQuickEdit = useCallback(() => {
        setQuickEditElement(null);
        setQuickEditPosition(null);
    }, []);

    const handleDuplicateElement = useCallback((element) => {
        if (!element) return;
        const duplicated = {
            ...element,
            id: `${element.id}-copy-${Date.now()}`,
            json: {
                ...element.json,
                styles: {
                    ...element.json.styles,
                    desktop: { ...element.json.styles.desktop, left: (element.json.styles.desktop?.left || 0) + 10, top: (element.json.styles.desktop?.top || 0) + 10 },
                    mobile: { ...element.json.styles.mobile, left: (element.json.styles.mobile?.left || 0) + 5, top: (element.json.styles.mobile?.top || 0) + 5 },
                },
            },
        };
        setPageStructure((prev) => {
            const newStructure = {
                ...prev,
                children: prev.children.map((section) => {
                    if (section.elements.some((el) => el.id === element.id)) {
                        const index = section.elements.findIndex((el) => el.id === element.id);
                        return {
                            ...section,
                            elements: [...section.elements.slice(0, index + 1), duplicated, ...section.elements.slice(index + 1)],
                        };
                    }
                    return section;
                }),
            };
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
            setHistoryIndex((prev) => prev + 1);
            return newStructure;
        });
        toast.success('Đã sao chép phần tử!');
    }, [historyIndex]);

    const handleDeleteElement = useCallback((element) => {
        if (!element) return;
        setPageStructure((prev) => {
            const newStructure = {
                ...prev,
                children: prev.children.filter((section) => section.id !== element.id).map((section) => ({
                    ...section,
                    elements: section.elements.filter((el) => el.id !== element.id),
                })),
            };
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
            setHistoryIndex((prev) => prev + 1);
            return newStructure;
        });
        if (selectedElement?.id === element.id) {
            setSelectedElement(null);
        }
        setQuickEditElement(null);
        setQuickEditPosition(null);
        toast.success('Đã xóa phần tử!');
    }, [selectedElement, historyIndex]);

    const handleMoveElement = useCallback((element, direction) => {
        if (!element) return;
        setPageStructure((prev) => {
            const newStructure = {
                ...prev,
                children: prev.children.map((section) => {
                    if (section.elements.some((el) => el.id === element.id)) {
                        const currentIndex = section.elements.findIndex((el) => el.id === element.id);
                        if (currentIndex === -1) return section;
                        const newIndex =
                            direction === 'up'
                                ? Math.max(0, currentIndex - 1)
                                : Math.min(section.elements.length - 1, currentIndex + 1);
                        if (newIndex === currentIndex) return section;
                        const newElements = [...section.elements];
                        const [movedElement] = newElements.splice(currentIndex, 1);
                        newElements.splice(newIndex, 0, {
                            ...movedElement,
                            json: {
                                ...movedElement.json,
                                styles: {
                                    ...movedElement.json.styles,
                                    desktop: { ...movedElement.json.styles.desktop, top: newIndex * 100 + 50 },
                                    mobile: { ...movedElement.json.styles.mobile, top: newIndex * 50 + 25 },
                                },
                            },
                        });
                        return { ...section, elements: newElements };
                    }
                    return section;
                }),
            };
            setHistory((prevHist) => [...prevHist.slice(0, historyIndex + 1), newStructure]);
            setHistoryIndex((prev) => prev + 1);
            return newStructure;
        });
        toast.info('Đã di chuyển phần tử!');
    }, [historyIndex]);

    const handleZoom = useCallback((direction) => {
        const newZoom = direction === 'in' ? Math.min(200, zoomLevel + 10) : Math.max(50, zoomLevel - 10);
        setZoomLevel(newZoom);
    }, [zoomLevel]);

    const handleGenerateCode = useCallback(() => {
        const generateHTML = (pageStructure) => {
            let html = `<div style="${Object.entries(pageStructure.styles.desktop).map(([k, v]) => `${k}: ${v}`).join(';')}">`;
            pageStructure.children.forEach((section) => {
                html += `<section style="${Object.entries(section.json.styles.desktop).map(([k, v]) => `${k}: ${v}`).join(';')}">`;
                section.elements.forEach((el) => {
                    const styles = Object.entries(el.json.styles.desktop).map(([k, v]) => `${k}: ${v}`).join(';');
                    switch (el.json.type) {
                        case 'heading':
                            html += `<h2 style="${styles}">${el.json.componentData.content}</h2>`;
                            break;
                        case 'paragraph':
                            html += `<p style="${styles}">${el.json.componentData.content}</p>`;
                            break;
                        case 'button':
                            html += `<button style="${styles}">${el.json.componentData.content}</button>`;
                            break;
                        case 'image':
                            html += `<img src="${el.json.componentData.src || el.json.componentData.images?.[0]?.src}" alt="${el.json.componentData.alt}" style="${styles}" />`;
                            break;
                        case 'form':
                            html += `<form style="${styles}">${el.json.children.map(child => child.type === 'input' ? `<input type="${child.componentData.type}" placeholder="${child.componentData.label}" style="${Object.entries(child.styles.desktop).map(([k, v]) => `${k}: ${v}`).join(';')}" />` : `<textarea placeholder="${child.componentData.label}" style="${Object.entries(child.styles.desktop).map(([k, v]) => `${k}: ${v}`).join(';')}"></textarea>`).join('')}<button style="background: #f97316; color: #fff; padding: 8px 16px; border-radius: 4px;">Submit</button></form>`;
                            break;
                        case 'button-group':
                            html += `<div style="${styles}">${el.json.children.map(child => `<button style="${Object.entries(child.styles.desktop).map(([k, v]) => `${k}: ${v}`).join(';')}">${child.componentData.content}</button>`).join('')}</div>`;
                            break;
                        case 'gallery':
                        case 'carousel':
                            html += `<div style="${styles}">${el.json.children.map(child => `<img src="${child.componentData.src}" alt="${child.componentData.alt}" style="${Object.entries(child.styles.desktop).map(([k, v]) => `${k}: ${v}`).join(';')}" />`).join('')}</div>`;
                            break;
                        case 'popup':
                            html += `<div style="${styles}">${el.json.componentData.content}${el.json.children.map(child => `<div style="${Object.entries(child.styles.desktop).map(([k, v]) => `${k}: ${v}`).join(';')}">${child.componentData.content || ''}</div>`).join('')}</div>`;
                            break;
                    }
                });
                html += `</section>`;
            });
            html += `</div>`;
            return html;
        };

        const html = generateHTML(pageStructure);
        const blob = new Blob([html], { type: 'text/html' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `landing-page-${Date.now()}.html`;
        a.click();
        URL.revokeObjectURL(url);
        toast.success('Đã xuất HTML thành công!');
    }, [pageStructure]);

    const handlePreview = useCallback(() => {
        const previewData = encodeURIComponent(JSON.stringify({ pageStructure, viewMode }));
        window.open(`/preview?data=${previewData}`, '_blank');
    }, [pageStructure, viewMode]);

    const handleSave = useCallback(() => {
        try {
            const saveData = { pageStructure, viewMode, zoomLevel, savedAt: new Date().toISOString() };
            localStorage.setItem('landing-page-builder', JSON.stringify(saveData));
            toast.success('Đã lưu trang thành công!');
        } catch (error) {
            console.error('Lỗi khi lưu:', error);
            toast.error('Lỗi khi lưu trang. Vui lòng thử lại.');
        }
    }, [pageStructure, viewMode, zoomLevel]);

    useEffect(() => {
        const autoSave = setTimeout(() => {
            if (pageStructure.children.length > 0) {
                const autoSaveData = {
                    pageStructure,
                    viewMode,
                    zoomLevel,
                    autoSavedAt: new Date().toISOString(),
                };
                try {
                    localStorage.setItem('landing-page-builder-autosave', JSON.stringify(autoSaveData));
                    toast.info('Đã tự động lưu!');
                } catch (error) {
                    console.error('Lỗi khi tự động lưu:', error);
                }
            }
        }, 30000);
        return () => clearTimeout(autoSave);
    }, [pageStructure, viewMode, zoomLevel]);

    useHotkeys('ctrl+s', handleSave, [handleSave]);
    useHotkeys('ctrl+z', undo, [undo]);
    useHotkeys('ctrl+shift+z', redo, [redo]);
    useHotkeys('ctrl+shift+c', () => {
        const section = pageStructure.children.find((s) => s.elements.some((el) => el.id === selectedElement?.id) || s.id === selectedElement?.id);
        if (section) handleCopySection(section.id);
    }, [selectedElement, pageStructure, handleCopySection]);
    useHotkeys('ctrl+shift+v', () => handlePasteSection(pageStructure.children.length), [handlePasteSection, pageStructure]);

    return (
        <DndProvider backend={HTML5Backend}>
            <div className="lpb-container">
                <link
                    rel="stylesheet"
                    href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.1/css/all.min.css"
                    crossOrigin="anonymous"
                    referrerPolicy="no-referrer"
                />
                <ToastContainer
                    position="top-right"
                    autoClose={3000}
                    hideProgressBar
                    className="lpb-toast"
                />
                <Toolbar
                    onSave={handleSave}
                    onPreview={handlePreview}
                    onGenerateCode={handleGenerateCode}
                    onExport={handleExportPage}
                    onImport={() => fileInputRef.current?.click()}
                    viewMode={viewMode}
                    onViewModeChange={setViewMode}
                    onUndo={undo}
                    onRedo={redo}
                    canUndo={historyIndex > 0}
                    canRedo={historyIndex < history.length - 1}
                />
                <input
                    type="file"
                    accept=".json"
                    style={{ display: 'none' }}
                    ref={fileInputRef}
                    onChange={handleImportPage}
                />
                <div className="lpb-main-content">
                    <div className={`lpb-sidebar-left ${leftSidebarCollapsed ? 'lpb-sidebar-collapsed' : ''}`}>
                        <ComponentLibrary
                            isCollapsed={leftSidebarCollapsed}
                            onToggle={() => setLeftSidebarCollapsed(!leftSidebarCollapsed)}
                            addSection={addSection}
                            addElement={addElementToSection}
                        />
                    </div>
                    <div className="lpb-canvas-container">
                        <Canvas
                            pageStructure={memoizedPageStructure}
                            selectedElement={selectedElement}
                            onSelectElement={handleSelectElement}
                            quickEditElement={quickEditElement}
                            quickEditPosition={quickEditPosition}
                            onQuickEdit={handleQuickEdit}
                            onCloseQuickEdit={handleCloseQuickEdit}
                            onDuplicateElement={handleDuplicateElement}
                            onDeleteElement={handleDeleteElement}
                            onMoveElement={handleMoveElement}
                            onInlineEdit={handleInlineEdit}
                            viewMode={viewMode}
                            addSection={addSection}
                            addElement={addElementToSection}
                            moveElement={moveElement}
                            ItemTypes={ItemTypes}
                            zoomLevel={zoomLevel}
                            setPageStructure={setPageStructure}
                            updatePosition={updatePosition}
                        />
                    </div>
                    <div className={`lpb-sidebar-right ${rightSidebarCollapsed ? 'lpb-sidebar-collapsed' : ''}`}>
                        <PropertiesPanel
                            selectedElement={selectedElement}
                            onUpdateElement={handleUpdateElement}
                            isCollapsed={rightSidebarCollapsed}
                            onToggle={() => setRightSidebarCollapsed(!rightSidebarCollapsed)}
                        />
                    </div>
                </div>
                <div className="lpb-status-bar">
                    <div className="lpb-status-info">
                        <span>Sections: {pageStructure.children.length}</span>
                        <span>|</span>
                        <span>Elements: {pageStructure.children.reduce((acc, section) => acc + (section.elements?.length || 0), 0)}</span>
                        <span>|</span>
                        <span>Selected: {selectedElement ? selectedElement.json?.type || 'None' : 'None'}</span>
                    </div>
                    <div className="lpb-status-controls">
                        <div className="lpb-view-mode">
                            <span>View:</span>
                            <div className="lpb-view-buttons">
                                {['desktop', 'tablet', 'mobile'].map((mode) => (
                                    <button
                                        key={mode}
                                        onClick={() => setViewMode(mode)}
                                        className={`lpb-view-button ${viewMode === mode ? 'lpb-view-button-active' : ''}`}
                                    >
                                        {mode.charAt(0).toUpperCase() + mode.slice(1)}
                                    </button>
                                ))}
                            </div>
                        </div>
                        <div className="lpb-zoom-controls">
                            <button className="lpb-zoom-button" title="Zoom Out" onClick={() => handleZoom('out')}>
                                <i className="fas fa-minus"></i>
                            </button>
                            <input
                                type="range"
                                min="50"
                                max="200"
                                value={zoomLevel}
                                onChange={(e) => setZoomLevel(Number(e.target.value))}
                                className="lpb-zoom-slider"
                            />
                            <span className="lpb-zoom-value">{zoomLevel}%</span>
                            <button className="lpb-zoom-button" title="Zoom In" onClick={() => handleZoom('in')}>
                                <i className="fas fa-plus"></i>
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </DndProvider>
    );
};

export default CreateLandingPage;
