import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import api from '@landinghub/api';
import { useLocation } from 'react-router-dom';
import '../styles/Dashboard.css';

const Pages = () => {
    const [userData, setUserData] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const location = useLocation();
    useEffect(() => {
        const fetchData = async () => {
            try {
                const res = await api.get('/api/user/info');
                setUserData(res.data);
            } catch (err) {
                console.error('Error fetching user data:', err);
                setError('Không thể tải dữ liệu người dùng.');
            } finally {
                setLoading(false);
            }
        };
        fetchData();
    }, []);

    const isCompact = location.pathname !== '/pages';

    if (loading) return <div>Đang tải...</div>;
    if (error) return <div style={{ color: 'red' }}>{error}</div>;

    return (
        <div className="dashboard-container">
            <Header />
            <div className="dashboard-main">
                <div className="dashboard-content">
                    <div className="pages-content">
                        <h1>Trang Pages</h1>
                        <section>
                            <h2>Nội dung mẫu</h2>
                            <p>Đây là trang Pages với giao diện giống Dashboard.</p>
                            <p>Tên: {userData.name || 'Chưa có thông tin'}</p>
                            <p>Email: {userData.email || 'Chưa có thông tin'}</p>
                            <p>Vai trò: {userData.role || 'Chưa có thông tin'}</p>
                        </section>
                    </div>
                </div>
                <Sidebar role={userData.role} isCompact={isCompact} />
            </div>
        </div>
    );
};

export default React.memo(Pages);
