import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../styles/doashboard-sidebar.css'
const Sidebar = ({ role }) => {
    const [isCompact, setIsCompact] = useState(false);
    const location = useLocation();

    const userMenu = [
        { name: 'Tổng quan', path: '/dashboard', icon: (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6" />
                </svg>
            )},
        { name: 'Quản lý Landing Page', path: '/pages', icon: (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                </svg>
            )},
        { name: 'Quản lý Leads', path: '/leads', icon: (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z" />
                </svg>
            )},
        { name: 'Thanh toán & Premium', path: '/payments', icon: (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h18M7 15h1m4 0h1m-7 4h12a3 3 0 003-3V8a3 3 0 00-3-3H6a3 3 0 00-3 3v8a3 3 0 003 3z" />
                </svg>
            )},
        { name: 'Báo cáo', path: '/reports', icon: (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                </svg>
            )},
        { name: 'Quảng cáo & Marketing', path: '/marketing', icon: (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5.882V19.24a1.76 1.76 0 01-3.417.592l-2.147-6.15M18 13a3 3 0 100-6M5.436 13.683A4.001 4.001 0 017 6h1.832c4.1 0 7.625-1.234 9.168-3v14c-1.543-1.766-5.067-3-9.168-3H7a3.988 3.988 0 01-1.564-.317z" />
                </svg>
            )},
        { name: 'Tương tác Khách hàng', path: '/chatbot', icon: (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z" />
                </svg>
            )},
    ];

    const adminMenu = [
        { name: 'Tổng quan Hệ thống', path: '/dashboard', icon: (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                </svg>
            )},
        { name: 'Quản lý Tài khoản', path: '/users', icon: (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                </svg>
            )},
        { name: 'Kiểm tra Thanh toán', path: '/payments', icon: (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h18M7 15h1m4 0h1m-7 4h12a3 3 0 003-3V8a3 3 0 00-3-3H6a3 3 0 00-3 3v8a3 3 0 003 3z" />
                </svg>
            )},
        { name: 'Quản lý Mẫu Marketplace', path: '/templates', icon: (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
                </svg>
            )},
        { name: 'Báo cáo Hệ thống', path: '/reports', icon: (
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                </svg>
            )},
    ];

    const menu = role === 'admin' ? adminMenu : userMenu;

    return (
        <aside className={`
            ${isCompact ? 'w-16' : 'w-64'} 
            bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900
            backdrop-blur-lg 
            border border-slate-700/50
            rounded-xl 
            transition-all duration-300 ease-in-out
            p-4 
            flex flex-col 
            gap-2
            shadow-2xl
            shadow-slate-900/50
            relative
            overflow-hidden
        `}>
            {/* Background pattern */}
            <div className="absolute inset-0 bg-gradient-to-br from-blue-500/5 via-transparent to-purple-500/5 pointer-events-none" />

            {/* Header */}
            <div className="flex items-center justify-between mb-6 relative z-10">
                {!isCompact && (
                    <div className="flex items-center gap-3">
                        <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-purple-600 rounded-lg flex items-center justify-center">
                            <svg className="w-5 h-5 text-white" fill="currentColor" viewBox="0 0 24 24">
                                <path d="M12 2L2 7l10 5 10-5-10-5zM2 17l10 5 10-5M2 12l10 5 10-5"/>
                            </svg>
                        </div>
                        <span className="text-white font-semibold text-lg">Dashboard</span>
                    </div>
                )}
                <button
                    onClick={() => setIsCompact(!isCompact)}
                    className="
                        p-2
                        rounded-lg
                        bg-slate-700/50
                        hover:bg-slate-600/50
                        text-slate-300
                        hover:text-white
                        transition-all duration-200
                        backdrop-blur-sm
                        border border-slate-600/30
                        hover:border-slate-500/50
                        group
                    "
                >
                    <svg
                        className={`w-4 h-4 transition-transform duration-300 ${isCompact ? 'rotate-180' : ''}`}
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                    >
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                    </svg>
                </button>
            </div>

            {/* Menu Items */}
            <nav className="flex-1 space-y-1 relative z-10">
                {menu.map((item, index) => {
                    const isActive = location.pathname === item.path;
                    return (
                        <Link
                            key={item.name}
                            to={item.path}
                            className={`
                                group relative flex items-center gap-3 px-3 py-2.5 rounded-xl
                                transition-all duration-200 ease-out
                                ${isActive
                                ? 'bg-gradient-to-r from-blue-500/20 to-purple-500/20 text-white border border-blue-400/30 shadow-lg shadow-blue-500/10'
                                : 'text-slate-300 hover:text-white hover:bg-slate-700/50 border border-transparent hover:border-slate-600/30'
                            }
                                ${isCompact ? 'justify-center' : ''}
                                backdrop-blur-sm
                            `}
                            title={isCompact ? item.name : ''}
                        >
                            {/* Active indicator */}
                            {isActive && (
                                <div className="absolute left-0 top-1/2 -translate-y-1/2 w-1 h-6 bg-gradient-to-b from-blue-400 to-purple-500 rounded-r-full" />
                            )}

                            {/* Icon */}
                            <div className={`
                                flex-shrink-0 
                                ${isActive ? 'text-blue-400' : 'text-slate-400 group-hover:text-slate-200'}
                                transition-colors duration-200
                            `}>
                                {item.icon}
                            </div>

                            {/* Text */}
                            {!isCompact && (
                                <span className={`
                                    font-medium text-sm
                                    ${isActive ? 'text-white' : 'text-slate-300 group-hover:text-white'}
                                    transition-colors duration-200
                                `}>
                                    {item.name}
                                </span>
                            )}

                            {/* Hover glow effect */}
                            <div className={`
                                absolute inset-0 rounded-xl 
                                bg-gradient-to-r from-blue-500/0 to-purple-500/0 
                                group-hover:from-blue-500/5 group-hover:to-purple-500/5
                                transition-all duration-300
                                ${isActive ? 'opacity-100' : 'opacity-0 group-hover:opacity-100'}
                            `} />
                        </Link>
                    );
                })}
            </nav>

            {/* Footer */}
            {!isCompact && (
                <div className="pt-4 border-t border-slate-700/50 relative z-10">
                    <div className="flex items-center gap-3 px-3 py-2 text-slate-400 text-sm">
                        <div className="w-6 h-6 bg-gradient-to-r from-green-400 to-blue-500 rounded-full flex items-center justify-center">
                            <div className="w-2 h-2 bg-white rounded-full animate-pulse" />
                        </div>
                        <span>System Online</span>
                    </div>
                </div>
            )}
        </aside>
    );
};

export default React.memo(Sidebar);
