// MarketplaceDetail.js
import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import { UserContext } from '../context/UserContext';
import { Check, X, Eye, Star, AlertTriangle, Download } from 'lucide-react';
import DogLoader from '../components/Loader';
import '../styles/MarketplaceDetail.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

const MarketplaceDetail = () => {
    const { user } = useContext(UserContext);
    const { id: pageId } = useParams(); // Get pageId from URL params
    const navigate = useNavigate();
    const [page, setPage] = useState(null);
    const [loading, setLoading] = useState(true);
    const [actionLoading, setActionLoading] = useState(false);
    const [hasPurchased, setHasPurchased] = useState(false);

    useEffect(() => {
        const fetchPage = async () => {
            setLoading(true);
            try {
                const response = await axios.get(`${API_BASE_URL}/api/marketplace/pages/${pageId}`);
                setPage(response.data.data);
                // Check if user has purchased this page
                if (user) {
                    const token = localStorage.getItem('token');
                    const purchaseResponse = await axios.get(
                        `${API_BASE_URL}/api/payments/check-purchase/${pageId}`,
                        { headers: { Authorization: `Bearer ${token}` } }
                    );
                    setHasPurchased(purchaseResponse.data.hasPurchased);
                }
            } catch (error) {
                console.error('Fetch Page Error:', error);
                toast.error(error.response?.data?.message || 'Kh√¥ng th·ªÉ t·∫£i trang');
                navigate('/marketplace');
            } finally {
                setLoading(false);
            }
        };

        if (pageId) {
            fetchPage();
        } else {
            toast.error('Kh√¥ng t√¨m th·∫•y ID trang');
            navigate('/marketplace');
        }
    }, [pageId, user, navigate]);

    const handlePurchase = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            toast.error('Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ mua h√†ng');
            navigate('/auth');
            return;
        }

        if (!pageId) {
            toast.error('ID trang kh√¥ng h·ª£p l·ªá');
            return;
        }

        setActionLoading(true);
        try {
            const response = await axios.post(
                `${API_BASE_URL}/api/payments/create-transaction`,
                { marketplace_page_id: pageId, payment_method: 'SANDBOX' },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            toast.success('T·∫°o giao d·ªãch th√†nh c√¥ng');
            window.location.href = response.data.data.payment_url;
        } catch (error) {
            console.error('L·ªói mua h√†ng:', error);
            if (error.response?.status === 401) {
                toast.error('Phi√™n ƒëƒÉng nh·∫≠p kh√¥ng h·ª£p l·ªá. Vui l√≤ng ƒëƒÉng nh·∫≠p l·∫°i.');
                navigate('/auth');
            } else if (error.response?.status === 400 && error.response.data.message.includes('ch√≠nh m√¨nh')) {
                toast.error('B·∫°n kh√¥ng th·ªÉ mua landing page c·ªßa ch√≠nh m√¨nh');
            } else {
                toast.error(error.response?.data?.message || 'Kh√¥ng th·ªÉ th·ª±c hi·ªán giao d·ªãch');
            }
        } finally {
            setActionLoading(false);
        }
    };

    const handleDownload = async (format) => {
        const token = localStorage.getItem('token');
        if (!token) {
            toast.error('Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ t·∫£i xu·ªëng');
            navigate('/auth');
            return;
        }

        setActionLoading(true);
        try {
            const endpoint = format === 'html'
                ? `${API_BASE_URL}/api/marketplace/pages/${pageId}/download/html`
                : `${API_BASE_URL}/api/marketplace/pages/${pageId}/download/iuhpage`;
            const response = await axios.get(endpoint, {
                headers: { Authorization: `Bearer ${token}` },
                responseType: 'blob'
            });

            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', `${page.title}.${format === 'html' ? 'zip' : 'iuhpage'}`);
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
            toast.success('T·∫£i xu·ªëng th√†nh c√¥ng');
        } catch (error) {
            console.error('Download Error:', error);
            toast.error(error.response?.data?.message || 'Kh√¥ng th·ªÉ t·∫£i xu·ªëng');
        } finally {
            setActionLoading(false);
        }
    };

    const handleToggleLike = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            toast.error('Vui l√≤ng ƒëƒÉng nh·∫≠p ƒë·ªÉ th√≠ch trang');
            navigate('/auth');
            return;
        }

        setActionLoading(true);
        try {
            const response = await axios.post(
                `${API_BASE_URL}/api/marketplace/pages/${pageId}/like`,
                {},
                { headers: { Authorization: `Bearer ${token}` } }
            );
            setPage(prev => ({
                ...prev,
                likes: response.data.data.likes,
                liked_by: response.data.data.liked ? [...prev.liked_by, user.id] : prev.liked_by.filter(id => id !== user.id)
            }));
            toast.success(response.data.data.liked ? 'ƒê√£ th√≠ch trang' : 'ƒê√£ b·ªè th√≠ch trang');
        } catch (error) {
            console.error('Toggle Like Error:', error);
            toast.error(error.response?.data?.message || 'Kh√¥ng th·ªÉ c·∫≠p nh·∫≠t tr·∫°ng th√°i th√≠ch');
        } finally {
            setActionLoading(false);
        }
    };

    if (loading || !page) {
        return <DogLoader />;
    }

    return (
        <div className="marketplace-detail-container">
            <Header />
            <div className="marketplace-detail-content">
                <div className="page-header" data-aos="fade-down">
                    <h1>{page.title}</h1>
                    {page.is_featured && (
