import React, { useEffect } from 'react';
import { sections } from '../../constants/sections'; // Import sections.js

const SectionPopup = ({ showPopup, setShowPopup, viewMode, handleAddSection }) => {
    useEffect(() => {
        const handleClosePopup = (e) => {
            if (e.key === 'Escape') {
                setShowPopup(false);
            }
        };
        document.addEventListener('keydown', handleClosePopup);
        return () => document.removeEventListener('keydown', handleClosePopup);
    }, [setShowPopup]);

    if (!showPopup) return null;

    return (
        <div
            style={{
                position: 'fixed',
                top: 0,
                left: 0,
                width: '100vw',
                height: '100vh',
                background: 'rgba(0, 0, 0, 0.5)', // Backdrop mờ
                zIndex: 9999,
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
            }}
            onClick={() => setShowPopup(false)}
        >
            <div
                style={{
                    position: 'relative',
                    background: '#ffffff',
                    borderRadius: '12px',
                    boxShadow: '0 4px 16px rgba(0, 0, 0, 0.2)',
                    zIndex: 10000,
                    width: viewMode === 'mobile' ? '90%' : viewMode === 'tablet' ? '80%' : '600px',
                    maxWidth: '90vw',
                    minWidth: '300px',
                    maxHeight: '80vh',
                    overflowY: 'auto',
                    padding: viewMode === 'mobile' ? '16px' : '24px',
                }}
                onClick={(e) => e.stopPropagation()}
            >
                <h2
                    style={{
                        fontSize: viewMode === 'mobile' ? '1.25rem' : '1.5rem',
                        fontWeight: '600',
                        marginBottom: '24px',
                        textAlign: 'center',
                        color: '#1f2937',
                    }}
                >
                    Chọn Section
                </h2>
                {sections.subCategories.map((category) => (
                    <div key={category.id} style={{ marginBottom: '24px' }}>
                        <h3
                            style={{
                                fontSize: viewMode === 'mobile' ? '1rem' : '1.25rem',
                                fontWeight: '500',
                                marginBottom: '12px',
                                color: '#374151',
                            }}
                        >
                            {category.name}
                        </h3>
                        <div
                            style={{
                                display: 'grid',
                                gridTemplateColumns:
                                    viewMode === 'mobile'
                                        ? '1fr'
                                        : viewMode === 'tablet'
                                            ? 'repeat(auto-fill, minmax(200px, 1fr))'
                                            : 'repeat(auto-fill, minmax(250px, 1fr))',
                                gap: '16px',
                            }}
                        >
                            {category.templates.map((template) => (
                                <div
                                    key={template.id}
                                    style={{
                                        border: '1px solid #e5e7eb',
                                        borderRadius: '8px',
                                        padding: '12px',
                                        textAlign: 'center',
                                        cursor: 'pointer',
                                        background: '#ffffff',
                                        transition: 'transform 0.2s, box-shadow 0.2s',
                                        boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
                                    }}
                                    onClick={() => {
                                        handleAddSection(template);
                                        setShowPopup(false);
                                    }}
                                    onMouseEnter={(e) =>
                                        (e.currentTarget.style.transform = 'scale(1.03)')
                                    }
                                    onMouseLeave={(e) => (e.currentTarget.style.transform = 'scale(1)')}
                                >
                                    <img
                                        src={template.previewImage}
                                        alt={template.name}
                                        style={{
                                            width: '100%',
                                            height: '120px',
                                            objectFit: 'cover',
                                            borderRadius: '6px',
                                            marginBottom: '12px',
                                        }}
                                    />
                                    <h4
                                        style={{
                                            fontSize: '1rem',
                                            fontWeight: '500',
                                            margin: '0 0 8px',
                                            color: '#1f2937',
                                        }}
                                    >
                                        {template.name}
                                    </h4>
                                    <p
                                        style={{
                                            fontSize: '0.875rem',
                                            color: '#6b7280',
                                            margin: '0',
                                            lineHeight: '1.4',
                                        }}
                                    >
                                        {template.description}
                                    </p>
                                    <button
                                        style={{
                                            marginTop: '12px',
                                            padding: '8px 16px',
                                            background: '#2563eb',
                                            color: '#ffffff',
                                            border: 'none',
                                            borderRadius: '6px',
                                            cursor: 'pointer',
                                            fontSize: '0.875rem',
                                            transition: 'background 0.2s',
                                        }}
                                        onMouseEnter={(e) =>
                                            (e.currentTarget.style.background = '#1d4ed8')
                                        }
                                        onMouseLeave={(e) =>
                                            (e.currentTarget.style.background = '#2563eb')
                                        }
                                    >
                                        Thêm
                                    </button>
                                </div>
                            ))}
                        </div>
                    </div>
                ))}
                <button
                    onClick={() => setShowPopup(false)}
                    style={{
                        position: 'absolute',
                        top: '12px',
                        right: '12px',
                        padding: '6px 12px',
                        background: '#6b7280',
                        color: '#ffffff',
                        border: 'none',
                        borderRadius: '6px',
                        cursor: 'pointer',
                        fontSize: '0.875rem',
                    }}
                >
                    Đóng
                </button>
            </div>
        </div>
    );
};

export default SectionPopup;
