import { parse } from 'node-html-parser';

export const CSS_VARIABLES = {
    '--primary': '#6366f1',
    '--primary-dark': '#4f46e5',
    '--primary-light': '#a5b4fc',
    '--secondary': '#f1f5f9',
    '--accent': '#06b6d4',
    '--success': '#10b981',
    '--warning': '#f59e0b',
    '--danger': '#ef4444',
    '--dark': '#1e293b',
    '--light': '#f8fafc',
    '--white': '#ffffff',
    '--text-primary': '#334155',
    '--text-secondary': '#64748b',
    '--text-light': '#94a3b8',
    '--text-inverse': '#ffffff',
    '--border-color': '#e2e8f0',
    '--border-radius': '8px',
    '--border-radius-lg': '12px',
    '--border-radius-xl': '16px',
    '--shadow-sm': '0 1px 2px 0 rgba(0, 0, 0, 0.05)',
    '--shadow': '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
    '--shadow-lg': '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
    '--shadow-xl': '0 20px 25px -5px rgba(0, 0, 0, 0.1)',
    '--canvas-desktop': '1200px',
};

export const parseStyles = (styleString) => {
    if (!styleString) return {};
    return styleString.split(';').reduce((acc, style) => {
        const [key, value] = style.split(':').map(s => s.trim());
        if (key && value) {
            const camelKey = key.replace(/-([a-z])/g, (g) => g[1].toUpperCase());
            acc[camelKey] = value;
        }
        return acc;
    }, {});
};

export const parseHTMLToPageData = (html) => {
    try {
        console.log('Parsing HTML from S3:', html);

        // Phân tích HTML sử dụng html-react-parser
        const root = parse(html, { blockTextElements: { style: true } });
        const pageContainer = root.querySelector ? root.querySelector('.page-container') : null;

        // Kiểm tra .page-container
        if (!pageContainer) {
            console.warn('No .page-container found in HTML');
            toast.warn('HTML không chứa .page-container. Khởi tạo trang trống.');
            return {
                canvas: {
                    width: parseInt(CSS_VARIABLES['--canvas-desktop']) || 1200,
                    background: CSS_VARIABLES['--white'] || '#ffffff',
                },
                elements: [],
            };
        }

        // Lấy dữ liệu canvas
        const canvasData = {
            width: parseFloat(pageContainer.style?.width) || parseInt(CSS_VARIABLES['--canvas-desktop']) || 1200,
            background: pageContainer.style?.background || CSS_VARIABLES['--white'] || '#ffffff',
        };

        const elements = [];
        // Duyệt qua các section và canvas elements
        pageContainer.querySelectorAll('.ladi-section, .lpb-canvas-element').forEach((elementNode, index) => {
            const type = elementNode.getAttribute('data-type') || 'section';
            const id = elementNode.getAttribute('id') || `element-${index}-${Date.now()}`;
            const styles = parseStyles(elementNode.getAttribute('style') || '');
            const componentData = parseDataAttribute(elementNode, 'data-component');

            // Lấy vị trí cho từng viewMode
            const position = {
                desktop: parseDataAttribute(elementNode, 'data-position-desktop') || {
                    x: parseFloat(styles.left) || 0,
                    y: parseFloat(styles.top) || 0,
                },
                tablet: parseDataAttribute(elementNode, 'data-position-tablet') || {
                    x: parseFloat(styles.left) || 0,
                    y: parseFloat(styles.top) || 0,
                },
                mobile: parseDataAttribute(elementNode, 'data-position-mobile') || {
                    x: parseFloat(styles.left) || 0,
                    y: parseFloat(styles.top) || 0,
                },
            };

            // Lấy kích thước
            const size = {
                width: parseFloat(styles.width) || (type === 'section' ? 420 : 200),
                height: parseFloat(styles.height) || (type === 'section' ? 400 : 50),
            };

            const children = [];
            // Xử lý children trong section hoặc container
            if (['section', 'container', 'box', 'form', 'grid', 'carousel', 'tabs', 'accordion', 'table', 'survey', 'collection', 'popup'].includes(type)) {
                const containerNode = elementNode.querySelector('.ladi-container') || elementNode;
                if (containerNode) {
                    containerNode.querySelectorAll('.lpb-child-element').forEach((child, childIndex) => {
                        const childType = child.getAttribute('data-type') || child.tagName.toLowerCase();
                        const childId = `child-${index}-${childIndex}-${Date.now()}`;
                        const childStyles = parseStyles(child.getAttribute('style') || '');
                        const childComponentData = parseDataAttribute(child, 'data-component');
                        const childPosition = {
                            desktop: parseDataAttribute(child, 'data-position-desktop') || {
                                x: parseFloat(childStyles.left) || 0,
                                y: parseFloat(childStyles.top) || 0,
                            },
                            tablet: parseDataAttribute(child, 'data-position-tablet') || {
                                x: parseFloat(childStyles.left) || 0,
                                y: parseFloat(childStyles.top) || 0,
                            },
                            mobile: parseDataAttribute(child, 'data-position-mobile') || {
                                x: parseFloat(childStyles.left) || 0,
                                y: parseFloat(childStyles.top) || 0,
                            },
                        };
                        const childSize = {
                            width: parseFloat(childStyles.width) || 200,
                            height: parseFloat(childStyles.height) || 50,
                        };

                        switch (childType) {
                            case 'heading':
                            case 'h1':
                            case 'h2':
                            case 'h3':
                            case 'h4':
                            case 'h5':
                            case 'h6':
                                children.push({
                                    id: childId,
                                    type: 'heading',
                                    componentData: {
                                        content: child.textContent.trim() || 'Tiêu đề',
                                        level: childType,
                                        ...childComponentData,
                                    },
                                    styles: {
                                        fontSize: childStyles.fontSize || '1.5rem',
                                        color: childStyles.color || CSS_VARIABLES['--text-primary'] || '#1f2937',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'paragraph':
                            case 'p':
                                children.push({
                                    id: childId,
                                    type: 'paragraph',
                                    componentData: {
                                        content: child.textContent.trim() || 'Đoạn văn',
                                        ...childComponentData,
                                    },
                                    styles: {
                                        fontSize: childStyles.fontSize || '1rem',
                                        color: childStyles.color || CSS_VARIABLES['--text-secondary'] || '#4b5563',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'button':
                                children.push({
                                    id: childId,
                                    type: 'button',
                                    componentData: {
                                        content: child.textContent.trim() || 'Nút',
                                        ...childComponentData,
                                    },
                                    styles: {
                                        background: childStyles.background || CSS_VARIABLES['--primary'] || '#2563eb',
                                        color: childStyles.color || CSS_VARIABLES['--text-inverse'] || '#ffffff',
                                        borderRadius: childStyles.borderRadius || CSS_VARIABLES['--border-radius'] || '8px',
                                        padding: childStyles.padding || '8px 16px',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'image':
                            case 'img':
                                children.push({
                                    id: childId,
                                    type: 'image',
                                    componentData: {
                                        src: child.getAttribute('src') || '',
                                        alt: child.getAttribute('alt') || 'Hình ảnh',
                                        ...childComponentData,
                                    },
                                    styles: {
                                        maxWidth: childStyles.maxWidth || '100%',
                                        borderRadius: childStyles.borderRadius || '0',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'video':
                                const source = child.querySelector('source');
                                children.push({
                                    id: childId,
                                    type: 'video',
                                    componentData: {
                                        src: source?.getAttribute('src') || '',
                                        ...childComponentData,
                                    },
                                    styles: {
                                        maxWidth: childStyles.maxWidth || '100%',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'icon':
                                children.push({
                                    id: childId,
                                    type: 'icon',
                                    componentData: {
                                        icon: child.className.match(/fa[sr] fa-[^\s]+/)?.[0] || 'fas fa-star',
                                        ...childComponentData,
                                    },
                                    styles: {
                                        fontSize: childStyles.fontSize || '2rem',
                                        color: childStyles.color || '#1f2937',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            case 'divider':
                                children.push({
                                    id: childId,
                                    type: 'divider',
                                    componentData: {
                                        ...childComponentData,
                                    },
                                    styles: {
                                        background: childStyles.background || '#e5e7eb',
                                        height: childStyles.height || '2px',
                                        width: childStyles.width || '100%',
                                        ...childStyles,
                                    },
                                    position: childPosition,
