                                    const popupChildId = `popup-child-${index}-${childIndex}-${popupChildIndex}-${Date.now()}`;
                                    const popupChildStyles = parseStyles(popupChild.getAttribute('style') || '');
                                    const popupChildComponentData = parseDataAttribute(popupChild, 'data-component');
                                    const popupChildPosition = {
                                        desktop: parseDataAttribute(popupChild, 'data-position-desktop') || {
                                            x: parseFloat(popupChildStyles.left) || 0,
                                            y: parseFloat(popupChildStyles.top) || 0,
                                        },
                                        tablet: parseDataAttribute(popupChild, 'data-position-tablet') || {
                                            x: parseFloat(popupChildStyles.left) || 0,
                                            y: parseFloat(popupChildStyles.top) || 0,
                                        },
                                        mobile: parseDataAttribute(popupChild, 'data-position-mobile') || {
                                            x: parseFloat(popupChildStyles.left) || 0,
                                            y: parseFloat(popupChildStyles.top) || 0,
                                        },
                                    };
                                    const popupChildSize = {
                                        width: parseFloat(popupChildStyles.width) || 200,
                                        height: parseFloat(popupChildStyles.height) || 50,
                                    };
                                    popupChildren.push({
                                        id: popupChildId,
                                        type: popupChildType,
                                        componentData: {
                                            content: popupChild.textContent.trim() || '',
                                            ...popupChildComponentData,
                                        },
                                        styles: popupChildStyles,
                                        position: popupChildPosition,
                                        size: popupChildSize,
                                    });
                                });
                                children.push({
                                    id: childId,
                                    type: childType,
                                    componentData: {
                                        title: child.querySelector('h3')?.textContent.trim() || '',
                                        content: child.querySelector('p')?.textContent.trim() || '',
                                        buttonText: child.querySelector('button')?.textContent.trim() || '',
                                        ...childComponentData,
                                    },
                                    styles: {
                                        display: childStyles.display || 'none',
                                        background: childStyles.background || '#ffffff',
                                        borderRadius: childStyles.borderRadius || '12px',
                                        ...childStyles,
                                    },
                                    position: childPosition,
                                    size: childSize,
                                    children: popupChildren,
                                });
                                break;
                            case 'html':
                                children.push({
                                    id: childId,
                                    type: 'html',
                                    componentData: {
                                        content: child.innerHTML || '<p>Custom HTML content</p>',
                                        ...childComponentData,
                                    },
                                    styles: childStyles,
                                    position: childPosition,
                                    size: childSize,
                                });
                                break;
                            default:
                                console.warn(`Unknown child type: ${childType}`, child);
                                break;
                        }
                    });
                }
            }

            // Tạo element chính
            elements.push({
                id,
                type,
                componentData: {
                    ...componentData,
                    backgroundColor: styles.backgroundColor || componentData.backgroundColor || '#ffffff',
                },
                position,
                size,
                styles: {
                    ...styles,
                    borderRadius: styles.borderRadius || CSS_VARIABLES['--border-radius'] || '8px',
                    boxShadow: styles.boxShadow || CSS_VARIABLES['--shadow'] || 'none',
                    zIndex: styles.zIndex || (type === 'section' ? 1 : 10),
                },
                children,
            });
        });

        const pageData = { canvas: canvasData, elements };
        console.log('Parsed pageData:', JSON.stringify(pageData, null, 2));
        return pageData;
    } catch (error) {
        console.error('Error parsing HTML:', error);
        toast.error('Lỗi khi phân tích nội dung trang: ' + error.message);
        return {
            canvas: {
                width: parseInt(CSS_VARIABLES['--canvas-desktop']) || 1200,
                background: CSS_VARIABLES['--white'] || '#ffffff',
            },
            elements: [],
        };
    }
};

// web/src/utils/pageUtils.js

// Hàm render nội dung của element (section, popup, child, v.v.)
const renderElementContent = (element) => {
    if (element.type === 'popup' || element.type === 'modal') {
        return renderPopupContent(element);
    }
    // Xử lý các loại element khác (section, button, image, v.v.)
    const childrenHTML = element.children?.map(child => renderElement(child)).join('') || '';
    return childrenHTML || element.componentData?.content || '';
};

// Hàm render nội dung popup
const renderPopupContent = (popup) => {
    const childrenHTML = popup.children?.map(child => renderElement(child)).join('') || '';
    return childrenHTML || popup.componentData?.content || '';
};

// Hàm chuyển styles sang CSS
const convertStylesToCSS = (styles) => {
    return Object.entries(styles || {})
        .map(([key, value]) => `${key.replace(/[A-Z]/g, m => `-${m.toLowerCase()}`)}:${value}`)
        .join(';');
};

// Hàm tạo CSS toàn cục
const generateCSS = (pageData) => {
    // Tạo CSS cho canvas
    const canvasStyles = convertStylesToCSS(pageData.canvas?.styles || {});
    let css = `
    #lpb-canvas {
      position: relative;
      width: ${pageData.canvas?.width || '100%'};
      height: ${pageData.canvas?.height || 'auto'};
      ${canvasStyles}
    }
  `;

    // Tạo CSS cho từng element
    pageData.elements.forEach(el => {
        const styles = convertStylesToCSS(el.styles);
        css += `
      #${el.id} {
        position: ${el.type === 'popup' || el.type === 'modal' ? 'fixed' : 'absolute'};
        top: ${el.position?.desktop?.y || 0}px;
        left: ${el.position?.desktop?.x || 0}px;
        width: ${el.size?.width || 'auto'}px;
        height: ${el.size?.height || 'auto'}px;
        ${styles}
      }
    `;
        // CSS cho children
        el.children?.forEach(child => {
            const childStyles = convertStylesToCSS(child.styles);
            css += `
        #${child.id} {
          position: absolute;
          top: ${child.position?.desktop?.y || 0}px;
          left: ${child.position?.desktop?.x || 0}px;
          width: ${child.size?.width || 'auto'}px;
          height: ${child.size?.height || 'auto'}px;
          ${childStyles}
        }
      `;
        });
    });

    return css;
};

// Hàm render một element
const renderElement = (element) => {
    if (element.visible === false && (!element.type === 'popup' || !element.type === 'modal')) {
        return '';
    }
    const styles = convertStylesToCSS(element.styles);
    return `
    <div 
      id="${element.id}"
      class="lpb-element lpb-${element.type}"
      style="${styles}"
    >
      ${renderElementContent(element)}
    </div>
  `;
};

// Hàm tạo script xử lý sự kiện
const generateEventScript = (pageData) => {
    // Extract tất cả events từ elements và children
    const events = [];

    pageData.elements.forEach(el => {
        if (el.componentData?.events) {
            events.push({
                elementId: el.id,
                events: el.componentData.events
            });
        }
        el.children?.forEach(child => {
            if (child.componentData?.events) {
                events.push({
                    elementId: child.id,
                    events: child.componentData.events
                });
            }
        });
    });

    return `
    // Landing Page Builder Runtime
    window.LPB = {
      // Cache cho API calls
      cache: new Map(),
      
      // Open popup
      openPopup: function(popupId, data) {
        const popup = document.getElementById(popupId);
        if (!popup) {
          console.error('Popup not found:', popupId);
          return;
        }
        
        // Update content if data provided
        if (data) {
          this.updatePopupContent(popupId, data);
        }
        
        popup.classList.add('active');
        document.body.style.overflow = 'hidden';
      },
      
      // Close popup
      closePopup: function(popupId) {
        const popup = document.getElementById(popupId);
        if (popup) {
          popup.classList.remove('active');
          document.body.style.overflow = '';
        }
      },
      
      // API call với cache
      async fetchAPI(endpoint, options = {}) {
        const cacheKey = endpoint + JSON.stringify(options.params || {});
        
        // Check cache
        if (this.cache.has(cacheKey)) {
          const cached = this.cache.get(cacheKey);
          const now = Date.now();
          const TTL = 5 * 60 * 1000; // 5 phút
          
          if (now - cached.timestamp < TTL) {
            return cached.data;
          }
        }
        
        // Fetch mới
        try {
          const response = await fetch(endpoint, {
            method: options.method || 'GET',
            headers: {
              'Content-Type': 'application/json',
              ...options.headers
            },
            body: options.data ? JSON.stringify(options.data) : undefined
          });
          
          if (!response.ok) {
            throw new Error(\`HTTP error! status: \${response.status}\`);
          }
          
          const data = await response.json();
          
          // Cache result
          this.cache.set(cacheKey, {
            data: data,
            timestamp: Date.now()
          });
          
          return data;
        } catch (error) {
          console.error('API Error:', error);
          throw error;
        }
      },
      
      // Update popup content
      updatePopupContent: function(popupId, data) {
        const popup = document.getElementById(popupId);
        if (!popup) return;
        
        const contentEl = popup.querySelector('.lpb-popup-content');
        if (contentEl && data.html) {
          contentEl.innerHTML = data.html;
        }
      },
      
      // Main event handler
      async handleEvent(elementId, eventType, config) {
        try {
          switch(config.type) {
            case 'openPopup':
              this.openPopup(config.params.popupId, config.data);
              break;
              
            case 'triggerApi':
              const data = await this.fetchAPI(
                config.params.apiConfig.endpoint,
                config.params.apiConfig
              );
              
              // Update element với data
              if (config.params.updateTarget) {
                const target = document.getElementById(config.params.updateTarget);
                if (target && data.html) {
                  target.innerHTML = data.html;
                }
              }
              break;
              
            case 'both':
              // Open popup với loading
              this.openPopup(config.params.popupId, { html: '<div>Loading...</div>' });
              
              // Fetch API
              const apiData = await this.fetchAPI(
                config.params.apiConfig.endpoint,
                config.params.apiConfig
              );
              
              // Update popup content
              this.updatePopupContent(config.params.popupId, apiData);
              break;
              
            case 'navigate':
              if (config.params.newTab) {
                window.open(config.params.url, '_blank');
              } else {
                window.location.href = config.params.url;
              }
              break;
              
            case 'scrollToSection':
              const section = document.getElementById(config.params.sectionId);
              if (section) {
                section.scrollIntoView({ 
                  behavior: config.params.smooth ? 'smooth' : 'auto' 
                });
              } else {
                console.warn('Section not found:', config.params.sectionId);
              }
              break;
              
            default:
              console.warn('Unknown event type:', config.type);
          }
        } catch (error) {
          console.error('Event handling error:', error);
        }
      }
    };
    
    // Auto-bind events sau khi DOM load
    document.addEventListener('DOMContentLoaded', function() {
      // Events config từ pageData
      const eventsConfig = ${JSON.stringify(events)};
      
      eventsConfig.forEach(config => {
        const element = document.getElementById(config.elementId);
        if (!element) {
          console.warn('Element not found:', config.elementId);
          return;
        }
        
        // Bind onClick
        if (config.events.onClick && config.events.onClick.type !== 'none') {
          element.addEventListener('click', function(e) {
            e.preventDefault();
            e.stopPropagation();
            window.LPB.handleEvent(config.elementId, 'onClick', config.events.onClick);
          });
          
          element.style.cursor = 'pointer';
        }
        
        // Bind onHover (prefetch)
        if (config.events.onClick?.params?.apiConfig?.preload) {
          element.addEventListener('mouseenter', function() {
            window.LPB.fetchAPI(
              config.events.onClick.params.apiConfig.endpoint,
              config.events.onClick.params.apiConfig
            ).catch(() => {}); // Silent fail
          });
        }
      });
    });
  `;
};

// Hàm render HTML tĩnh
export const renderStaticHTML = (pageData) => {
    // Render HTML cho elements (trừ popup/modal)
    const elementsHTML = pageData.elements
        .filter(el => el.type !== 'popup' && el.type !== 'modal')
        .map(el => {
            if (el.visible === false) return '';
            return renderElement(el);
        })
        .join('');

    // Render HTML cho popups
    const popups = pageData.elements.filter(el => el.type === 'popup' || el.type === 'modal');
    const popupsHTML = popups.map(popup => {
        const isVisible = pageData.visiblePopups?.includes(popup.id) || false;
        return `
      <div 
        id="${popup.id}" 
        class="lpb-popup ${isVisible ? 'active' : ''}" 
        style="display:${isVisible ? 'block' : 'none'}; position:fixed; top:50%; left:50%; transform:translate(-50%,-50%); z-index:9999;"
      >
        <div class="lpb-popup-overlay" onclick="LPB.closePopup('${popup.id}')"></div>
        <div class="lpb-popup-content">
          ${renderPopupContent(popup)}
          <button onclick="LPB.closePopup('${popup.id}')">✕</button>
        </div>
      </div>
    `;
    }).join('');

    // Tạo event runtime script
    const eventScript = generateEventScript(pageData);

    // Combine tất cả
    return `
<!DOCTYPE html>
<html lang="vi">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Landing Page</title>
  <style>
    ${generateCSS(pageData)}
    
    /* Popup styles */
    .lpb-popup { display: none; }
    .lpb-popup.active { display: block; }
    .lpb-popup-overlay { 
      position: fixed; 
      top: 0; 
      left: 0; 
      right: 0; 
      bottom: 0; 
      background: rgba(0,0,0,0.5); 
      z-index: -1; 
    }
    .lpb-popup-content {
      background: white;
      padding: 30px;
      border-radius: 8px;
      position: relative;
      max-width: 600px;
      max-height: 80vh;
      overflow-y: auto;
    }
  </style>
</head>
<body>
  <div id="lpb-canvas">
    ${elementsHTML}
  </div>
  
  <!-- All popups -->
  ${popupsHTML}
  
  <!-- Event Runtime -->
  <script>
    ${eventScript}
  </script>
</body>
</html>
  `;
};

// Note: Hàm parseDataAttribute không có trong mã gốc, giả sử nó được định nghĩa ở nơi khác hoặc bỏ qua nếu không cần.
function parseDataAttribute(node, attr) {
    // Giả sử hàm này tồn tại ở mã gốc, nếu không thì cần định nghĩa hoặc điều chỉnh.
    return JSON.parse(node.getAttribute(attr) || '{}');
}
