import React, { useRef, useMemo, useCallback, useState } from 'react';
import { useDrop, useDrag } from 'react-dnd';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import QuickEditToolkit from './QuickEditToolkit';
import '../../styles/CreateLanding.css';

const ItemTypes = {
    SECTION: 'section',
    ELEMENT: 'element',
    EXISTING_ELEMENT: 'existingElement',
};

const Element = React.memo(
    ({
         component,
         index,
         isSelected,
         onSelectElement,
         handleElementHover,
         onDuplicateElement,
         onDeleteElement,
         onMoveElement,
         updatePosition,
         canvasRef,
         sectionId,
         onInlineEdit,
         pageStructure,
         zoomLevel,
         viewMode,
     }) => {
        const { type, componentData = {}, styles = { desktop: {}, mobile: {} }, children = [] } = component.json || {};
        const containerStyle = {
            ...styles[viewMode] || {},
            position: 'absolute',
            left: styles[viewMode]?.left || 0,
            top: styles[viewMode]?.top || 0,
            maxWidth: '100%',
            boxSizing: 'border-box',
            transform: isSelected ? 'translate(-2px, -2px)' : 'none',
            width: styles[viewMode]?.width || 'auto',
            height: styles[viewMode]?.height || 'auto',
        };

        const [{ isDragging }, drag] = useDrag(() => ({
            type: ItemTypes.EXISTING_ELEMENT,
            item: { id: component.id, sectionId, json: component.json },
            end: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (clientOffset && canvasRef.current && sectionId && monitor.didDrop()) {
                    const sectionRect = document.getElementById(sectionId)?.getBoundingClientRect();
                    if (!sectionRect) return;

                    const newX = Math.round((clientOffset.x - sectionRect.left) / (zoomLevel / 100) / 10) * 10;
                    const newY = Math.round((clientOffset.y - sectionRect.top) / (zoomLevel / 100) / 10) * 10;

                    const section = pageStructure?.children?.find((s) => s.id === sectionId);
                    let adjustedY = newY;
                    if (section) {
                        section.elements.forEach((el) => {
                            if (el.id !== component.id) {
                                const elX = el.json.styles?.[viewMode]?.left || 0;
                                const elY = el.json.styles?.[viewMode]?.top || 0;
                                if (Math.abs(newX - elX) < 50 && Math.abs(newY - elY) < 50) {
                                    adjustedY = elY + 20;
                                }
                            }
                        });
                    }
                    updatePosition(component.id, sectionId, {
                        desktop: { left: newX, top: adjustedY },
                        mobile: { left: Math.round(newX / 2), top: Math.round(adjustedY / 2) },
                    });
                }
            },
            collect: (monitor) => ({
                isDragging: !!monitor.isDragging(),
            }),
        }));

        const handleMouseDown = (e) => {
            e.stopPropagation();
            onSelectElement(component);
        };

        const handleInlineEdit = (e) => {
            if (onInlineEdit && e.target.contentEditable === 'true') {
                onInlineEdit(component.id, sectionId, e.target.innerText);
            }
        };

        const renderContent = () => {
            const renderChildren = () => {
                return children.map((child, idx) => (
                    <Element
                        key={child.id}
                        component={{ id: child.id, json: child }}
                        index={idx}
                        isSelected={isSelected && component.id === child.id}
                        onSelectElement={onSelectElement}
                        handleElementHover={handleElementHover}
                        onDuplicateElement={onDuplicateElement}
                        onDeleteElement={onDeleteElement}
                        onMoveElement={onMoveElement}
                        updatePosition={updatePosition}
                        canvasRef={canvasRef}
                        sectionId={sectionId}
                        onInlineEdit={onInlineEdit}
                        pageStructure={pageStructure}
                        zoomLevel={zoomLevel}
                        viewMode={viewMode}
                    />
                ));
            };

            switch (type) {
                case 'form':
                    return (
                        <form onSubmit={(e) => e.preventDefault()} style={{ ...styles[viewMode] }}>
                            {renderChildren()}
                            <button
                                type="submit"
                                style={{ ...styles[viewMode]?.button, background: '#2563eb', color: '#fff', padding: '8px 16px', borderRadius: '6px' }}
                                onMouseDown={(e) => e.stopPropagation()}
                            >
                                Submit
                            </button>
                        </form>
                    );
                case 'button-group':
                    return (
                        <div style={{ ...styles[viewMode] }}>
                            {renderChildren()}
                        </div>
                    );
                case 'gallery':
                case 'carousel':
                    return (
                        <div style={{ ...styles[viewMode], display: type === 'carousel' ? 'flex' : 'flex', flexWrap: type === 'gallery' ? 'wrap' : 'nowrap', gap: '10px', overflowX: type === 'carousel' ? 'auto' : 'visible' }}>
                            {renderChildren()}
                        </div>
                    );
                case 'heading':
                    return (
                        <h2
                            style={{ ...styles[viewMode], fontSize: componentData.fontSize || '1.5rem' }}
                            contentEditable
                            suppressContentEditableWarning
                            onBlur={handleInlineEdit}
                            onMouseDown={(e) => e.stopPropagation()}
                        >
                            {componentData.content || 'Tiêu đề'}
                        </h2>
                    );
                case 'paragraph':
                    return (
                        <p
                            style={{ ...styles[viewMode], fontSize: componentData.fontSize || '1rem' }}
                            contentEditable
                            suppressContentEditableWarning
                            onBlur={handleInlineEdit}
                            onMouseDown={(e) => e.stopPropagation()}
                        >
                            {componentData.content || 'Đoạn văn'}
                        </p>
                    );
                case 'button':
                    return (
                        <button
                            style={{ ...styles[viewMode], padding: '8px 16px', borderRadius: '6px', background: componentData.background || '#2563eb', color: componentData.color || '#fff' }}
                            contentEditable
                            suppressContentEditableWarning
                            onBlur={handleInlineEdit}
                            onMouseDown={(e) => e.stopPropagation()}
                        >
                            {componentData.content || 'Nút'}
                        </button>
                    );
                case 'image':
                    return (
                        <img
                            src={componentData.src || componentData.images?.[0]?.src || 'https://via.placeholder.com/150'}
                            alt={componentData.alt || 'Image'}
                            style={{ ...styles[viewMode], maxWidth: '100%', height: 'auto' }}
                        />
                    );
                case 'input':
                    return (
                        <input
                            type={componentData.type || 'text'}
                            placeholder={componentData.label || 'Input'}
                            style={{ ...styles[viewMode], width: '100%', padding: '8px', borderRadius: '6px' }}
                            onMouseDown={(e) => e.stopPropagation()}
                        />
                    );
                case 'textarea':
                    return (
                        <textarea
                            placeholder={componentData.label || 'Textarea'}
                            style={{ ...styles[viewMode], width: '100%', padding: '8px', borderRadius: '6px' }}
                            onMouseDown={(e) => e.stopPropagation()}
                        />
                    );
                case 'popup':
                    return (
                        <div style={{ ...styles[viewMode] }}>
                            <p style={{ margin: 0 }}>{componentData.content || 'Popup nội dung'}</p>
                            {renderChildren()}
                        </div>
                    );
                default:
                    return (
                        <div style={{ ...styles[viewMode] }}>
                            {componentData.title || componentData.content || 'Unknown'}
                            {renderChildren()}
                        </div>
                    );
            }
        };

        return (
            <div
                ref={drag}
                data-id={component.id}
                className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${isDragging ? 'lpb-canvas-element-dragging' : ''}`}
                onMouseEnter={(e) => handleElementHover(e, component)}
                onMouseDown={handleMouseDown}
                style={containerStyle}
            >
                {renderContent()}
            </div>
        );
    }
);

const Section = React.memo(
    ({
         section,
         index,
         selectedElement,
         onSelectElement,
         handleElementHover,
         onDuplicateElement,
         onDeleteElement,
         onMoveElement,
         updatePosition,
         addElement,
         canvasRef,
         moveElement,
         onInlineEdit,
         onSelectSection,
         viewMode,
         pageStructure,
         zoomLevel,
     }) => {
        const [{ isOver }, drop] = useDrop(() => ({
            accept: [ItemTypes.ELEMENT, ItemTypes.EXISTING_ELEMENT],
            hover: (item, monitor) => {
                if (!monitor.isOver({ shallow: true })) return;
                const clientOffset = monitor.getClientOffset();
                if (clientOffset && canvasRef.current) {
                    const sectionRect = document.getElementById(section.id)?.getBoundingClientRect();
                    if (!sectionRect) return;
                    item.dropPosition = {
                        left: Math.round((clientOffset.x - sectionRect.left) / (zoomLevel / 100) / 10) * 10,
                        top: Math.round((clientOffset.y - sectionRect.top) / (zoomLevel / 100) / 10) * 10,
                    };
                }
            },
            drop: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) {
                    toast.error('Không thể xác định vị trí thả. Vui lòng thử lại!');
                    return;
                }
                const sectionRect = document.getElementById(section.id)?.getBoundingClientRect();
                if (!sectionRect) {
                    toast.error('Không tìm thấy section!');
                    return;
                }

                let position = {
                    left: Math.round((clientOffset.x - sectionRect.left) / (zoomLevel / 100) / 10) * 10,
                    top: Math.round((clientOffset.y - sectionRect.top) / (zoomLevel / 100) / 10) * 10,
                };

                const sectionElements = section.elements || [];
                sectionElements.forEach((el) => {
                    if (el.id !== (item.id || item.json?.id)) {
                        const elLeft = el.json.styles?.[viewMode]?.left || 0;
                        const elTop = el.json.styles?.[viewMode]?.top || 0;
                        if (Math.abs(position.left - elLeft) < 50 && Math.abs(position.top - elTop) < 50) {
                            position.top = elTop + 20;
                        }
                    }
                });

                if (item.json) {
                    addElement(section.id, {
                        id: item.id,
                        json: {
                            ...item.json,
                            styles: {
                                desktop: { ...item.json.styles.desktop, left: position.left, top: position.top },
                                mobile: { ...item.json.styles.mobile, left: Math.round(position.left / 2), top: Math.round(position.top / 2) },
                            },
                        },
                    }, section.elements.length);
                    toast.success('Đã thêm phần tử vào section!');
                } else if (item.id) {
                    updatePosition(item.id, section.id, {
                        desktop: { left: position.left, top: position.top },
                        mobile: { left: Math.round(position.left / 2), top: Math.round(position.top / 2) },
                    });
                    moveElement(section.id, item.id, section.elements.length);
                    toast.success('Đã di chuyển phần tử!');
                } else {
                    toast.error('Dữ liệu thả không hợp lệ!');
                }
            },
            collect: (monitor) => ({
                isOver: !!monitor.isOver(),
            }),
        }));

        const handleSectionClick = (e) => {
            e.stopPropagation();
            onSelectSection(section);
        };

        return (
            <div
                ref={drop}
                id={section.id}
                className={`lpb-canvas-section ${isOver ? 'lpb-canvas-section-over' : ''} ${selectedElement?.id === section.id ? 'lpb-canvas-section-selected' : ''}`}
                style={{
                    ...section.json.styles?.[viewMode] || {},
                    position: 'relative',
                    margin: '0 auto 20px auto',
                    maxWidth: viewMode === 'mobile' ? '100%' : viewMode === 'tablet' ? '768px' : '1200px',
                }}
                onMouseDown={handleSectionClick}
            >
                <h3 style={{ margin: 0, fontSize: '1rem', color: '#1f2937' }}>{section.json.componentData?.title || 'Section'}</h3>
                <div className="lpb-canvas-section-content">
                    {(section.elements || []).map((element, idx) => (
                        <Element
                            key={element.id}
                            component={element}
                            index={idx}
                            isSelected={selectedElement?.id === element.id}
                            onSelectElement={onSelectElement}
                            handleElementHover={handleElementHover}
                            onDuplicateElement={onDuplicateElement}
                            onDeleteElement={onDeleteElement}
                            onMoveElement={onMoveElement}
                            updatePosition={updatePosition}
                            canvasRef={canvasRef}
                            sectionId={section.id}
                            onInlineEdit={onInlineEdit}
                            pageStructure={pageStructure}
                            zoomLevel={zoomLevel}
                            viewMode={viewMode}
                        />
                    ))}
                </div>
            </div>
        );
    }
);

const Canvas = React.memo(
    ({
         pageStructure,
         selectedElement,
         onSelectElement,
         quickEditElement,
         quickEditPosition,
         onQuickEdit,
         onCloseQuickEdit,
         onDuplicateElement,
         onDeleteElement,
         onMoveElement,
         viewMode,
         addSection,
         addElement,
         moveElement,
         ItemTypes,
         zoomLevel,
         setPageStructure,
         updatePosition,
         onInlineEdit,
     }) => {
        const canvasRef = useRef(null);
        const [dropIndex, setDropIndex] = useState(null);

        const [{ isOver }, drop] = useDrop(() => ({
            accept: [ItemTypes.SECTION],
            hover: (item, monitor) => {
                if (!monitor.isOver({ shallow: true })) return;
                const clientOffset = monitor.getClientOffset();
                if (clientOffset && canvasRef.current) {
                    const rect = canvasRef.current.getBoundingClientRect();
                    const y = clientOffset.y - rect.top;
                    const index = Math.min(pageStructure?.children?.length || 0, Math.floor(y / 200));
                    setDropIndex(index);
                }
            },
            drop: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (!clientOffset || !canvasRef.current) {
                    toast.error('Không thể xác định vị trí thả. Vui lòng thử lại!');
                    return;
                }
                if (!item.json || !item.json.styles) {
                    toast.error('Dữ liệu thả không hợp lệ!');
                    return;
                }
                const rect = canvasRef.current.getBoundingClientRect();
                const index = Math.min(pageStructure?.children?.length || 0, Math.floor((clientOffset.y - rect.top) / 200));
                addSection(
                    {
                        id: item.id,
                        json: {
                            ...item.json,
                            styles: {
                                desktop: { ...item.json.styles.desktop, width: item.json.styles.desktop.width || '1200px', minHeight: item.json.styles.desktop.minHeight || '200px' },
                                mobile: { ...item.json.styles.mobile, width: item.json.styles.mobile.width || '100%', minHeight: item.json.styles.mobile.minHeight || '150px' },
                            },
                        },
                        elements: [],
                    },
                    index
                );
                toast.success('Đã thêm section mới!');
                setDropIndex(null);
            },
            collect: (monitor) => ({
                isOver: !!monitor.isOver(),
            }),
        }));

        const handleElementHover = useCallback(
            (e, component) => {
                if (quickEditElement?.id === component.id) return;
                const rect = e.currentTarget.getBoundingClientRect();
                const canvasRect = canvasRef.current?.getBoundingClientRect() || { left: 0, top: 0 };
                onQuickEdit(component, {
                    left: (rect.left - canvasRect.left) / (zoomLevel / 100),
                    top: (rect.top - canvasRect.top + 20) / (zoomLevel / 100),
                });
            },
            [quickEditElement, onQuickEdit, zoomLevel]
        );

        const handleSelectSection = useCallback(
            (section) => {
                onSelectElement(section);
            },
            [onSelectElement]
        );

        useHotkeys(
            'delete',
            () => {
                if (selectedElement) {
                    onDeleteElement(selectedElement);
                }
            },
            [selectedElement, onDeleteElement]
        );

        if (!pageStructure || !pageStructure.children) {
            return (
                <div className="lpb-canvas-wrapper">
                    <div
                        ref={(node) => {
                            canvasRef.current = node;
                            drop(node);
                        }}
                        className={`lpb-canvas ${viewMode === 'mobile' ? 'lpb-canvas-mobile' : viewMode === 'tablet' ? 'lpb-canvas-tablet' : ''}`}
                        onMouseLeave={onCloseQuickEdit}
                        style={{
                            zoom: `${zoomLevel / 100}`,
                            position: 'relative',
                            minHeight: '100%',
                        }}
                    >
                        <div className="lpb-canvas-content">
                            <div className="lpb-canvas-empty-state">
                                <i className="fas fa-layer-group"></i>
                                <p>Kéo thả section để bắt đầu</p>
                            </div>
                        </div>
                    </div>
                </div>
            );
        }

        return (
            <div className="lpb-canvas-wrapper">
                <div
                    ref={(node) => {
                        canvasRef.current = node;
                        drop(node);
                    }}
                    className={`lpb-canvas ${viewMode === 'mobile' ? 'lpb-canvas-mobile' : viewMode === 'tablet' ? 'lpb-canvas-tablet' : ''}`}
                    onMouseLeave={onCloseQuickEdit}
                    style={{
                        zoom: `${zoomLevel / 100}`,
                        position: 'relative',
                        minHeight: '100%',
                        background: pageStructure.styles?.[viewMode]?.background || '#fff',
                    }}
                >
                    <div className="lpb-canvas-content">
                        {pageStructure.children.length === 0 ? (
                            <div className="lpb-canvas-empty-state">
                                <i className="fas fa-layer-group"></i>
                                <p>Kéo thả section để bắt đầu</p>
                            </div>
                        ) : (
                            <>
                                {pageStructure.children.map((section, index) => (
                                    <React.Fragment key={section.id}>
                                        {dropIndex === index && (
                                            <div
                                                className="lpb-canvas-drop-zone"
                                                style={{
                                                    height: '10px',
                                                    background: 'rgba(37, 99, 235, 0.1)',
                                                    border: '1px dashed #2563eb',
                                                    margin: '5px auto',
                                                    width: viewMode === 'mobile' ? '100%' : viewMode === 'tablet' ? '768px' : '1200px',
                                                }}
                                            />
                                        )}
                                        <Section
                                            key={section.id}
                                            section={section}
                                            index={index}
                                            selectedElement={selectedElement}
                                            onSelectElement={onSelectElement}
                                            handleElementHover={handleElementHover}
                                            onDuplicateElement={onDuplicateElement}
                                            onDeleteElement={onDeleteElement}
                                            onMoveElement={onMoveElement}
                                            updatePosition={updatePosition}
                                            addElement={addElement}
                                            canvasRef={canvasRef}
                                            moveElement={moveElement}
                                            onInlineEdit={onInlineEdit}
                                            onSelectSection={handleSelectSection}
                                            viewMode={viewMode}
                                            pageStructure={pageStructure}
                                            zoomLevel={zoomLevel}
                                        />
                                    </React.Fragment>
                                ))}
                                {dropIndex === pageStructure.children.length && (
                                    <div
                                        className="lpb-canvas-drop-zone"
                                        style={{
                                            height: '10px',
                                            background: 'rgba(37, 99, 235, 0.1)',
                                            border: '1px dashed #2563eb',
                                            margin: '5px auto',
                                            width: viewMode === 'mobile' ? '100%' : viewMode === 'tablet' ? '768px' : '1200px',
                                        }}
                                    />
                                )}
                            </>
                        )}
                    </div>
                    <QuickEditToolkit
                        element={quickEditElement}
                        position={quickEditPosition}
                        onEdit={() => onSelectElement(quickEditElement)}
                        onDuplicate={() => onDuplicateElement(quickEditElement)}
                        onDelete={() => onDeleteElement(quickEditElement)}
                        onMoveUp={() => onMoveElement(quickEditElement, 'up')}
                        onMoveDown={() => onMoveElement(quickEditElement, 'down')}
                        onClose={onCloseQuickEdit}
                    />
                </div>
            </div>
        );
    }
);

export default Canvas;
