import React, { useCallback } from 'react';
import '../../styles/CreateLanding.css';

const QuickEditToolkit = ({ element, position, onEdit, onDuplicate, onDelete, onMoveUp, onMoveDown, onClose }) => {
    const { id, type, isChild, hasChildren } = element;

    // Handle edit with context
    const handleEdit = useCallback(() => {
        onEdit(id, { action: 'edit' });
    }, [id, onEdit]);

    // Handle add child (for section/container/popup/modal/form)
    const handleAddChild = useCallback(() => {
        onEdit(id, { action: 'addChild' });
    }, [id, onEdit]);

    // Handle change image (for image)
    const handleChangeImage = useCallback(() => {
        onEdit(id, { action: 'changeImage' });
    }, [id, onEdit]);

    // Handle edit action (for button)
    const handleEditAction = useCallback(() => {
        onEdit(id, { action: 'editAction' });
    }, [id, onEdit]);

    // Handle edit text style (for heading/paragraph)
    const handleEditTextStyle = useCallback(() => {
        onEdit(id, { action: 'editTextStyle' });
    }, [id, onEdit]);

    // Handle edit structure (for form/popup/modal)
    const handleEditStructure = useCallback(() => {
        onEdit(id, { action: 'editStructure' });
    }, [id, onEdit]);

    // Handle duplicate
    const handleDuplicate = useCallback(() => {
        onDuplicate(id);
    }, [id, onDuplicate]);

    // Handle delete
    const handleDelete = useCallback(() => {
        if (window.confirm('Bạn có chắc muốn xóa element này?')) {
            onDelete(id);
        }
    }, [id, onDelete]);

    // Handle move up
    const handleMoveUp = useCallback(() => {
        onMoveUp(id);
    }, [id, onMoveUp]);

    // Handle move down
    const handleMoveDown = useCallback(() => {
        onMoveDown(id);
    }, [id, onMoveDown]);

    // Define buttons based on element type
    const buttons = [
        {
            icon: 'fas fa-edit',
            title: 'Chỉnh sửa',
            onClick: handleEdit,
            ariaLabel: `Chỉnh sửa ${type}`,
            showFor: ['section', 'container', 'heading', 'paragraph', 'button', 'image', 'form', 'divider', 'spacer', 'popup', 'modal'],
        },
        {
            icon: 'fas fa-copy',
            title: 'Sao chép',
            onClick: handleDuplicate,
            ariaLabel: `Sao chép ${type}`,
            showFor: ['section', 'container', 'heading', 'paragraph', 'button', 'image', 'form', 'divider', 'spacer', 'popup', 'modal'],
        },
        {
            icon: 'fas fa-trash',
            title: 'Xóa',
            onClick: handleDelete,
            className: 'text-red-500',
            ariaLabel: `Xóa ${type}`,
            showFor: ['section', 'container', 'heading', 'paragraph', 'button', 'image', 'form', 'divider', 'spacer', 'popup', 'modal'],
        },
        {
            icon: 'fas fa-arrow-up',
            title: 'Di chuyển lên',
            onClick: handleMoveUp,
            ariaLabel: `Di chuyển ${type} lên`,
            showFor: ['heading', 'paragraph', 'button', 'image', 'form', 'divider', 'spacer'],
            disabled: !isChild,
        },
        {
            icon: 'fas fa-arrow-down',
            title: 'Di chuyển xuống',
            onClick: handleMoveDown,
            ariaLabel: `Di chuyển ${type} xuống`,
            showFor: ['heading', 'paragraph', 'button', 'image', 'form', 'divider', 'spacer'],
            disabled: !isChild,
        },
        {
            icon: 'fas fa-plus',
            title: 'Thêm child',
            onClick: handleAddChild,
            ariaLabel: `Thêm child cho ${type}`,
            showFor: ['section', 'container', 'form', 'popup', 'modal'],
            disabled: !hasChildren,
        },
        {
            icon: 'fas fa-image',
            title: 'Thay đổi ảnh',
            onClick: handleChangeImage,
            ariaLabel: `Thay đổi ảnh cho ${type}`,
            showFor: ['image'],
        },
        {
            icon: 'fas fa-bolt',
            title: 'Chỉnh sửa hành động',
            onClick: handleEditAction,
            ariaLabel: `Chỉnh sửa hành động cho ${type}`,
            showFor: ['button'],
        },
        {
            icon: 'fas fa-font',
            title: 'Chỉnh sửa kiểu chữ',
            onClick: handleEditTextStyle,
            ariaLabel: `Chỉnh sửa kiểu chữ cho ${type}`,
            showFor: ['heading', 'paragraph'],
        },
        {
            icon: 'fas fa-cogs',
            title: 'Chỉnh sửa cấu trúc',
            onClick: handleEditStructure,
            ariaLabel: `Chỉnh sửa cấu trúc cho ${type}`,
            showFor: ['form', 'popup', 'modal'],
        },
        {
            icon: 'fas fa-times',
            title: 'Đóng',
            onClick: onClose,
            ariaLabel: `Đóng toolkit`,
            showFor: ['section', 'container', 'heading', 'paragraph', 'button', 'image', 'form', 'divider', 'spacer', 'popup', 'modal'],
        },
    ];

    // Filter buttons based on element type
    const filteredButtons = buttons.filter(
        (button) => button.showFor.includes(type) && (button.disabled === undefined || !button.disabled)
    );

    // Adjust position to avoid overlapping element
    const adjustedPosition = {
        x: position.x + 10, // Offset to the right
        y: position.y - 40, // Above the element
    };

    return (
        <div
            className="lpb-quick-edit-toolkit"
            style={{
                position: 'absolute',
                top: adjustedPosition.y,
                left: adjustedPosition.x,
                zIndex: 1000,
                background: '#ffffff',
                border: '1px solid #d1d5db',
                borderRadius: '8px',
                boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
                display: 'flex',
                gap: '6px',
                padding: '6px',
                fontFamily: '"Segoe UI", sans-serif',
            }}
            role="toolbar"
            aria-label={`Toolkit chỉnh sửa cho ${type}`}
        >
            {filteredButtons.map((button, index) => (
                <button
                    key={index}
                    onClick={button.onClick}
                    className={`p-2 hover:bg-gray-100 rounded transition-colors duration-200 ${button.className || 'text-gray-600'}`}
                    title={button.title}
                    aria-label={button.ariaLabel}
                    disabled={button.disabled}
                >
                    <i className={button.icon}></i>
                </button>
            ))}
        </div>
    );
};

export default QuickEditToolkit;
