import React, { useRef, useCallback, useState } from 'react';
import { useDrop, useDrag } from 'react-dnd';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';
import { ResizableBox } from 'react-resizable';
import 'react-resizable/css/styles.css';
import '../../styles/Canvas.css';

const ItemTypes = {
    ELEMENT: 'element',
    EXISTING_ELEMENT: 'existingElement',
};

const Element = React.memo(
    ({
         element,
         isSelected,
         onSelectElement,
         onUpdatePosition,
         onUpdateSize,
         viewMode,
         zoomLevel,
     }) => {
        const { id, type, componentData = {}, position = { x: 0, y: 0, z: 1 }, size = { width: 'auto', height: 'auto' }, styles = {} } = element;
        const ref = useRef(null);

        const [{ isDragging }, drag] = useDrag(() => ({
            type: ItemTypes.EXISTING_ELEMENT,
            item: { id },
            end: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                if (clientOffset) {
                    const canvasRect = document.querySelector('.lpb-canvas').getBoundingClientRect();
                    const newX = (clientOffset.x - canvasRect.left) / (zoomLevel / 100);
                    const newY = (clientOffset.y - canvasRect.top) / (zoomLevel / 100);
                    console.log('Drag - clientOffset:', clientOffset, 'canvasRect:', canvasRect, 'New Position:', { newX, newY });
                    onUpdatePosition(id, { x: newX, y: newY });
                }
            },
            collect: (monitor) => ({
                isDragging: !!monitor.isDragging(),
            }),
        }));

        const handleResize = (e, { size }) => {
            onUpdateSize(id, { width: size.width, height: size.height });
        };

        const handleClick = (e) => {
            e.stopPropagation();
            onSelectElement(id, e.ctrlKey);
        };

        const renderContent = () => {
            switch (type) {
                case 'heading':
                    return <h2 style={{ fontSize: componentData.fontSize || '1.5rem', color: componentData.color || '#1f2937' }}>{componentData.content || 'Tiêu đề'}</h2>;
                case 'paragraph':
                    return <p style={{ fontSize: componentData.fontSize || '1rem', color: componentData.color || '#1f2937' }}>{componentData.content || 'Đoạn văn'}</p>;
                case 'button':
                    return <button style={{ padding: '8px 16px', borderRadius: '8px', background: componentData.background || '#2563eb', color: componentData.color || '#fff' }}>{componentData.content || 'Nút'}</button>;
                case 'image':
                    return <img src={componentData.src || 'https://via.placeholder.com/150'} alt="Image" style={{ maxWidth: '100%', height: 'auto' }} />;
                case 'form':
                    return (
                        <form style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                            <input type="text" placeholder="Input" style={{ padding: '8px', borderRadius: '8px' }} />
                            <button type="submit" style={{ background: '#2563eb', color: '#fff', padding: '8px 16px', borderRadius: '8px' }}>Submit</button>
                        </form>
                    );
                default:
                    return <div>{componentData.content || 'Unknown'}</div>;
            }
        };

        return (
            <ResizableBox
                width={size.width === 'auto' ? 200 : size.width}
                height={size.height === 'auto' ? 50 : size.height}
                onResizeStop={handleResize}
                handleStyles={{
                    topLeft: { display: isSelected ? 'block' : 'none' },
                    topRight: { display: isSelected ? 'block' : 'none' },
                    bottomLeft: { display: isSelected ? 'block' : 'none' },
                    bottomRight: { display: isSelected ? 'block' : 'none' },
                }}
                className="lpb-canvas-element"
            >
                <div
                    ref={drag}
                    className={`lpb-canvas-element ${isSelected ? 'lpb-canvas-element-selected' : ''} ${isDragging ? 'lpb-canvas-element-dragging' : ''}`}
                    style={{
                        position: 'absolute',
                        left: position.x,
                        top: position.y,
                        zIndex: position.z,
                        width: size.width,
                        height: size.height,
                        ...styles,
                    }}
                    onClick={handleClick}
                >
                    {renderContent()}
                    {isSelected && (
                        <>
                            <div className="lpb-selection-handle lpb-selection-handle-top-left" />
                            <div className="lpb-selection-handle lpb-selection-handle-top-right" />
                            <div className="lpb-selection-handle lpb-selection-handle-bottom-left" />
                            <div className="lpb-selection-handle lpb-selection-handle-bottom-right" />
                        </>
                    )}
                </div>
            </ResizableBox>
        );
    }
);

const Canvas = React.memo(
    ({
         pageData,
         selectedIds,
         onSelectElement,
         onUpdatePosition,
         onUpdateSize,
         onDeleteElement,
         onAddElement,
         viewMode,
         zoomLevel,
     }) => {
        const canvasRef = useRef(null);
        const [guidelines, setGuidelines] = useState([]);

        const [{ isOver }, drop] = useDrop(() => ({
            accept: [ItemTypes.ELEMENT, ItemTypes.EXISTING_ELEMENT],
            hover: (item, monitor) => {
                if (!monitor.isOver({ shallow: true })) return;
                const clientOffset = monitor.getClientOffset();
                if (clientOffset && canvasRef.current) {
                    const canvasRect = canvasRef.current.getBoundingClientRect();
                    const x = (clientOffset.x - canvasRect.left) / (zoomLevel / 100);
                    const y = (clientOffset.y - canvasRect.top) / (zoomLevel / 100);
                    // Snap to grid (5px)
                    const snapX = Math.round(x / 5) * 5;
                    const snapY = Math.round(y / 5) * 5;
                    setGuidelines([
                        { vertical: true, position: snapX },
                        { vertical: false, position: snapY },
                    ]);
                }
            },
            drop: (item, monitor) => {
                const clientOffset = monitor.getClientOffset();
                console.log('Drop - clientOffset:', clientOffset, 'Item:', item);
                if (!clientOffset || !canvasRef.current) {
                    toast.error('Không thể xác định vị trí thả!');
                    return;
                }
                const canvasRect = canvasRef.current.getBoundingClientRect();
                const newX = (clientOffset.x - canvasRect.left) / (zoomLevel / 100);
                const newY = (clientOffset.y - canvasRect.top) / (zoomLevel / 100);
                const snapX = Math.round(newX / 5) * 5;
                const snapY = Math.round(newY / 5) * 5;
                console.log('Drop - canvasRect:', canvasRect, 'New Position:', { snapX, snapY });

                if (item.json) {
                    onAddElement({
                        id: `${item.id}-${Date.now()}`,
                        type: item.json.type,
                        componentData: item.json.componentData,
                        position: { x: snapX, y: snapY, z: 1 },
                        size: item.json.size || { width: 200, height: 50 },
                        styles: item.json.styles || {},
                    });
                    toast.success('Đã thêm phần tử!');
                } else if (item.id) {
                    onUpdatePosition(item.id, { x: snapX, y: snapY });
                    toast.success('Đã di chuyển phần tử!');
                }
                setGuidelines([]);
            },
            collect: (monitor) => ({
                isOver: !!monitor.isOver(),
            }),
        }));

        useHotkeys('delete', () => {
            if (selectedIds.length) {
                selectedIds.forEach((id) => onDeleteElement(id));
            }
        }, [selectedIds, onDeleteElement]);

        return (
            <div className="lpb-canvas-wrapper">
                <div
                    ref={(node) => {
                        canvasRef.current = node;
                        drop(node);
                    }}
                    className={`lpb-canvas ${viewMode === 'mobile' ? 'lpb-canvas-mobile' : viewMode === 'tablet' ? 'lpb-canvas-tablet' : ''}`}
                    style={{
                        minHeight: '100%',
                        background: pageData.canvas.background,
                    }}
                >
                    {guidelines.map((guide, idx) => (
                        <div
                            key={idx}
                            className={`lpb-guideline ${guide.vertical ? 'lpb-guideline-vertical' : 'lpb-guideline-horizontal'}`}
                            style={{ [guide.vertical ? 'left' : 'top']: guide.position }}
                        />
                    ))}
                    {pageData.elements.map((element) => (
                        <Element
                            key={element.id}
                            element={element}
                            isSelected={selectedIds.includes(element.id)}
                            onSelectElement={onSelectElement}
                            onUpdatePosition={onUpdatePosition}
                            onUpdateSize={onUpdateSize}
                            viewMode={viewMode}
                            zoomLevel={zoomLevel}
                        />
                    ))}
                    {isOver && (
                        <div
                            className="lpb-canvas-drop-zone"
                            style={{
                                width: viewMode === 'mobile' ? '100%' : viewMode === 'tablet' ? '768px' : '1200px',
                            }}
                        />
                    )}
                </div>
            </div>
        );
    }
);

export default Canvas;
