// File: src/library/popups.js
export const popups = {
    name: 'Popups',
    subCategories: [
        {
            id: 'basic-popups',
            name: 'Basic Popups',
            templates: [
                {
                    id: 'popup-1',
                    name: 'Basic Popup',
                    icon: 'fa-window-restore',
                    description: 'A simple popup',
                    previewImage: 'https://res.cloudinary.com/your_cloud_name/image/upload/v1234567890/popup.jpg',
                    json: {
                        type: 'popup',
                        componentData: {
                            title: 'Popup Title',
                            content: 'Popup Content',
                            dataSource: {
                                type: 'api',
                                url: 'https://api.example.com/popup',
                                key: 'message',
                            },
                            events: {
                                onClick: { type: 'navigate', url: '/close' },
                            },
                        },
                        size: { width: 300, height: 200 },
                        styles: { backgroundColor: '#ffffff', display: 'none' },
                    },
                },
                {
                    id: 'popup-2',
                    name: 'Basic Popup',
                    icon: 'fa-window-restore',
                    description: 'A simple popup',
                    previewImage: 'https://res.cloudinary.com/your_cloud_name/image/upload/v1234567890/popup.jpg',
                    json: {
                        type: 'popup',
                        componentData: {
                            title: 'Popup Title',
                            content: 'Popup Content',
                            dataSource: {
                                type: 'api',
                                url: 'https://api.example.com/popup',
                                key: 'message',
                            },
                            events: {
                                onClick: { type: 'navigate', url: '/close' },
                            },
                        },
                        size: { width: 300, height: 200 },
                        styles: { backgroundColor: '#ffffff', display: 'none' },
                    },
                },


                {
                    id: 'promo-popup',
                    name: 'Promo Popup',
                    icon: 'fa-gift',
                    description: 'A promotional popup with a form',
                    previewImage: 'https://res.cloudinary.com/your_cloud_name/image/upload/v1234567890/promo-popup.jpg',
                    json: {
                        type: 'popup',
                        componentData: {
                            title: 'Special Offer!',
                            content: 'Sign up for our newsletter to get 20% off.',
                            fields: [
                                { type: 'email', placeholder: 'Enter your email', label: 'Email' },
                            ],
                            buttonText: 'Claim Offer',
                            dataSource: { type: 'static' },
                            events: {
                                onSubmit: { type: 'submitForm', apiUrl: '/api/newsletter' },
                            },
                        },
                        size: { width: 350, height: 250 },
                        styles: { backgroundColor: '#ffffff', display: 'none' },
                    },
                },
            ],
        },
    ],
};
