// F:\landinghub-iconic\apps\web\src\components\Login.js
import React, { useState } from 'react';
import api from '@landinghub/api';
import { useGoogleLogin } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const navigate = useNavigate();
    const [form, setForm] = useState({ email: '', password: '' });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    // Xử lý thay đổi input
    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    // Đăng nhập bằng email & password
    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);
        try {
            const res = await api.post('/api/auth/login', form);
            localStorage.setItem('token', res.data.token);
            navigate('/dashboard');
        } catch (err) {
            setError(err.response?.data?.msg || 'Đăng nhập thất bại');
        } finally {
            setLoading(false);
        }
    };

    // Đăng nhập bằng Google
    const handleGoogleLogin = useGoogleLogin({
        onSuccess: async (tokenResponse) => {
            console.log('Google token response:', tokenResponse);

            // tokenResponse có thể chứa access_token nhưng ta cần id_token
            const res = await fetch(
                `https://www.googleapis.com/oauth2/v3/tokeninfo?id_token=${tokenResponse.id_token}`
            );
            const profile = await res.json();

            // Gửi profile + id_token lên backend
            const serverRes = await api.post('/api/auth/google/callback', {
                token: tokenResponse.id_token,
            });

            localStorage.setItem('token', serverRes.data.token);
            navigate('/dashboard');
        },
        onError: (error) => console.error('Google login failed:', error),
        flow: 'implicit',
    });


    return (
        <div style={{ padding: 24, maxWidth: 400, margin: 'auto' }}>
            <h2 style={{ textAlign: 'center', marginBottom: 20 }}>Đăng Nhập</h2>

            {error && (
                <div style={{ color: 'red', marginBottom: 12, textAlign: 'center' }}>
                    {error}
                </div>
            )}

            <form onSubmit={handleLogin}>
                <input
                    type="email"
                    name="email"
                    value={form.email}
                    onChange={handleChange}
                    placeholder="Email"
                    required
                    style={{ width: '100%', marginBottom: 12, padding: 10 }}
                />
                <input
                    type="password"
                    name="password"
                    value={form.password}
                    onChange={handleChange}
                    placeholder="Mật khẩu"
                    required
                    autoComplete="current-password"
                    style={{ width: '100%', marginBottom: 12, padding: 10 }}
                />
                <button
                    type="submit"
                    disabled={loading}
                    style={{
                        width: '100%',
                        padding: 12,
                        backgroundColor: '#1976d2',
                        color: '#fff',
                        border: 'none',
                        cursor: 'pointer',
                    }}
                >
                    {loading ? 'Đang đăng nhập...' : 'Đăng Nhập'}
                </button>
            </form>

            <hr style={{ margin: '20px 0' }} />

            <button
                onClick={handleGoogleLogin}
                style={{
                    width: '100%',
                    padding: 12,
                    backgroundColor: '#db4437',
                    color: '#fff',
                    border: 'none',
                    cursor: 'pointer',
                }}
            >
                Đăng Nhập Với Google
            </button>
        </div>
    );
};

export default Login;
