import React, { useEffect } from "react";

const ChatLD = () => {
    useEffect(() => {
        const loadCozeChat = async () => {
            try {
                // G·ªçi backend ƒë·ªÉ l·∫•y access_token
                const res = await fetch(`${process.env.REACT_APP_API_URL}/api/coze/token`, {
                    method: "POST",
                });
                const data = await res.json();

                if (!data.access_token) {
                    console.error("Kh√¥ng l·∫•y ƒë∆∞·ª£c access_token t·ª´ backend:", data);
                    return;
                }

                // T·∫°o script SDK c·ªßa Coze
                const script = document.createElement("script");
                script.src = "https://sdk.coze.com/openai.js";
                script.async = true;

                script.onload = () => {
                    // T·∫°o chatbot Coze khi SDK s·∫µn s√†ng
                    // eslint-disable-next-line no-undef
                    new CozeWebChat({
                        bot_id: process.env.REACT_APP_COZE_BOT_ID,
                        client_id: process.env.REACT_APP_COZE_CLIENT_ID,
                        token: data.access_token,
                        theme: "light", // dark ho·∫∑c light
                        position: "bottom-right",
                        title: "LD Assistant ü§ñ",
                        welcome_message: "Xin ch√†o! T√¥i l√† LD - Tr·ª£ l√Ω AI c·ªßa h·ªá th·ªëng LandingHub.",
                        layout: "bubble", // ho·∫∑c "window"
                    });
                };

                document.body.appendChild(script);
            } catch (err) {
                console.error("L·ªói khi kh·ªüi t·∫°o ChatLD:", err);
            }
        };

        loadCozeChat();
    }, []);

    return (
        <div>
            <h2 style={{ textAlign: "center", marginTop: 20 }}>
                LD Chat Assistant ƒëang s·∫µn s√†ng ü§ñ
            </h2>
        </div>
    );
};

export default ChatLD;
