import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from '../context/UserContext';
import Header from '../components/Header';
import Sidebar from '../components/Sidebar';
import { jwtDecode } from 'jwt-decode';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import InfiniteScroll from 'react-infinite-scroll-component';
import AOS from 'aos';
import 'aos/dist/aos.css';
import '../styles/Marketplace.css';
import DogLoader from '../components/Loader';

const Marketplace = () => {
    const { user } = useContext(UserContext);
    const [loading, setLoading] = useState(true);
    const [userRole, setUserRole] = useState(null);
    const [pages, setPages] = useState([]);
    const [error, setError] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('all');
    const [searchQuery, setSearchQuery] = useState('');
    const [sortBy, setSortBy] = useState('newest');
    const [priceRange, setPriceRange] = useState({ min: '', max: '' });
    const [hasMore, setHasMore] = useState(true);
    const [page, setPage] = useState(1);
    const [featuredPages, setFeaturedPages] = useState([]);
    const [bestsellers, setBestsellers] = useState([]);

    const navigate = useNavigate();
    const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

    const categories = [
        { value: 'all', label: 'T�t c�' },
        { value: 'Th��ng m�i i�n t�', label: 'Th��ng m�i i�n t�' },
        { value: 'Landing Page', label: 'Landing Page' },
        { value: 'Blog', label: 'Blog' },
        { value: 'Portfolio', label: 'Portfolio' },
        { value: 'Doanh nghi�p', label: 'Doanh nghi�p' },
        { value: 'Gi�o d�c', label: 'Gi�o d�c' },
        { value: 'S� ki�n', label: 'S� ki�n' },
        { value: 'B�t �ng s�n', label: 'B�t �ng s�n' },
        { value: '�m th�c', label: '�m th�c' },
        { value: 'Du l�ch', label: 'Du l�ch' },
        { value: 'Y t�', label: 'Y t�' },
        { value: 'Th�i trang', label: 'Th�i trang' },
        { value: 'Kh�c', label: 'Kh�c' }
    ];

    const sortOptions = [
        { value: 'newest', label: 'M�i nh�t' },
        { value: 'oldest', label: 'Ci nh�t' },
        { value: 'price_low', label: 'Gi� th�p �n cao' },
        { value: 'price_high', label: 'Gi� cao �n th�p' },
        { value: 'popular', label: 'Ph� bi�n nh�t' },
        { value: 'bestseller', label: 'B�n ch�y nh�t' },
        { value: 'rating', label: '�nh gi� cao nh�t' }
    ];

    useEffect(() => {
        const initializeAuth = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                navigate('/auth');
                setLoading(false);
                return;
            }
            try {
                const decodedToken = jwtDecode(token);
                if (!decodedToken.role || !decodedToken.userId) {
                    navigate('/auth');
                    setLoading(false);
                    return;
                }
                setUserRole(decodedToken.role);
            } catch (err) {
                console.error('L�i gi�i m� token:', err);
                navigate('/auth');
            } finally {
                setLoading(false);
            }
        };

        if (user?.role) {
            setUserRole(user.role);
            setLoading(false);
        } else {
            initializeAuth();
        }
    }, [user, navigate]);

    useEffect(() => {
        AOS.init({ duration: 600, once: true, offset: 100 });
    }, []);

    useEffect(() => {
        if (userRole) {
            loadFeaturedPages();
            loadBestsellers();
        }
    }, [userRole]);

    useEffect(() => {
        if (userRole) {
            setPages([]);
            setPage(1);
            setHasMore(true);
            loadPages(1);
        }
    }, [userRole, selectedCategory, sortBy]);

    const loadFeaturedPages = async () => {
        try {
            const response = await axios.get(`${API_BASE_URL}/api/marketplace/featured/list?limit=5`);
            setFeaturedPages(response.data.data || []);
        } catch (err) {
            console.error('L�i t�i featured pages:', err);
        }
    };

    const loadBestsellers = async () => {
        try {
            const response = await axios.get(`${API_BASE_URL}/api/marketplace/bestsellers/list?limit=5`);
            setBestsellers(response.data.data || []);
        } catch (err) {
            console.error('L�i t�i bestsellers:', err);
        }
    };

    const loadPages = async (pageNum = 1) => {
        try {
            setLoading(pageNum === 1);
            const params = new URLSearchParams({
                page: pageNum,
                limit: 12,
                sort: sortBy
            });

            if (selectedCategory !== 'all') {
                params.append('category', selectedCategory);
            }

            if (searchQuery) {
                params.append('search', searchQuery);
            }

            if (priceRange.min) {
                params.append('price_min', priceRange.min);
            }

            if (priceRange.max) {
                params.append('price_max', priceRange.max);
            }

            const response = await axios.get(`${API_BASE_URL}/api/marketplace?${params}`);

            const newPages = response.data.data || [];
            setPages(prev => pageNum === 1 ? newPages : [...prev, ...newPages]);
            setHasMore(newPages.length === 12);
            setError('');
        } catch (err) {
            console.error('L�i t�i marketplace pages:', err);
            setError('Kh�ng th� t�i marketplace: ' + (err.response?.data?.message || err.message));
        } finally {
            setLoading(false);
        }
    };

    const loadMore = () => {
        if (!loading && hasMore) {
            const nextPage = page + 1;
            setPage(nextPage);
            loadPages(nextPage);
        }
    };

    const handleSearch = (e) => {
        e.preventDefault();
        setPages([]);
        setPage(1);
        setHasMore(true);
        loadPages(1);
    };

    const handleViewDetail = (pageId) => {
        navigate(`/marketplace/${pageId}`);
    };

    const formatPrice = (price) => {
        if (price === 0) return 'Mi�n ph�';
        return new Intl.NumberFormat('vi-VN', {
            style: 'currency',
            currency: 'VND'
        }).format(price);
    };

    const calculateDiscount = (price, originalPrice) => {
        if (!originalPrice || originalPrice <= price) return 0;
        return Math.round(((originalPrice - price) / originalPrice) * 100);
    };

    if (loading && page === 1) {
        return <DogLoader />;
    }

    return (
        <div className="marketplace-container">
            <Sidebar userRole={userRole} />
            <div className="marketplace-main">
                <Header />
                <div className="marketplace-content">
                    {/* Hero Section */}
                    <div className="marketplace-hero" data-aos="fade-down">
                        <h1> Marketplace Landing Page</h1>
                        <p>Kh�m ph� v� mua c�c landing page ch�t l��ng cao t� c�ng �ng</p>
                    </div>

                    {/* Search and Filters */}
                    <div className="marketplace-filters" data-aos="fade-up">
                        <form onSubmit={handleSearch} className="search-bar">
                            <input
                                type="text"
                                placeholder="T�m ki�m landing page..."
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                            />
                            <button type="submit">=
                                T�m ki�m</button>
                        </form>

                        <div className="filter-row">
                            <select
                                value={selectedCategory}
                                onChange={(e) => setSelectedCategory(e.target.value)}
                                className="filter-select"
                            >
                                {categories.map(cat => (
                                    <option key={cat.value} value={cat.value}>
                                        {cat.label}
                                    </option>
                                ))}
                            </select>

                            <select
                                value={sortBy}
                                onChange={(e) => setSortBy(e.target.value)}
                                className="filter-select"
                            >
                                {sortOptions.map(opt => (
                                    <option key={opt.value} value={opt.value}>
                                        {opt.label}
                                    </option>
                                ))}
                            </select>

                            <div className="price-range">
                                <input
                                    type="number"
                                    placeholder="Gi� t�i thi�u"
                                    value={priceRange.min}
                                    onChange={(e) => setPriceRange({ ...priceRange, min: e.target.value })}
                                />
                                <span>-</span>
                                <input
                                    type="number"
                                    placeholder="Gi� t�i a"
                                    value={priceRange.max}
                                    onChange={(e) => setPriceRange({ ...priceRange, max: e.target.value })}
                                />
                                <button onClick={handleSearch}>�p d�ng</button>
                            </div>
                        </div>
                    </div>

                    {/* Featured Pages */}
                    {featuredPages.length > 0 && (
                        <div className="featured-section" data-aos="fade-up">
                            <h2>P N�i b�t</h2>
                            <div className="featured-grid">
                                {featuredPages.map((page) => (
                                    <div key={page._id} className="featured-card" onClick={() => handleViewDetail(page._id)}>
                                        <div className="featured-image">
                                            <img src={page.main_screenshot || '/placeholder.png'} alt={page.title} />
                                            {calculateDiscount(page.price, page.original_price) > 0 && (
                                                <div className="discount-badge">
                                                    -{calculateDiscount(page.price, page.original_price)}%
                                                </div>
                                            )}
                                        </div>
                                        <div className="featured-info">
                                            <h3>{page.title}</h3>
                                            <div className="featured-price">
                                                <span className="current-price">{formatPrice(page.price)}</span>
                                                {page.original_price && (
                                                    <span className="original-price">{formatPrice(page.original_price)}</span>
                                                )}
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    )}

                    {/* Error Message */}
                    {error && (
                        <div className="error-message" data-aos="fade-in">
                            {error}
                        </div>
                    )}

                    {/* Main Grid */}
                    <div className="marketplace-grid-section" data-aos="fade-up">
                        <h2>=� T�t c� Landing Page</h2>
                        <InfiniteScroll
                            dataLength={pages.length}
                            next={loadMore}
                            hasMore={hasMore}
                            loader={<div className="loader">ang t�i...</div>}
                            endMessage={
                                pages.length > 0 && (
                                    <p style={{ textAlign: 'center', marginTop: '20px', color: '#666' }}>
                                        � hi�n th� t�t c� landing page
                                    </p>
                                )
                            }
                        >
                            <div className="marketplace-grid">
                                {pages.map((page) => (
                                    <div key={page._id} className="marketplace-card" data-aos="zoom-in">
                                        <div className="card-image" onClick={() => handleViewDetail(page._id)}>
                                            <img src={page.main_screenshot || '/placeholder.png'} alt={page.title} />
                                            {page.is_bestseller && (
                                                <div className="bestseller-badge">=% B�n ch�y</div>
                                            )}
                                            {calculateDiscount(page.price, page.original_price) > 0 && (
                                                <div className="discount-badge">
                                                    -{calculateDiscount(page.price, page.original_price)}%
                                                </div>
                                            )}
                                        </div>
                                        <div className="card-content">
                                            <div className="card-category">{page.category}</div>
                                            <h3 className="card-title">{page.title}</h3>
                                            <p className="card-description">
                                                {page.description.substring(0, 100)}
                                                {page.description.length > 100 ? '...' : ''}
                                            </p>
                                            <div className="card-meta">
                                                <span>=A {page.views}</span>
                                                <span>d {page.likes}</span>
                                                <span>P {page.rating.toFixed(1)}</span>
                                                <span>=� {page.sold_count}</span>
                                            </div>
                                            <div className="card-footer">
                                                <div className="card-price">
                                                    <span className="current-price">{formatPrice(page.price)}</span>
                                                    {page.original_price && (
                                                        <span className="original-price">{formatPrice(page.original_price)}</span>
                                                    )}
                                                </div>
                                                <button
                                                    className="view-detail-btn"
                                                    onClick={() => handleViewDetail(page._id)}
                                                >
                                                    Xem chi ti�t
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </InfiniteScroll>

                        {pages.length === 0 && !loading && (
                            <div className="empty-state">
                                <p>Không tônf taij landing page nào</p>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Marketplace;
