import React, { useState, useCallback, useMemo } from 'react';
import { useDrag } from 'react-dnd';
import { useHotkeys } from 'react-hotkeys-hook';
import { toast } from 'react-toastify';

const ItemTypes = {
    SECTION: 'section',
    ELEMENT: 'element',
};

const DraggableComponent = React.memo(({ item, isSection = false, onDrag, onDragEnd, onSelect }) => {
    const dragType = isSection ? ItemTypes.SECTION : ItemTypes.ELEMENT;
    const [isSelected, setIsSelected] = useState(false);

    const [{ isDragging }, drag] = useDrag(() => ({
        type: dragType,
        item: () => {
            const newItem = { ...item, id: `${item.id}-${Date.now()}` }; // Gán id duy nhất khi kéo
            onDrag?.();
            setIsSelected(true);
            onSelect?.(newItem.id);
            return newItem;
        },
        end: (item, monitor) => {
            onDragEnd?.();
            if (!monitor.didDrop()) {
                setIsSelected(false);
            }
        },
        collect: (monitor) => ({
            isDragging: !!monitor.isDragging(),
        }),
    }));

    const handleClick = useCallback(
        (e) => {
            e.stopPropagation();
            setIsSelected(true);
            onSelect?.(item.id);
        },
        [item.id, onSelect]
    );

    const renderPreview = () => {
        const { type = 'unknown', content, styles = {}, isContainer = false } = item.json || {};
        const styleProps = {
            ...styles,
            opacity: isDragging ? 0.5 : 1,
            margin: '5px',
            maxWidth: '100%',
            boxSizing: 'border-box',
            padding: isContainer ? '10px' : '0',
            border: '1px solid #ddd',
            borderRadius: '5px',
            position: 'relative',
            outline: isSelected ? '2px solid #4a90e2' : 'none', // Highlight khi chọn
        };

        const getTemplateClass = () => {
            switch (type) {
                case 'form':
                case 'contact-form':
                    return 'lpb-template-form';
                case 'gallery':
                case 'carousel':
                    return 'lpb-template-gallery';
                case 'pricing':
                    return 'lpb-template-pricing';
                case 'testimonials':
                    return 'lpb-template-testimonials';
                case 'faq':
                    return 'lpb-template-faq';
                default:
                    return '';
            }
        };

        if (isContainer) {
            switch (type) {
                case 'form':
                    return (
                        <div
                            ref={drag}
                            data-id={item.id}
                            style={styleProps}
                            className={`lpb-template-item ${getTemplateClass()}`}
                            onClick={handleClick}
                        >
                            <form style={{ width: '100%' }}>
                                {(content?.fields || []).map((field, index) => (
                                    <input
                                        key={index}
                                        type="text"
                                        placeholder={field || `Trường ${index + 1}`}
                                        style={{ marginBottom: '8px', width: '100%', ...styles }}
                                    />
                                ))}
                                <button type="submit" style={{ ...styles, marginTop: '8px' }}>Submit</button>
                            </form>
                        </div>
                    );
                case 'button-group':
                    return (
                        <div
                            ref={drag}
                            data-id={item.id}
                            style={styleProps}
                            className={`lpb-template-item ${getTemplateClass()}`}
                            onClick={handleClick}
                        >
                            <div style={{ display: 'flex', gap: '10px', width: '100%' }}>
                                {(content || []).map((btn, index) => (
                                    <button key={index} style={{ ...styles, marginRight: index < content.length - 1 ? '10px' : 0 }}>
                                        {btn || `Nút ${index + 1}`}
                                    </button>
                                ))}
                            </div>
                        </div>
                    );
                case 'gallery':
                    return (
                        <div
                            ref={drag}
                            data-id={item.id}
                            style={styleProps}
                            className={`lpb-template-item ${getTemplateClass()}`}
                            onClick={handleClick}
                        >
                            <div style={{ display: 'flex', gap: '10px', width: '100%' }}>
                                {(content || []).map((src, index) => (
                                    <img
                                        key={index}
                                        src={src || 'https://via.placeholder.com/100'}
                                        alt={`Gallery ${index}`}
                                        style={{ width: '100px', ...styles }}
                                    />
                                ))}
                            </div>
                        </div>
                    );
                default:
                    return (
                        <div
                            ref={drag}
                            data-id={item.id}
                            style={styleProps}
                            className={`lpb-template-item ${getTemplateClass()}`}
                            onClick={handleClick}
                        >
                            {item.name || 'Unknown Component'}
                        </div>
                    );
            }
        }

        switch (type) {
            case 'section':
                return (
                    <div
                        ref={drag}
                        data-id={item.id}
                        style={styleProps}
                        className={`lpb-template-item ${getTemplateClass()}`}
                        onClick={handleClick}
                    >
                        <h3>{content?.title || 'Section'}</h3>
                        {content?.subtitle && <p>{content.subtitle}</p>}
                    </div>
                );
            case 'heading':
                return (
                    <div
                        ref={drag}
                        data-id={item.id}
                        style={styleProps}
                        className={`lpb-template-item ${getTemplateClass()}`}
                        onClick={handleClick}
                    >
                        <h2>{content || 'Tiêu đề'}</h2>
                    </div>
                );
            case 'paragraph':
                return (
                    <div
                        ref={drag}
                        data-id={item.id}
                        style={styleProps}
                        className={`lpb-template-item ${getTemplateClass()}`}
                        onClick={handleClick}
                    >
                        <p>{content || 'Đoạn văn'}</p>
                    </div>
                );
            case 'button':
                return (
                    <div
                        ref={drag}
                        data-id={item.id}
                        style={styleProps}
                        className={`lpb-template-item ${getTemplateClass()}`}
                        onClick={handleClick}
                    >
                        <button style={styles}>{content || 'Nút'}</button>
                    </div>
                );
            case 'image':
                return (
                    <div
                        ref={drag}
                        data-id={item.id}
                        style={styleProps}
                        className={`lpb-template-item ${getTemplateClass()}`}
                        onClick={handleClick}
                    >
                        <img
                            src={content || 'https://via.placeholder.com/150'}
                            alt={item.name || 'Image'}
                            style={{ ...styles, maxWidth: '100%', height: 'auto' }}
                        />
                    </div>
                );
            default:
                return (
                    <div
                        ref={drag}
                        data-id={item.id}
                        style={styleProps}
                        className={`lpb-template-item ${getTemplateClass()}`}
                        onClick={handleClick}
                    >
                        {item.name || 'Unknown Component'}
                    </div>
                );
        }
    };

    return renderPreview();
});

export default DraggableComponent;
