import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEllipsisV } from '@fortawesome/free-solid-svg-icons';
import '../../styles/PageContent.css'
const LandingList = ({
  landingPages,
  filteredPages,
  menuOpen,
  setMenuOpen,
  handlePreviewPage,
  handleEditPage,
  handlePublishPage,
  handleDeletePage,
}) => {
  const handleMenuClick = (pageId, event) => {
    event.stopPropagation();
    setMenuOpen(menuOpen === pageId ? null : pageId);
  };

  return (
    <div className="landing-list">
      <table>
        <thead>
          <tr>
            <th>Tên</th>
            <th>Trạng thái</th>
            <th>Lượt xem</th>
            <th>Chuyển đổi</th>
            <th>Doanh thu</th>
            <th>Ngày tạo</th>
            <th>Ngày cập nhật</th>
            <th>Hành động</th>
          </tr>
        </thead>
        <tbody>
          {filteredPages.map((page) => (
            <tr key={page.id} onClick={() => handleEditPage(page)}>
              <td>{page.name}</td>
              <td>
                <span style={{ color: page.statusColor }}>{page.status}</span>
              </td>
              <td>{page.views}</td>
              <td>{page.conversions}</td>
              <td>{page.revenue}</td>
              <td>{page.created_at}</td>
              <td>{page.lastUpdated}</td>
              <td className="actions">
                <button
                  className="menu-button"
                  onClick={(e) => handleMenuClick(page.id, e)}
                >
                  <FontAwesomeIcon icon={faEllipsisV} />
                </button>
                {menuOpen === page.id && (
                  <div className="menu-dropdown">
                    <button onClick={() => handlePreviewPage(page)}>Xem trước</button>
                    <button onClick={() => handleEditPage(page)}>Chỉnh sửa</button>
                    <button onClick={() => handlePublishPage(page.id)}>Triển khai</button>
                    <button onClick={() => handleDeletePage(page.id)}>Xóa</button>
                  </div>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default LandingList;
