import React, { useEffect, useState } from "react";
import { MessageCircle, X } from "lucide-react";

const ChatLD = ({ enabled = true }) => {
    const [open, setOpen] = useState(false);
    const [loaded, setLoaded] = useState(false);

    useEffect(() => {
        if (!enabled) return;

        const initChat = async () => {
            try {
                // L·∫•y access_token t·ª´ backend
                const tokenRes = await fetch(`${process.env.REACT_APP_API_URL}/api/coze/token`, {
                    method: "POST",
                });
                const data = await tokenRes.json();

                if (!data.access_token) {
                    console.error("Kh√¥ng l·∫•y ƒë∆∞·ª£c access_token t·ª´ backend:", data);
                    return;
                }

                // T·∫£i SDK Coze
                const script = document.createElement("script");
                script.src = "https://sdk.coze.com/openai.js";
                script.async = true;
                script.onload = () => setLoaded(true);
                document.body.appendChild(script);

                // Khi script ƒë√£ s·∫µn s√†ng, kh·ªüi t·∫°o bot khi m·ªü chat
            } catch (err) {
                console.error("L·ªói khi kh·ªüi t·∫°o ChatLD:", err);
            }
        };

        initChat();
    }, [enabled]);

    useEffect(() => {
        if (!open || !loaded) return;

        // eslint-disable-next-line no-undef
        new CozeWebChat({
            bot_id: process.env.REACT_APP_COZE_BOT_ID,
            client_id: process.env.REACT_APP_COZE_CLIENT_ID,
            token: "public", // Kh√¥ng c·∫ßn n·∫øu CozeWebChat t·ª± x·ª≠ l√Ω token
            layout: "window",
            theme: "light",
            position: "bottom-right",
            title: "LD Assistant ü§ñ",
            welcome_message: "Xin ch√†o! T√¥i l√† LD - Tr·ª£ l√Ω AI c·ªßa h·ªá th·ªëng LandingHub.",
        });
    }, [open, loaded]);

    return (
        <>
            {/* Icon chat tr√≤n g√≥c ph·∫£i */}
            {!open && enabled && (
                <button
                    onClick={() => setOpen(true)}
                    style={{
                        position: "fixed",
                        bottom: "24px",
                        right: "24px",
                        width: "56px",
                        height: "56px",
                        borderRadius: "50%",
                        backgroundColor: "#2563eb",
                        color: "#fff",
                        border: "none",
                        boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
                        cursor: "pointer",
                        zIndex: 10000,
                    }}
                >
                    <MessageCircle size={28} />
                </button>
            )}

            {/* Khung chat */}
            {open && (
                <div
                    style={{
                        position: "fixed",
                        bottom: "24px",
                        right: "24px",
                        width: "400px",
                        height: "550px",
                        backgroundColor: "#fff",
                        borderRadius: "16px",
                        boxShadow: "0 8px 24px rgba(0,0,0,0.2)",
                        zIndex: 10000,
                        overflow: "hidden",
                        display: "flex",
                        flexDirection: "column",
                    }}
                >
                    <div
                        style={{
                            padding: "12px 16px",
                            backgroundColor: "#2563eb",
                            color: "white",
                            fontWeight: "bold",
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "space-between",
                        }}
                    >
                        <span>LD Assistant ü§ñ</span>
                        <X
                            size={22}
                            onClick={() => setOpen(false)}
                            style={{ cursor: "pointer" }}
                        />
                    </div>

                    {/* Frame chat Coze */}
                    <iframe
                        title="LD Chat"
                        src={`https://www.coze.com/chatbot/${process.env.REACT_APP_COZE_BOT_ID}`}
                        style={{
                            width: "100%",
                            height: "100%",
                            border: "none",
                            flexGrow: 1,
                        }}
                    ></iframe>
                </div>
            )}
        </>
    );
};

export default ChatLD;
