const EXTENDED_LIBRARY = {
    sections: {
        name: 'Sections',
        icon: 'fas fa-layer-group',
        subCategories: [
            {
                id: 'basic',
                name: 'Basic Sections',
                templates: [
                    {
                        id: 'section-1',
                        name: 'Section 1',
                        json: {
                            type: 'section',
                            content: { title: 'Section 1', subtitle: 'Subtitle' },
                            styles: { width: '1200px', height: 'auto', background: '#f9f9f9' },
                        },
                    },
                ],
            },
        ],
    },
    elements: {
        name: 'Elements',
        icon: 'fas fa-cube',
        subCategories: [
            {
                id: 'basic',
                name: 'Basic Elements',
                templates: [
                    {
                        id: 'heading',
                        name: 'Heading',
                        json: {
                            type: 'heading',
                            content: 'Tiêu đề',
                            styles: { fontSize: '24px', color: '#333' },
                        },
                    },
                    {
                        id: 'paragraph',
                        name: 'Paragraph',
                        json: {
                            type: 'paragraph',
                            content: 'Đoạn văn bản',
                            styles: { fontSize: '16px', color: '#666' },
                        },
                    },
                    {
                        id: 'button',
                        name: 'Button',
                        json: {
                            type: 'button',
                            content: 'Nút',
                            styles: { padding: '10px 20px', background: '#007bff', color: '#fff' },
                        },
                    },
                ],
            },
            {
                id: 'complex',
                name: 'Complex Elements',
                templates: [
                    {
                        id: 'form',
                        name: 'Form',
                        json: {
                            type: 'form',
                            isContainer: true,
                            content: { fields: ['Name', 'Email'] },
                            styles: {
                                width: '300px',
                                height: 'auto',
                                background: '#fff',
                                padding: '15px',
                                border: '1px solid #ddd',
                            },
                        },
                    },
                    {
                        id: 'button-group',
                        name: 'Button Group',
                        json: {
                            type: 'button-group',
                            isContainer: true,
                            content: ['Button 1', 'Button 2'],
                            styles: {
                                width: '300px',
                                height: 'auto',
                                display: 'flex',
                                gap: '10px',
                            },
                        },
                    },
                    {
                        id: 'gallery',
                        name: 'Gallery',
                        json: {
                            type: 'gallery',
                            isContainer: true,
                            content: ['https://via.placeholder.com/100', 'https://via.placeholder.com/100'],
                            styles: {
                                width: '220px',
                                height: 'auto',
                                display: 'flex',
                                gap: '10px',
                            },
                        },
                    },
                ],
            },
        ],
    },
    // Thêm các category khác nếu cần
};

export default EXTENDED_LIBRARY;
